/* Tabla creature_template */
DELETE FROM creature_template WHERE entry = "30174" OR entry = "30175";
UPDATE creature_template SET unit_flags = '8', vehicleid = '0' WHERE entry = "30174";

/* Tabla creature */
DELETE FROM creature WHERE id = '30174';
