UPDATE `db_script_string` SET `content_loc7`='Mañana vamos a comenzar el entrenamiento de nuestros dragones prometedores, así que no olvides tu juguetes para masticar.' WHERE entry=2000005028;
UPDATE `db_script_string` SET `content_loc7`='Nuestra más antigua garra de dragones está aún muy lejos de la madurez, pero con paciencia y estudio, estamos seguros de que los dragones pronto estarán listos.' WHERE entry=2000005029;
