UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Eh, $Gciudadano:ciudadana;! Pareces fuerte. Nosotros, los guardias, no tenemos una presencia demasiado densa ahí fuera, podría necesitar tu ayuda...' WHERE `entry` = 197;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $N', `text0_1_loc7` = 'Saludos, $N' WHERE `entry` = 328;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $N', `text0_1_loc7` = 'Saludos, $N' WHERE `entry` = 384;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El director Riddlevox me encargó ocuparme del programa de desarrollo de la trituradora del sindicato. ¡Llevo pocos meses en este trabajo y ya me he metido en un buen lío! ¡Tu ayuda me vendría muy bien, $N!' WHERE `entry` = 519;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mira, la nueva trituradora de Ventura y Compañía es mejor que la que tenía antes, ¡desde las manivelas intercambiables hasta los controles! Mi jefe me ha dado un plazo muy estricto, pero ni a mis chicos ni a mí se nos ocurría nada. $B$BNo funcionaba nada, así que decidimos \"coger prestado\" uno de sus prototipos. Envié a Rabiaire al Fangal a robar uno, ¡pero todavía no ha regresado!' WHERE `entry` = 520;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mataste a Aullido Fantasma.  ¡Eres un pendenciero!' WHERE `entry` = 521;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buenas, $C. ¿Quieres que te enseñe más secretos del mundo de la magia?', `text0_1_loc7` = 'Buenas, $C. ¿Quieres que te enseñe más secretos del mundo de la magia?' WHERE `entry` = 538;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $C. Mi consejo para ti es este: cuando viajes por el mundo, ten cuidado con la magia porque puede acabar con el profano.', `text0_1_loc7` = 'Hola, $C. Mi consejo para ti es este: cuando viajes por el mundo, ten cuidado con la magia porque puede acabar con el profano.' WHERE `entry` = 539;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos. Soy un instructor de magos, y tú eres $gun mago:una maga;.', `text0_1_loc7` = 'Saludos. Soy un instructor de magos, y tú eres $gun mago:una maga;.' WHERE `entry` = 558;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bien hallado, $C. Mi consejo para ti es este: cuando viajes por el mundo sé precavido con la magia porque puede acabar con el profano.', `text0_1_loc7` = 'Bien hallado, $C. Mi consejo para ti es este: cuando viajes por el mundo sé precavido con la magia porque puede acabar con el profano.' WHERE `entry` = 559;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos. Soy un mago instructor para gnomos, y tú eres $gun mago:una maga;.', `text0_1_loc7` = 'Saludos. Soy un instructor para gnomos mago, y tú eres $gun mago:una maga;.' WHERE `entry` = 560;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $C. ¿Quieres un consejo? ¡Traba amistad con un mago! Tenemos algunos trucos en la manga, hacemos...', `text0_1_loc7` = 'Saludos, $C. ¿Quieres un consejo? ¡Traba amistad con un mago! Tenemos algunos trucos en la manga, hacemos...' WHERE `entry` = 561;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos. Soy un instructor de magos no-muertos y tú eres $Gun mago:una maga;.', `text0_1_loc7` = 'Saludos. Soy una instructora de magos no-muertos y tú eres $Gun mago:una maga;.' WHERE `entry` = 562;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $C. Mi consejo para ti es este: cuando viajes por el mundo, ten cuidado con la magia porque puede acabar con el profano.', `text0_1_loc7` = 'Saludos, $C. Mi consejo para ti es este: cuando viajes por el mundo, ten cuidado con la magia porque puede acabar con el profano.' WHERE `entry` = 563;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. ¿Has venido para aprender todo sobre las artes ocultas?', `text0_1_loc7` = 'Saludos, $C. ¿Has venido para aprender todo sobre las artes ocultas?' WHERE `entry` = 564;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Compadezco a los de tu clase porque los secretos de la magia verdadera están fuera de vuestro alcance...', `text0_1_loc7` = 'Saludos, $C. Compadezco a los de tu clase porque los secretos de la magia verdadera están fuera de vuestro alcance...' WHERE `entry` = 565;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aún no te ha llegado la hora. Te ayudaré durante tu regreso al reino de los vivos... por un precio.', `text0_1_loc7` = 'Aún no te ha llegado la hora. Te ayudaré durante tu regreso al reino de los vivos... por un precio.' WHERE `entry` = 580;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debido a la autorización de Lady Sylvanas, el número de mortacechadores crece. Más espadas que clavar a nuestros enemigos cuando menos se lo esperan.', `text0_1_loc7` = 'Debido a la autorización de Lady Sylvanas, el número de mortacechadores crece. Más espadas que clavar a nuestros enemigos cuando menos se lo esperan.' WHERE `entry` = 581;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué deseas, $C? Llego tarde y a mi jefe no le gusta nada la impuntualidad.' WHERE `entry` = 623;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si, lo soy. ¿Te importa?' WHERE `entry` = 624;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mano destrozada verá la Horda en su antigua gloria, libre de la vergüenza de los Jefes de Guerra', `text0_1_loc7` = 'Mano destrozada verá la Horda en su antigua gloria, libre de la vergüenza de los Jefes de Guerra' WHERE `entry` = 638;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué hace un joven $c como tú visitando a un viejo campesino como yo?' WHERE `entry` = 678;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Naralex duerme nuevamente!$B$BHace tiempo, Naralex vino hasta esta caverna con el honorable fin de entrar en el Sueño Esmeralda y convertir los Baldíos en un bosque frondoso. Se le nubló la vista y sus pensamientos dieron paso a visiones serpenteantes.$B$BAhora, bestias reptiles descienden de sus sueños para penetrar en la tierra. Hay que despertarlo de la pesadilla o lo perderemos para siempre y se desatará un mal terrible.$B$BSolo después de matar a sus corruptos sirvientes, los Nobles del Colmillo, podré iniciar el ritual del despertar.' WHERE `entry` = 698;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Los Nobles del Colmillo están muertos! ¡No se han perdido todas las esperanzas!$B$BNo obstante, el ritual del despertar es una tarea compleja. No puedo hacerlo solo.$B$BMe ocuparé de todos los preparativos necesarios, pero necesito que me protejas. Las pesadillas invocadas desde el viaje de Naralex por el Sueño Esmeralda han demostrado ser formidables. Sin tu ayuda, esta misión está condenada al fracaso.$B$BAvísame cuando estés $Glisto:lista; para defenderme y yo te conduciré hacia la cámara de Naralex.' WHERE `entry` = 699;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sal de aquí, $C, o también te encadenarán a ti.' WHERE `entry` = 761;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Marlgen... uno de los carceleros. Él tiene la llave de mis grilletes.' WHERE `entry` = 762;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Banco de Ventormenta está ubicado en el Distrito de Mercaderes, exactamente al noroeste de las puertas de la ciudad. Mira, te lo muestro en el mapa.', `text0_1_loc7` = 'El Banco de Ventormenta está ubicado en el Distrito de Mercaderes, exactamente al noroeste de las puertas de la ciudad. Mira, te lo muestro en el mapa.', `text1_0_loc7` = 'La contaduría de Ventormenta se encuentra junto a las puertas principales del Distrito de los Mercaderes de Ventormenta. Y si te cansas de contar tu dinero no olvides visitar la posada la Rosa Áurea para tomar un trago.', `text1_1_loc7` = 'La contaduría de Ventormenta se encuentra junto a las puertas principales del Distrito de los Mercaderes de Ventormenta. Y si te cansas de contar tu dinero no olvides visitar la posada la Rosa Áurea para tomar un trago.' WHERE `entry` = 764;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estúpida nieve... ¡Eh!,¿ tu eres, $n, verdad? Acabo de recibir un mensaje para a ti.' WHERE `entry` = 767;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Has sido convocado por Yago Llavenegra, maestro hechicero de Forjaz. Harias bien en responderle inmediatamente. Lo encontrarás en el distrito  La Caverna Abandonada de la ciudad.' WHERE `entry` = 768;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $R, soy Tharnariun Espinarbolado.' WHERE `entry` = 769;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, ¿te refieres a Johnson? He oído que está escondido.', `text0_1_loc7` = 'Ah, ¿te refieres a Johnson? He oído que está escondido.', `text1_0_loc7` = '¡Ah! Tirth, le he estado buscando durante días, pero se ve que no tiene ganas de que le encuentren. ¡Menudo pollo es!', `text1_1_loc7` = '¡Ah! Tirth, le he estado buscando durante días, pero se ve que no tiene ganas de que le encuentren. ¡Menudo pollo es!', `text2_0_loc7` = 'Bueno, no me oyó decir esto, pero... quizá si se le preguntas a Kronkpiloto encima de en la torre, él te lo dirá...', `text2_1_loc7` = 'Bueno, no me oyó decir esto, pero... quizá si se le preguntas a Kronkpiloto encima de en la torre, él te lo dirá...' WHERE `entry` = 779;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace unos días que Tirth no es el mismo, ni tampoco su asistente.', `text0_1_loc7` = 'Hace unos días que Tirth no es el mismo, ni tampoco su asistente.', `text1_0_loc7` = '¿Johnson? Creo que anda por aquí en alguna parte...', `text1_1_loc7` = '¿Johnson? Creo que anda por aquí en alguna parte...', `text2_0_loc7` = '¿\"desplumado\"? Escucé al Maestro del circuito Kronkpiloto encima de la torre que el sabia donde está.', `text2_1_loc7` = '¿\"desplumado\"? Escucé al Maestro del circuito Kronkpiloto encima de la torre que el sabia donde está.' WHERE `entry` = 781;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Al fin! ¡Al fin he salido de esta celda!$B$BEl Kirin Tor me envió desde Dalaran para investigar los avances de Arugal con los no-muertos aquí en el Bosque de Argénteos. No teníamos ni idea de que el zahorí había perdido la cordura.$B$B¡Tengo que informar de inmediato acerca de este desaguisado! Pero antes de que me vaya, deja que abra la cerradura mágica.$B$BVenciste a Rethilgore, así que quizás sobrevivas a lo que te espera más allá...' WHERE `entry` = 798;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Al fin! ¡Que alguien me saque de esta celda!$B$BEl sumo ejecutor Hadrec nos envió para recoger información sobre la fortaleza, así que se podría idear un plan para derrocar a Arugal de una vez por todas.$B$BNo obstante, este viejo zahorí tiene muchos trucos ocultos y nos ha detectado a través de una sala mágica. Me confinaron en esta prisión. Vincent no tuvo tanta suerte.$B$BTengo que volver con Hadrec para rendirle informe de inmediato. Pero antes te abriré el cerrojo de la puerta del patio. Quizás puedas probar suerte contra los enemigos que acechan desde el otro lado.' WHERE `entry` = 799;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una vez seguí la senda del Martillo Crepuscular. Obsesionado con resucitar a los dioses antiguos, me interné en las profundidades de esta guarida para intentar absorber el poder de Aku\'mai, ¡bestia maldita!$B$BTras presenciar el sacrificio de incontables inocentes, recuperé el sentido.$B$B¡Estaba atrapado, pero ahora soy libre! ¡Y ahora estoy tan lejos de mi tierra natal Khaz Modan!$B$BValiente $Gaventurero:aventurera;, te enseñaré el camino a una tierra muy especial a cambio de que mates a Aku\'mai. Si lo deseas, puedo transportarte a la ciudad de Darnassus.' WHERE `entry` = 818;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a mi taberna, $Gfatigado viajero:fatigada viajera;. ¿En qué puedo ayudarte?', `text0_1_loc7` = 'Te doy la bienvenida a mi taberna, $Gfatigado viajero:fatigada viajera;. ¿En qué puedo ayudarte?' WHERE `entry` = 820;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Descansa un rato.', `text0_1_loc7` = 'Descansa un rato.' WHERE `entry` = 821;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Al igual que el viento de las llanuras, siempre eres $Gbienvenido:bienvenida; aquí.', `text0_1_loc7` = 'Al igual que el viento de las llanuras, siempre eres $Gbienvenido:bienvenida; aquí.' WHERE `entry` = 822;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Llena tu jarra y coge una silla. Tenemos historias que contar y barriles que vaciar.', `text0_1_loc7` = 'Llena tu jarra y coge una silla. Tenemos historias que contar y barriles que vaciar.' WHERE `entry` = 823;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu cuerpo parece desfallecido y tu espíritu agotado. Descansa bajo nuestro techo y deja que Elune inspire tus sueños.', `text0_1_loc7` = 'Tu cuerpo parece desfallecido y tu espíritu agotado. Descansa bajo nuestro techo y deja que Elune inspire tus sueños.' WHERE `entry` = 824;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pasa, pasa. Pero no te sientes demasiado cerca de la estufa. Hoy todavía no ha explotado pero es más seguro que te sientes en otro sitio.', `text0_1_loc7` = 'Pasa, pasa. Pero no te sientes demasiado cerca de la estufa. Hoy todavía no ha explotado pero es más seguro que te sientes en otro sitio.' WHERE `entry` = 825;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $R, encantada de conocerte.' WHERE `entry` = 838;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Bueno, bueno... mira lo que trajo el gato.. y ¿cómo puedo ayudarte, $?' WHERE `entry` = 878;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontrarás al maestro de grifos en la muralla del noreste, con vista al Valle de los Héroes.', `text0_1_loc7` = 'Encontrarás al maestro de grifos en la muralla del noreste, con vista al Valle de los Héroes.', `text1_0_loc7` = 'Ahhh, buscas a Dungar Tragolargo. ¡Cuenta con uno de los grifos más rápidos de este lado de Khaz Modan! Lo encontrarás en la muralla del Distrito de Mercaderes.', `text1_1_loc7` = 'Ahhh, buscas a Dungar Tragolargo. ¡Cuenta con uno de los grifos más rápidos de este lado de Khaz Modan! Lo encontrarás en la muralla del Distrito de Mercaderes.', `text2_0_loc7` = '¿Quieres darte un paseo en grifo? Dungar tiene estos pájaros en la muralla del Distrito de Mercaderes. ¡No olvides agarrarte fuerte!', `text2_1_loc7` = '¿Quieres darte un paseo en grifo? Dungar tiene estos pájaros en la muralla del Distrito de Mercaderes. ¡No olvides agarrarte fuerte!', `text3_0_loc7` = 'Grifos, ¿eh? En realidad, nunca me han atraído estas bestias. Cada uno con sus cosas.$B$BPuedes encontrar a Dungar Tragolargo en la muralla del Distrito de Mercaderes.', `text3_1_loc7` = 'Grifos, ¿eh? En realidad, nunca me han atraído estas bestias. Cada uno con sus cosas.$B$BPuedes encontrar a Dungar Tragolargo en la muralla del Distrito de Mercaderes.' WHERE `entry` = 879;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontrarás a Aldwin Risante en el Centro de Viajeros de Ventormenta, en el Distrito de Mercaderes.', `text0_1_loc7` = 'Encontrarás a Aldwin Risante en el Centro de Viajeros de Ventormenta, en el Distrito de Mercaderes.', `text1_0_loc7` = '¿Que vas a crear tu hermandad? Entonces, ¡buena suerte!$B$BVe a ver a Aldwin Risante en el Centro de Viajeros de Ventormenta. Está en el Distrito de Mercaderes, que está justo al entrar desde el Valle de los Héroes.', `text1_1_loc7` = '¿A que vas a crear tu hermandad? Entonces, ¡buena suerte!$B$BVe a ver a Aldwin Risante en el Centro de Viajeros de Ventormenta. Está en el Distrito de Mercaderes, que está girando a la derecha cuando entras desde el Valle de los Héroes.', `text2_0_loc7` = 'Ahhh, lo que buscas es el Centro de Viajeros de Ventormenta. Es el primer edificio a la izquierda si atraviesas las puertas delanteras del Valle de los Héroes.', `text2_1_loc7` = 'Ahhh, lo que estás buscando es el Centro de Viajeros de Ventormenta. Es el primer edificio a la izquierda si atraviesas las puertas delanteras del Valle de los Héroes.' WHERE `entry` = 882;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué tipo de instructor estás buscando?', `text0_1_loc7` = '¿Qué tipo de instructor estás buscando?' WHERE `entry` = 898;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los magos se reúnen en el Sagrario del Mago, en el Barrio de los Magos. Vaya torre la que tienen allí. Hace que nuestros cuarteles parezcan casuchas de campesinos.', `text0_1_loc7` = 'Los magos se reúnen en el Sagrario del Mago, en el Barrio de los Magos. Vaya torre la que tienen allí. Hace que nuestros cuarteles parezcan casuchas de campesinos.', `text1_0_loc7` = 'Buscas pistas sobre lo arcano, ¿verdad? Entonces no hay mejor lugar que el Sagrario del Mago, en el Barrio de los Magos.', `text1_1_loc7` = 'Buscas pistas sobre lo arcano, ¿verdad? Entonces no hay mejor lugar que el Sagrario del Mago, en el Barrio de los Magos.', `text2_0_loc7` = '¡Todo indica que quieres hablar con Maginor Dumas! Casi siempre está en el Sagrario del Mago, en el Barrio de los Magos. Por supuesto, nunca sabes hacia qué portal viajan esos magos, ni en qué momento lo hacen.', `text2_1_loc7` = '¡Todo indica que quieres hablar con Maginor Dumas! Casi siempre está en el Sagrario del Mago, en el Barrio de los Magos. Por supuesto, nunca sabes hacia qué portal viajan esos magos, ni en qué momento lo hacen.' WHERE `entry` = 899;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entonces querrás hablar con Osborne. No encontrarás mejor maestro de las artes del sigilo en todo Ventormenta. Búscalo en el Casco Antiguo.', `text0_1_loc7` = 'Entonces querrás hablar con Osborne. No encontrarás mejor maestro de las artes del sigilo en todo Ventormenta. Búscalo en el Casco Antiguo.', `text1_0_loc7` = 'Bah, los pícaros no han hecho otra cosa que darnos más trabajo aquí en Ventormenta. Me gustaría poder sacar a Osborne y a Matías fuera del Casco Antiguo para meterlos directamente en las mazmorras.', `text1_1_loc7` = 'Bah, los pícaros no han hecho otra cosa que darnos más trabajo aquí en Ventormenta. Me gustaría poder sacar a Osborne y a Matías fuera del Casco Antiguo para meterlos directamente en las mazmorras.', `text2_0_loc7` = 'Si quieres encontrar pícaros, ve al Casco Antiguo. Está en la zona noreste de Ventormenta.', `text2_1_loc7` = 'En el Casco Antiguo es donde debes ir si quieres encontrar a los pícaros. Está en la esquina noreste de Ventromenta.' WHERE `entry` = 900;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Normalmente los guerreros se encuentran en la taberna El Cerdo Borracho o en el cuartel del Casco Antiguo. Pero créeme, lo mejor será buscar en la taberna.', `text0_1_loc7` = 'Normalmente los guerreros se encuentran en la taberna El Cerdo Borracho o en el cuartel del Casco Antiguo. Pero créeme, lo mejor será buscar en la taberna.', `text1_0_loc7` = '¿Buscas instrucción? Podría instruirte mejor que cualquiera en Ventormenta, pero la guardia lo prohíbe. Creo que vas a tener que quedarte por aquí, pues uno de ellos anda por el cuartel o en la taberna El Cerdo Borracho en el Casco Viejo.', `text1_1_loc7` = '¿Buscas instrucción? Podría instruirte mejor que cualquiera en Ventormenta, pero la guardia lo prohíbe. Creo que vas a tener que quedarte por aquí, pues uno de ellos anda por el cuartel o en la taberna El Cerdo Borracho en el Casco Viejo.', `text2_0_loc7` = 'Busca por el Casco Antiguo y en el cuartel o en la taberna El Cerdo Borracho. Si vas a la taberna, no olvides probar algunas de las mejores cervezas. Ponen en evidencia la mala calidad de los brebajes de los enanos, te lo digo yo.', `text2_1_loc7` = 'Busca por el Casco Antiguo y en el cuartel o en la taberna El Cerdo Borracho. Si vas a la taberna, no olvides probar algunas de las mejores cervezas. Ponen en evidencia la mala calidad de los brebajes de los enanos, te lo digo yo.' WHERE `entry` = 901;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bueno, puedes encontrar a Theridan o a Maldryn en la Poza de la Luna que está en el parque. Ve hacia la zona oeste de Ventormenta, al norte del Barrio de los Magos, pero al oeste de la Plaza de la Catedral.', `text0_1_loc7` = 'Bueno, puedes encontrar a Theridan o a Maldryn en la Poza de la Luna que está en el parque. Ve hacia la zona oeste de Ventormenta, al norte del Barrio de los Magos, pero al oeste de la Plaza de la Catedral.', `text1_0_loc7` = 'El parque es el lugar al que tienes que dirigirte si buscas instructores druidas. A Theridan y a Maldryn los sueles encontrar junto a la Poza de la Luna, en el centro del distrito.', `text1_1_loc7` = 'El parque es el lugar al que tienes que dirigirte si buscas instructores druidas. A Theridan y a Maldryn los sueles encontrar junto a la Poza de la Luna, en el centro del distrito.', `text2_0_loc7` = '¿Eres hijo de la naturaleza? Entonces no tienes que buscar en la Poza de la Luna que está en el parque.', `text2_1_loc7` = '¿Eres hijo de la naturaleza? Entonces no tienes que buscar en la Poza de la Luna que está en el parque.' WHERE `entry` = 902;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Catedral de la Luz, en la Plaza de la Catedral, es el hogar de los sacerdotes de Ventormenta. Allí encontrarás a la suma sacerdotisa Laurena.', `text0_1_loc7` = 'La Catedral de la Luz, en la Plaza de la Catedral, es el hogar de los sacerdotes de Ventormenta. Allí encontrarás a la suma sacerdotisa Laurena.', `text1_0_loc7` = 'Laurena es la suma sacerdotisa de Ventormenta. La encontrarás en la Catedral de la Luz, en la Plaza de la Catedral.', `text1_1_loc7` = 'Laurena es la suma sacerdotisa de Ventormenta. La encontrarás en la Catedral de la Luz, en la Plaza de la Catedral.', `text2_0_loc7` = 'Sugeriría que buscaras en la Catedral de la Luz, en la Plaza de la Catedral.', `text2_1_loc7` = 'Yo aconsejaría buscar en la Catedral de la Luz en Plaza de la Catedral.' WHERE `entry` = 903;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si quieres convertirte en un poderoso paladín, no encontrarás mejor maestro que Lord Grisillo Quiebrasombras. Lo encontrarás en la Catedral de la Luz, en la Plaza de la Catedral.', `text0_1_loc7` = 'Si quieres convertirte en un poderoso paladín, no encontrarás mejor maestro que Lord Grisillo Quiebrasombras. Lo encontrarás en la Catedral de la Luz, en la Plaza de la Catedral.' WHERE `entry` = 904;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Thorfin Petrescudo vino desde Forjaz para transmitirnos su pericia con la caza. Si no te importa respirar el aire contaminado del Distrito de los Enanos, entonces lo encontrarás en su casa, situada en la zona noreste del distrito.', `text0_1_loc7` = 'Thorfin Petrescudo vino desde Forjaz para transmitirnos su pericia con la caza. Si no te importa respirar el aire contaminado del Distrito de los Enanos, entonces lo encontrarás en su casa, situada en la zona noreste del distrito.', `text1_0_loc7` = '¿Un cazador? Bien, entonces querrás hablar con Thorfin Petrescudo, en el Distrito de los Enanos.$B$BOh, ¡y asegúrate de decirle que si vuelvo a pisar algún regalo de su mascota tendrá que instruir a los cazadores desde detrás de las barras de las Mazmorras!', `text1_1_loc7` = '¿Un cazador? Bien, entonces querrás hablar con Thorfin Petrescudo, en el Distrito de los Enanos.$B$BOh, ¡y asegúrate de decirle que si vuelvo a pisar algún regalo de su mascota tendrá que instruir a los cazadores desde detrás de las barras de las Mazmorras!' WHERE `entry` = 905;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué? No conozco instructores brujos en Ventormenta.$B$BNo obstante... dicen que se han celebrado actos demoníacos en El Cordero Degollado, en el Barrio de los Magos. Supongo que podrías mirar allí.', `text0_1_loc7` = '¿Qué? No conozco instructores brujos en Ventormenta.$B$BNo obstante... dicen que se han celebrado actos demoníacos en El Cordero Degollado, en el Barrio de los Magos. Supongo que podrías mirar allí.', `text1_0_loc7` = '¿Brujos, eh? Bien, no digas que te lo he dicho yo, pero he oído rumores de que esos tipos se reúnen en una taberna llamada El Cordero Degollado, en el Barrio de los Magos.', `text1_1_loc7` = '¿Brujos, eh? Bien, no digas que te lo he dicho yo, pero he oído rumores de que esos tipos se reúnen en una taberna llamada El Cordero Degollado, en el Barrio de los Magos.' WHERE `entry` = 906;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué te trae a esta parte del mundo, $n?', `text0_1_loc7` = '¿Qué te trae a esta parte del mundo, $n?' WHERE `entry` = 907;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué tipo de instructor de profesión estás buscando?', `text0_1_loc7` = '¿Qué tipo de instructor de profesión estás buscando?' WHERE `entry` = 918;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sin necesitas instrucción en alquimia, hay un solo lugar en Ventormenta al que tienes que ir... El Rincón del Alquimista. Lo encontrarás en el Barrio de los Magos, en la calle de tiendas que está situada hacia las afueras.', `text0_1_loc7` = 'Sin necesitas instrucción en alquimia, hay un solo lugar en Ventormenta al que tienes que ir... El Rincón del Alquimista. Lo encontrarás en el Barrio de los Magos, en la calle de tiendas que está situada hacia las afueras.', `text1_0_loc7` = 'Escondida en el Barrio de los Magos hay una pequeña tienda llamada El Rincón del Alquimista. Cuando atravieses el Barrio de los Magos verás todo tipo de extrañas tiendas, pero si no te separas del borde exterior seguro que la encontrarás.', `text1_1_loc7` = 'Escondida en el Barrio de los Magos hay una pequeña tienda llamada El Rincón del Alquimista. Cuando atravieses el Barrio de los Magos verás todo tipo de extrañas tiendas, pero si no te separas del borde exterior seguro que la encontrarás.' WHERE `entry` = 919;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Therum Forjahonda es el enano al que buscas. Uno de los mejores herreros de por aquí. Lo encontrarás en el corazón del Distrito de los Enanos.', `text0_1_loc7` = 'Therum Forjahonda es el enano al que buscas. Uno de los mejores herreros de por aquí. Lo encontrarás en el corazón del Distrito de los Enanos.', `text1_0_loc7` = '¿Quieres decir que no puedes oler el humo de las forjas de los enanos desde aquí? Bueno, si quieres aprender herrería estarás buscando a Therum Forjahonda. Está en el Distrito de los Enanos, justo al norte de la Plaza de la Catedral.', `text1_1_loc7` = '¿Quieres decir que no puedes oler el humo de las forjas de los enanos desde aquí? Bueno, si quieres aprender herrería estarás buscando a Therum Forjahonda. Está en el Distrito de los Enanos, justo al norte de la Plaza de la Catedral.' WHERE `entry` = 920;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si lo que deseas es convertirte en $Gun gran cocinero:una gran cocinera;, ve a la taberna El Cerdo Borracho, situada en el Casco Antiguo, y habla con Stephen Lomocenteno. Ese hombre prepara unas costillas como nadie en el mundo.$B$BHombre, ya me ha entrado hambre y ahora daría cualquier cosas por comerme sus famosas costillas. Pero ¡no cuentes con que te dará la receta!', `text0_1_loc7` = 'Si lo que deseas es convertirte en $Gun gran cocinero:una gran cocinera;, ve a la taberna El Cerdo Borracho, situada en el Casco Antiguo, y habla con Stephen Lomocenteno. Ese hombre sabe prepara unas costillas como nadie en el mundo.$B$BHombre, ya me ha entrado hambre y ahora daría cualquier cosas por comerme sus famosas costillas. Pero ¡no cuentes con que te dará la receta!', `text1_0_loc7` = 'Hay un gran cocinero que trabaja en la taberna El Cerdo Borracho del Casco Antiguo. He visto cómo maneja el cuchillo cuando cocina y, entre tú y yo, no creo que siempre haya sido un cocinero. Pero no me entiendas mal... si sigues queriendo instruirte, él es el mejor de la ciudad.', `text1_1_loc7` = 'Hay un gran cocinero que trabaja en la taberna El Cerdo Borracho del Casco Antiguo. He visto cómo maneja el cuchillo cuando cocina y, entre tú y yo, no creo que siempre haya sido un cocinero. Pero no me entiendas mal... si sigues queriendo instruirte, él es el mejor de la ciudad.' WHERE `entry` = 921;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El instructor en ingeniería que vive en Ventormenta es un gnomo ingenioso al que todos conocen como Lilliam Chispeje. Lo encontrarás al final del Distrito de los Enanos.', `text0_1_loc7` = 'El instructor en ingeniería que vive en Ventormenta es un gnomo ingenioso al que todos conocen como Lilliam Chispeje. Lo encontrarás al final del Distrito de los Enanos.', `text1_0_loc7` = 'Eh, eso me recuerda que tengo que reparar mis botas cohete. Pero te daré un buen consejo, no mezcles las botas cohete con los grifos. $B$BOh, pero necesitas que te ayude con algunas señas... dirígete al Distrito de los Enanos, encontrarás a Lilliam Chispeje por la parte de atrás.', `text1_1_loc7` = 'Eh, eso me recuerda que tengo que reparar mis botas cohete. Pero te daré un buen consejo, no mezcles las botas cohete y los grifos. $B$BOh, pero necesitas que te ayude con algunas señas... dirígete al Distrito de los Enanos, encontrarás a Lilliam Chispeje por la parte de atrás.' WHERE `entry` = 922;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Catedral de la Luz es el centro de todas las prácticas curativas de Ventormenta. Ahí encontrarás a Shaina Fuller para que te enseñe su oficio.', `text0_1_loc7` = 'La Catedral de la Luz es el centro de todas las prácticas curativas de Ventormenta. Ahí encontrarás a Shaina Fuller para que te enseñe su oficio.', `text1_0_loc7` = 'No hay ningún lugar mejor para aprender primeros auxilios que bajo la atenta mirada de Shaina Fuller en la Catedral de la Luz.$B$BPara ir allí solo tienes que buscar los tejados amarillos o la altísima aguja de la Catedral, la podrás ver desde casi cualquier punto de la ciudad.', `text1_1_loc7` = 'No hay ningún lugar mejor para aprender primeros auxilios que bajo la atenta mirada de Shaina Fuller en la Catedral de la Luz.$B$BPara ir allí solo tienes que buscar los tejados amarillos o la altísima aguja de la Catedral, la podrás ver desde casi cualquier punto de la ciudad.' WHERE `entry` = 923;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A Tannysa la encontrarás en la entrada de El Rincón del Alquimista, en el Barrio de los Magos.', `text0_1_loc7` = 'A Tannysa la encontrarás en la entrada de El Rincón del Alquimista, en el Barrio de los Magos.', `text1_0_loc7` = 'No hay nada como la comunión con la naturaleza... al menos es lo que me dicen los elfos de la noche. Yo no soy de los que se pasan el día hablando con las flores y con los árboles, pero seguro que Tannysa podría ayudarte. Está en el Barrio de los Magos en el exterior de El Rincón del Alquimista.', `text1_1_loc7` = 'No hay nada como la comunión con la naturaleza... al menos es lo que me dicen los elfos de la noche. Yo no soy de los que se pasan el día hablando con las flores y con los árboles, pero seguro que Tannysa podría ayudarte. Está en el Barrio de los Magos en el exterior de El Rincón del Alquimista.' WHERE `entry` = 924;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quieres aprender el oficio de la peletería? Simon Peletero podría enseñártelo. Lo encontrarás en el Casco Antiguo en La Piel Protectora.', `text0_1_loc7` = '¿Quieres aprender el oficio de la peletería? Simon Peletero podría enseñártelo. Lo encontrarás en el Casco Antiguo en La Piel Protectora.', `text1_0_loc7` = '¿Te interesa el cuero, eh? Creo que deberías hablar con Simon Peletero en el Casco Antiguo. Lleva una tienda llamada La Piel Protectora.', `text1_1_loc7` = '¿Te interesa el cuero, eh? Creo que deberías hablar con Simon Peletero en el Casco Antiguo. Lleva una tienda llamada La Piel Protectora.' WHERE `entry` = 925;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gelman Petramano es la persona indicada. Creo que tiene una casa en el Distrito de los Enanos, pero no recuerdo exactamente dónde. Yo lo buscaría por allí.', `text0_1_loc7` = 'Gelman Petramano es la persona indicada. Creo que tiene una casa en el Distrito de los Enanos, pero no recuerdo exactamente dónde. Yo lo buscaría por allí.', `text1_0_loc7` = 'Fueron los enanos los que enviaron un especialista en minería desde Forjaz para enseñarnos a excavar. ¿Tan difícil puede ser coger un pico y dar golpes a una piedra? Pero bueno, como está aquí seguramente deberías ir a hablar con él. Su nombre es Gelman y vive en el Distrito de los Enanos.', `text1_1_loc7` = 'Fueron los enanos los que enviaron un especialista en minería desde Forjaz para enseñarnos a excavar. ¿Tan difícil puede ser coger un pico y dar golpes a una piedra? Pero bueno, como está aquí seguramente deberías ir a hablar con él. Su nombre es Gelman y vive en el Distrito de los Enanos.' WHERE `entry` = 927;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ahora bien, si lo que quieres es aprender el oficio del desuello, entonces deberás dirigirte al Casco Antiguo y entrar en La Piel Protectora.', `text0_1_loc7` = 'Ahora bien, si lo que quieres es aprender el oficio del desuello, entonces deberás dirigirte al Casco Antiguo y entrar en La Piel Protectora.', `text1_0_loc7` = 'Podría atravesar a un orco y cortar un trol en pedazos, ¿sabes? Hasta podría tallar mis iniciales en las costillas de un ogro, pero soy incapaz de desollar a un animal. ¡Imagínate!$B$BBueno, si tú tienes suficiente estómago ve a ver a Maris Granger en La Piel Protectora del Casco Antiguo.', `text1_1_loc7` = 'Podría atravesar a un orco y cortar un trol en pedazos, ¿sabes? Hasta podría tallar mis iniciales en las costillas de un ogro, pero soy incapaz de desollar a un animal. ¡Imagínate!$B$BBueno, si tú tienes suficiente estómago ve a ver a Maris Granger en La Piel Protectora del Casco Antiguo.' WHERE `entry` = 928;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Uno de los empleados de los Textiles de Duncan, Georgio Bolero, es uno de los mejores sastres de Ventormenta. Busca la tienda en el Barrio de los magos.', `text0_1_loc7` = 'Uno de los empleados de los Textiles de Duncan, Georgio Bolero, es uno de los mejores sastres de Ventormenta. Busca la tienda en el Barrio de los magos.', `text1_0_loc7` = '¿Un sastre? Bien, si te quedas por aquí un rato seguramente oirás hablar de Georgio Bolero. Ha estado marcando la moda durante meses. Si quieres instruirte con él podrás encontrarlo en el borde exterior del Barrio de los Magos.', `text1_1_loc7` = '¿Un sastre? Bien, si te quedas por aquí un rato seguramente oirás hablar de Georgio Bolero. Ha estado marcando la moda durante meses. Si quieres instruirte con él podrás encontrarlo en el borde exterior del Barrio de los Magos.' WHERE `entry` = 929;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espero ete listo pa trabajar' WHERE `entry` = 931;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$gBienvenido:Bienvenida; a las carreras! ¡Disfruta de tu estancia, y no apuestes todo tu dinero de una vez!', `text0_1_loc7` = '¡$gBienvenido:Bienvenida; a las carreras! ¡Disfruta de tu estancia, y no apuestes todo tu dinero de una vez!', `text1_0_loc7` = '¡Hola, $c! ¡$gBienvenido:Bienvenida; a las carreras del Desierto de Sal!', `text1_1_loc7` = '¡Hola, $c! ¡$gBienvenido:Bienvenida; a las carreras del Desierto de Sal!', `text2_0_loc7` = '¿Quién tiene el coche más rápido? ¿Quién sabe?', `text2_1_loc7` = '¿Quién tiene el coche más rápido? ¿Quién sabe?', `text3_0_loc7` = 'Los goblin empaquetan un montón de empuje, Pero los Gnomos tiene un par de trucos en sus coches…', `text3_1_loc7` = 'Los goblin empaquetan un montón de empuje, Pero los Gnomos tiene un par de trucos en sus coches…' WHERE `entry` = 932;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Hacia dónde quieres dirigirte?', `text0_1_loc7` = '¿Hacia dónde quieres dirigirte?' WHERE `entry` = 933;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si vamos a apoderarnos de estas tierras, tenemos que crear espacios para descansar nuestros cuerpos cansados por la batalla. Coloca tu hacha junto al fuego y nárrame tus maravillosas batallas.', `text0_1_loc7` = 'Si vamos a apoderarnos de estas tierras, tenemos que crear espacios para descansar nuestros cuerpos cansados por la batalla. Coloca tu hacha junto al fuego y nárrame tus maravillosas batallas.' WHERE `entry` = 938;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No hay nada como pasar un día pescando en el Lago de Cristal. Arnold Leland es quien me enseñó. Suele estar en el muelle del Distrito del Canal, pescando durante todo el día. $B$BAh, y no le creas ninguna de las historias que cuenta sobre monstruos en los canales.', `text0_1_loc7` = 'No hay nada como pasar un día pescando en el Lago de Cristal. Arnold Leland es quien me enseñó. Suele estar en el muelle del Distrito del Canal, pescando durante todo el día. $B$BAh, y no le creas ninguna de las historias que cuenta sobre monstruos en los canales.', `text1_0_loc7` = 'Ese vago de Leland... todo lo que hace es sentarse en los muelles de la parte exterior del Distrito de los Mercaderes y pasarse todo el día pescando. Espero que, pescando durante tanto tiempo, sea realmente bueno. Si quieres aprender, ¿por qué no vas a hablar con él?', `text1_1_loc7` = 'Ese vago de Leland... todo lo que hace es sentarse en los muelles de la parte exterior del Distrito de los Mercaderes y pasarse todo el día pescando. Espero que, pescando durante tanto tiempo, sea realmente bueno. Si quieres aprender, ¿por qué no vas a hablar con él?' WHERE `entry` = 940;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lucan Cordell tiene una pequeña tienda junto al canal, cerca de las mazmorras. Seguro que te podrá enseñar las artes del encantamiento.', `text0_1_loc7` = 'Lucan Cordell tiene una pequeña tienda junto al canal, cerca de las mazmorras. Seguro que te podrá enseñar las artes del encantamiento.', `text1_0_loc7` = 'Ah, estás buscando a Lucan Cordell. Lleva una tienda en las afueras del Barrio de la Magia, de camino a las Mazmorras.', `text1_1_loc7` = 'Ah, estás buscando a Lucan Cordell. Lleva una tienda en las afueras del Barrio de la Magia, de camino a las Mazmorras.' WHERE `entry` = 941;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Transito este camino a solas, ideando diversas maneras de defender mi honor y liberar esta tierra de nuestros enemigos.$B$B¿En qué puedo ayudarte, $N?' WHERE `entry` = 942;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, he visto la chamán que buscas. La última vez que la vi, se ocultaba en las colinas encima de la tribu Crines de Acero. Si eres sagaz, serás capaz de encontrar el camino a su choza en las colinas, no está muy lejos al norte de aquí. Ese camino debería permitirte encontrarla sin luchar con los Jabaespín.' WHERE `entry` = 943;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La orden de los Mano Destrozada existía antes que Thrall y seguirá existiendo después de Thrall.' WHERE `entry` = 1038;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Zando\'zan de los Mano Destrozada. ¿En qué puede ayudarte Zando\'zan? ¡No puede! ¡No lo hará!' WHERE `entry` = 1039;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has venido para recibir instrucción? ¿O solo has venido para hacer perder el tiempo a un guerrero?', `text0_1_loc7` = '¿Has venido para recibir instrucción? ¿O solo has venido para hacer perder el tiempo a un guerrero?' WHERE `entry` = 1040;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La máquina recobra vida cuando la tocas.' WHERE `entry` = 1041;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te traerá hidromiel$BTe traerá cerveza$BUna sonrisa de oreja a oreja$BNos ha servido de año en año$BLo llamamos Jim Sonrisas.' WHERE `entry` = 1058;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta es una muestra de la flora exótica, no vista en otras partes de Teldrassil.' WHERE `entry` = 1059;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta planta destaca entre la flora normal de Teldrassil y emite un extraño brillo violáceo.' WHERE `entry` = 1060;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos. Mi función consiste en proporcionar acceso y, si es admisible, explicaciones sobre la síntesis de terráneos por parte de los Creadores del lugar conocido como Azeroth. Esto listo para compartir esta información contigo cuando lo desees.' WHERE `entry` = 1079;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los terráneos son una raza sintética que ha sido diseñada por los Creadores y que actúa como una de las razas originales que poblaron el mundo conocido como Azeroth. Los terráneos fueron creados para ayudar a los Creadores en la colonización de las regiones más recónditas de este mundo.$B$BSon una variante de matriz de ente subterráneo utilizado en otros mundos fundados por los Creadores. La construcción de esta raza prototipo produjo varias anomalías que se manifestaron tras la síntesis inicial.' WHERE `entry` = 1080;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se trata de una de las matrices de síntesis que los Creadores usaron durante la creación de uno de los mundos. Cada matriz de síntesis sirve para lograr los objetivos de los Creadores. En el caso de Azeroth, se modificó una matriz de ente subterráneo para crear un ser con una mayor durabilidad, compatible con la configuración profunda de las regiones. Los terráneos son el producto de esta modificación.$B$BCuando los terráneos fueron activados por los Creadores, presentaron anomalías no previstas que afectaron a sus matrices de síntesis debido al efecto de entornos de tensión excesiva.' WHERE `entry` = 1081;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Antes de poder responder adecuadamente a esta pregunta, recibirás información adicional sobre los terráneos.$B$BProcesando.$B$BPara comprender las anomalías de los terráneos, antes hay que entender lo que ellos representaron en la fase de diseño. Los terráneos representan el intento de los Creadores de mejorar el paradigma de supervivencia estándar de la matriz de ente subterráneo. Esto se llevó a cabo incorporando bases resistentes de construcción inherentes al mundo en la mencionada matriz.' WHERE `entry` = 1082;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se trata del material básico utilizado en la síntesis de razas. Se empleó un corte transversal de la corteza de Azeroth como base de síntesis de los terráneos, en lugar de la base de construcción de biomasa típica utilizada por los Creadores.$B$BUna investigación sobre la composición del mundo permitió a los Creadores formular la teoría de que se podía sintetizar un ser mejorado que pudiera personificar la resistencia esencial de este mundo. Esto se logró eligiendo como base una mezcla de varios compuestos de núcleo mineral de Azeroth.' WHERE `entry` = 1083;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En otras palabras, esto es correcto.$B$BLas características físicas de los terráneos son las de un humanoide de menor estatura, si bien su composición es la de varios compuestos de núcleo mineral de Azeroth. Su diseño potencia la fuerza y la energía sin sacrificar la capacidad de procesamiento cognitivo. Su piel y musculatura son prácticamente inmunes al daño físico y, una vez sometidos a cualquier modificación insignificante, los terráneos muestran una notable resistencia a fuerzas mágicas no deseadas.' WHERE `entry` = 1084;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Para asimilar esta información, es preciso comprender que los Creadores querían sintetizar una raza que, tras su partida, constituyera por mucho tiempo una parte integral del ciclo de desarrollo de este mundo. Esta raza constituiría un instrumento para alcanzar los objetivos de desarrollo a largo plazo que los Creadores tenían en relación con Azeroth. $B$BCabría añadir que la denominación \"terráneo\" fue considerada adecuada por los Creadores, ya que hacía referencia a su composición.' WHERE `entry` = 1085;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La única anomalía en la síntesis de los terráneos es la tendencia a la desestabilización de la matriz cuando se los utiliza en entornos sometidos a grandes dosis de tensión.$B$BApéndice: la escultura profunda en barro resultaba adecuada al nuevo diseño de matriz y nunca se ajustó a los requerimientos de duplicación de un entorno sometido a altos niveles de tensión.$B$BLa desestabilización no se detectó sino hasta bien avanzado el ciclo primario de funcionamiento de los terráneos. Los Creadores empezaron a trabajar en modificaciones post síntesis, en lugar de proceder a la retirada y a la resíntesis.' WHERE `entry` = 1086;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '[Se produce una pausa evidente tras formular esta pregunta.]$B$BLa información que solicitas no está actualmente disponible en este ciclo del depósito de datos. Realmente lamentamos esta omisión.$B$BSolicita esta información una vez que se haya realizado la actualización del ciclo de datos en este depósito de información. También puede solicitar esta información en otro depósito de información situado en Azeroth.' WHERE `entry` = 1087;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Existen dos resultados potenciales cuando la matriz de síntesis de terráneos se desestabiliza. Iniciando representación de ayuda visual número uno.$B$BLa primera matriz de variante es un producto degenerativo de síntesis, denominada en este caso con la nomenclatura común de \"trogg\". Esta variante mantiene una fuerza y una resistencia comparables a las de los terráneos, pero su potencia de procesamiento cognitivo ha experimentado serios problemas. La fuerza de voluntad y una notable astucia son los únicos factores que mueven la psique de un \"trogg\".' WHERE `entry` = 1088;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me resulta imposible procesar su referencia cronológica en comparación con el registro de este dato, acepte mis disculpas.$B$BEl trogg retiene parte de la composición de los diferentes compuestos del centro de la piedra de Azeroth, pero la pérdida de poder cognitivo convierte a esta variante en inaceptable para los Creadores.$B$BEl trogg se procrea según una biomasa estándar. Por defecto consume piedra y cualquier otra cosa que pueda conseguir, incluyendo otros troggs. Los Creadores consideran inaceptable el canibalismo.' WHERE `entry` = 1089;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Iniciando representación de ayuda visual número dos.$B$BLa segunda variante de resultado de la matriz de terráneos retiene numerosos elementos deseados cuando se los compara con la matriz subterránea estándar. La nomenclatura común para esta variante es \"Enano\".$B$BEsta variante conserva la fortaleza y la resistencia inherente a los terráneos. En algunos casos, el enano presenta incluso poderes cognitivos superiores a los de la matriz de los terráneos. No obstante, el enano no presenta la composición física de los compuestos del núcleo mineral de Azeroth.' WHERE `entry` = 1090;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los datos almacenados en este depósito efectivamente se relacionan con tu astuta afirmación.$B$BEl enano guarda afinidad con la composición mineral de Azeroth, pero debido a anomalía medioambiental relacionada con las tensiones elevadas de la matriz de síntesis de los terráneos, el enano vuelve a una composición de biomasa predeterminada como la que se observa en la matriz subterránea estándar.$B$BPor esta razón, la variante de enano se sintetiza a partir de la degeneración de la matriz de creación de terráneos, tal y como has deducido en tu consulta.' WHERE `entry` = 1091;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No puedo procesar tu referencia cronológica en comparación con el registro de estos datos. Lo siento mucho.$B$BA pesar de que el enano conserva gran parte de la afinidad mental de los terráneos con la composición mineral de Azeroth, el retorno a la biomasa viviente constituye la mayor degeneración de la matriz. Los enanos se alimentan y procrean utilizando los medios típicos de otras biomasas estándar.$B$BLos Creadores ven al enano como variante aceptable, con su propio derecho de génesis.' WHERE `entry` = 1092;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Creadores son... los Creadores.$B$BSon los seres que fundaron y configuraron este mundo de acuerdo con su propio diseño. Son la razón de la existencia de este mundo, así como la de muchos otros mundos. Son la fuente desde la cual fluye Azeroth.' WHERE `entry` = 1093;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Creadores conocen la magnitud de la información alojada en este depósito. A continuación, dispondrás de una copia portátil de los datos almacenados aquí una vez que entres en sintonía con los Discos de Norgannon.$B$BTen en cuenta los requisitos que deberás cumplir para garantizar la total asimilación de este conocimiento. Cuando te resulte cómodo, accede a los Discos de Norgannon para iniciar la síntesis de tus discos.' WHERE `entry` = 1094;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Así que quieres ser un herrero? Coge un mandil y un martillo, $Gmuchacho:muchacha; y veamos qué sabes hacer.' WHERE `entry` = 1118;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Eres $Gtonto:tonta;? Eso no es un arma, es un cuchillo de cocina. No instruyo a principiantes.' WHERE `entry` = 1133;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Vienes a molestarme para eso? Estarías mejor corriendo por la guarida de un trol con un conejo muerto atado a la espalda, que vistiendo esa basura que llevas encima.' WHERE `entry` = 1135;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu nombre es $N, ¿no? Ahí tienes una pieza muy bien hecha. Me haré cargo de ti y te instruiré en el oficio para que puedas crear una armadura de tal calidad, que hasta el propio rey Magni pueda ponérsela.' WHERE `entry` = 1136;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde hace unos cinco meses, la división de Tuercespina Ventura y Cia. Consiguió un veneno especial de un comerciante sospechoso de la Bahía del Botín llamado Loquillo Pinchabujas. ¡Pinchabujas había robado al parecer, el veneno de Zanzil! $B$BSí, Zanzil el Desterrado - el troll que fue exiliado de su propia tribu debido a la naturaleza de sus \"experimentos\" en la nigromancia $B$BNaturalmente, una vez que tuvieron el veneno, procedieron a probarlo entre sí. Los resultados fueron desastrosos.' WHERE `entry` = 1138;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Gran Capataz Puzik Gallywix finalmente intervino, llevándose el veneno sobrante a su torre, aquí en El Fangal, con el fin de estudiar e investigar la naturaleza inestable de las toxinas. Se tomó la decisión finalmente de compartir el secreto del veneno con la Hermandad Defias a cambio de una oferta muy grande de un veneno estable y de uso corriente. $B$BA Klaven Mortoalerta de la Hermandad Defias se le encargó la tarea de modificar las propiedades químicas de la mezcla de Zanzil.' WHERE `entry` = 1158;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Avanzando al presente: Los trabajos en la fórmula han dado sus frutos,  permitiendo la creación de un Zángano más controlado, sin embargo, el inconveniente es que el compuesto inactivo es extremadamente volátil $B$BGallywix, todo el mundo de esa torre y todo lo que le rodea están infectados por el veneno. Este afecta a nuestras mentes y nuestros cuerpos. Nuestros órganos internos se emblandecen y son extremadamente frágiles. Esta es tu oportunidad, $N. No tengo la fuerza para seguir adelante y tengo la sensación de que pronto me convertiré en uno de ”ellos”.' WHERE `entry` = 1159;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las defensas de la torre se encuentran en su momento más débil. Los patrulleros, centinelas, vigías, e incluso Gallywix están infectados y muy vulnerables a tus ataques y habilidades especiales. $B$BQuiero verte entrar en la torre y cuando salgas, quiero verte cubierto con la sangre de nuestros enemigos, la cabeza del Gran Capataz Puzik Gallywix en tus manos, y hasta el último de ese veneno en tu bolsa. Llévale todo lo recuperado a Shenthul. Él sabrá qué hacer... espero. $b' WHERE `entry` = 1160;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espero que tu interrupción obedezca a una buena causa. Justo estaba haciendo algo muy importante.', `text0_1_loc7` = 'Espero que tu interrupción obedezca a una buena causa. Justo estaba haciendo algo muy importante.' WHERE `entry` = 1202;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GOtro:Otra; herborista, mmm... Dudo que tus habilidades sean tan buenas como las mías, así que tal vez pueda instruirte en unas cuantas técnicas.', `text0_1_loc7` = '$GOtro:Otra; herborista, mmm... Dudo que tus habilidades sean tan buenas como las mías, así que tal vez pueda instruirte en unas cuantas técnicas.' WHERE `entry` = 1203;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, $Gun compañero pescador:una compañera pescadora;. ¿Necesitas tal vez un poco de instrucción? Quizás pueda enseñarte un par de cosas.', `text0_1_loc7` = 'Ah, $Gun compañero pescador:una compañera pescadora;. ¿Necesitas tal vez un poco de instrucción? Quizás pueda enseñarte un par de cosas.' WHERE `entry` = 1205;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bienvenido a Ventormenta. llevó vastante tiempo su reconstrucción, pero ha merecido la pena. Así que ¿estás de visita, o estás aquí por negocios?' WHERE `entry` = 1206;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí no vienen demasiados cocineros para recibir instrucción, pero supongo que puedo enseñarte algo si estás $Gpreparado:preparada;.', `text0_1_loc7` = 'Aquí no vienen demasiados cocineros para recibir instrucción, pero supongo que puedo enseñarte algo si estás $Gpreparado:preparada;.' WHERE `entry` = 1207;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Acaso doy la impresión de tener el tiempo o la energía para agasajar a todo turista que entra por mi puerta? Dime qué necesitas y lo haré rápido.', `text0_1_loc7` = '¿Acaso doy la impresión de tener el tiempo o la energía para agasajar a todo turista que entra por mi puerta? Dime qué necesitas y lo haré rápido.' WHERE `entry` = 1208;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No suelo recibir a muchos desolladores aquí buscando instrucción, pero supongo que puedo enseñarte algo si estás $Gpreparado:preparada;.', `text0_1_loc7` = 'No suelo recibir a muchos desolladores aquí buscando instrucción, pero supongo que puedo enseñarte algo si estás $Gpreparado:preparada;.' WHERE `entry` = 1210;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puede que por ahora, se hayan acabado las grandes guerras, pero debemos mantener vivas nuestras habilidades.' WHERE `entry` = 1215;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ventormenta es el pilar de la raza humana y se mantiene gracias a la fuerza de voluntad, aun a pesar de encontrarse en permanente peligro.', `text0_1_loc7` = 'Ventormenta es el pilar de la raza humana y se mantiene gracias a la fuerza de voluntad, aun a pesar de encontrarse en permanente peligro.' WHERE `entry` = 1216;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos. ¿Buscas instrucción en el arte de la guerra?', `text0_1_loc7` = 'Saludos. ¿Buscas instrucción en el arte de la guerra?' WHERE `entry` = 1217;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No importa cuánto aprendamos, siempre queda más por saber.', `text0_1_loc7` = 'No importa cuánto aprendamos, siempre queda más por saber.' WHERE `entry` = 1218;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El papel del guerrero es el de proteger. Así como protegemos a nuestros compatriotas en la batalla, también debemos proteger los intereses de los Renegados.', `text0_1_loc7` = 'El papel del guerrero es el de proteger. Así como protegemos a nuestros compatriotas en la batalla, también debemos proteger los intereses de los Renegados.' WHERE `entry` = 1219;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Oh, un compañero médico. Será un placer para mí enseñarte cualquier cosa que pueda servirte en tus viajes.', `text0_1_loc7` = 'Oh, un compañero médico. Será un placer para mí enseñarte cualquier cosa que pueda servirte en tus viajes.' WHERE `entry` = 1220;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí, $Ghijo:hija; mía? ¿En qué puedo ayudarte?', `text0_1_loc7` = '¿Sí, $Ghijo:hija; mía? ¿En qué puedo ayudarte?' WHERE `entry` = 1221;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He venido desde Forjaz para contribuir en las tareas de reconstrucción. Si logro enseñaros gran parte de mis conocimientos, tal vez entonces pueda dejaros y volver a casa.', `text0_1_loc7` = 'He venido desde Forjaz para contribuir en las tareas de reconstrucción. Si logro enseñaros gran parte de mis conocimientos, tal vez entonces pueda dejaros y volver a casa.' WHERE `entry` = 1225;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí? Me has interrumpido, así que espero que tengas una buena razón para ello.', `text0_1_loc7` = '¿Sí? Me has interrumpido, así que espero que tengas una buena razón para ello.' WHERE `entry` = 1226;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $N' WHERE `entry` = 1228;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde la caída de Dalaran y la destrucción de Quel\'Thalas, este tipo de mercancías han sido difíciles de conseguir. Pero Lucan Cordell y alguno de nuestros otros comerciantes han realizado un buen trabajo al asignar mercancía de calidad a nuestros pedidos.', `text0_1_loc7` = 'Desde la caída de Dalaran y la destrucción de Quel\'Thalas, es difícil conseguir mercancía de este tipo. Sin embargo, Lucan Cordell y otros de nuestros mercaderes llevan tiempo satisfaciendo nuestros pedidos con mercancía de gran calidad.', `text1_0_loc7` = 'Yo solo vendo equipamiento arcano de la mayor calidad, fabricado por los artesanos y magos de Ventormenta.', `text1_1_loc7` = 'Yo solo vendo equipamiento arcano de la mayor calidad, fabricado por los artesanos y magos de Ventormenta.' WHERE `entry` = 1231;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde la caída de Dalaran y la destrucción de Quel\'Thalas, es difícil conseguir mercancía de este tipo. Sin embargo, Lucan Cordell y otros de nuestros mercaderes llevan tiempo satisfaciendo nuestros pedidos con mercancía de gran calidad.', `text0_1_loc7` = 'Desde la caída de Dalaran y la destrucción de Quel\'Thalas, es difícil conseguir mercancía de este tipo. Sin embargo, Lucan Cordell y otros de nuestros mercaderes llevan tiempo satisfaciendo nuestros pedidos con mercancía de gran calidad.', `text1_0_loc7` = 'Yo solo vendo equipamiento arcano de la mayor calidad, fabricado por los artesanos y magos de Ventormenta.', `text1_1_loc7` = 'Yo solo vendo equipamiento arcano de la mayor calidad, fabricado por los artesanos y magos de Ventormenta.' WHERE `entry` = 1232;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Realizado por los más finos artesanos de todo Teldrassil, cuidadosamente transportado de Kalimdor a Azeroth en señal de la creciente alianza entre la gente del Gran Árbol y los humanos de esta tierra. Si necesitas algo, solo tienes que pedirlo.', `text0_1_loc7` = 'Realizado por los más finos artesanos de todo Teldrassil, cuidadosamente transportado de Kalimdor a Azeroth en señal de la creciente alianza entre la gente del Gran Árbol y los humanos de esta tierra. Si necesitas algo, solo tienes que pedirlo.' WHERE `entry` = 1233;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde la caída de Dalaran y la destrucción de Quel\'Thalas, es difícil conseguir mercancía de este tipo. Sin embargo, Lucan Cordell y otros de nuestros mercaderes llevan tiempo satisfaciendo nuestros pedidos con mercancía de gran calidad.', `text0_1_loc7` = 'Desde la caída de Dalaran y la destrucción de Quel\'Thalas, es difícil conseguir mercancía de este tipo. Sin embargo, Lucan Cordell y otros de nuestros mercaderes llevan tiempo satisfaciendo nuestros pedidos con mercancía de gran calidad.', `text1_0_loc7` = 'Yo solo vendo equipamiento arcano de la mayor calidad, fabricado por los artesanos y magos de Ventormenta.', `text1_1_loc7` = 'Yo solo vendo equipamiento arcano de la mayor calidad, fabricado por los artesanos y magos de Ventormenta.' WHERE `entry` = 1234;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los mejores precios en todo Ventormenta, no encontrarás mejores precios. Y bien, ¿en qué puedo ayudarte?', `text0_1_loc7` = 'Los mejores precios en todo Ventormenta, no encontrarás mejores precios. Y bien, ¿en qué puedo ayudarte?', `text1_0_loc7` = 'Los mejores precios en todo Ventormenta, no encontrarás mejores precios. Y bien, ¿en qué puedo ayudarte?', `text1_1_loc7` = 'Los mejores precios en todo Ventormenta, no encontrarás mejores precios. Y bien, ¿en qué puedo ayudarte?', `text2_0_loc7` = '¿Solo estás examinando mis mercancías o hay algo específico que quieras encontrar hoy?', `text2_1_loc7` = '¿Solo estás examinando mis mercancías o hay algo específico que quieras encontrar hoy?' WHERE `entry` = 1235;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los mejores precios en todo Ventormenta, no encontrarás mejores precios. Y bien, ¿en qué puedo ayudarte?', `text0_1_loc7` = 'Los mejores precios en todo Ventormenta, no encontrarás mejores precios. Y bien, ¿en qué puedo ayudarte?', `text1_0_loc7` = 'Los mejores precios en todo Ventormenta, no encontrarás mejores precios. Y bien, ¿en qué puedo ayudarte?', `text1_1_loc7` = 'Los mejores precios en todo Ventormenta, no encontrarás mejores precios. Y bien, ¿en qué puedo ayudarte?', `text2_0_loc7` = '¿Solo estás examinando mis mercancías o hay algo específico que quieras encontrar hoy?', `text2_1_loc7` = '¿Solo estás examinando mis mercancías o hay algo específico que quieras encontrar hoy?' WHERE `entry` = 1236;
