UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solo vendo el mejor equipamiento, probado y comprobado. Y los precios son fijos, así que si quieres regatear, vete a hablar con un goblin.', `text0_1_loc7` = 'Solo vendo el mejor equipamiento, probado y comprobado. Y los precios son fijos, así que si quieres regatear, vete a hablar con un goblin.' WHERE `entry` = 1238;
