UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Que la Luz guíe tu camino y te asista en tus viajes. Si necesitas ayuda para encontrar lo que buscas, estaré justo allí.', `text0_1_loc7` = 'Que la Luz guíe tu camino y te asista en tus viajes. Si necesitas ayuda para encontrar lo que buscas, estaré justo allí.' WHERE `entry` = 1241;
