UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vigila tu espalda.',  `text1_0_loc7` = 'La Mansión Ravenholdt no te reconoce, $C.', `text2_0_loc7` = 'Gritos. Al Maestro Ravenholdt no le gustan los gritos.' WHERE `entry` = 5933;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Osas poner el pie en la Mansión Ravenholdt, babosa?', `text0_1_loc7` = '¿Osas poner el pie en la Mansión Ravenholdt, babosa?', `text1_0_loc7` = 'Salir de aquí sin una espada clavada en tu espalda o veneno corriendo por tus venas, sería un gran logro.', `text1_1_loc7` = 'Salir de aquí sin una espada clavada en tu espalda o veneno corriendo por tus venas, sería un gran logro.' WHERE `entry` = 5938;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Que Elune camine contigo, $C. El Templo de la Luna ha venido a ayudar a los desamparados ciudadanos de Auberdine.', `text0_1_loc7` = 'Que Elune camine contigo, $C. El Templo de la Luna ha venido a ayudar a los desamparados ciudadanos de Auberdine.' WHERE `entry` = 5943;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Shoja\'my está delante de la posada. Y ahora vete, estoy ocupado.', `text0_1_loc7` = 'Shoja\'my está delante de la posada. Y ahora vete, estoy ocupada.', `text1_0_loc7` = '¿Mascotas que cuidar? ¡Pues asegúrate de limpiar su porquería! Busca a Shoja\'my delante de la posada.', `text1_1_loc7` = '¿Mascotas que cuidar? ¡Pues asegúrate de limpiar su porquería! Busca a Shoja\'my delante de la posada.' WHERE `entry` = 5973;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ve al Valle del Honor y busca a Xon\'cha, cerca de los establos.', `text0_1_loc7` = 'Ve al Valle del Honor y busca a Xon\'cha, cerca de los establos.', `text1_0_loc7` = 'Xon\'cha guardará en el establo a tus mascotas extra. Búscale en el Valle del Honor, en los establos que están entre la arena y el Recinto de los Cazadores. ¡Ahora, vete!', `text1_1_loc7` = 'Xon\'cha guardará en el establo a tus mascotas extra. Búscale en el Valle del Honor, en los establos que están entre la arena y el Recinto de los Cazadores. ¡Ahora, vete!' WHERE `entry` = 5974;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, sí, debes buscar a Seikwa. Creo que la última vez que la vi estaba fuera de la posada.', `text0_1_loc7` = 'Ah, sí, debes buscar a Seikwa. Creo que la última vez que la vi estaba fuera de la posada.', `text1_0_loc7` = 'Seikwa puede encargarse de tus animales cuando tengas la necesidad. La encontrarás delante de la posada.', `text1_1_loc7` = 'Seikwa puede encargarse de tus animales cuando tengas la necesidad. La encontrarás delante de la posada.' WHERE `entry` = 5976;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Necesitas la ayuda de Bulrug. Le encontrarás en la colina inferior, entre la posada y la Ribera de Cima del Trueno.', `text0_1_loc7` = 'Necesitas la ayuda de Bulrug. Le encontrarás en la colina inferior, entre la posada y la Ribera de Cima del Trueno.', `text1_0_loc7` = 'La preocupación que sientes por el bienestar de tus compañeros animales es digna de elogio. Bulrug podrá ayudarte con eso, está en la colina inferior. Búscale entre la Ribera y la posada.', `text1_1_loc7` = 'La preocupación que sientes por el bienestar de tus compañeros animales es digna de elogio. Bulrug podrá ayudarte con eso, está en la colina inferior. Búscale entre la Ribera y la posada.' WHERE `entry` = 5977;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Por extraño que parezca, podrás encontrar a Morganus, el maestro de establos, delante de los establos.', `text0_1_loc7` = 'Por extraño que parezca, podrás encontrar a Morganus, el maestro de establos, delante de los establos.', `text1_0_loc7` = '¿No eres muy inteligente, eh? Mmm... Vamos a ver... Si fueras un maestro de establos, ¿no te parece que estarías cerca de los establos? Busca a Morganus allí.', `text1_1_loc7` = '¿No eres muy inteligente, eh? Mmm... Vamos a ver... Si fueras un maestro de establos, ¿no te parece que estarías cerca de los establos? Busca a Morganus allí.' WHERE `entry` = 5978;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes confiar en que Alassin cuidará bien de tus mascotas. Vive en el Enclave Cenarion, donde se reúnen los cazadores.', `text0_1_loc7` = 'Puedes confiar en que Alassin cuidará bien de tus mascotas. Vive en el Enclave Cenarion, donde se reúnen los cazadores.', `text1_0_loc7` = 'Durante cientos de años, Alassin ha cuidado a los animales amigos de nuestros cazadores. Los cazadores de por aquí, siempre confían en Alassin para el cuidado de sus animales. Si también le estás buscando, hazlo en el Enclave Cenarion.', `text1_1_loc7` = 'Durante cientos de años, Alassin ha cuidado a los animales amigos de nuestros cazadores. Los cazadores de por aquí, siempre confían en Alassin para el cuidado de sus animales. Si también le estás buscando, hazlo en el Enclave Cenarion.' WHERE `entry` = 5980;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todos somos piezas de un puzle mucho mayor, $N. Al igual que tú guías la pieza de un puzzle hacia su destino, también nosotros somos guiados por alguien.$B$BY, quizás, aquellos que nos guían a nosotros, también son guiados por una fuerza superior.' WHERE `entry` = 5981;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, gentil Seriadne. Todos los animales están a salvo a su cuidado. Podrás encontrarla justo fuera de la posada de Dolanaar.', `text0_1_loc7` = 'Ah, gentil Seriadne. Todos los animales están a salvo a su cuidado. Podrás encontrarla justo fuera de la posada de Dolanaar.', `text1_0_loc7` = '¿Buscas a alguien que se preocupe por los animales? Entonces deberías buscar a Seriadne fuera de la posada de Dolanaar.', `text1_1_loc7` = '¿Buscas a alguien que se encargue de cuidar a tus animales? Pues deberías ir a ver a Seriadne, puedes encontrarla fuera de la posada de Dolanaar. Ella es la que estás buscando.' WHERE `entry` = 5982;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La buena de Erma, lleva años cuidando a los animales de la zona. Estoy seguro de que estará encantada de cuidar a tus animales. Puedes encontrarla justo fuera de la Posada Orgullo de León.', `text0_1_loc7` = 'La buena de Erma, lleva años cuidando a los animales de la zona. Estoy seguro de que estará encantada de cuidar a tus animales. Puedes encontrarla justo fuera de la Posada Orgullo de León.', `text1_0_loc7` = 'Erma se encarga del cuidado de los animales de la zona. Si necesitas hablar con ella, ahora está justo fuera de la Posada Orgullo de León.', `text1_1_loc7` = 'Erma se encarga del cuidado de los animales de la zona. Si necesitas hablar con ella, ahora está justo fuera de la Posada Orgullo de León.' WHERE `entry` = 5983;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tienes que buscar a Jenova Petrescudo, está en el Distrito de los Enanos. Se encarga de cuidar a las mascotas de los cazadores mientras su padre las entrena.', `text0_1_loc7` = 'Tienes que buscar a Jenova Petrescudo, está en el Distrito de los Enanos. Se encarga de cuidar a las mascotas de los cazadores mientras su padre las entrena.', `text1_0_loc7` = 'Si necesitas un alojamiento para tu mascota mientras estás entrenando a otra, deberías hablar con Jenova Petrescudo, en el Distrito de los Enanos.', `text1_1_loc7` = 'Si necesitas un alojamiento para tu mascota mientras estás entrenando a otra, deberías hablar con Jenova Petrescudo, en el Distrito de los Enanos.' WHERE `entry` = 5984;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al puesto de mando de IV:7, $N.' WHERE `entry` = 5994;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí, $C? ¿Vienes a informar?' WHERE `entry` = 6033;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Mina Dentefrío está en territorio de la Horda, en las montañas al norte del Cementerio Lobo Gélido.$B$BPertenece a los kóbolds.' WHERE `entry` = 6058;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este monumento está dedicado a todos los que dieron su vida por proteger Ventormenta. Nuestro pueblo ha tenido que sufrir increíbles dificultades para mantener su libertad y para poder controlar su propio destino. Aquí es donde recordamos los sacrificios que nuestros ciudadanos hicieron durante la primera guerra que destrozó a nuestro pueblo.' WHERE `entry` = 6062;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestras unidades no están mejoradas y no tengo suficientes suministros para hacerlo.' WHERE `entry` = 6073;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La sangre de nuestros enemigos se derramará sobre el campo de batalla. Los grandiosos elementales responderán a nuestra llamada y aplastarán a todo aquel que se interponga en nuestro camino.' WHERE `entry` = 6093;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las páginas del diario parecen contener información sobre comercios, compradores y otras cosas relacionadas con los negocios.' WHERE `entry` = 6104;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Nunca mires al Comandante directamente a los ojos.' WHERE `entry` = 6105;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si necesitas información que el Círculo Cenarion pueda tener archivada, yo soy la persona que buscas. Pero primero, necesitas saber qué información buscas para poder ayudarte.$B$B¿Te das cuenta de cómo va esto? ¡Fantástico!' WHERE `entry` = 6153;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No escuches los corazones del mundo... la respuesta está dentro de ti. Tú sabes la verdad.' WHERE `entry` = 6154;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El pasado vive en estos eriales, $C. Debemos tener cuidado y no despertarlo.' WHERE `entry` = 6155;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al Registro de hermandades de Entrañas. ¿Qué quieres?' WHERE `entry` = 6159;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al Círculo Cenarion, $C. ¿Cómo puedo ayudarte?' WHERE `entry` = 6160;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Este bosque puede resultar confuso. Ten cuidado ahí fuera.' WHERE `entry` = 6193;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Descuentos de temporada para ti y los tuyos!', `text0_1_loc7` = '¡Descuentos de temporada para ti y los tuyos!', `text1_0_loc7` = 'De la granja a la mesa, ¡todo lo bueno de los Pastos de Bosquehumeante!', `text1_1_loc7` = 'De la granja a la mesa, ¡todo lo bueno de los Pastos de Bosquehumeante!', `text2_0_loc7` = '¡Es la temporada de los grandes descuentos! ¡La, la, la, la!', `text2_1_loc7` = '¡Es la temporada de los grandes descuentos! ¡La, la, la, la!', `text3_0_loc7` = '¡Que tengas un feliz festín del Festival de Invierno! ¡Puedes empezar a disfrutarlo con los manjares de los Pastos de Bosquehumeante!', `text3_1_loc7` = '¡Que tengas un feliz festín del Festival de Invierno! ¡Puedes empezar a disfrutarlo con los manjares de los Pastos de Bosquehumeante!', `text4_0_loc7` = '¡Asegúrate de decirle al Gran Padre Invierno la sorpresa secreta que te gustaría recibir este Festival de Invierno!', `text4_1_loc7` = '¡Asegúrate de decirle al Gran Padre Invierno la sorpresa secreta que te gustaría recibir este Festival de Invierno!', `text5_0_loc7` = '¡No permitas que el tintineo de las campanas no deje sonar el tintineo de las monedas de tu bolsillo! ¡Compra manjares de los Pastos de Bosquehumeante para celebrar el Festival de Invierno!', `text5_1_loc7` = '¡No permitas que el tintineo de las campanas no deje sonar el tintineo de las monedas de tu bolsillo! ¡Compra manjares de los Pastos de Bosquehumeante para celebrar el Festival de Invierno!', `text6_0_loc7` = 'Si tuviera castañas, te aseguro que estaría asándolas al fuego ahora mismo. ¡Mmmm!', `text6_1_loc7` = 'Si tuviera castañas, te aseguro que estaría asándolas al fuego ahora mismo. ¡Mmmm!', `text7_0_loc7` = '¡No te olvides de comprar mucho papel de regalo para las vacaciones! Nada dice \"Feliz Festival de Invierno\" como armas y armaduras envueltas en papel de regalo para tus amigos y tu familia.', `text7_1_loc7` = '¡No te olvides de comprar mucho papel de regalo para las vacaciones! Nada dice \"Feliz Festival de Invierno\" como armas y armaduras envueltas en papel de regalo para tus amigos y tu familia.' WHERE `entry` = 6194;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has aprendido a empuñar una nueva arma, o solo a hacerme perder el tiempo?' WHERE `entry` = 6233;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Feliz Festival de Invierno... mmm... $Gpequeño: pequeña;. Los niños buenos tienen un... ehm... descuento especial en cualquier tienda de Pastos de Bosquehumeante. Mmm.',  `text1_0_loc7` = 'Tengo bastante claro lo que el viejo Gran Padre Invierno va a hacer cuando se acabe este turno. Tengo una botella de licor de Festival de Invierno en mi bolsa, y no va a quedar ni una gota.', `text2_0_loc7` = 'No... No te puedes sentar encima de mí.', `text3_0_loc7` = 'Sí, claro, adelante, dile al viejo Gran Padre Invierno qué te gustaría recibir este Festival de Invierno.$B$B¡Jo, jo, jo, date prisa!' WHERE `entry` = 6250;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay quienes dicen que el éxito de un ejército depende de su fuerza y valentía. ¡Y qué cierto es! Pero un ejército también necesita suministros para estar en buena forma y mi trabajo es asegurarme de que tienen lo que necesitan.' WHERE `entry` = 6257;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Eh, colega. Has venio al sitio perfecto pa aprender a empuñar una nueva arma.' WHERE `entry` = 6275;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué quieres aprender, mon?' WHERE `entry` = 6277;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me encantaría instruirte en el uso de las armas que domino, siempre y cuando tengas la facultad de aprender.' WHERE `entry` = 6279;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$N, me alegro de ver a veteranos como tú en el campo! Siento informarte de que nuestros suministros son demasiado escasos para lanzar asaltos terrestres a gran escala contra la Horda.$B$BHabla con nuestro intendente; tiene misiones de suministros disponibles.' WHERE `entry` = 6288;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has venido a aprender a combatir con armas?' WHERE `entry` = 6289;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué arma te interesa en particular?' WHERE `entry` = 6290;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No puedo enseñarte mi gracia a la hora de luchar, pero sí a utilizar algunas armas.' WHERE `entry` = 6291;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Puedo indicarte cómo encontrar otro maestro de armas, si lo prefieres. ¿Qué armas te interesan?' WHERE `entry` = 6292;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quieres aprender a usar un arma, eh? Bueno, no te quedes ahí con cara de idiota, habla, $Gchico:chica;.' WHERE `entry` = 6293;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué arma buscas aprender?' WHERE `entry` = 6294;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Eh, hola! ¿Has venido a aprender cómo usar una nueva arma?' WHERE `entry` = 6295;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Muerte al Lobo Gélido!' WHERE `entry` = 6313;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los carneros indígenas de Alterac son cruciales para nuestra caballería.' WHERE `entry` = 6316;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muy bien, $N. Debemos ser breves, hay mucho que hacer.' WHERE `entry` = 6333;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Que Cenariuste guíe, $C.$B$BEstoy aquí, con mis hermanas, en esta tierra corrupta en una misión de gran importancia para el Círculo Cenarion. Pero exijo que solo se dirijan a mí aquellos que tengan el suficiente poder... enviar a un ser de menos poder sería una insensatez.' WHERE `entry` = 6334;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te saludo, $R.' WHERE `entry` = 6336;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Por fin soy libre de la corrupción que me tenía atado a este lugar...' WHERE `entry` = 6354;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias por lo que has hecho, $N.$B$BProtege el Cetro. Que te ayude en tu lucha contra el mal que ha invadido este lugar...' WHERE `entry` = 6373;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mucho tiempo atrás, mis hermanas dríades y yo entramos en estas cuevas para liberar el espíritu atormentado de mi tío, Zaetar. Sin embargo, la corrupción que ya se había extendido por estas cuevas, pronto se apoderó de nosotras.$B$BFuimos condenadas a vagar por estos tres pasadizos; las toxinas envenenaban nuestras mentes...$B$BAunque mi espíritu ahora puede descasar, sé que he fallado en mi misión. Y me embarga una gran tristeza...' WHERE `entry` = 6380;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, Zaetar... <Celebras aparta su mirada.>$B$BMi padre, Rémulos, intentó detenerle...$B$BZaetar era muy obstinado y, cuando se enamoró de una princesa elemental de la tierra, mi padre supo que esa unión estaba condenada, pero Zaetar se negó a escuchar.$B$BLos centauros son el resultado de su error. Nacieron deformes y llenos de odio; mi tío Zaetar murió a manos de sus hijos...$B$BLa malvada princesa, Theradras, sigue guardando sus restos aquí, en las Cuevas de Cristal.' WHERE `entry` = 6381;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a la Liga de Expedicionarios. Si necesitas información, y esa información viene en algún libro, quizás pueda ayudarte.' WHERE `entry` = 6383;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando las toxinas que flotaban en Maraudon empezaron a filtrarse en mi mente, comencé a perder el contacto con la realidad. Mis hermanas dríades, preocupadas por si lo utilizaba para hacer el mal, me lo quitaron en secreto y lo partieron en dos antes de esconderlo en las cuevas.$B$BPronto, ellas también cayeron víctimas de la locura y las dos partes del Cetro, desprotegidas, fueron a parar al malvado Lenguavil. El sátiro le confió una de las partes a Noxxion, un elemental malvado, y se quedó con la otra.' WHERE `entry` = 6384;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sin el conocimiento de los encantamientos necesarios para unir ambas partes, Lord Lenguavil no podía usar el Cetro. Sin embargo, ahora que soy libre de la maldición de la corrupción de Lenguavil, podré ayudarte si quieres traerme las dos partes. La cabeza y la vara del cetro.' WHERE `entry` = 6385;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Hijos de Cenarius, Rémulos y Zaetar.$B$BMi padre, Rémulos, era fuerte y hermoso, y mi tío, Zaetar, muy astuto y de complexión pequeña. Si bien eran respetados por igual, Zaetar siempre sintió que no recibía la misma gloria y atención que su hermano.$B$BDespués de que mis hermanas y yo nacimos, Zaetar, cegado por los celos, partió para intentar superar a su hermano... Fue el creador de su propio fin. Todavía me produce una gran tristeza, incluso con el paso del tiempo...' WHERE `entry` = 6393;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $N. Soy el doctor Gregory Víctor, cirujano de traumatismos cerebrales de la Horda.' WHERE `entry` = 6413;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegra verte de nuevo, doctor $N. He estado siguiendo tu progreso y, debo decir, que ya es hora de que aprendas algunas técnicas de vendaje más Avanzadas.$B$BVen a verme de vez en cuando, según vayas progresando, y te enseñaré todo lo que sé.' WHERE `entry` = 6414;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $N. Soy el doctor Gustaf VanHowzen, cirujano de traumatismos cerebrales de la Alianza.' WHERE `entry` = 6415;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estoy a cargo del reclutamiento para el clan Lobo Gélido. Como Soldado de Lobo Gélido, es tu responsabilidad venir a informarme según avances en tu clasificación dentro del clan.$B$BEn este campo gélido se gana honor y respeto.' WHERE `entry` = 6475;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Saludos, $R! Soy el teniente Haggerdin, oficial a cargo del reclutamiento de la Guardia de Pico Tormenta.$B$BSegún vayas obteniendo prestigio y notoriedad dentro del clan, ven a hablar conmigo y veremos lo que puedo hacer para mejorar tu insignia. Si alguna vez pierdes tu insignia, podré darte una de repuesto.' WHERE `entry` = 6476;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, creo que serías un buen líder. ¿Estás aquí para organizar una hermandad que luche por la Alianza?' WHERE `entry` = 6514;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Qué quieres saber sobre el Vuelo Azul?' WHERE `entry` = 6534;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Al maestro Nesingwary le espera una gran cacería. Encuentro mucho más relajante ayudarle a prepararse para un día en la jungla, que para el combate. Aunque echo de menos algunos de esos momentos gloriosos en el campo de batalla.' WHERE `entry` = 6535;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Conozco estos bosques como la palma de mi mano... Pero has de saber que todavía pueden enseñarnos mucho, y todavía hay muchos peligros ocultos.' WHERE `entry` = 6553;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Eh, hola. No, Marvon no está aquí y no volverá hasta dentro de mucho. De hecho, estoy empezando a preocuparme...$B$BEspero que vuelva pronto. Llegó un gran cofre para él y es demasiado pesado para mí, no puedo meterlo dentro. He tenido que dejarlo fuera, al lado de la puerta...' WHERE `entry` = 6557;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Has aprendido todo lo que puedo enseñarte por el momento, doctor $N. Vuelve a verme cuando tengas más experiencia y te enseñaré una medicina más Avanzada.' WHERE `entry` = 6573;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Este lugar... está maldito.$B$BConseguí escapar por los pelos, y no puedo soportar la idea de tener que volver a bajar a esos túneles.$B$BMi hermano y mis hermanas, sin embargo... No tuvieron tanta suerte. Atrapados dentro, han sido contaminados por la bruma corrupta que emana del jardín malvado.$B$BPor favor, tienes que ayudarnos.' WHERE `entry` = 6574;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Necesitas más suministros?' WHERE `entry` = 6594;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quieres ver lo que vendo?' WHERE `entry` = 6595;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Incapaz... sin poder... restaurar... debo restaurar... Zevrim controla... acabar con este tormento...' WHERE `entry` = 6694;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias, mortal. Solo puedo ofrecerte un servicio antes de regresar al Sueño.$B$BLa puerta del maestro Zevrim, Alzzin, bloquea tu camino. Seré tu instrumento de destrucción. La puerta se abrirá, solo tienes que darme la orden.' WHERE `entry` = 6695;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Necesito muchos más suministros para mejorar nuestras unidades.' WHERE `entry` = 6735;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí, $C?' WHERE `entry` = 6755;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Necesito muchos más suministros para mejorar nuestras unidades.' WHERE `entry` = 6777;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Casi tengo suficientes suministros para mejorar nuestras tropas.' WHERE `entry` = 6778;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestras unidades no están mejoradas y no tengo suficientes suministros para hacerlo.' WHERE `entry` = 6785;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu habilidad supera a la mía, pero he oído que el Viejo Heming en la Bahía del Botín, tiene ejemplares de \"La lubina y tú\". Seguro que te ayuda a mejorar tu habilidad.', `text0_1_loc7` = 'Tu habilidad supera a la mía, pero he oído que el Viejo Heming en la Bahía del Botín, tiene ejemplares de \"La lubina y tú\". Seguro que te ayuda a mejorar tu habilidad.' WHERE `entry` = 6793;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos minas en Alterac: La Mina Ferrohondo en territorio de la Alianza y la Mina Dentefrío, en territorio de la Horda. Ambas minas son una gran fuente de minerales y, en el pasado, se utilizaban para almacenar suministros de sobra.$B$BAhora, la Horda, la Alianza y los habitantes locales luchan por su control.' WHERE `entry` = 6796;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las torres y los búnkeres del Valle de Alterac son puntos de estrategia vitales. Si controlamos una torre, nuestras tropas podrán disparar a gran distancia.' WHERE `entry` = 6797;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los cementerios del Valle de Alterac, son donde las almas de los fallecidos encuentran descanso y socorro. Si mueres, tu espíritu irá al cementerio más cercano controlado por los nuestros.' WHERE `entry` = 6798;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Torre de la Punta domina el paso entre la Tierra de Disputa y los territorios del sur de la Horda.$B$BAhora mismo no pertenece a nadie.' WHERE `entry` = 6833;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Torre Oeste Lobo Gélido protege la puerta entre la Aldea Lobo Gélido y el Bastión Lobo Gélido.$B$BActualmente pertenece a la Horda.' WHERE `entry` = 6838;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los capitanes de la Horda y la Alianza han entrado en el campo. Nuestra capitana, Balinda Piedrahogar, dirige a las tropas Pico Tormenta desde la Avanzada Piedrahogar y el capitán enemigo, Galvangar, ladra órdenes desde el interior del Baluarte Sangrehielo.$B$B¡A la batalla!' WHERE `entry` = 6846;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los capitanes de la Horda y la Alianza han entrado en el campo. Nuestro capitán, Galvangar, dirige a los Lobo Gélido desde el Baluarte Sangre Fría y su oponente, la capitana Balinda Piedrahogar, se esconde en la Avanzada Piedrahogar.$B$B¡La batalla ha comenzado!' WHERE `entry` = 6848;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Valle de Alterac ha sido el hogar del clan Lobo Gélido durante muchos años. No permitiremos que los enanos y su Alianza entren en casa. ¡Y por eso luchamos!$B$BPara recuperar Alterac, hay que derrotar a su general, Vanndar Pico Tormenta, en Dun Baldar.$B$B¡Por la Horda!' WHERE `entry` = 6853;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Pico Tormenta descubrieron unos antiguos artilugios en el Valle de Alterac, pero a los orcos Lobo Gélido no les gustan mucho los expedicionarios. Si los enanos tienen que luchar por conseguir la sabiduría de sus ancestros, ¡que así sea!$B$BPara ganar esta batalla, hay que derrotar al orco Drek\'Thar, en el Bastión Lobo Gélido. ¡Que la suerte sea contigo, $C!' WHERE `entry` = 6854;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La capitana Balinda Piedrahogar lidera a las tropas de la Alianza de la primera línea. Está en la Avanzada Piedrahogar, en la sección norte de la Tierra de Disputa.$B$BActualmente Balinda está en la Avanzada Piedrahogar.' WHERE `entry` = 6859;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Drek\'Thar es el líder militar y espiritual del clan Lobo Gélido en Alterac, lidera a los suyos desde el Bastión Lobo Gélido.$B$BSi le derrotamos, las tropas de la Horda en Alterac se desmoronarán.' WHERE `entry` = 6861;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El capitán Galvangar es la mano derecha de Drek\'Thar. Lidera a las tropas de la Horda desde el Baluarte Sangrehielo, en la sección sur de la Tierra de Disputa.$B$BEl capitán Galvangar está en el Baluarte Sangrehielo.' WHERE `entry` = 6863;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tú gran $Gjefe:jefa; ahora! ¡Tú $Grey:reina;! ¡Todos saludar $Gal rey:a la reina; $N de Gordok!$B$B¡Ahora que tú ser $Gjefe:jefa;, todos los esbirros te darán un tributo! ¿Has dejao algunos vivos, eh, $Gjefe:jefa;?' WHERE `entry` = 6876;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quieres jugar a un juego, $Gapestoso:apestosa;?' WHERE `entry` = 6877;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tengo la llave de las tierras de la libertad! ¡Persígueme y verás!' WHERE `entry` = 6878;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sigues con vida? Me sorprendes. ¡Mira cómo corro!' WHERE `entry` = 6879;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Se acabó, no somos amigos!' WHERE `entry` = 6881;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mmm... ¿Tú matar al rey, sí? Eso significa que tú ocupar su lugar. ¡Eso te convierte en $Gel nuevo rey:la nueva reina;! ¡Viva $Gel rey:la reina;!' WHERE `entry` = 6882;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Vete de aquí, $Gforastero:forastera;, o te consumirá como ha hecho con todo lo que toca.' WHERE `entry` = 6896;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Conoces a los Altonato? ¿Los Shen\'dralar?' WHERE `entry` = 6897;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los Shen\'dralar eran los arcanistas más venerados de la reina Azshara. Fue entre estos muros, donde se cumplieron las demandas de la reina, a menudo en secreto.$B$BDespués de la destrucción del pozo y el subsiguiente letargo que se apoderó de los Altonato y de las sectas menores de elfos de la noche, el príncipe de Eldre\'Thalas, Tortheldrin, comenzó la construcción de las torres que ves en las salas del ala oeste.' WHERE `entry` = 6898;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El Príncipe planeó encarcelar a un demonio de gran poder en un campo de fuerza. Las torres canalizarían las energías latentes hacia el campo, manteniéndolo operativo.$B$BMientras estuviera atrapado, Immol\'thar serviría como energía para el pueblo de Eldre\'Thalas.' WHERE `entry` = 6899;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Las energías absorbidas sustentarían la magia de los Altonato, permitiendo a aquellos que estaban en Eldre\'Thalas alimentar su infinita y creciente magia.$B$BMiles de años pasaron, y el poder requerido para mantener encarcelado a Immol\'thar a la vez que se mantenía la vida de Eldre\'Thalas, comenzó a cambiar. El coste de mantener a Immol\'thar, comenzó a eclipsar la cantidad de energía que podía ser absorbida y distribuida. La antigua red perfecta se convirtió en una gran pérdida.' WHERE `entry` = 6900;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los fantasmas Altonato que ves vagando por el patio y las salas de Eldre\'Thalas fueron asesinados, no por ogros Gordok invasores ni sátiros, ¡sino por el mismísimo Príncipe!' WHERE `entry` = 6901;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Fueron destruidos para preservar la vida inmortal del Príncipe y aumentar sus ilimitados poderes. El poder antiguamente dividido entre miles, ahora solo pertenece al príncipe y los zelotes de Shen\'dralar. Los que sobrevivieron, emanan poder directamente en las torres para mantener encarcelado a Immol\'thar.$B$BSi el demonio rompiese sus cadenas, seguramente destruiría lo poco que queda de este antiguo bastión de los poderes arcanos.' WHERE `entry` = 6902;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No debemos enfurecer al Maestro. Su ira es cruel e irracional.', `text0_1_loc7` = 'No debemos enfurecer al Maestro. Su ira es cruel e irracional.' WHERE `entry` = 6911;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Oh, el horror! ¡La tragedia! Estamos a punto de conseguir un gran avance, pero todo podría perderse.' WHERE `entry` = 6917;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un examen superficial del esqueleto indica que la carne fue quemada. Una visión espeluznante.' WHERE `entry` = 6919;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Si no hablar con Mizzle, nunca ser $Grey:reina;! ¡Habla con Mizzle el Astuto!' WHERE `entry` = 6923;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debes entender que 10000 años de existencia me han proporcionado una perspectiva de la vida única. Tus tribulaciones carecen de sentido.' WHERE `entry` = 6925;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<En el papiro hay un dibujo esquemático de un robot de reparación de campo.>' WHERE `entry` = 6930;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Entiendes a la perfección la creación y el funcionamiento de este robot.>' WHERE `entry` = 6932;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La tela con la que está tejido nuestro mundo, es extremadamente delicada. Solo se necesita algo de conocimiento y saber aplicar dicho conocimiento para rasgar la tela. Así es como nacen las discordias.' WHERE `entry` = 6933;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo soy quien ejecuta las órdenes del Jefe de Guerra, nadie me puede cuestionar. Yo soy la Horda.' WHERE `entry` = 6935;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solo vivo para servir a nuestro rey.' WHERE `entry` = 6936;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Quizás algún día, tus contribuciones a la Alianza sean tan grandes como las de los héroes que has visto hoy. Hónrales, $N.' WHERE `entry` = 6937;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Puedo ayudarte?' WHERE `entry` = 6948;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, pero lo conozco por su verdadero nombre: Eldre\'Thalas. Fue un bastión de poder mágico para los elfos de la noche durante muchos, muchos años.$B$B¿Habías oído hablar de Eldre\'Thalas, joven? Se podría decir que coincide con mi situación actual... Pero no creo en las coincidencias.' WHERE `entry` = 6949;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Unos miembros del Círculo Cenarion han descubierto información muy preocupante sobre la situación actual de Eldre\'Thalas. Estoy en un punto muerto, no sé qué hacer y buscaré sin duda la ayuda de individuos capaces de combatir.$B$BSin embargo, para poder emprender cualquier acción en Eldre\'Thalas, es necesario comprender el funcionamiento de un objeto conocido como la fruta de la fertilidad.' WHERE `entry` = 6950;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En el pasado, Eldre\'Thalas era un nexo de poder mágico y, quienes habitaban dentro de sus muros sagrados, tenían un inmenso poder. Una de esas elfas de la noche fue Millicent Serena, una naturista que canalizaba el poder de la naturaleza en los potentes artefactos que forjaba.$B$BLa fruta de la fertilidad fue su mayor logro... Utilizó una poza de la luna especialmente preparada, para sembrar una hiedra exótica y vibrante que fue imbuida de un aura de protección y crecimiento. Todo Eldre\'Thalas floreció a partir de su existencia.' WHERE `entry` = 6951;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, parece ser que algo siempre sale mal. Yo, al igual que el resto del Círculo Cenarion, asumí que la creación de Milli fue destruida durante La Guerra de los Ancestros. Pero, como he llegado a descubrir, no es cierto.$B$BLa fruta de la fertilidad sobrevivió... Aunque ahora se llama gangrevid. El señor demoníaco de esa sección de Eldre\'Thalas, Alzzin el Formaferal, la está corrompiendo.' WHERE `entry` = 6952;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No puedo ni imaginármelo, $N. Lo único que sé, es que no pienso permitir que eso ocurra. Hay que destruirla... Si no por mantenernos a salvo de la mancha demoníaca, al menos en memoria de Millicent Serena... Milli...$B$BY, $Gamigo mío:amiga mía; espero que me ayudes.' WHERE `entry` = 6953;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Saludos, $Ghéroe:heroína;! Tengo las últimas noticias de ambos continentes y de lugares que ni siquiera conoces.', `text0_1_loc7` = '¡Saludos, $Ghéroe:heroína;! Tengo las últimas noticias de ambos continentes y de lugares que ni siquiera conoces.' WHERE `entry` = 6957;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tendrás que encontrar a Nat Pagle en el Marjal Revolcafango para mejorar tus habilidades.', `text0_1_loc7` = 'Tendrás que encontrar a Nat Pagle en el Marjal Revolcafango para mejorar tus habilidades.' WHERE `entry` = 6960;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo enseñarte a pescar.', `text0_1_loc7` = 'Puedo enseñarte a pescar.' WHERE `entry` = 6961;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los orcos hundieron muchas cajas de suministros en el Lago Sempiterno, en Crestagrana.', `text0_1_loc7` = 'Los orcos hundieron muchas cajas de suministros en el Lago Sempiterno, en Crestagrana.' WHERE `entry` = 6980;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La guerra conlleva unos costes, $C... Unos que, a menudo, se pasan por alto. Esos costes son los huérfanos, niños que han perdido a sus padres en los demasiado numerosos conflictos que asolan Azeroth. Esos niños, que no tienen a quien acudir, a menudo vienen a vivir aquí hasta que son lo suficientemente mayores para no depender de nadie.$B$BUna semana al año, en la primavera, la Alianza intenta darles algo a estos inocentes. Y esto se llama la Semana de los Niños.$B', `text0_1_loc7` = 'La guerra conlleva unos costes, $C... Unos que, a menudo, se pasan por alto. Esos costes son los huérfanos, niños que han perdido a sus padres en los demasiado numerosos conflictos que asolan Azeroth. Esos niños, que no tienen a quien acudir, a menudo vienen a vivir aquí hasta que son lo suficientemente mayores para no depender de nadie.$B$BUna semana al año, en la primavera, la Alianza intenta darles algo a estos inocentes. Y esto se llama la Semana de los Niños.' WHERE `entry` = 7011;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, joven. Te doy la bienvenida a Cima del Trueno. Aquí encontrarás un refugio seguro de las cargas del mundo. Todos los miembros de la Horda son bienvenidos aquí.$B$BLos espíritus del presente y del pasado conviven aquí, en este sagrado lugar. Hónrales y, al hacerlo, hónrate a ti $Gmismo:misma;.' WHERE `entry` = 7013;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cocinar es algo para toda la vida, y veo que compartimos la misma pasión.', `text0_1_loc7` = 'Cocinar es algo para toda la vida, y veo que compartimos la misma pasión.' WHERE `entry` = 7015;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Dirge Hojágil de Gadgetzan es un maestro de las artes culinarias. Búscale si quieres que tu habilidad mejore.', `text0_1_loc7` = 'Dirge Hojágil de Gadgetzan es un maestro de las artes culinarias. Búscale si quieres que tu habilidad mejore.' WHERE `entry` = 7017;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo enseñarte a cocinar.', `text0_1_loc7` = 'Puedo enseñarte a cocinar.' WHERE `entry` = 7021;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A estas alturas, solo el famoso doctor Gregory Víctor puede ayudarte a mejorar tu habilidad. Puedes encontrarle en las Tierras Altas de Arathi, en Sentencia.', `text0_1_loc7` = 'A estas alturas, solo el famoso doctor Gregory Víctor puede ayudarte a mejorar tu habilidad. Puedes encontrarle en las Tierras Altas de Arathi, en Sentencia.' WHERE `entry` = 7024;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El estimado doctor Gustav VanHowzen, puede darte una instrucción mejor. Puedes encontrarle en Theramore, cuidando a los enfermos en el cuartel.', `text0_1_loc7` = 'El estimado doctor Gustav VanHowzen, puede darte una instrucción mejor. Puedes encontrarle en Theramore, cuidando a los enfermos en el cuartel.' WHERE `entry` = 7026;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He oído decir que Deneb Camino, en Stromgarde, tiene un libro que podría ayudarte a mejorar tus técnicas de primeros auxilios.', `text0_1_loc7` = 'He oído decir que Deneb Camino, en Stromgarde, tiene un libro que podría ayudarte a mejorar tus técnicas de primeros auxilios.' WHERE `entry` = 7027;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo enseñarte técnicas de primeros auxilios.', `text0_1_loc7` = 'Puedo enseñarte técnicas de primeros auxilios.' WHERE `entry` = 7028;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Búscala en la parte superior del lado oeste del Valle de la Fuerza.', `text0_1_loc7` = 'Búscala en la parte superior del lado oeste del Valle de la Fuerza..', `text1_0_loc7` = 'Tienes que ir a la Sala de las Leyendas. Está en la parte superior del Valle de la Fuerza, en el lado oeste.', `text1_1_loc7` = 'Tienes que ir a la Sala de las Leyendas. Está en la parte superior del Valle de la Fuerza, en el lado oeste.' WHERE `entry` = 7046;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Así que ahora, de pronto, eres $Gun \'Defensor de la Alianza\':una \'Defensora de la Alianza;? Bueno, supongo que querrás ir a la Sala de los Campeones. Está en el Casco Antiguo.', `text0_1_loc7` = '¿Así que ahora, de pronto, eres $Gun \'Defensor de la Alianza\':una \'Defensora de la Alianza;? Bueno, supongo que querrás ir a la Sala de los Campeones. Está en el Casco Antiguo.', `text1_0_loc7` = 'Bueno, bueno... ¿Ascendiendo en el mundo, eh? Estás buscando la Sala de los Campeones, y la encontrarás en la parte sureste del Casco Antiguo. ¡Buena suerte!', `text1_1_loc7` = 'Bueno, bueno... ¿Ascendiendo en el mundo, eh? Estás buscando la Sala de los Campeones, y la encontrarás en la parte sureste del Casco Antiguo. ¡Buena suerte!' WHERE `entry` = 7047;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si has venido a buscar la mejor artesanía de este o cualquier otro continente del mundo, entonces has venido al lugar adecuado.$B$B¡Grimand Elmore, a tu servicio!' WHERE `entry` = 7055;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sssí, ess verda. ¡Toy tan ciego como un murciélago! Y borrrracho como... Ehhh, borrrracho como algo que ssse emborrrracha.$B$BTe diré algo, $R. La bebida mantiene misss sssentidosss alerta. To\'l mundo sssabe que cuando pierdesss un sssentido, losss otrosss sssentidosss tán másss alerta. Mi sssentido de la bebida esss increíble últimamente.' WHERE `entry` = 7094;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hemos elegido abandonar la protección y el confort de las Profundidades... Ehh... Mmm... Sí, eso, para EXPLORAR el mundo.' WHERE `entry` = 7095;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Después de todo, quizás este lugar no era el indicado para una tienda...' WHERE `entry` = 7114;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Burninate es mi nombre, la herrería, mi juego favorito... Y, eeeh... Vendo algo de comida y agua. ¿Te apetece carne?' WHERE `entry` = 7115;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola de nuevo, $N! No sé cuánto tiempo más voy a seguir intentando establecer una tienda aquí, en medio de ninguna parte. De todas formas, como me ayudaste en el pasado, te enseñaré un par de cosas que quizás te parezcan interesantes.' WHERE `entry` = 7116;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando Lolo no quiere derramar ná de su valiosa cerveza o mancharse el vestidito, me llama. Sip. El viejo Hansel es el enano al que usan pa administrar algo de justicia y mucho dolor.' WHERE `entry` = 7117;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como capataz de este lugar, mi deber es asegurarme de que hay cierto nivel de tedio en todas las formas de negocio, trabajo, descanso y relajación que ocurren aquí.$B$BSi algo que te manden hacer te parece demasiado fácil y sin complicaciones, ven a hablar conmigo inmediatamente.' WHERE `entry` = 7120;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hermandad del Torio o no, si uno de los Hierro Negro se acerca a mí, ¡probará mi azadón justo en su cabeza! Y bien, ¿dónde puedo enviarte, $Gmuchacho: muchacha;?' WHERE `entry` = 7122;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mientras esos enanos se queden ahí, no tendremos problemas. ¿Dónde te diriges, $N?' WHERE `entry` = 7123;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿No ves que estoy pescando aquí?' WHERE `entry` = 7175;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vete de aquí, $Gciudadano:ciudadana;. No hay nada que ver aquí.' WHERE `entry` = 7179;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Eh, tú! ¡Si quieres ganarte el respeto de los Lanza Negra, puedes empezar donando valiosos recursos a quienes los necesitan! Las telas y las vendas no crecen el los árboles, ¿sabes?$B$BEn lugar de vender los paños que te encuentras en tus aventuras, ¡dónalos! Hacerlo te proporcionará el reconocimiento de los Lanza Negra.' WHERE `entry` = 7186;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'En estos tiempos difíciles, una simple donación de paño haría mucho por ayudar a aquellos menos afortunados que nosotros mismos. El paño es necesario para hacer ropas y vendas, ambas cosas son muy necesarias para la guerra.$B$BSi quieres donar paños a los orcos de Orgrimmar, los aceptaré y me aseguraré de que recibas el reconocimiento adecuado por ello.$B$B¡Por la Horda!' WHERE `entry` = 7188;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Eh, tú! Por tu bien, debes ayudar a los Renegados en estos tiempos nefastos. Escucha atentamente, no me gusta repetirme.$B$BActualmente tenemos una terrible escasez de paño. Nos estamos quedando sin vendas y sin telas y aceptamos donaciones para aumentar los inventarios. Si bien no recibirás una compensación monetaria, por eso se llama donación, tu ayuda no pasará desapercibida.' WHERE `entry` = 7189;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te saludo, $Ghermano: hermana;... Me gustaría hablar contigo unos minutos, si es posible.$B$BNosotros, los taurens, hemos sufrido muchos problemas y tribulaciones para establecer nuestro hogar. Ahora nos encontramos con una gran escasez de paño. Si te sobran materiales, te ruego que los dones. Te ganarás la gratitud de los taurens de todas partes por ayudarnos.' WHERE `entry` = 7190;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $Gamigo:amiga;, me gustaría hablar contigo unos minutos, si es posible.$B$BHemos sufrido muchos problemas y tribulaciones para establecer Teldrassil. Por si no fuera bastante con los constantes conflictos, ahora nos encontramos con una gran escasez de paño. Si te sobran materiales, te ruego que los dones. Te ganarás la gratitud de los elfos de la noche de todas partes por ayudarnos.' WHERE `entry` = 7193;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Exiliados de Gnomeregan están en un terrible estado, ¡las reservas de paño se están agotando! ¿Quizás puedas ayudarnos, eh?$B$BTráeme paños que puedas tener para ayudarnos a llenar nuestras reservas. Con esa donación, te ganarás el respeto y la confianza de los gnomos de todas partes. ¡Y eso es algo que el dinero no puede comprar! Bueno, supongo que el paño sí lo puede comprar, pero eso va más allá de...' WHERE `entry` = 7194;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Eh, tú! Si quieres conseguir el respeto de los enanos de Forjaz, ¡empieza a donar valiosos recursos! Forjaz se encuentra con una gran escasez de paños. Las telas y los vendas no crecen el los árboles, ¿sabes?$B$BEn lugar de vender los paños que te encuentras en tus aventuras, ¡dónalos! Hacerlo te proporcionará el respeto de los ciudadanos de Forjaz.' WHERE `entry` = 7195;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GAmigo:Amiga; Ventormenta necesita tu ayuda y tu generosidad,$B$BNuestra antigua gran reserva de textiles está disminuyendo críticamente. Ventormenta se encuentra en pleno proceso a dos bandas para rellenar nuestras reservas para los fabricantes de telas y vendas. Tu donación de paños ayudaría inmensamente a Ventormenta, ¡y te proporcionaría el respeto de los ciudadanos!' WHERE `entry` = 7196;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tengo to\'s los suministros de cocina que podrías necesitar, colega!' WHERE `entry` = 7237;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Si tá roto, Esco puede arreglarlo!' WHERE `entry` = 7239;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Zul\'jin volverá algún día, $R. Acuérdate de lo que te digo. Hasta entonces, todos mantenemos nuestra posición y hacemos todo lo que podemos para que la población trol del bosque permanezca intacta.$B$BDicho eso, como maestro cazador del Poblado Sañadiente, es mi responsabilidad asegurar que el mundo que nos rodea, es un lugar seguro para habitar.$B$BHay que controlar a las bestias de esta zona.$B$B¿Estás buscando trabajo?' WHERE `entry` = 7242;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Kilram, maestro del hacha.' WHERE `entry` = 7243;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No te acerques demasiado, $N. Puedo aplastarte y abrirte el cerebro a golpes con mi martillo.' WHERE `entry` = 7245;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Atrás o te cortaré en dos.' WHERE `entry` = 7247;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Acabo de completar un nuevo implosionador dimensional que me hará más rico de lo que cualquier goblin puede soñar. Todo lo que necesito es un ingeniero gnomo capacitado para construir el desgarrador dimensional que lo conecta y probarlo.' WHERE `entry` = 7249;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias a la tecnología que consiguieron recuperar los astutos gnomos que se adentraron en Gnomeregan, he conseguido construir mi mayor invento: ¡El transportador de materia! Ahora, todo lo que necesito es un hábil ingeniero gnomo para construir un potente localizador que se adapte al transportador.' WHERE `entry` = 7251;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Coge la llave y abre estas cadenas! La tiene Hitah\'ya el Guardián... En la parte de atrás de la cueva...' WHERE `entry` = 7257;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ofertas de trabajo/Perdido y encontrado/Se busca:' WHERE `entry` = 7274;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$gBienvenido:Bienvenida;! Te doy la bienvenida al Zoo de Singularidades Luna Negra de Yebb Tesorillos: ¡Un tesoro de leyendas vivientes y rarezas míticas! ¡Que disfrutes de la visita, $gamigo:amiga;!$B$B¡Pero cuidado con lo que tocas! En las exhibiciones hay bestias y artefactos tanto letales como divinos. Podrían quemar la mano caprichosa de un observador demasiado curioso...' WHERE `entry` = 7314;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Dónde te puedo mandar hoy, $C?',  `text1_0_loc7` = 'El Cruce parece ser el centro de actividad de Los Baldíos, yo prefiero la tranquilidad del Campamento Taurajo. ¿Dónde te voy a mandar?', `text2_0_loc7` = 'Muchos son los caminos de la Madre Tierra. Decidas donde decidas ir, que ella te proteja.', `text3_0_loc7` = 'Trata bien al jinete del viento, ya que te lleva a tu destino. Que tus ancestros te protejan, $N.' WHERE `entry` = 7315;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola, $gamigo:amiga;, te doy la bienvenida al mayor espectáculo de todo Azeroth!$B$BPor favor, pasa y disfruta de todo lo que podemos ofrecerte. ¡Sorpréndete con las maravillas que la Feria de la Luna Negra ha descubierto en este vasto y misterioso mundo! No hemos reparado en gastos para poder ofrecer una diversión tan increíble que deleitará a niños de todas las edades.' WHERE `entry` = 7334;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No todo se trata de dinero! Recibimos donaciones muy generosas, algunas de ellas extremadamente sustanciales. Y, además, nuestra compañía está motivada por la necesidad de animar este mundo devastado por la guerra. No te preocupes por nosotros. Deja que nosotros nos preocupemos por cómo divertirte.' WHERE `entry` = 7336;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Canje de vales! ¡Cambiamos vuestros vales por premios! ¡Venid!' WHERE `entry` = 7337;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vete de aquí, $gpalurdo:palurda;.',  `text1_0_loc7` = 'Esto no es una atracción, $gpaleto:paleta;. Lárgate.', `text2_0_loc7` = 'Si estás intentando ganarme con tu simpatía, no gastes saliva. O mejor, trágatela toda a ver si te ahogas...', `text3_0_loc7` = 'Por si no ha quedado claro, no estoy aquí de relaciones públicas.', `text4_0_loc7` = 'Si te metes con Silas, te metes conmigo. Que tengas un buen día.', `text5_0_loc7` = 'Necesito tanto una segunda cabeza, como perder la primera...', `text6_0_loc7` = 'Viví en La Masacre durante un tiempo. Fue divertido. Mucho.', `text7_0_loc7` = 'Al contrario de muchos ogros, yo sí sé para lo que sirve una letrina exterior, para darte una paliza. Y ahora, déjalo.' WHERE `entry` = 7338;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sabía desde hace mucho tiempo que vendrías a buscarme, $N.$B$BTodos los seres sensibles del mundo dependen en gran medida de las decisiones que toman. Las decisiones conllevan más decisiones y, estas, nos convierten en lo que somos. Ahí es yace tu fortuna, quién eres y las decisiones que tomas.$B$B¿$gListo:lista; para descubrir dónde yace tu fortuna?' WHERE `entry` = 7339;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pues muy bien...$B$BTu señor te ha encargado la tarea de proteger sus campos de maíz de cazadores furtivos y ladrones. Una noche de patrulla, te encuentras con un hombre demacrado con una túnica raída que está robando en el campo. Rápidamente, le confrontas y él te suplica que le perdones la vida. Dice que está robando maíz para alimentar a su familia porque el señor de la tierra, tu señor, exige demasiados impuestos. De hecho, tu señor es famoso por su dureza a la hora de imponer tasas en sus tierras.$B$BToma tu decisión.' WHERE `entry` = 7340;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te espera tu próxima decisión.$B$BEres el verdugo de tu señor. Te han enviado a un hombre para que le ejecutes por traicionar a tu señor y a tu pueblo. Conoces a este individuo. Era un buen amigo tuyo que, misteriosamente, desapareció un día. También sabes que los crímenes por los que ha sido juzgado, han causado la muerte de muchos civiles inocentes. Cuando te ve, te suplica que le perdones la vida y dice que una gran autoridad le tendió una trampa para incriminarle.$B$BToma tu decisión.' WHERE `entry` = 7341;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Te doy la bienvenida a la feria, joven! Tenemos maravillas de todos los confines del mundo para ti. Oh, y por favor, avísame si consigues reunir una baraja de cartas menores de la Luna Negra, al maestro... quiero decir, a mí, me interesaría mucho...' WHERE `entry` = 7355;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos esparcido cartas de la Luna Negra por todo el mundo, para ver si algún individuo capaz, podría completar uno de los ocho palos. Si consigues reunir las ocho cartas de uno de los palos, te recompensaremos con una de las cartas superiores de la Luna Negra, que funciona como una potente alhaja, además de ser un vínculo a la Feria de la Luna Negra.' WHERE `entry` = 7356;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si consigues completar una baraja de Bestias, te recompensaremos con un naipe superior de la Luna Negra del dragón azul. Es muy útil para restaurar energías mágicas.' WHERE `entry` = 7357;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si consigues completar una baraja de Portales, te recompensaremos con un naipe superior de la Luna Negra de El Vacío Abisal. Este naipe puede volver a meter tu espíritu en tu cuerpo cuando hayas muerto y restaurar tu salud.' WHERE `entry` = 7358;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si consigues completar una baraja de Elementales, te recompensaremos con un naipe superior de la Luna Negra de La Vorágine. Este naipe puede causar un gran daño a un enemigo con quien estés luchando.' WHERE `entry` = 7359;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si consigues completar una baraja de Señores de la Guerra, te recompensaremos con un naipe superior de la Luna Negra del Heroísmo. Este naipe puede curarte cuando estés en combate de melé.' WHERE `entry` = 7360;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu fortuna ha sido decidida, $c. Durante las próximas horas, encontrarás que tu espíritu adopta la naturaleza de tu verdadero ser. Ten cuidado, ya que tu fortuna, y tu destino, nunca son tan transparentes como puedes creer.$B$BSi quieres una pequeña visión para ayudarte a considerar tu futuro, dímelo. Te daré encantado tu fortuna por escrito, siempre que tengas espacio en tu inventario. Todo ha sido el resultado de tus respuestas a mis preguntas.' WHERE `entry` = 7364;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí tienes tu fortuna, $N. Espero que te ayude.' WHERE `entry` = 7365;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Llega a la ciudad la Feria de la Luna Negra! ¡Que nadie se lo pierda!',  `text1_0_loc7` = '¡Aseguraos de ver todas las maravillas increíbles de la Feria de la Luna Negra cuando llegue a la ciudad!', `text2_0_loc7` = '¡Llega la Feria de la Luna Negra! ¡Diversión para niños de TODAS las edades! ¡Vamos, venid todos!', `text3_0_loc7` = 'Venid todos, ¡el espectáculo más asombroso de todo Azeroth, la Feria de la Luna Negra! ¡Hurra!' WHERE `entry` = 7366;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Está aquí! ¡La Feria de la Luna Negra está abierta! ¡No os perdáis el espectáculo más excitante de Azeroth!',  `text1_0_loc7` = '¡Llega a la ciudad la Feria de la Luna Negra! ¡El espectáculo más apasionante que jamás hayáis visto!', `text2_0_loc7` = 'La Feria de la Luna Negra solo estará abierta durante un breve período de tiempo antes de que se bajen las cortinas. ¡Aseguraos de venir hoy!', `text3_0_loc7` = '¡Niños y niñas de todas las edades, ha llegado la Feria de la Luna Negra! ¡Hurra!' WHERE `entry` = 7374;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ahora mismo, la Feria de la Luna Negra está en las doradas praderas de Mulgore, al suroeste de Cima del Trueno. ¡Que nadie se lo pierda!' WHERE `entry` = 7377;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Seguro que hay muchos bichejos por aquí.', `text0_1_loc7` = 'Seguro que hay muchos bichejos por aquí.', `text1_0_loc7` = '¿Te lo estás pasando bien?', `text1_1_loc7` = '¿Te lo estás pasando bien?', `text2_0_loc7` = '¡No te olvides de comprar comida y regalos!', `text2_1_loc7` = '¡No te olvides de comprar comida y regalos!', `text3_0_loc7` = 'La Feria de la Luna Negra es el espectáculo más importante de todo Azeroth.', `text3_1_loc7` = 'La Feria de la Luna Negra es el espectáculo más importante de todo Azeroth.' WHERE `entry` = 7382;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Te doy la bienvenida a la Feria de la Luna Negra, $c!$B$BMe llamo Kerri Hicks y estrecharía tu mano, pero me da miedo romperla, ¡porque soy la mujer más fuerte del mundo!' WHERE `entry` = 7383;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Venid! ¡Alitas súper picantes aquí mismo!',  `text1_0_loc7` = 'Perritos de la Luna Negra, ¡recién sacados de la barbacoa! Venid a probarlos mientras están calientes.', `text2_0_loc7` = 'Tú tienes dinero y nosotros, comida. ¡¿Qué estás esperando?!', `text3_0_loc7` = '¡Edición limitada! Rana crujiente solo aquí, en la Feria de la Luna Negra.' WHERE `entry` = 7387;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Todo lo que tengo a la venta lo he adquirido yo misma. Tienes mi garantía personal de autenticidad.', `text1_1_loc7` = '¿Cartas de la Feria de la Luna Negra? Busca al Profesor. Ven a verme si eres tratante de extrañas antigüedades y artefactos.', `text2_1_loc7` = 'Echa un vistazo a mis mercancías mientras todavía puedas. Estaremos aquí toda la semana y, después, nos iremos a algún sitio remoto.', `text3_1_loc7` = '¿Tienes alguna idea de lo que tengo que sufrir para traer estos incomparables objetos y ponerlos a la venta? ¡Ven a verlo con tus propios ojos!' WHERE `entry` = 7389;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Eh, que alguien me ayude a coger esta cosa!',  `text1_0_loc7` = '¡Se escapa!', `text2_0_loc7` = '¡Vuelve aquí!', `text3_0_loc7` = '¡Ranas y bolas de cuero a la venta!' WHERE `entry` = 7390;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Oh... Siento decirte que, ahora mismo, no puedo leer tu fortuna, ya lo he hecho una vez para ti.$B$BVuelve a verme dentro de unas horas. ¡Las fuerzas del Destino se habrán calibrado de nuevo y podré ver tu futuro otra vez!' WHERE `entry` = 7393;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos. Soy Chronos, el que nunca olvida. Mi memoria es perfecta y exacta incluso con el más ínfimo detalle.$B$BConfía en mí.' WHERE `entry` = 7395;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola! ¡$gBienvenido:bienvenida;! Me temo que mi puesto todavía no está abierto... Se necesitan muchas reparaciones...$B$BPero es mi obligación poner una sonrisa en tu cara, así lo he jurado. Así que, sonríe por favor, o tendré que cortarme la mano y no consumir carne viva hasta que se regenere...' WHERE `entry` = 7397;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No puedo encontrar a mi ranita, Jubjub. Le encanta la cerveza Hierro Negro, es lo único que bebe, ¡pero es tan difícil de conseguir! Me he quedado sin nada... ¡Y seguro que Jubjub se ha ido para encontrar más!$B$BOjalá vuelva...' WHERE `entry` = 7400;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debemos asegurar la supervivencia de este campamento a toda costa, $C. No hay que dar cuartel a nuestros enemigos, ya que ellos no nos lo darían si lo necesitásemos nosotros.' WHERE `entry` = 7402;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Deliciosas bebidas a la venta! Sacia tu sed con algunos de los mejores brebajes de la Feria de la Luna Negra.', `text1_1_loc7` = '¡Olvida tu timidez, invita a una ronda para todos!', `text2_1_loc7` = 'Siempre se aceptan propinas, camarada.', `text3_1_loc7` = 'Solo... Otra tanda de Gran Reserva de la Luna Negra. ¡Ven a por la tuya antes de que se acaben todas!' WHERE `entry` = 7404;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Croac!',  `text1_0_loc7` = 'Bésame.' WHERE `entry` = 7406;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, joven $C. ¡Aquí fue donde tu Jefe de Guerra desafió a Orgrim Martillo Maldito! Si bien todo ocurrió hace muchos años, la imagen sigue viva en mi recuerdo.' WHERE `entry` = 7415;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Has venido a aprender, ¿eh? Bueno, quizás tenga algo de trabajo para ti, escoria.' WHERE `entry` = 7434;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esto si te lo puedo prometer: el Valle será nuestro.' WHERE `entry` = 7438;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Liga de Arathor ha jurado recuperar Arathi para los humanos de Stromgarde y la Alianza. Es una dura batalla, pero una batalla que merece la pena luchar.' WHERE `entry` = 7516;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Es la época del Festival de la Cosecha. Época de grandes regalos y de conmemoración. Participamos en banquetes para celebrar todo aquello por lo que tenemos que dar gracias, y recordamos a nuestros ancestros y héroes pasados, dando gracias por el legado que nos han dejado.$B$BParticipa en la celebración. También voy a ofrecer unos fuegos artificiales para celebrar este jubiloso momento del año.' WHERE `entry` = 7535;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo siento, $C, pero necesitas más experiencia para sobrevivir en el Valle de Alterac.', `text0_1_loc7` = 'Lo siento, $C, pero necesitas más experiencia para sobrevivir en el Valle de Alterac.' WHERE `entry` = 7616;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si me lo preguntas a mí, lo mejor sobre la pesca es la tranquilidad. Te da tiempo para pensar, para reflexionar sobre los eventos pasados y planear los futuros.$B$BOh, y también es una buena excusa para beber. Mucho.' WHERE `entry` = 7638;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '*Ffff* *PLAF*$B$BHe sido instruido para aceptar peces exóticos de gran calidad y devolver valiosos accesorios de pesca de Nat Pagle. Peces solicitados.$B$B*BUUM* *Bzzz*' WHERE `entry` = 7641;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo siento, $C, pero necesitas tener más poder para que pueda permitirte entrar en la Cuenca de Arathi.', `text0_1_loc7` = 'Lo siento, $C, pero necesitas tener más poder para que pueda permitirte entrar en la Cuenca de Arathi.' WHERE `entry` = 7642;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Throm\'ka, $C! ¿Estás $Gpreparado:preparada; para luchar por la Horda en la Garganta Grito de Guerra?' WHERE `entry` = 7652;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te saludo, $N. ¿Quieres que te ayude en tu viaje a la Garganta Grito de Guerra?', `text0_1_loc7` = 'Te saludo, $N. ¿Quieres que te ayude en tu viaje a la Garganta Grito de Guerra?' WHERE `entry` = 7653;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$C, necesitamos tu ayuda en la Garganta Grito de Guerra. ¿Nos ayudarás en nuestra lucha contra los escoltas Grito de Guerra?' WHERE `entry` = 7656;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$N, ¿tienes alguna idea de a lo que nos enfrentamos en el Valle de Alterac? ¿Tas $Glisto:lista; para luchar al clan Lobo Gélido?' WHERE `entry` = 7658;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espero que estés aquí para ir al Valle de Alterac, $N. Nos vendrá bien toda la ayuda que podamos conseguir.' WHERE `entry` = 7662;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Cuenca de Arathi fue nuestro hogar, ¡y volverá a serlo con tu ayuda! ¿Nos ayudarás, $C?' WHERE `entry` = 7664;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te he estado esperando, $N. ¿Estás $Gpreparado:preparada; para ayudarnos en le Cuenca de Arathi?' WHERE `entry` = 7666;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Uolaaa, la Bruja de la Tormenta.$B$BSu poder es el poder del cielo, la lluvia y la tierra destrozada. Ojalá reine algún día de nuevo, como madre de los Gurubashi. $B$BAhora, Wushoolay vive lejos de aquí. Pero, algún día, podría regresar...' WHERE `entry` = 7674;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todavía no tienes suficiente poder, $C. Vete.', `text0_1_loc7` = 'Todavía no tienes suficiente poder, $C. Vete.' WHERE `entry` = 7678;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Necesitamos más ayuda en la Cuenca de Arathi de la que te puedas imaginar, $N. Tampoco podrías imaginarte el honor y la reputación que se pueden adquirir en este tiempo de gran necesidad. Te suplico que busques al maestro de batalla de la Cuenca de Arathi en esta ciudad.', `text0_1_loc7` = 'Necesitamos más ayuda en la Cuenca de Arathi de la que te puedas imaginar, $N. Tampoco podrías imaginarte el honor y la reputación que se pueden adquirir en este tiempo de gran necesidad. Te suplico que busques al maestro de batalla de la Cuenca de Arathi en esta ciudad.' WHERE `entry` = 7679;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Necesitas ser más fuerte, $C, antes de poder ayudarnos en el Valle de Alterac.', `text0_1_loc7` = 'Necesitas ser más fuerte, $C, antes de poder ayudarnos en el Valle de Alterac.' WHERE `entry` = 7683;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $C. En estos momentos, en el Valle del Honor hay tanta necesidad como honor y renombre para el que vaya a ayudar. ¿Te unirás a nuestra lucha contra la Guardia Pico Tormenta?', `text0_1_loc7` = 'Hola, $C. En estos momentos, en el Valle del Honor hay tanta necesidad como honor y renombre para el que vaya a ayudar. ¿Te unirás a nuestra lucha contra la Guardia Pico Tormenta?' WHERE `entry` = 7684;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$C, necesitamos mucha ayuda en la Garganta Grito de Guerra contra los centinelas Ala de Plata. ¡Y podrás conseguir gran honor y reputación con los escoltas Grito de Guerra! ¿Tienes valor para entrar en la batalla?', `text0_1_loc7` = '$C, necesitamos mucha ayuda en la Garganta Grito de Guerra contra los centinelas Ala de Plata. ¡Y podrás conseguir gran honor y reputación con los escoltas Grito de Guerra! ¿Tienes valor para entrar en la batalla?' WHERE `entry` = 7689;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me temo que no durarías mucho en el Valle de Alterac, $C. Regresa cuando tengas unos cuantos años más.', `text0_1_loc7` = 'Me temo que no durarías mucho en el Valle de Alterac, $C. Regresa cuando tengas unos cuantos años más.' WHERE `entry` = 7693;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El maestro de pesca Anzuelo ha llenado estas aguas con peces suculentos de primera para poder celebrar el torneo.$B$BCuando haya un ganador, ¡te pagaré por cada cinco peces suculentos que me traigas!' WHERE `entry` = 7696;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias por el ofrecimiento, $C. Desafortunadamente todavía no tienes la suficiente experiencia para capear los peligros de la Cuenca de Arathi.', `text0_1_loc7` = 'Gracias por el ofrecimiento, $C. Desafortunadamente todavía no tienes la suficiente experiencia para capear los peligros de la Cuenca de Arathi.' WHERE `entry` = 7699;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$C, ¿Nos ayudarás en la Cuenca de Arathi en este momento de tanta necesidad? Hay más posibilidades que nunca de incrementar el honor y el prestigio, y con tu ayuda le pondremos las cosas difíciles a los Rapiñadores. ¿Podemos contar contigo?', `text0_1_loc7` = '$C, ¿Nos ayudarás en la Cuenca de Arathi en este momento de tanta necesidad? Hay más posibilidades que nunca de incrementar el honor y el prestigio, y con tu ayuda le pondremos las cosas difíciles a los Rapiñadores. ¿Podemos contar contigo?' WHERE `entry` = 7700;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La vida tiene etapas y cada una de ellas llega a su debido tiempo. Tú aún te encuentras en la primera etapa de tu vida. Por favor, $C, regresa a nosotros cuando hayas vivido más.', `text0_1_loc7` = 'La vida tiene etapas y cada una de ellas llega a su debido tiempo. Tú aún te encuentras en la primera etapa de tu vida. Por favor, $C, regresa a nosotros cuando hayas vivido más.' WHERE `entry` = 7704;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$C, no nos ruboriza reconocer que necesitamos toda la ayuda que podamos conseguir para luchar en la Garganta Grito de Guerra. Pero obtendrás más honor ahora que en ningún otro momento pasado si decides prestarnos tus habilidades. Además, los centinelas Ala de Plata verán con mejores ojos tus esfuerzos por ayudarnos. ¿Te unirás a la batalla?', `text0_1_loc7` = '$C, no nos ruboriza reconocer que necesitamos toda la ayuda que podamos conseguir para luchar en la Garganta Grito de Guerra. Pero obtendrás más honor ahora que en ningún otro momento pasado si decides prestarnos tus habilidades. Además, los centinelas Ala de Plata verán con mejores ojos tus esfuerzos por ayudarnos. ¿Te unirás a la batalla?' WHERE `entry` = 7705;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nosotros, los trols Zandalar llegamos aquí desde islas muy lejanas porque no podemos ignorar la amenaza que afronta nuestro mundo.' WHERE `entry` = 7721;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No puedes enseñarle a un perro viejo nuevos trucos. Al menos eso es lo que alguien me dijo en una ocasión. Por suerte para ti, he descubierto que esto no es así.$B$BAsí pues, ¿quieres que comience a borrar los talentos de tu mascota?', `text0_1_loc7` = 'No puedes enseñarle a un perro viejo nuevos trucos. Al menos eso es lo que alguien me dijo en una ocasión. Por suerte para ti, he descubierto que esto no es así.$B$BAsí pues, ¿quieres que comience a borrar los talentos de tu mascota?' WHERE `entry` = 7722;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $Gviajero:viajera;. Mientras permanezcas dentro de los muros del Fuerte Cenarion estarás a salvo.$B$BVe con cautela cuando abandones la protección del Fuerte pues últimamente hay muchos silítidos sueltos.' WHERE `entry` = 7727;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo proporciono al fuerte diversos productos necesarios para lanzar hechizos así como otros componentes relacionados con la alquimia.' WHERE `entry` = 7728;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un gran mal acecha, $R. ¿Puedes sentirlo?$B$B¡Ah! Claro que no. Dame tu armadura y tus armas para que pueda renovar su energía.' WHERE `entry` = 7729;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mira detrás de ti, $R. La colmena se altera a medida que la tierra que nos rodea tiembla y se estremece.$B$BHa llegado la hora de que cumpla una promesa que hice un siglo atrás. Las arenas se mueven de nuevo a medida que los Qiraji preparan su plan de guerra.' WHERE `entry` = 7731;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Por qué ahora, $R? No lo entiendo. Todavía queda mucho por hacer...' WHERE `entry` = 7732;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Puedes oírlo, $Gforastero:forastera;? Cada día que pasa, el zumbido se hace más fuerte. Estos cristales son los que están volviendo locos a esos bichos.' WHERE `entry` = 7734;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mira a los cielos, $C. ¡Hace mil años que no se ven Vuelos en esta zona! Esos Qiraji están tramando algo, ¡lo presiento!' WHERE `entry` = 7735;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Bronzebeard se ha perdido otra vez! Está claro que ese enano no tiene los sentidos tan desarrollados como sus hermanos.' WHERE `entry` = 7736;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Márchate, infiel! ¡Que el poder del Abismo te golpee con su fuerza!', `text0_1_loc7` = '¡Márchate, infiel! ¡Que el poder del Abismo te golpee con su fuerza!' WHERE `entry` = 7744;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $C. $GBienvenido:Bienvenida; al Fuerte Cenarion. Desde aquí atacaremos a los silítidos cuando estén en su apogeo. Espero que hayas venido a echar una mano.' WHERE `entry` = 7746;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Es un placer volver a verte, $N. Tu ayuda ha sido crucial para preservar la seguridad de nuestras líneas de abastecimiento. Espero que comprendas la importancia de la logística implicada en una gran batalla como esta.' WHERE `entry` = 7747;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué bicho se presenta antes los Señores del Consejo? ¡Sin duda, uno que quiere morir aplastado!', `text0_1_loc7` = '¿Qué bicho se presenta antes los Señores del Consejo? ¡Sin duda, uno que quiere morir aplastado!' WHERE `entry` = 7754;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es una larga historia así que es mejor que tomes asiento.$B$BCuando llegamos por primera vez a Silithus, la maestra Mar\'alith fue la primera de los elfos de la noche del Fuerte Cenarion en unirse a la expedición. Nuestro día a día era de lo más normal: un grupo se aventuraba en las diversas colmenas para buscar artefactos perdidos.' WHERE `entry` = 7755;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Lo es! Esas colmenas estaban atestadas de silítidos que no se alegraban mucho de vernos.$B$BTodo marchaba bien hasta que nos topamos con un cristal jeroglífico en Colmen\'Regal. ¡La atrajo como si de un imán se tratase! Ella puso su bonita mejilla contra el cristal y al instante entró en un estado de trance. Naturalmente, todos estábamos muy preocupados. La cogimos y la sacamos de allí al momento.' WHERE `entry` = 7756;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La llevamos ante el Comandante con la esperanza de que los druidas del Círculo pudieran ayudarla. En efecto, fueron capaces de conseguir que despertara. Supusimos que ya no teníamos nada más que hacer en el Círculo y que podíamos retomar nuestro trabajo.$B$BPasaron varias semanas y todo iba bien. Brann aún andaba por allí así que el trabajo estaba bien estructurado. Y un buen día, de repente, ella apareció en nuestro campamento.' WHERE `entry` = 7757;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Eres $Gtonto:tonta;? ¿De quién iba a estar hablando si no? Sí, la chica, la maestra Mar\'alith. En cualquier caso, ella estaba de vuelta y no de muy buen humor. Y entonces comenzaron las exigencias.' WHERE `entry` = 7758;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Exigía que volviéramos inmediatamente a Colmen\'Regal! ¡Qué descaro por su parte! Brann le dijo que era imposible volver a esas colmenas sin contar con el respaldo de un ejército. Se habían vuelto todavía más peligrosas. Esos apestosos insectos habían triplicado su número desde la primera vez que nos aventuramos en las colmenas.$B$B¡Y entonces fue cuando Natalia perdió la razón!' WHERE `entry` = 7759;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué quieres decir, qué quieres decir...? Pues eso, ¡que se volvió loca! Empezó a golpes con nosotros y, aunque yo no tengo por costumbre pegar a una chica, cuando desperté tras haberme desvanecido, estaba furioso con ella. ¡Era como si Brann, Rutgar, el mono y yo fuéramos orcos a ojos de ella! Es un milagro que fuéramos capaces de evitar su ataque. ¡Estaba como poseída!' WHERE `entry` = 7760;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Quién sabe! De cualquier modo, esa fue la última vez que la vi. Quizás Frankal pueda darte más datos.$B$BNo pretendo evitar este tema pues estoy seguro de que el trabajo que estás haciendo para el Comandante es importante, pero aquí estamos inmersos en algo muy grande. ¡REALMENTE GRANDE! Todas nuestras peticiones de un ejército a Forjaz hasta el momento han caído en saco roto. Parece ser que estamos en la obligación de proteger el reino u otra cosa sin sentido. Tras presentar tu informe ante el Comandante, regresa aquí con algunos amigos. ¡Necesitamos tu ayuda!' WHERE `entry` = 7761;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Oh no, otra vez no... Siéntate $Gmuchacho:muchacha;.$B$BEstoy seguro de que Rutgar ya te ha contado algo así que me ahorraré los detalles e iré directo al grano.' WHERE `entry` = 7762;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Estupendo! Pues la maestra había vuelto por segunda vez a las colmenas pero Brann no entendía nada de lo que estaba pasando. Antes de que reinara la confusión, ella empezó a hablar con algo o alguien. Y lo que es aún peor, empezó a salmodiar en un idioma extraño que ninguno de nosotros conocíamos.' WHERE `entry` = 7763;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Y que lo digas! Sobre todo porque entre Brann, Rutgar, el mono y yo hablamos 38 idiomas, incluido un poco de titán, tanto vanir como aesir.$B$BEn todo caso, sea lo que fuere que decía, no hablaba con nadie que estuviera \"presente\". Después, asintió con la cabeza y pasó al ataque... ¡con todas sus fuerzas!' WHERE `entry` = 7764;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mira $Gchico:chica;, lo que nos atacó no era una simple sacerdotisa. ¡Sus ojos tenían un brillo maléfico y echaba espuma por la boca! Llegó, empezó a gritar a lo loco con lo que consiguió que cada uno de nosotros saliera corriendo en una dirección y finalmente empezó a destrozar nuestras mentes una a una. Yo empecé a oír palabras extrañas en mi cabeza y a ver cómo los demás perdían el conocimiento aquí y allá. ¡Era demencial! Hubiera sido nuestro fin de no ser por el condenado mono.' WHERE `entry` = 7765;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Preguntar qué? Es un mono, idiota. En cualquier caso, el mono se puso frenético al ver al viejo Brann caer desplomado sobre la arena. La elfa de la noche no podía hacer nada para detener a Glibb, que no paraba de darle golpes.' WHERE `entry` = 7766;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Y entonces... Ella salió corriendo directamente hacia la colmena. ¿Y sabes qué es lo más gracioso? Que ni un solo bicho de esos se interpuso en su camino o intentó detenerla. Corría entre ellos como si fueran amigos de toda la vida.' WHERE `entry` = 7767;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ajá.' WHERE `entry` = 7768;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El cristal está cubierto de complejos jeroglíficos y runas. No se entiende nada.' WHERE `entry` = 7770;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Adónde quieres volar?', `text0_1_loc7` = '¿Adónde quieres volar?' WHERE `entry` = 7778;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los espíritus de Silithus nos llaman, $N. La tierra llora pues tiene una triste historia que contar...' WHERE `entry` = 7779;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ave $C. ¿No te he visto hace poco en una reunión privada? Me suena tu cara pero esta ciudad está tan concurrida...$B$BSi has estado en esa reunión, te habrán dado un código secreto. Dime tu código y recibirás como recompensa un premio especial.', `text0_1_loc7` = 'Ave $C. ¿No te he visto hace poco en una reunión privada? Me suena tu cara pero esta ciudad está tan concurrida...$B$BSi has estado en esa reunión, te habrán dado un código secreto. Dime tu código y recibirás como recompensa un premio especial.' WHERE `entry` = 7780;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$N! Espero que vengas para ponerte a trabajar, porque tienes mucho que hacer aquí, en la isla del Caminante del Sol.$B$BDesde la destrucción de la Fuente del Sol por Arthas y la Plaga, somos una raza a la deriva en un mar de incertidumbres. Nos balanceamos al filo del olvido. Esto cambiará, $C. Al mismo tiempo que aprendes, ayudarás a nuestra recuperación.$B$B¡Terrallende nos espera!' WHERE `entry` = 7781;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El Círculo Cenarion es muy sabio y conoce muchos de los secretos de la tierra. Los artesanos harían bien en prestar atención a las palabras de estos druidas.' WHERE `entry` = 7782;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hubo un tiempo en que la Aguja del Sol disfrutó de una buena ventaja sobre La Fuente del Sol. Nuestras vidas han dado un vuelco, $N, pero aun así debemos continuar. Somos así; somos supervivientes.$B$BSi quieres sobrevivir a estos tiempos tumultuosos y seguir adelante, DEBES aprender a sobrevivir. Todos los elfos de sangre deben hacerlo. Debes dominar tu insaciable hambre de magia antes de que ella te domine a ti.' WHERE `entry` = 7784;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tu continuo servicio a los elfos de sangre es crucial para nuestra supervivencia! Todos tenemos que ponernos manos a la obra para salir de este embrollo.$B$BSi estás $Glisto:lista; para trabajar, empecemos.' WHERE `entry` = 7785;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El conocimiento es poder; poder VERDADERO, joven aprendiz. Lo mejor que puedes hacer es acumular tanto como puedas y atender bien a quienes ya lo han hecho.$B$BAntes de arrasar La Fuente del Sol, nos engañábamos creyendo que habíamos alcanzado la cumbre de nuestra civilización. Necesitamos la Plaga para darnos cuenta... y, en cierto modo, volver a la realidad.' WHERE `entry` = 7786;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en \"silenciosa\", si no te importa.' WHERE `entry` = 7787;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si te diriges a la ciudad, tengo algo importante que encomendarte.' WHERE `entry` = 7788;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$gBienvenido:Bienvenida;! Adelante, no tengas vergüenza. Esto de aquí es mi mayor tesoro... ¡el Ultracañón Pimpampum 5000! ¿No es precioso? Parece que tienes un poco de barriga pero creo que te servirá. Volar es muy divertido. ¿Quieres aprender?' WHERE `entry` = 7789;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Oh, es muy sencillo! Se utiliza como cualquier otro aparato eléctrico. No te preocupes que yo me encargo de encender los fusibles. Cuando estés en el aire te parecerá que estás flotando pero esa sensación desaparece pronto. Tu objetivo en este caso es llegar hasta el lago que está allí a lo lejos. No te olvides de desplegar las alas en el momento oportuno antes de comenzar el descenso para aterrizar en el agua o de lo contrario... bueno, yo no vendo seguros de vida.' WHERE `entry` = 7790;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola $c y $gbienvenido:bienvenida; a la Feria de la Luna Negra. Hay tanto que hacer aquí que puede resultar un tanto abrumador para aquellos que nos visitan por primera vez. Por eso estoy aquí para ayudarte. ¿Tienes alguna pregunta?' WHERE `entry` = 7792;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Excelente pregunta! Tenemos muchas cosas en esta feria. Si quieres comida o bebida, dirígete a la barraca de Stamp y Sylannia. Mi favorito es el Gran Reserva de la Luna Negra.$B$BSi por el contrario prefieres comprar algo más exótico, Lhara seguro que tiene lo que buscas pues su especialidad son las rarezas de todas las partes del mundo.$B$BY luego está Flik, que andará por ahí en alguna parte. Si lo encuentras verás que vende pelotas para lanzar y ranas para tener como mascotas.' WHERE `entry` = 7793;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Me alegro de que me hagas esa pregunta. Los vales para la Feria de la Luna Negra son nuestro modo de darte la posibilidad de echar una mano en la feria y a cambio poder canjear tus vales por regalos especiales.$B$BPara empezar tendrás que hablar con Chronos, Kerri Hicks, Cristo y Yebb Nebblegear para decidir a quién quieres ayudar. Ellos siempre tienen algún trabajo por hacer que ayudará a mejorar nuestras atracciones.$B$BCuando ya tengas suficientes vales, habla con Gelvas Roñoso para que te dé la lista de todos los regalos disponibles.' WHERE `entry` = 7794;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Nadie sabe con certeza quién creó las cartas de la Luna Negra o cuál es su propósito real pero sí puedo decirte que las encontrarás por todo Azeroth, generalmente en manos de algún villano que desea desvelar sus misterios.' WHERE `entry` = 7795;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te recomiendo que visites a Sayge para que te diga la buenaventura. Ya sé, ya sé... es un gnoll. Sí, pero es un gnoll muy especial que puede adivinar el futuro.$B$B¡Y continuamente ampliamos la feria con todo tipo de atracciones nuevas!' WHERE `entry` = 7796;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $N' WHERE `entry` = 7800;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $Gviajero:viajera;. Fue aquí, hace casi 1000 años, donde los Qiraji comenzaron su oleada de destrucción por Kalimdor.$B$BSe perdieron miles de vidas durante la guerra, la mayoría de ellas en este desierto maldito durante la invasión inicial.$B$BYo soy una de los pocos que quedamos, atados tanto al pasado como al presente. No sé por qué tengo que permanecer aquí pero sé que no puedo hacer nada para evitarlo.' WHERE `entry` = 7801;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Me han asesinado cien veces y siempre despierto de nuevo aquí. Algunos dicen que pesa una maldición sobre mí y otros...' WHERE `entry` = 7802;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Que se trata de una bendición: un heraldo en una nueva era.' WHERE `entry` = 7803;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hay fuerzas ancestrales acechando, fuerzas que no se habían percibido durante miles de años. El mundo llora, $R. Quizás se avecina un nuevo cataclismo. Un malestar que surge de dentro... $B$BEl mal está calando en nuestras tierras. Del Sueño salen pesadillas, que perturban nuestro despertar...' WHERE `entry` = 7804;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Márchate inmediatamente! No tengo nada que tratar contigo.' WHERE `entry` = 7816;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ya estás de vuelta! ¿Qué puedo hacer por ti en esta ocasión?' WHERE `entry` = 7817;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La guerra contra la Horda está en su apogeo, $N. Es un momento de gran necesidad para la Alianza y es necesario que cada uno de nosotros cumplamos con nuestro cometido.' WHERE `entry` = 7818;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La guerra se cierne sobre nosotros, $N. Es hora de que cada uno de los miembros de la Horda atienda la llamada y se una a la lucha contra la acobardada Alianza.' WHERE `entry` = 7819;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me llegan continuamente noticias de tus proezas contra el Consejo Abisal, $N. ¡Estás haciendo grandes cosas ahí fuera!$B$B¡Sigue así y conseguiremos que el esfuerzo valga la pena!' WHERE `entry` = 7839;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$N, me alegro de volver a verte.$B$BRecuerda mantenerte centrado en tu aprendizaje. Tú representas nuestro futuro y, si vacilas, nuestro futuro será oscuro de verdad.' WHERE `entry` = 7869;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te doy la bienvenida a la posada de la Plaza Alalcón. ¿Quieres que te prepare comida, o una habitación?' WHERE `entry` = 7872;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Vas a hacerme daño, $R?' WHERE `entry` = 7880;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Narain coloca un sobre cerrado sobre su frente.>$B$BQué es... Mmmm... ¡Terciopelo!$B$B¿Tengo razón? $B$BSi estás aquí para intentar volver a aprender un oficio, usa ese libro de ahí...$B$B<Narain señala \"Adivinar el futuro para torpes\".',  `text1_0_loc7` = '¡No me lo digas! ¡Shhh! ¿Estás aquí por la espada Crematoria? Sí... Sí... ¡No! Me he equivocado.$B$B¿En qué puedo ayudarte, $R?$B$BSi estás aquí para intentar volver a aprender un oficio, usa ese libro de ahí...$B$B<Narain señala \"Adivinar el futuro para torpes\".', `text2_0_loc7` = '¡Narain Sabelotodo, a su servicio! ¡Ingeniero retirado y maestro adivino! ¡Puedo adivinar tu futuro incluso antes de que ocurra!$B$BSi estás aquí para intentar volver a aprender un oficio, usa ese libro de ahí...$B$B<Narain señala \"Adivinar el futuro para torpes\".' WHERE `entry` = 7902;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu camino y el mío son distintos. Debes buscar otro instructor, $C.', `text0_1_loc7` = 'Tu camino y el mío son distintos. Debes buscar otro instructor, $C.' WHERE `entry` = 7903;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quieres más adiestramiento sobre la manipulación del poder del naaru, $N?', `text0_1_loc7` = '¿Quieres más adiestramiento sobre la manipulación del poder del naaru, $N?' WHERE `entry` = 7904;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ha llegado la hora de un nuevo año y la luna brilla con fuerza.$B$BHa llegado la hora... de que los ancestros se despierten.' WHERE `entry` = 7933;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En tiempos remotos, vivíamos para la caza. La caza de gloria, de honor... $B$B¿Es que ahora es tan diferente?', `text0_1_loc7` = 'En tiempos remotos, vivíamos para la caza. La caza de gloria, de honor... $B$B¿Es que ahora es tan diferente?' WHERE `entry` = 7935;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu intento de aprender dracónico es inútil, mortal. ¿Y por qué ibas a querer hablar esta lengua antigua? Es realmente desconcertante...' WHERE `entry` = 7937;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las estrellas de nuestro tiempo están cambiando su trayectoria, faros de esperanza y fuerza para los años venideros.', `text0_1_loc7` = 'Las estrellas de nuestro tiempo están cambiando su trayectoria, faros de esperanza y fuerza para los años venideros.' WHERE `entry` = 7956;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a la Bahía del Botín, $Gcompañero:compañera;. Espero que disfrutes tu estancia en mi ciudad y que transcurra sin incidentes, y recalco esto último. Si tienes sed tenemos mucha bebida y si tienes miedo, muchos porteros.$B$BEntonces... ¿qué puede hacer este humilde sirviente del Cártel Bonvapor por ti? Mejor aún, ¿qué puedes hacer tú por mí, eh?' WHERE `entry` = 7965;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No lo tomes a mal, prefiero no estar día tras día en un túnel lleno de silítidos, pero si el estar aquí escondidos observando los movimientos de estas criaturas nos va a ayudar a ganar la guerra, entonces estaré todo el tiempo que sea necesario.' WHERE `entry` = 8064;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No te vayas si no te quieres perder el siguiente espectáculo de fuegos artificiales! Empieza a las 6PM. ¡Son a cada hora en punto!', `text0_1_loc7` = '¡No te vayas si no te quieres perder el siguiente espectáculo de fuegos artificiales! Empieza a las 6PM. ¡Son a cada hora en punto!', `text1_0_loc7` = 'No sé que hay en estos barriles ¡pero sabe a gloria!', `text1_1_loc7` = 'No sé que hay en estos barriles ¡pero sabe a gloria!', `text2_0_loc7` = 'Tras los fuegos artificiales, sacarán copas para que podamos brindar.', `text2_1_loc7` = 'Tras los fuegos artificiales, sacarán copas para que podamos brindar.', `text3_0_loc7` = '¡Bien $Ghallado, amigo:hallada, amiga;!', `text3_1_loc7` = '¡Bien $Ghallado, amigo:hallada, amiga;!' WHERE `entry` = 8081;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay una pila de regalos envueltos aquí.' WHERE `entry` = 8109;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La etiqueta de este regalo dice: \"Para mayores de 40.\"' WHERE `entry` = 8110;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ahora que la guerra está en plena ebullición, contamos con buenos combatientes tanto de la Horda como de la Alianza.$B$BSi tan solo pudiéramos disuadirlos de atacarse los unos a los otros...' WHERE `entry` = 8114;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La etiqueta de este regalo dice: \"Para mayores de 20.\"' WHERE `entry` = 8115;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El diario de Jandice Barov está lleno de calumnias y sandeces sobre los no-muertos. Ya casi al final del libro, se encuentra lo que parece ser un patrón de sastrería para confeccionar algún tipo de bolsa. Tus conocimientos de confección son insuficientes para descifrar el patrón.' WHERE `entry` = 8120;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solo aquellos que hayan demostrado no ser hostiles con los Fauces de Madera pueden pasar por el fuerte sin recelos de ser atacados. ¿Te has esforzado para ganar nuestra confianza, forastero?' WHERE `entry` = 8123;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué es lo que buscas, $C?' WHERE `entry` = 8124;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La última novedad este año: la PX-238 Maravoltio Invernal. ¡Acércate, no seas $Gtímido:tímida;!$B$BY rellena estos formularios antes de probarla, aún estamos corrigiendo algunos problemas de este chisme...' WHERE `entry` = 8125;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Soy un Oficial encomiado en misión en nombre de Ventormenta. Mi deber es ayudar a los aventureros que hayan recibido un sello de mención de honor de la Alianza.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Ventormenta por tu cumplimiento del deber y buen servicio.$B$B', `text0_1_loc7` = 'Saludos, $C. Soy un oficial encomiado en misión en nombre de Ventormenta. Mi deber es ayudar a los aventureros que hayan recibido un sello de mención de honor de la Alianza.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Ventormenta por tu cumplimiento del deber y buen servicio.$B$B' WHERE `entry` = 8130;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Soy un oficial encomiado en misión en nombre de Orgrimmar. Mi deber es ayudar a los aventureros que hayan recibido un sello de honor de la Horda.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Orgrimmar por tu cumplimiento del deber y buen servicio.|n', `text0_1_loc7` = 'Saludos, $C. Soy una oficial encomiada en misión en nombre de Orgrimmar. Mi deber es ayudar a los aventureros que hayan recibido un sello de honor de la Horda.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Orgrimmar por tu cumplimiento del deber y buen servicio.' WHERE `entry` = 8131;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Soy un oficial encomiado en misión en nombre de Cima del Trueno. Mi deber es ayudar a los aventureros que hayan recibido un sello de honor de la Horda.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Cima del Trueno por tu cumplimiento del deber y buen servicio.|n', `text0_1_loc7` = 'Saludos, $C. Soy una oficial encomiada en misión en nombre de Cima del Trueno. Mi deber es ayudar a los aventureros que hayan recibido un sello de honor de la Horda.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Cima del Trueno por tu cumplimiento del deber y buen servicio.' WHERE `entry` = 8132;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Soy un oficial encomiado en misión en nombre de Entrañas. Mi deber es ayudar a los aventureros que hayan recibido un sello de honor de la Horda.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Entrañas por tu cumplimiento del deber y buen servicio.', `text0_1_loc7` = 'Saludos, $C. Soy una oficial encomiada en misión en nombre de Entrañas. Mi deber es ayudar a los aventureros que hayan recibido un sello de honor de la Horda.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Entrañas por tu cumplimiento del deber y buen servicio.' WHERE `entry` = 8133;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Feliz Festival de Invierno, $Gamigo:amiga;!', `text0_1_loc7` = '¡Feliz Festival de Invierno, $Gamigo:amiga;!', `text1_0_loc7` = '¡Felices Fiestas y paz!', `text1_1_loc7` = '¡Felices Fiestas y paz!', `text2_0_loc7` = '¡Te deseo felices fiestas, $Gamigo:amiga;!', `text2_1_loc7` = '¡Te deseo felices fiestas, $Gamigo:amiga;!', `text3_0_loc7` = 'Ah, muérdago...', `text3_1_loc7` = 'Ah, muérdago...' WHERE `entry` = 8139;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Qué buen tiempo estamos teniendo aquí, ¿no crees?', `text1_1_loc7` = '¿Por qué nunca hay ningún girocronátomo cerca cuando lo necesitas?', `text2_1_loc7` = 'Yo me reiré la última... ¡ya verán! Ah, hola $C. No te había visto.', `text3_1_loc7` = 'La inventiva, mi $Gquerido:querida; $C, se compone de un dos por ciento de inspiración embotellada, cuatro por ciento de exposición al sol, seis por ciento de probabilidad de que la tormenta pase antes del mediodía y noventa y tres por ciento de Risitas.$B$BEspera, ¿eso hace el cien por cien? Tengo mis notas por aquí, en alguna parte...' WHERE `entry` = 8143;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes encontrar al ancestro Barbabronce en Forjaz, hogar ancestral de los Barbabronce.', `text0_1_loc7` = 'Puedes encontrar al ancestro Barbabronce en Forjaz, hogar ancestral de los Barbabronce.' WHERE `entry` = 8162;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El ancestro Rocaestruendo pasa los años en una cueva en lo alto de Roca Machacamiedo en Las Estepas Ardientes.', `text0_1_loc7` = 'El ancestro Rocaestruendo pasa los años en una cueva en lo alto de Roca Machacamiedo en Las Estepas Ardientes.' WHERE `entry` = 8168;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El ancestro Filoveloz vaga por los caminos de Darnassus en lo alto de Teldrassil.', `text0_1_loc7` = 'El ancestro Filoveloz vaga por los caminos de Darnassus en lo alto de Teldrassil.' WHERE `entry` = 8176;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $N. Venimos desde Claro de la Luna para propagar el espíritu del Festival Lunar por todas partes.$B$B¿No te vas a unir a nuestra celebración?' WHERE `entry` = 8205;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En Claro de la Luna sí que saben hacer fiestas. ¿Quién lo hubiera imaginado?', `text0_1_loc7` = 'En Claro de la Luna sí que saben hacer fiestas. ¿Quién lo hubiera imaginado?', `text1_0_loc7` = 'Prueba estos lanzacohetes. ¡Son geniales!', `text1_1_loc7` = 'Prueba estos lanzacohetes. ¡Son geniales!', `text2_0_loc7` = 'Este destello de luna conduce hasta Ventormenta. Utiliza tu invitación cuando estés $glisto:lista;.', `text2_1_loc7` = 'Este destello de luna conduce hasta Ventormenta. Utiliza tu invitación cuando estés $glisto:lista;.', `text3_0_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...', `text3_1_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...' WHERE `entry` = 8218;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Los de Amparo de la Noche sí que saben lo que es una buena fiesta!', `text0_1_loc7` = '¡Los de Amparo de la Noche sí que saben lo que es una buena fiesta!', `text1_0_loc7` = 'Estos fuegos artificiales seguro que provocan un buen estruendo, ¿no crees?', `text1_1_loc7` = 'Estos fuegos artificiales seguro que provocan un buen estruendo, ¿no crees?', `text2_0_loc7` = 'Esta luz lunar te llevará hasta Forjaz. ¿Pero quién querría perderse esta fiesta?', `text2_1_loc7` = 'Esta luz lunar te llevará hasta Forjaz. ¿Pero quién querría perderse esta fiesta?', `text3_0_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...', `text3_1_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...' WHERE `entry` = 8219;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡La celebración lunar este año es magnífica!', `text0_1_loc7` = '¡La celebración lunar este año es magnífica!', `text1_0_loc7` = '¡Se dice que estos fuegos artificiales utilizan el poder de la luna!', `text1_1_loc7` = '¡Se dice que estos fuegos artificiales utilizan el poder de la luna!', `text2_0_loc7` = 'Este rayo de luna más intenso te llevará hasta Darnassus.', `text2_1_loc7` = 'Este rayo de luna más intenso te llevará hasta Darnassus.', `text3_0_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...', `text3_1_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...' WHERE `entry` = 8220;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si no lo supiera, ¡diría que esta fiesta la organizaron los orcos!', `text0_1_loc7` = 'Si no lo supiera, ¡diría que esta fiesta la organizaron los orcos!', `text1_0_loc7` = '¿Has visto? ¡El último cohete casi le quema la camisa a ese enano! ¡Ja, ja, ja!', `text1_1_loc7` = '¿Has visto? ¡El último cohete casi le quema la camisa a ese enano! ¡Ja, ja, ja!', `text2_0_loc7` = 'Este rayo de luna te llevará hasta Orgrimmar. Utiliza tu invitación cuando desees ir.', `text2_1_loc7` = 'Este rayo de luna te llevará hasta Orgrimmar. Utiliza tu invitación cuando desees ir.', `text3_0_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...', `text3_1_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...' WHERE `entry` = 8221;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Me encanta el Festival Lunar! ¡Es la ocasión ideal para mover las patas!', `text0_1_loc7` = '¡Me encanta el Festival Lunar! ¡Es la ocasión ideal para mover las patas!', `text1_0_loc7` = 'No sé exactamente cómo funcionan estos fuegos artificiales, ¡pero seguro que son geniales!', `text1_1_loc7` = 'No sé exactamente cómo funcionan estos fuegos artificiales, ¡pero seguro que son geniales!', `text2_0_loc7` = 'Esta luz lunar te llevará hasta Cima del Trueno. Utilízala cuando desees abandonar el festival.', `text2_1_loc7` = 'Esta luz lunar te llevará hasta Cima del Trueno. Utilízala cuando desees abandonar el festival.', `text3_0_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...', `text3_1_loc7` = 'No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...' WHERE `entry` = 8222;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos trabajado muy duro para ver cómo nuestras tierras volvían a su antigua gloria, $C. Debemos estar siempre alerta contra aquellos que han invadido y profanado Quel\'thalas. No descansaremos hasta que estén fuera de nuestra vista.$B$B¡No dejemos que exista nadie que se interponga en el camino de los sin\'dorei!' WHERE `entry` = 8226;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Es un caos, $C, y es todo por mi culpa. Me dijeron que saliera del muelle mientras tenía tiempo, pero no hice caso. No.$B$B¿Qué voy a hacer ahora? Esos malditos elfos de sangre, los desdichados, se han largado con todo mi material mágico y se lo han comido, creo. Estoy segura de que no lo recuperaré nunca. ¡Y los múrlocs Malaescama me robaron lo poco que quedaba en medio de la noche! Para rematar la faena, mi tripulación ha abandonado el barco.$B$BCreo que voy a llorar.' WHERE `entry` = 8227;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $C. Estamos en tiempos verdaderamente preocupantes si esto es en lo que se va a convertir nuestra amada Quel\'Thalas.' WHERE `entry` = 8228;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los desdichados son una espina que tengo clavada, $N. Cuando no son un peligro para ellos mismos porque explotan fuentes Arcanas inestables para satisfacer sus antojos, traman ardides alocados contra nuestra respetable sociedad.' WHERE `entry` = 8229;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espero que Loralthalis esté bien, $C. Quizás debería subir a Aguja Ocaso Marchito yo mismo. ¿Quizás es lo que debería haber hecho en primer lugar?' WHERE `entry` = 8232;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un hombre solo puede vivir consigo y su vergüenza durante tanto tiempo, $C, antes de que tenga que actuar para deshacer los errores que ha cometido.' WHERE `entry` = 8233;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Un desastre, eso es lo que es! ¡Todas esas criaturas han aparecido de la nada y corretean por todas partes, fuera de control! Y, ¿dónde se han metido el magister y sus aprendices? Me dejan aquí solo sin siquiera decir \"Hola, Wyllithen, ¿te quieres venir?\"$B$B¡Menuda cara!' WHERE `entry` = 8234;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Eh! Tienes pinta de ser $Gun:una:c; $C con un paladar muy exigente para los vinos. Tengo que pedirte un favor y creo que no podrás rechazar la oferta que te voy a hacer... ¡Olvídalo!' WHERE `entry` = 8238;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué estás mirando, $Gmuchacho:muchacha;? ¿Es que nunca has visto un enano?$B$BSerá mejor que te pongas en camino si no quieres tener problemas.' WHERE `entry` = 8239;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, debes de ser $Gel muchacho:la muchacha; $Gdesignado:designada; para mostrarme los alrededores del bosque Canción Eterna. Te llevó tu tiempo llegar hasta aquí, ¿no es así?$B$BMuy bien, vámonos. No perdamos ni un minuto más.' WHERE `entry` = 8240;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ave $C. ¿No te he visto hace poco en una reunión privada? Me suena tu cara pero esta ciudad está tan concurrida...$B$BSi has estado en esa reunión, te habrán dado un código secreto. Dime tu código y recibirás como recompensa un premio especial.', `text0_1_loc7` = 'Ave $C. ¿No te he visto hace poco en una reunión privada? Me suena tu cara pero esta ciudad está tan concurrida...$B$BSi has estado en esa reunión, te habrán dado un código secreto. Dime tu código y recibirás como recompensa un premio especial.' WHERE `entry` = 8269;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El contenido de las páginas de este tomo parece sufrir continuos cambios. Sientes como si el libro absorbiera los pensamientos de tu mente.' WHERE `entry` = 8321;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Thrall ha sido muy generoso acogiéndome en su dominio. De ningún modo quisiera abusar de su hospitalidad. Causarle problemas a nuestro líder sería una gran deshonra para mí.' WHERE `entry` = 8332;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Intenta no llamar mucho la atención. Estoy esperando a que el Rey me dé audiencia. No es que me importe esperar... de hecho, me siento bastante segura rodeada de todos estos fornidos guardias.' WHERE `entry` = 8333;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Saludos, $Gamigo:amiga;! ¿Puedo ofrecerte un condensador ectoplásmico? ¿O quizás un fabuloso asimilador de espectros omnidireccional?' WHERE `entry` = 8335;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Estaré bien, no te preocupes por mí. Saldré de aquí cuando haya realizado algunos rituales de purificación en esta habitación. Este lugar está contaminado con magia negativa, $N.' WHERE `entry` = 8344;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Nuestras nuevas atracciones, totalmente gratuitas, o tonques que es el ingenioso nombre que les hemos puesto, se controlan mediante cualquiera de las consolas de control que se encuentran por toda la feria.$B$BTodo lo que tienes que hacer es activar cualquier consola de control que esté disponible. Cada tonque está totalmente equipado con una serie de artefactos y de manera aleatoria podrás descargar una fuerza de ataque especial contra tus enemigos.$B$BVive batallas épicas con tus amigos, pasea con tu tonque o provoca el caos. ¡Tú eliges!' WHERE `entry` = 8347;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Se comenta que Kel\'Thuzad ansía el legendario Atiesh, el gran báculo del guardián.$B$BSí, el mismísimo báculo que Medivh portó en su día.' WHERE `entry` = 8350;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ten cuidado, $R. Los de la Hermandad de la Luz no somos tan indulgentes como nuestros hermanos cuando se trata de impartir justicia a aquellos que hacen el mal en el mundo.$B$B¡Ay de aquellos que provoquen la ira de Korfax!$B$BY ahora, si no deseas nada más de mí ni de los otros miembros de la Hermandad, haz el favor de dejarnos para que podamos proseguir con nuestra reunión.' WHERE `entry` = 8351;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Rey Exánime intenta hacer sentir su presencia en nuestro mundo. No debemos permitir que esto ocurra.' WHERE `entry` = 8352;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué es la Hermandad de la Luz? Bien... Todos nosotros somos miembros de El Alba Argenta pero, a diferencia de otros, carecemos de moralidad, culpabilidad y otras emociones humanas inútiles. Podría decirse que somos como un miembro de la Cruzada Escarlata pero sin estupidez, falta de liderazgo y fanatismo ciego.$B$BNo permitas que el Comandante Escarlata te diga lo contrario.$B$BY que Neófitus no sepa que te he dicho esto... Sería una pena que tuviera que mutilarlo brutalmente en caso de que se produjera una desafortunada reyerta.' WHERE `entry` = 8353;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los de El Alba Argenta serán unos idealistas impotentes pero su ilusorio líder sabe que no son capaces de aguantar. Estoy aquí en calidad de emisaria de la Cruzada Escarlata. Una embajadora, si preferís... No hay duda de que no podríamos infligir ni el menor de los daños a los ejércitos de Kel\'Thuzad, unidos o no, pero debemos intentar contenerlos el mayor tiempo posible a la espera de que se produzca un milagro.$B$B¿En cuanto a mí? Moriré luchando...$B$B<Marjhan se encoge de hombros.>' WHERE `entry` = 8354;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi título es engañoso. Cuando me conozcas bien te darás cuenta de que soy muy simpático...' WHERE `entry` = 8355;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Casi envían a mi hermano, Loksey, a esta reunión. No es capaz ni de cuidar a sus canes y mucho menos de representar de manera competente a la Cruzada Escarlata.' WHERE `entry` = 8356;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estaba espe...$B$B<Rimblat tose.>$B$B¡Oh, perdona! Soy Rimblat del Anillo de la Tierra.' WHERE `entry` = 8357;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $C. Por favor, discúlpame. Estoy manteniendo una conversación con Rimblat. Él cree que la tierra todavía tiene salvación.' WHERE `entry` = 8358;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué es lo que quieres, roedor? Desaparece inmediatamente de mi vista si no quieres descubrir por qué me llaman el Colérico.' WHERE `entry` = 8359;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy lo que vosotros denomináis un capitalista, $R.' WHERE `entry` = 8366;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Oh, buenas... mmmm ¿cómo te llamas?$B$BNo importa, ¿qué te trae a mi hogar alejado del hogar, aquí en la bella campiña?' WHERE `entry` = 8374;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $C. Confío en que te lo estés pasando bien. ¿Qué opinas de la fiesta? Realmente espero que no se nos esté acabando nada. Nada mata una fiesta más rápido que quedarse sin bebidas y entremeses, ¿no crees?$B$BSí, bueno, tengo que ir a atender a mis otros invitados. Disfruta de tu estancia.' WHERE `entry` = 8375;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿No es divertidísimo? Es magnífico que Lord Saltheril organice estas fiestas todas las noches, especialmente con todo lo que está ocurriendo en el sur. La verdad es que una fiesta te permite olvidarlo todo, ¿verdad?' WHERE `entry` = 8379;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Al igual que Kel\'Thuzad, yo fui una maga del Kirin Tor. Y al igual que Kel\'Thuzad, busco el legendario Atiesh, el gran báculo del guardián. Por este motivo, nuestros destinos están unidos, $C.' WHERE `entry` = 8388;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La progenie de Aegwynn, el que sería el último guardián de Tirisfal... Medivh.' WHERE `entry` = 8389;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Es una historia de leyenda que no te voy a contar por falta de tiempo y apetencia. Tan solo has de saber que Medivh fue el último de los guardianes. El gran báculo de Atiesh fue la piedra angular tanto de su poder como de su corrupción.' WHERE `entry` = 8390;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La corrupción de un titán caído. La corrupción del Destructor. Después de todo, el báculo fue forjado a partir de la semilla del odio. Eso es, por supuesto, lo que me ha traído hasta aquí...' WHERE `entry` = 8391;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tras la caída de Karazhan, el Kirin Tor envió a un grupo de magos a buscar la torre y a hacerse con todos los objetos que tuvieran relevancia con la excusa de \"investigar.\" Fue entonces cuando se encontró el legendario báculo y se trajo de nuevo a Dalaran.' WHERE `entry` = 8392;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Lo que vino después fue una serie de trágicos acontecimientos que acabaron con la muerte de todos los magos del Kirin Tor que habían estado en contacto con Atiesh.$B$BSi te das cuenta, se trataba de un objeto que tenía sentimientos, cualidad que Sargeras le otorgó...' WHERE `entry` = 8393;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '<Angela asiente con la cabeza.>$B$BTras esta sucesión de muertes, los Seis decidieron encerrar el báculo tras unas barreras de magia impenetrables que alguien debía custodiar: el vigía del báculo. Yo ocuparía ese puesto.' WHERE `entry` = 8394;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No queda mucho que contar. Archimonde destruyó Dalaran casi por completo y el báculo se perdió. Se cuenta que podría haber sido hecho añicos por los propios mecanismos de seguridad de la barrera.$B$BLos hay que creen que el báculo fue la causa de la represalia de Archimonde.' WHERE `entry` = 8395;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tras la caída de Dalaran, abandoné el Kirin Tor. Durante años busqué el báculo para poder liberarme de la labor que el Kirin Tor me había encomendado. Encontraría el báculo aunque me llevara toda una vida. Así es como entré a formar parte de El Alba Argenta y el motivo por el que estoy aquí.' WHERE `entry` = 8396;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Le he seguido la pista a los diversos trozos del báculo por todo este mundo y así descubrí que Kel\'Thuzad ansía reconstruirlo. Varios fragmentos obran ya en su poder, custodiados por sus tenientes más fieles.$B$BNadie puede imaginar lo importante que es que el gran báculo permanezca fuera del alcance de Kel\'Thuzad. Francamente, desconozco cuáles serían las consecuencias aparte, claro está, de una catástrofe de enormes proporciones.' WHERE `entry` = 8397;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, viajeros. Tengo orden de tratar a quien posea una llave de Karazhan como un invitado especial del Maestro. Hacedme todas las preguntas que quieras. ¿En qué puedo ayudaros?' WHERE `entry` = 8401;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Somos demasiado compasivos con los desdichados. Deberíamos clavarle una espada a cada uno de ellos.$B$BSu osadía en el fondeadero Vela del Sol es imperdonable.' WHERE `entry` = 8402;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Llevo todo el día matando descerebrados seres de la Plaga en La Cicatriz Muerta. Espero que vengas a echar una mano y no solo a entretenerme con tu charla.' WHERE `entry` = 8403;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vigila por donde andas.' WHERE `entry` = 8404;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Malditas sean todas esas alimañas! Supongo que no podrías ayudarme con esta situación, ¿verdad?' WHERE `entry` = 8406;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Eres $Gtonto:tonta;? ¿Te has olvidado de las CRIATURAS ESQUELÉTICAS que ensombrecían el paisaje en tu camino hasta aquí?$B$B<Rohan suspira.>$B$BSi prefieres el aire libre, encontrarás criaturas esqueléticas por todas las Tierras de la Peste tanto del Este como del Oeste. Créeme, no puedes dejarlos escapar. Ellos no lo harán contigo si tienen la oportunidad.$B$BSi por el contrario prefieres las mazmorras, has de ir sin duda a Stratholme y Scholomance en busca de trozos de hueso.' WHERE `entry` = 8409;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Otro día maravilloso y lóbrego aquí en Tranquillien, ¿no te parece, $C?$B$B¿Qué puedo hacer por ti?' WHERE `entry` = 8410;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, amigo. ¿En qué puedo ayudarte hoy?' WHERE `entry` = 8412;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Te diriges a Tranquillien, $C? ¿Hay algo que pueda hacer por ti?' WHERE `entry` = 8413;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Ah! Buena pregunta.$B$BSi prefieres ir de caza al aire libre, encontrarás elementales en las Tierras de la Peste, Azshara, Las Estepas Ardientes, Frondavil, Silithus, Cuna del Invierno e incluso en el Cráter de Un\'Goro.$B$BSi te gusta correr riesgos quizás prefieras ir a La Masacre. He oído que sus muros encierran muchos elementales. También puedes ir en busca de los elementales que trabajan para los enanos Hierro Negro de las Profundidades de Roca Negra.' WHERE `entry` = 8414;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás de broma? ¿De verdad no lo sabes?$B$BSi eres más bien cobarde y no te gusta mancharte mucho las manos, encontrarás fragmentos de hierro negro en las criaturas que habitan Las Estepas Ardientes y La Garganta de Fuego.$B$BSi tienes espíritu aventurero, en las mazmorras de Cumbre de Roca Negra y las Profundidades de Roca Negra hay gran número de fragmentos.' WHERE `entry` = 8415;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si eres de los que prefieren estar al aire libre, los antárboles de Frondavil y los azotadores de Un\'Goro son una excelente fuente de hojas.$B$BSi te gusta correr riesgos, La Masacre Este y Oeste albergan una cantidad casi ilimitada de hojas salvajes.' WHERE `entry` = 8416;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$C, mira lo que han hecho con nuestras queridas tierras. ¡La Plaga debe ser destruida!' WHERE `entry` = 8417;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Va a costar un poco, pero creo que podemos salvar la vida de este elfo de sangre.' WHERE `entry` = 8418;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién eres? Siento... tanto... frío.' WHERE `entry` = 8419;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Es vergonzoso que nosotros, los sin\'dorei, hayamos perdido tanto de lo que solíamos llamar hogar. No pasará mucho tiempo antes de que recobremos nuestra fuerza y recuperemos nuestras tierras del sur del control de la Plaga.' WHERE `entry` = 8421;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estoy demasiado ocupado como para andarme con juegos, $R. ¡Dime qué quieres o desaparece de mi vista!' WHERE `entry` = 8422;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡El Sumo Ejecutor y la Dama Aurífera me están haciendo esperar, pero no tengo tiempo para esto! ¡Mis amigos están ahí fuera muriendo, $C!' WHERE `entry` = 8423;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Envié a la forestal Salissa al sur al enclave y al forestal Lethvalin al oeste, a Tranquillien, hace más de un día! ¿Has visto a alguno de los dos ¿Eres la ayuda que debían enviar?' WHERE `entry` = 8424;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A veces siento que nunca volveré a ser un simple funcionario al servicio de la Dama Aurífera. No estoy hecho para la guerra.$B$BAfortunadamente están aquellos, como tú, que arden en deseos de ver la antigua belleza de Quel\'Thalas devuelta a ella. ¡Aplasta a la Plaga, $C! ¡Aplástalos dondequiera que puedas!' WHERE `entry` = 8425;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me han informado de que pudiste ayudar a la forestal Valanna y al teniente en Zeb\'Sora.$B$BGracias, $C. Supongo que ahora debería encaminarme de vuelta al Enclave del Errante.' WHERE `entry` = 8428;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué te trae por aquí, $C?' WHERE `entry` = 8429;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, hola, $R. Como puedes ver, todavía no tengo abierto para los clientes. Si me disculpas, tengo mucho que hacer.' WHERE `entry` = 8431;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es un gran placer volver a verte, $C. No, en serio. Eres la razón por la que estoy haciendo dinero aquí rápidamente.$B$BBueno, ¿qué te puedo vender hoy?' WHERE `entry` = 8432;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te doy la bienvenida a Tranquillien, $C. Debo disculparme por el estado en que la encuentras. Como ves, hemos tenido algunos problemas, pero los Renegados y su Lady Sylvanas Brisaveloz han sido lo suficientemente corteses como para prestarnos mucha ayuda en nuestro tiempo de necesidad.$B$BSi puedes echar una mano, no dudes en hacerlo. Todos podemos hacer uso de la ayuda que podamos en nuestra lucha contra la miríada de fuerzas formadas contra nosotros.' WHERE `entry` = 8433;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Es muy importante que averigüemos qué están tramando los elfos de la noche, $C. Centra todos tus esfuerzos en ese fin.' WHERE `entry` = 8439;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sois vosotros los refuerzos? ¡Los elfos de la noche han sido vencidos!' WHERE `entry` = 8441;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Menos mal que desactivaste el cristal lunar en An\'owyn cuando lo hiciste, $C! Ven cuando te apetezca ayudarme con mi investigación.' WHERE `entry` = 8442;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Cómo he de trabajar en estas condiciones? ¡Y con todos estos Renegados por aquí! ¡Cuánto me aflige ver a Tranquillien y Quel\'Thalas así!' WHERE `entry` = 8445;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Informa, $R! ¿Qué noticias importantes tienes? No te entretengas... La Dama Aurífera y yo estamos en plena discusión estratégica.' WHERE `entry` = 8447;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida; a la Capilla de la Esperanza de la Luz, $Gforastero:forastera;.' WHERE `entry` = 8448;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestras caravanas parten dos veces al día pero también es cierto que la Plaga les ha salido al encuentro para detener a Didier y sus mulas y así evitar que el envío llegue a las primeras líneas. Es lo más arriesgado que he visto en mi vida. Si contáramos con valientes que custodiaran los envíos...' WHERE `entry` = 8452;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tengo mucho que hacer y poco tiempo para hacerlo, $R! Por favor, apártate de mi camino.', `text0_1_loc7` = '¡Tengo mucho que hacer y poco tiempo para hacerlo, $R! Por favor, apártate de mi camino.' WHERE `entry` = 8454;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El elfo de sangre recupera rápidamente la conciencia cuando le viertes la pócima en la boca.>$B$BTe debo la vida, $C. Sin tu ayuda habría sucumbido a la dolorosa tortura de la Plaga.' WHERE `entry` = 8456;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El elfo de sangre yace en el suelo, sumido en un letargo artificial.>' WHERE `entry` = 8457;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '<La Renegada está retorcida en el suelo haciendo una mueca. No parece que pueda ponerse de pie por sí sola en este momento.>' WHERE `entry` = 8459;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ahhhhggg... este dolor de cabeza me está matando.$B$BTe deben de haber enviado a rescatarme. ¡Ya podrían haberse dado más prisa! ¿Tienes idea de lo que les hacen a los prisioneros aquí?' WHERE `entry` = 8460;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El elfo de sangre yace desfallecido en la mesa. Aunque aún respira, parece haber sido víctima de potentes toxinas sedativas.>' WHERE `entry` = 8461;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Varnis tose con violencia mientras el jarabe vigorizante hace efecto.>$B$BGracias por rescatarme. ¡Debo volver a Tranquillien enseguida!' WHERE `entry` = 8463;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las corrientes de aire fresco que soplan desde Canción Eterna de vez en cuando realmente me afectan al estómago, ¿sabes lo que quiero decir?' WHERE `entry` = 8464;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si lo que quieres explicar es un asunto de importancia, $C, tal vez sería más apropiado que pidieras una audiencia con la Dama Aurífera. Sin embargo, debo advertirte que está muy ocupada y tal vez tengas que esperar mucho.' WHERE `entry` = 8466;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Normalmente yo no debería hablar contigo directamente; lo normal sería que pidieras una audiencia a la señora. Sin embargo, te encargué personalmente una tarea, así que ¿qué querías comentar?' WHERE `entry` = 8467;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este panel contiene varios anuncios y noticias sobre recompensas.' WHERE `entry` = 8469;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tenemos trabajo importante que hacer, $N. Ataquemos a nuestros enemigos mientras tengan la guardia baja. Enseñémosles que la venganza de los sin\'dorei no olvida ni perdona.' WHERE `entry` = 8470;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Vaya, eso es un problema! ¿Cómo se supone que debo obtener las muestras de metal para los magistri si las minas están inundadas con esa chusma gnoll?' WHERE `entry` = 8472;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Dónde podría estar? Envié a mi aprendiz, Shatharia, a las Minas Sondaluz, al oeste, hace algún tiempo. ¡Necesitamos recuperar esas muestras de mena, y rápido, o nuestro experimento fracasará!' WHERE `entry` = 8474;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sin la entrega de la mena de Sondaluz de una forma tan oportuna, nuestra investigación habría fracasado.$B$BEspera a que Shatharia vuelva a asomar la cabeza por aquí. ¡Tengo unas cuantas tareas interesantes para ella, puedes estar $Gseguro:segura;!' WHERE `entry` = 8475;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los sin\'dorei hemos perdido demasiado a lo largo de los últimos años. Nuestras tierras, nuestras casas, hasta nuestras identidades... $B$BYa va siendo hora de que empecemos a recuperarlo.' WHERE `entry` = 8476;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Antes de que podamos superar obstáculos externos, como la Plaga y los trols Amani, debemos superar obstáculos internos. Los desdichados han sido una molestia durante el tiempo suficiente; es hora de derrotarlos.' WHERE `entry` = 8477;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Crees que podrías ayudarme a salir de aquí?' WHERE `entry` = 8478;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entre los diversos fragmentos de pergamino que contienen listas de verificación y otra información de los Errantes, destaca uno. ¡Es el dibujo de un trol horripilante, quizás la criatura más espantosa que hayas visto!' WHERE `entry` = 8479;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Se pueden obtener muchas ventajas conociendo al enemigo. Apréndete esa lección, $N.$B$BCuanto más sepamos sobre la Plaga, más bien preparados estaremos para derrotarlos.' WHERE `entry` = 8485;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida; al Enclave del Errante, $C. Quédate todo el tiempo que quieras. Son bienvenidos todos los que nos apoyan en las Tierras Fantasma.' WHERE `entry` = 8487;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las fuerzas Arcanas perdidas pueden hacer cosas raras a la tierra, a los elementos e incluso a los espíritus de los muertos.' WHERE `entry` = 8488;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Sumo Ejecutor es un hombre muy ocupado, $C. Tal vez yo pueda ayudarte con lo que necesites.' WHERE `entry` = 8489;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Me alegro de volver a verte, $Gquerido:querida;! Formamos un buen equipo. Ya nos veremos.' WHERE `entry` = 8491;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu ayuda con los trols Sombrapino está siendo indispensable, $C. Sigue así y tal vez podamos volver a ocuparnos de la Plaga.' WHERE `entry` = 8495;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Has asestado el golpe decisivo contra los trols Sombrapino, $N. Solo espero que podamos reunir nuestras fuerzas lo suficientemente rápido como para rematarlos antes de que reciban refuerzos de Zul\'Aman.$B$BEstaría bien que Tranquillien nos proporcionara refuerzos.' WHERE `entry` = 8496;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ciertamente, $N. Sólo tienes que decirme de que reputación quieres conocerlo.$B' WHERE `entry` = 8498;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ciertamente, $N.$B$BLos aventureros que el Alba ve como Venerados deben presentar en conjunto un total de 14 insignias para acceder a artículos superiores. Para tener acceso a los artículos de mayor poder aún, se les requeriría presentar un total de 90 insignias.$B$B¡Los que son Venerados por el Alba encuentra el costo de los artículos superiores bastante razonable! El costo de los artículos más poderosos sigue siendo alto. $B' WHERE `entry` = 8499;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ciertamente, $N.$B$BLos aventureros que el Alba ve como Honorables deben presentar en conjunto un total de 40 insignias para acceder a artículos superiores. Para tener acceso a los artículos de mayor poder aún, se les requerirá presentar un total de 150 insignias.$B$BDebo admitir, que le precio es alto incluso para ellos. $B' WHERE `entry` = 8500;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La situación es más desalentadora de lo que se ha hecho creer a muchos. Los Renegados están aquí como aliados en la lucha contra nuestro enemigo común, la Plaga.$B$B¡Confía en mí cuando digo que sin ellos, las Tierras Fantasma estarían perdidas y la Plaga ya habría invadido todo el Bosque Canción Eterna!$B$B¡Pero, más que nada en el mundo, necesitamos tu ayuda para recobrar nuestras antaño gloriosas tierras!' WHERE `entry` = 8501;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ciertamente, $N.$B$BLos aventureros que el Alba ve como Exaltados deben presentar en conjunto un total de 12 insignias para acceder a artículos superiores. Para tener acceso a los artículos de mayor poder aún, se les requerirá presentar un total de 54 insignias.$B$BLos Exaltados a los ojos del Alba reciben el mayor descuento. $B' WHERE `entry` = 8502;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ciertamente, $N.$B$BLos aventureros que el Alba ve como Amistosos deben presentar en conjunto un total de 60 insignias para acceder a artículos superiores. Para tener acceso a los artículos de mayor poder aún, se les requerirá presentar un total de 220 insignias.$B$BDebo admitir, que el precio es bastante alto para los aventureros que no tienen el favor del Alba. $B' WHERE `entry` = 8503;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estas piedras rúnicas protegen las tierras de Canción Eterna de ser contaminadas por la Plaga. La piedra rúnica del oeste fracasó hace mucho, forzándonos a quemar la tierra que había a su alrededor.$B$BMientras viva, no permitiré que las piedras rúnicas restantes nos fallen. Tal es mi juramento.' WHERE `entry` = 8504;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Bueno, no has venido hasta aquí para parlotear con una taza de té en la mano. Veamos cómo podrías ayudarnos a salir de esta situación.' WHERE `entry` = 8505;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El maestro artesano Omarion simplemente desapareció una noche, como un susurro al viento... ¡Paf! ¡Y desapareció! Ahora soy el único que puede fabricar los objetos para los chicos que están en primera línea de batalla.' WHERE `entry` = 8513;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los sin\'dorei y los Renegados comparten su objetivo en las Tierras Fantasma, $N. Juntos acabaremos con la Plaga y quién sabe... tal vez los Renegados sean valiosos aliados en nuestro intento de alcanzar Terrallende.' WHERE `entry` = 8515;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestros sagrarios obtienen el poder de las corrientes de líneas-Ley que cruzan nuestras tierras, invisibles a simple vista.$B$BAhora que ha desaparecido La Fuente del Sol, nos proporcionan una fuente de energía continua y fiable.' WHERE `entry` = 8534;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El hecho de que la Aldea Brisa Pura siga en pie es una prueba irrefutable de la tenacidad de nuestra raza. Nunca más concederemos la derrota a la Plaga.' WHERE `entry` = 8535;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Abrid paso, escoria! Revilgaz me ha librado de la horca pero el castigo no será mucho mejor.', `text0_1_loc7` = '¡Abrid paso, escoria! Revilgaz me ha librado de la horca pero el castigo no será mucho mejor.' WHERE `entry` = 8536;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Dar\'Khan cree que nos rendiremos ante la Plaga sin oponer resistencia. Le demostraremos que se equivoca, $N.' WHERE `entry` = 8548;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿No ves que estoy intentando mantener una conversación con la dama? Oh, muy bien, de todos modos parece preocupada.$B$BSolo ofrezco las mejores bebidas, tanto de cosecha local como de procedencia exótica. ¿Quieres echar un vistazo?' WHERE `entry` = 8549;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Es eso una lista del Refugio de Saltheril? Sin duda, su señoría necesitará una botella de mi mejor calidad. Y, por supuesto, cualquier amigo de Lord Saltheril merece un precio especial por un añejo tan excelente.' WHERE `entry` = 8550;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si tienes dinero, tengo comida y bebida para satisfacer tu apetito. Todo el dinero se destina a mantener el Retiro en plena forma.' WHERE `entry` = 8552;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Eso que llevas ahí es una lista de Lord Saltheril? Sí, te puedo vender un lote de aperitivos, recién sacados del fuego.' WHERE `entry` = 8553;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tengo todo tipo de artículos a la venta, $C. Echa un vistazo.' WHERE `entry` = 8556;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una lista... qué curioso. Ah, sí, el envío de fuegos artificiales para Lord Saltheril. No los ha pagado; ¿has venido a comprarlos en su nombre, $C?' WHERE `entry` = 8557;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Es que esos no-muertos no tienen fin? Parece como si por cada necrófago o esqueleto que matáramos, dos ocuparan su lugar.' WHERE `entry` = 8558;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$C, ¿no habrás visto un mensajero cuando venías hacia aquí? ¡Estamos esperando información de Ciudad de Lunargenta y su mensajero llega tarde!' WHERE `entry` = 8561;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A veces me quedo aquí, observando el bosque más allá del lago, y veo la tierra como una vez fuera... verde y llena de vida.' WHERE `entry` = 8564;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tenemos que encontrar la manera perfecta de ocuparnos de esos apestosos trols Sombrapino!' WHERE `entry` = 8565;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debo decir que administraste mi veneno y te ocupaste de esos trols muy despiadadamente, $C. ¡Tienes mucho talento!' WHERE `entry` = 8566;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Debemos mantener un estado de vigilia, $C! Entre Dar\'Khan el Traidor y su Plaga, y los trols Sombrapino respaldados por la fuerza de Zul\'Aman, apenas mantenemos la cabeza sobre el agua. ¡No deberíamos estar dividiendo nuestra atención!$B$BEspero que estés aquí para ayudar.' WHERE `entry` = 8569;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Me alegro de que hayas venido a ayudarnos, $C. Cuanto antes nos ocupemos de estos trols, antes podremos dedicarnos a devolver a la Plaga al lugar de donde vino.' WHERE `entry` = 8570;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ya nos hemos ocupado de un grupo de trols; falta otro. ¡No tienes idea de cuánto aprecio lo que estás haciendo, $N! Antes de que aparecieras no pensaba que fuéramos a tener nunca bajo control el problema de los Sombrapino.' WHERE `entry` = 8571;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Qué te trae por aquí, $N? No vemos mucha gente por este lugar. Con razón, supongo.' WHERE `entry` = 8575;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Deseas aprender la profesión de herrero o hay algo más que quieras decirme?' WHERE `entry` = 8576;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Tenemos mucha suerte de estar vivos!' WHERE `entry` = 8579;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No se ven muchos aprendices por aquí, ¿verdad? Estoy empezando a pensar que es porque los magistri nos dan todo el trabajo peligroso que prefieren no hacer ellos mismos.' WHERE `entry` = 8580;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La nuestra es la senda de la pureza y la rectitud en la Luz.', `text0_1_loc7` = 'La nuestra es la senda de la pureza y la rectitud en la Luz.' WHERE `entry` = 8582;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Somos uno en la Luz, $Ghermano:hermana;, pero no puedo instruirte.' WHERE `entry` = 8584;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Vienes en busca de instrucción, cazador?', `text0_1_loc7` = '¿Vienes en busca de instrucción, cazador?' WHERE `entry` = 8585;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo soy instructor de cazadores. A ti te interesa el instructor de $C.' WHERE `entry` = 8586;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te entrenaría, pero no eres un guerrero.' WHERE `entry` = 8588;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nosotros, los profesionales de las artes Arcanas y Elementales seguimos un camino interminable de descubrimiento. ¿Por eso buscas mi consejo? ¿Deseas más instrucción?', `text0_1_loc7` = 'Nosotros, los profesionales de las artes Arcanas y Elementales seguimos un camino interminable de descubrimiento. ¿Por eso buscas mi consejo? ¿Deseas más instrucción?' WHERE `entry` = 8589;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Por los naaru, tú no eres mago! Si buscas instrucción, debes buscar a un instructor de $C.', `text0_1_loc7` = '¡Por los naaru, tú no eres mago! Si buscas instrucción, debes buscar a un instructor de $C.' WHERE `entry` = 8590;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aunque no puedo entrenarte, joven $C, puedo ofrecerte mi bendición. ¡Camina con valentía bajo la Luz!', `text0_1_loc7` = 'Aunque no puedo entrenarte, joven $C, puedo ofrecerte mi bendición. ¡Camina con valentía bajo la Luz!' WHERE `entry` = 8592;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muchos son los caminos de la Luz, $C. Debes decidir cuál seguir en este mundo o en cualquier otro.' WHERE `entry` = 8616;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A medida que te vayas ganando un nombre en nuestra ciudad podré ofrecerte más artículos, $N. Disponemos de pocas reservas de equipo y solo podemos permitirnos ofrecérselas a gente en quien confiemos.' WHERE `entry` = 8617;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ofrezco el refugio que puedo en nuestras tierras destruidas.', `text0_1_loc7` = 'Ofrezco el refugio que puedo en nuestras tierras destruidas.' WHERE `entry` = 8618;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Me alegro de verte, $G hermano : hermana;! Este lugar me aterroriza. Mientras estás aquí, asegúrate de aprovechar las oportunidades que estos Renegados ofrecen.' WHERE `entry` = 8619;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No es culpa mía... ¡¡¡NO ES CULPA MÍA!!!$B$BSi hubieran hecho lo que les dije, todo habría ido bien. ¡Pero no! No me escucharon.$B$BAhora están todos muertos.$B$BSolo quiero irme a casa. A casa, a Lunargenta. Estoy tan cansado...' WHERE `entry` = 8620;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Comercio con la comodidad... Calma tu salvaje impulso, $R. Mi posada es tu hogar...' WHERE `entry` = 8634;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $C. Si aún no lo has hecho, deberías hablar con el Errante Solanna.' WHERE `entry` = 8638;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Te agradezco mucho tu ayuda, $C!' WHERE `entry` = 8650;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espera en la cola, ¿de acuerdo? La comida de Terrallende no me sienta nada bien.', `text0_1_loc7` = 'Espera en la cola, ¿de acuerdo? La comida de Terrallende no me sienta nada bien.' WHERE `entry` = 8656;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me llamo Sid. Me alegro de ver alguna cara nueva por aquí. ¿Qué puedo hacer por ti?' WHERE `entry` = 8658;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aunque hemos llegado a la tierra prometida, $N, aún queda mucho por hacer. Nuestra gente no llegará a Kael\'thas si nos lanzamos a la desesperada a través de Terrallende.$B$BDebemos encontrar rutas seguras, asegurarnos los recursos y esquivar o destruir a nuestros enemigos.' WHERE `entry` = 8659;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Lo que daría por un cristal de sanación justo ahora!' WHERE `entry` = 8662;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Como hemos llegado hasta aquí y cómo vamos ha salir?' WHERE `entry` = 8664;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Me alegra volver a verte y que te encuentras bien, $N!' WHERE `entry` = 8667;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mirren y yo somos dos de los mejores cazadores de cada lado del Portal Oscuro. Quédate, puede que aprendas un par de cosas.' WHERE `entry` = 8672;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espero que no busques cerveza. ¡No vamos a compartirla y basta!$B$BNo es culpa nuestra que a nadie se le ocurriera traer cerveza a Terrallende excepto a nosotros.' WHERE `entry` = 8673;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Por la esperanza de que no toda Terrallende sea tan inhóspita como esta región. Este es un cambio bastante grande de las verdes praderas de Canción Eterna.' WHERE `entry` = 8674;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay en Terrallende presencias demoníacas mucho más poderosas que nada que hayamos visto en el viejo mundo.$B$BHabrá un gran poder para aquellos que no las rehúyan, sino que aprendan a aprovechar y llegar a dominar su fuerza.' WHERE `entry` = 8675;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nací aquí, $R. Este es mi mundo – todo lo que conozco... De niño, mi madre y mi padre me obsequiaban historias de tu mundo y de su esplendor. Cómo ansiaba dejar este lugar.$B$BDesgraciadamente, el destino es un amante cruel. Mi madre y mi padre fallecieron hace tiempo y están enterrados en este mismo cementerio. Yo permanezco para defender su honor y el honor de mi familia – los hijos de Lothar.' WHERE `entry` = 8676;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué puedo hacer por ti?', `text0_1_loc7` = '¿Qué puedo hacer por ti?' WHERE `entry` = 8678;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Terrallende rezuma energía mágica, está en el mismo aire. ¿La sientes, $N? No se me ocurre un lugar más apropiado para nuestra raza. ¡Pronto los sin\'dorei tendrán una nueva tierra de la que dirán que es su hogar!' WHERE `entry` = 8680;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mi marido lleva días desaparecido. Nos fuimos juntos de peregrinación y se suponía que deberíamos estar de vuelta juntos también.$B$BEsta angustia me ha hecho abusar un poco de los cristales de maná.' WHERE `entry` = 8681;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'En realidad, los dracohalcones, si se crían adecuadamente, son bastante mansos. ¿No te parece fascinante? Ser tan agradables y sociables si se crían bien y tan fieros y letales en si hábitat natural... Los adoro con locura.' WHERE `entry` = 8682;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No... te preocupes... por... mí' WHERE `entry` = 8683;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La verdad es que no creo que nuestra situación sea tan mala. Estoy segura de que todo se rectificará pronto y el poder de La Fuente del Sol se restablecerá. No es posible que sea tan malo como todo el mundo pretende que es.' WHERE `entry` = 8684;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ha valido la pena hacerse amigo de los Errantes; yo puedo llevar a cabo investigaciones con sujetos que solo puedo encontrar en Terrallende y ellos... disfrutan de mi encantadora personalidad y de mi atractiva y dura apariencia.' WHERE `entry` = 8685;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Era solo un bebé cuando mi padre fue destinado a este páramo. Todo lo que siempre he sabido de él es lo que otros me han contado.$B$B¿Cambian tus sueños, $N? Los míos, no. Tengo un sueño: un horizonte carmesí me envuelve mientras la Legión, en cantidad innumerable, batalla en la distancia. Me arrodillo ante el cadáver de un hombre, es de suponer que mi padre, y lloro. Mientras lucha por conseguir aire, con todo el cuerpo aplastado, susurra algo. A pesar de todos mis esfuerzos, soy incapaz de oír lo que intenta decirme.' WHERE `entry` = 8688;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Crees que estoy siendo cruel con este gusano?$B$B¡Han matado a dos de mis hombres este mes! Somos demasiado amables con esta escoria de los desdichados.' WHERE `entry` = 8698;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida; a la aldea Brisa Pura. Lunargenta prácticamente nos ha abandonado, pero nos negamos a marcharnos. Este es nuestro hogar.' WHERE `entry` = 8699;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mi trabajo consiste en una única tarea: asegurar que el Sagrario del Oeste no tenga problemas.$B$BTendrás que disculpar mi temperamento, pero, como puedes ver, la situación se nos ha ido un poco de las manos.', `text1_1_loc7` = '¿Podría haber elfos de la noche saboteadores tras esto? Lo dudo. Con la carga que hemos estado poniendo en el Sagrario del Oeste solo era cuestión de tiempo que algo fuera mal.' WHERE `entry` = 8700;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás disfrutando del festival, $C?$B$BMientras los celadores de las llamas se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...', `text0_1_loc7` = '¿Estás disfrutando del festival, $C?$B$BMientras los celadores de las llamas se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...' WHERE `entry` = 8703;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si buscas trabajo, $C, agarra un pico y ponte a cavar. ¡Hay ruinas hasta donde alcanza la vista!' WHERE `entry` = 8712;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $N. Mis compañeros de los Hijos de Lothar y yo llevamos casi veinte años atrapados en este deprimente mundo. Hemos aguantado lo mejor que hemos podido, pero es asombroso ver que los peligros a los que nos enfrentamos hoy no son más que nuestros fracasos pasados.$B$B¿Nos ayudarás tú a superarlos?' WHERE `entry` = 8713;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La corrupción de Terrallende llega a extremos increíbles, $C. El Círculo Cenarion intenta comprender cómo han podido sobrevivir esas criaturas. El estudio de esta tierra desolada y maldita puede ayudarnos a encontrar la forma de curar algunas partes de nuestro propio mundo.' WHERE `entry` = 8715;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es triste ver lo que le ha pasado a esta tierra. La expedición de Cenarion hará lo que pueda para comprender el daño que se ha causado y repararlo.' WHERE `entry` = 8716;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $C. Tu llegada a Thrallmar no podría haber ocurrido en mejor momento. El Jefe de Guerra cuenta con nosotros, ya que no puede estar aquí él mismo.' WHERE `entry` = 8718;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Thrallmar te necesita, $C. El Jefe de Guerra te necesita. Debes estar a punto para cumplir con tu deber.' WHERE `entry` = 8719;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pocos ataques se ganan por pura fuerza. Al final, lo que lleva a la victoria es la astucia, los reflejos y el conocimiento de tu arma.', `text0_1_loc7` = 'Pocos ataques se ganan por pura fuerza. Al final, lo que lleva a la victoria es la astucia, los reflejos y el conocimiento de tu arma.' WHERE `entry` = 8724;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Otro héroe? Las parcas nos sonríen en este día, $N.' WHERE `entry` = 8733;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He oído hablar de tus hazañas, $N. Sé que, mientras estés en tierras de los Renegados te comportarás con dignidad y como un auténtico héroe de los sin\'dorei.' WHERE `entry` = 8740;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te doy la bienvenida, $C.' WHERE `entry` = 8750;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a la Avanzada del Halcón, $C. Yo me hago cargo de las necesidades de los peregrinos y los ayudo a continuar su largo viaje. Si todavía no has emprendido la peregrinación, puedo proporcionarte la información que necesites.' WHERE `entry` = 8752;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Camina siempre en la Luz, $N.' WHERE `entry` = 8754;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida; al Retiro del Errante. Somos forestales que hemos jurado defender a nuestra gente de los enemigos cercanos y lejanos.' WHERE `entry` = 8755;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Oh... ¿nos conocemos?', `text0_1_loc7` = 'Oh... ¿nos conocemos?', `text1_0_loc7` = 'Lo siento, no hablo con el servicio.', `text1_1_loc7` = 'Lo siento, no hablo con el servicio.', `text2_0_loc7` = '¿Y tú eres...?', `text2_1_loc7` = '¿Y tú eres...?', `text3_0_loc7` = 'Creo que me has confundido con alguien que quiere hablar contigo.', `text3_1_loc7` = 'Creo que me has confundido con alguien que quiere hablar contigo.' WHERE `entry` = 8759;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me he metido en una buena. La clarividente Regulkult intentó avisarme, pero mi descuido ha terminado por pasarme factura.$B$BBajé aquí para analizar las ruinas de Zeth\'Gor, pero me impliqué con la Legión en mi camino. Ahora todo lo que he logrado es una herida profunda en la pierna.' WHERE `entry` = 8767;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aunque no puedo ofrecerte instrucción, joven $C, puedo darte mi bendición. ¡Camina con valentía hacia la Luz!$B$B¿Hay algo más?', `text0_1_loc7` = 'Aunque no puedo ofrecerte instrucción, joven $c, puedo darte mi bendición. ¡Camina con valentía hacia la Luz!$B$B¿Hay algo más?' WHERE `entry` = 8768;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Es muy importante que averigüemos qué traman los elfos de la noche, $C! Centra todos tus esfuerzos en ese objetivo.' WHERE `entry` = 8769;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No hace mucho, en este sitio tuvo lugar una terrible batalla. Nosotros, Nethergarde, y El Alba Argenta mantuvimos a raya una oleada de demonios que surgieron del Portal Oscuro.$B$BAquellos eran días oscuros llenos de sangre y desesperación...$B$BPero los reinos de Azeroth se unieron a la batalla y conseguimos expulsar a los demonios. La lucha continúa del otro lado del Portal...$B$BEste campamento ahora es un lugar tranquilo; por desgracia, esa tranquilidad contrasta con los horrores que hay del otro lado.' WHERE `entry` = 8770;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las heroicidades de la estirpe de los Aterratrols son legendarias. Idolatro a ese hombre.$B$BY, encontrarlo vivo, en Terrallende...$B$B<Romus mira fijamente más allá de ti.>$B$BCuando lo vi por primera vez, casi me desmayé. La conmoción fue insoportable. Mientras me arrodillaba ante él, tuve que reprimir las lágrimas.$B$BPuedes apostar tu dulce trasero a que voy a hacer todo lo que esté en mi poder para asegurarme de que los Hijos de Lothar se mantienen a salvo.' WHERE `entry` = 8771;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Romus me ha informado de la tragedia que sufrió mi hermosa ciudad. No hay palabras que describan la pena que sentí cuando supe lo que le había ocurrido a Lordaeron y a mi amada Stromgarde.$B$BHace veinte años, nadie había oído hablar de la Legión Ardiente... ¡Malditos demonios!$BTe lo aseguro, $N; si algún día vuelvo a Azeroth, haré que Stromgarde recupere toda su antigua gloria. Lo juro...' WHERE `entry` = 8772;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Este emisor me está volviendo loca! Me falta todo tipo de piezas que necesito para repararlo.' WHERE `entry` = 8775;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$N! ¡Gracias de nuevo por toda tu ayuda.' WHERE `entry` = 8776;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No, todavía no he terminado con esas reparaciones. Pero no debería faltar mucho.' WHERE `entry` = 8777;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Sollozando> Odio los cementerios. Hay muchos muertos. Y mi dulce Luhanaa...' WHERE `entry` = 8780;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debemos esforzarnos por comprender el equilibrio que mantienen los espíritus elementales de este mundo.', `text0_1_loc7` = 'Debemos esforzarnos por comprender el equilibrio que mantienen los espíritus elementales de este mundo.' WHERE `entry` = 8786;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos. ¿Has venido a ayudar?' WHERE `entry` = 8788;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $R. ¿Qué tal te va?' WHERE `entry` = 8790;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Tienes noticias de tu casa, $C?' WHERE `entry` = 8791;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Acércate, $Gamigo:amiga;. No dejes que mis ojos te asusten.$B$BPuede que perdiera la vista hace muchos años, pero nunca la necesité para ver las cosas que importan de verdad.' WHERE `entry` = 8792;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Coge una red, joven $C. ¡Nuestro pueblo necesita sustento!' WHERE `entry` = 8793;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola. Confío en que el día te encuentre bien, ¿es así?' WHERE `entry` = 8794;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Qué alegría ver a otro sin\'dorei $Ghermano:hermana;! Como puedes imaginar, no hay mucha conversación por aquí.' WHERE `entry` = 8795;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me honra conocerte.' WHERE `entry` = 8796;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos. Soy el anacoreta Delan – uno de los draenei. Soy maestro, si se puede llamar así. Y, ¿quién puedes ser tú?' WHERE `entry` = 8798;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $C.' WHERE `entry` = 8800;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos. Soy la vindicadora Palanaar, paladín de la Luz y viajera. Estoy aquí para ayudar a los elfos de la noche.' WHERE `entry` = 8802;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Observa con atención El Exodar, joven $R. Es difícil imaginar que sobrevivimos a tal catástrofe.$B$B<Menelaous suspira.>$B$BEl profeta afirma que es aquí donde, al fin, se conocerá nuestro destino. Diez vidas pasadas huyendo de la locura de la Legión Ardiente... por fin aquí resistimos.$B$BSe acabó el huir... Estoy preparado para morir.' WHERE `entry` = 8806;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Que los espíritus te guíen, $R.' WHERE `entry` = 8810;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Fuerza a los clanes, $C! ¿Qué te trae por aquí?' WHERE `entry` = 8811;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te damos la bienvenida entre nosotros, $C. Al menos por el momento.' WHERE `entry` = 8813;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No tengo más trabajo para ti, $N. Quiero que sepas que el pargo rojo que compartimos fue algo especial que nunca olvidaré...' WHERE `entry` = 8822;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Por supuesto que tengo un trabajo para ti! Siempre nos viene bien un cazador. Esperemos que te vaya mejor que a los draenei que tuvimos que mandar de vuelta a El Exodar... para enterrarlos.$B$B<Acteon ríe.>$B$B¡Es broma! Si hubieras visto tu cara... Los mandamos a El Exodar para tratarlos de heridas leves. Nada de lo que no pudieran recuperarse en unos días...' WHERE `entry` = 8823;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tu trabajo aquí ha terminado, chamán. Vuelve con tu gente.' WHERE `entry` = 8826;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Tienes los conocimientos para continuar, chamán? ¿Sabes por qué estás aquí?' WHERE `entry` = 8827;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $R' WHERE `entry` = 8828;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es un buen día para estar vivo y fusionarse con los elementos.' WHERE `entry` = 8829;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mira a tu alrededor, chamán. ¿Ves el equilibrio de los elementos en la tierra, el aire, el agua y el fuego que los ha tocado a todos?' WHERE `entry` = 8832;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No te asustes... mis grifos puede que no estén muy vivos que digamos, pero aun así te llevarán sin problemas a tu destino. Dime... ¿qué torre buscas?', `text0_1_loc7` = 'No te asustes... mis grifos puede que no estén muy vivos que digamos, pero aun así te llevarán sin problemas a tu destino. Dime... ¿qué torre buscas?' WHERE `entry` = 8834;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si aún no has aprendido a usar las vendas, me encantará enseñarte los fundamentos de su creación y aplicación. La diferencia entre la vida y la muerte puede reducirse a menudo a vendar heridas adecuadamente.' WHERE `entry` = 8835;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ahora estoy muy ocupado, joven $C. Discúlpame, por favor.', `text0_1_loc7` = 'Ahora estoy muy ocupado, joven $C. Discúlpame, por favor.' WHERE `entry` = 8838;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Me han enviado de El Exodar para supervisar la construcción de la Avanzada Azur.' WHERE `entry` = 8840;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un alquimista puede hacer milagros con hierbas ordinarias, convirtiéndolas en pociones estables cuyos efectos van desde curas a respirar bajo el agua, a todo lo que hay en medio.' WHERE `entry` = 8844;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buen día tengas, $C.$B$BEstoy esperando oír de uno de los consejeros de los Altos Señores feudales acerca de una audiencia. Fue una auténtica lucha solo el poder tener una oportunidad de presentar mis credenciales.$B$BHe oído describir al Martillo Salvaje como \"cauteloso con los forasteros\", pero se ha demostrado que eso es un eufemismo. Con algo de suerte las cosas mejorarán cuando tenga la oportunidad de reunirme con Barbapluma, quien, según me han dicho, aprueba un vínculo más estrecho con la Alianza.' WHERE `entry` = 8847;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Qué quieres?$B$BNo es muy cortés dirigirse a un desconocido sin ninguna razón, ¿no crees?' WHERE `entry` = 8848;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GQuieto:Quieta;, $Gcompañero:compañera;. El Cocinitas soy yo, y lo que me va es cocinar. Los MacSalsafloja somos una familia con una larga tradición cocinera y mercader. Sabemos como satisfacer los más delicados paladares.' WHERE `entry` = 8849;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Necesitas algo?' WHERE `entry` = 8850;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Que la Luz te acompañe, $C.' WHERE `entry` = 8851;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Parece que por fin ha llegado la ayuda! Los omenai te damos la bienvenida a los restos de nuestro campamento, $N.' WHERE `entry` = 8852;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ikan lo intenta una y otra vez. Creo que esos Zarrapuchas son muy parecidos a Los Perdidos, ya sabes... perdidos.' WHERE `entry` = 8853;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Llevará su tiempo, pero estos Tábidos aprenderán los caminos de la Luz!' WHERE `entry` = 8854;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida;, $N. Soy Landro Tirolargo, patrón de la Llama Negra. Puede que sea la primera vez que oyes hablar de mi local. No me sorprende; hacemos todo lo posible para que no se hable demasiado de nosotros. Hay que evitar meterse en líos entre las facciones.$B$BEn la Llama Negra nos especializamos en ofrecer los mejores servicios y el mejor entretenimiento que el dinero puede comprar; desde objetos difíciles de encontrar hasta peleas de gladiadores, pasando por lucrativos juegos de azar.$B$B¿Buscas algo en particular?' WHERE `entry` = 8855;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Necesitas algo, $C? Quizás pueda ayudarte.' WHERE `entry` = 8858;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las Tierras Fantasma no se recuperarán con solo la fuerza militar, $N. Aquí es donde entran en juego los magistri.$B$BNuestra astucia y nuestro conocimiento de lo Arcano no tienen igual.' WHERE `entry` = 8860;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '<La sacerdotisa Il\'dinare te mira con recelo.>' WHERE `entry` = 8861;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Soy una elfa de la noche, $Gextranjero:extranjera;. Aunque puede que no sepas nada de mi especie, nosotros sabemos mucho de ti y de tus progenitores. Puede que no te llames Eredar, pero, en apariencia, compartís muchas similitudes.$B$BHas ayudado a mis aliados, y te estoy agradecida por ello; pero, por el momento, andas por un camino incierto. Quizás con el tiempo puedas disipar las dudas y temores que albergo en el corazón.' WHERE `entry` = 8862;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ándate con cuidado, forastero.' WHERE `entry` = 8864;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Has sido de gran ayuda hasta ahora, $N. Harías bien en ayudar a los demás en el campamento.$B$BY no te preocupes por esos goblins, me estoy haciendo cargo de todo.' WHERE `entry` = 8865;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué miras?' WHERE `entry` = 8867;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Adamant Coracero, de la gran ciudad de Forjaz. Como probablemente puedas suponer viendo mi uniforme, una vez formé parte de la guardia de élite de Forjaz. ¡Sí, fui la mano derecha de Magni! Al menos, hasta que me retiré. Ahora me intereso por la arqueología. En realidad es una afición; además, ¡pensé que sería una gran forma de ver mundo!' WHERE `entry` = 8868;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué puedo haber hecho para dar a los dioses motivos para arremeter contra mí con tal ferocidad? Todo lo que quería ha desaparecido – muerto o ha sido destruido. Mi familia, arrebatada por las bestias del bosque. Mi hogar, arrasado. Mi nave, hundida.$B$BSoy un hombre roto, perdido en una isla rota.$B$B¿Quizás estás aquí para terminar el trabajo? Hazlo rápido, no opondré resistencia. Toma mi vida, $Gextranjero:extranjera;; soy demasiado cobarde como para hacerlo yo mismo. Mi mano tiembla... el filo no será certero.' WHERE `entry` = 8870;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Antes de que los elfos de la noche invitaran a los draenei a unirse a ellos aquí, este claro estaba casi olvidado.$B$BLos kaldorei tienen un profundo vínculo con estas tierras, y nosotros, los draenei, nos sentimos honrados de compartir la tarea de revitalizar Canción del Bosque.' WHERE `entry` = 8873;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Me alegro de ver una cara nueva por aquí. Nuestra Avanzada es pequeña y nuestros enemigos, muy numerosos. Estamos rodeados por seres que quieren eliminarnos: sátiros al oeste, orcos del aserradero Grito de Guerra al sur y demonios del Cerro Lumbrevil.$B$BEs un alivio que los draenei se hayan unido a nosotros para reforzar Canción del Bosque contra todos esos peligros.' WHERE `entry` = 8874;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegra volver a oír las voces de otros aquí, en Canción del Bosque. Me había empezado a sentir como si fuera el último de los kaldorei que fuera a poner la vista en estas tierras antes de su descenso a la corrupción.' WHERE `entry` = 8875;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sientes el mal al sur? ¡Por la Mano, debe ser purificado!' WHERE `entry` = 8876;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aunque has asestado un duro golpe al mal demoníaco del sur, sigo sintiendo problemas en esa región.' WHERE `entry` = 8878;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hay demonios que matar, $C. ¿Tienes agallas para hacer lo que hay que hacer?' WHERE `entry` = 8883;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Saludad todos al héroe exaltado!', `text1_1_loc7` = '¡La historia de nuestro viaje será narrada en años venideros!', `text2_1_loc7` = 'Por fin tengo una historia que contarle a mis nietos... ¡Un traidor del reino estaba viviendo entre nosotros!', `text3_1_loc7` = 'A decir verdad, nunca me gustó ese gnomo...', `text4_1_loc7` = 'Tenemos suerte de haber sobrevivido a ese calvario. ¡De no ser por ti, todas las personas de esta isla podrían haber muerto!', `text5_1_loc7` = 'Me pregunto cuántos quedarán entre nosotros. Conocía a Chispik desde hacía muchos años. Nunca habría imaginado que era un traidor.' WHERE `entry` = 8893;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida; a Canción del Bosque, $C. ¿Podemos ayudarte?' WHERE `entry` = 8939;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mientras buscamos la ayuda de los pueblos de este mundo contra la Legión Ardiente, debemos aprender a vivir en paz con ellos.$B$BNo olvides esto mientras viajes por Azeroth. Que la Luz te guíe.' WHERE `entry` = 8953;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué es ese $Tchiquillo:chiquilla;? ¿Quieres a tu mami? Bueno, una lástima. ¿Quizás deberías hacer una visita al club de oficiales de Thrallmar para recuperarla?',  `text1_0_loc7` = '¿Sabes dónde estás? ¡Esto es el Bastión del Honor, cariño! ¡Ámalo o lárgate!', `text2_0_loc7` = '¡No estamos de excursión, gusano! ¡Estás en Terrallende! ¡Adáptate o te devolveré a través del portal de una patada!', `text3_0_loc7` = '¡No tengo tiempo ni ganas de explicarte nada, idiota! Bueno, ¿tienes algo importante que decirme o solo estás aquí para invitarme a tomar una copa, princesa?', `text4_0_loc7` = '¡¿Cuál es tu fallo principal?!', `text5_0_loc7` = 'Te he visto andar por ahí. ¿Por qué no nos haces un favor a todos y dejas tu equipamiento en la armería?', `text6_0_loc7` = '¿Quién, en nombre de Kurdran, te ha dejado pasar por el Portal Oscuro?', `text7_0_loc7` = 'Bueno, bueno, bueno, ¿qué tenemos aquí? ¿$tUn:Una; civil? ¿No es precioso? ¡No vas a obtener un tratamiento especial por mi parte!' WHERE `entry` = 8961;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No alces la voz, $C!',  `text1_0_loc7` = '¡Si tienes algo que decir, dilo!', `text2_0_loc7` = '¿Eres $Gun:una; $Tcivil:civil;?', `text3_0_loc7` = '¡Estoy ocupado, hazlo rápido!' WHERE `entry` = 8962;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has hablado ya con la suma sacerdotisa? Está ansiosa por oír la historia de la peregrinación desde tu perspectiva.' WHERE `entry` = 8963;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hoy es un gran día, $C!' WHERE `entry` = 8965;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿No ves que estoy ocupado?' WHERE `entry` = 8978;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi nombre es Gurf, $Gextranjero:extranjera;. ¡Gurf el Valiente!' WHERE `entry` = 8991;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Moordo, cazador anciano de Semprepino.' WHERE `entry` = 8992;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, $Gel Prometido:la Prometida; ha llegado. $GBienvenido:Bienvenida; al Bastión Semprepino.' WHERE `entry` = 8994;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buenas nuevas, $C. Soy Semprepino el Menor, heredero al trono de Semprepino.' WHERE `entry` = 8996;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Eres tú quien defenderá a mi gente y anunciará una nueva era de prosperidad para Semprepino?' WHERE `entry` = 8997;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una vez serví a Medivh... o fingí hacerlo. Ayudé al Ojo Violeta a vigilar muy de cerca al maestro.$B$BPero ahora, todo en lo que pienso es en... hacer herraduras. Una y otra vez.' WHERE `entry` = 9003;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entonces, ¿has oído hablar del Ojo Violeta? A lo mejor luego tendré tiempo para ti.$B$BTengo que volver al trabajo.' WHERE `entry` = 9004;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '<Eralan parece estar mascando muerteflor.>$B$BSi quieres hacer una poción de amor, estás en el sitio equivocado. Cegar, lisiar y matar es mi negocio, y espero que también el tuyo.' WHERE `entry` = 9005;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Luz nos da la fuerza y la magia para triunfar.', `text0_1_loc7` = 'La Luz nos da la fuerza y la magia para triunfar.' WHERE `entry` = 9007;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, joven chamán. Mi arrecife es tu casa.' WHERE `entry` = 9014;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Juntos abordaremos esta corrupción del agua.' WHERE `entry` = 9015;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Has venido a por suministros de herrería, verdad?' WHERE `entry` = 9019;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tengo carne fresca, pescado y setas. ¡Y mucho para beber!' WHERE `entry` = 9020;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $N. A medida que asciendas de categoría entre las fuerzas del Bastión del Honor, te otorgaré acceso a algunos de los objetos que tenemos en reserva.' WHERE `entry` = 9021;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$GBienvenido:Bienvenida; a Thrallmar, $N! Como sigues ayudándonos en nuestra causa, te otorgaré más acceso a nuestros suministros.' WHERE `entry` = 9023;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Me necesitas para cuidar de tus compañeros animales?' WHERE `entry` = 9024;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Reparto el correo a y desde Bruma Azur, $C. Si necesitas que se entregue un mensaje, es más que probable que se envíe primero a través de mí.' WHERE `entry` = 9043;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo que significa ser de la Mano de Argus... Somos agentes de la Luz. Servimos sin preguntas. Morimos sin preguntas.' WHERE `entry` = 9044;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, soy de los Tábidos. Espero que no te moleste. Tábido o draenei, siempre hay quien te margina desde dentro de la sociedad. Lo único que importa es conocerse mejor y comunicarse con los que forman este mundo.' WHERE `entry` = 9050;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Siéntate y ponte $Gcómodo:cómoda;.', `text0_1_loc7` = 'Siéntate y ponte $Gcómodo:cómoda;.' WHERE `entry` = 9051;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buen día, $C. ¿Necesitas un hipogrifo fiable?' WHERE `entry` = 9052;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Si tienes que viajar a El Exodar, tengo el hipogrifo perfecto para ti!' WHERE `entry` = 9053;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Shh! Silencio absoluto; estoy observando a los múrlocs.$B$BVivir en un caparazón de tortuga gigante es un sacrificio que estoy dispuesto a hacer por la ciencia. Después de todo, si los múrlocs me ven aquí, ¡reaccionarán ante mi presencia y contaminarán mis datos!$B$B¡Como el primer gnómico de la Liga de Expedicionarios, voy con valentía a lugares a los que ningún enano ha ido antes!' WHERE `entry` = 9054;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los alumnos son un poco como ovejas. Son útiles y hasta agradables cuando los orientas y les prestas mucha atención.$B$BLos dejas solos un minuto y acaban cayéndose por un precipicio o ahogándose en un río poco profundo.' WHERE `entry` = 9061;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este letrero contiene varios anuncios y avisos de recompensas por llevar a cabo tareas peligrosas de sondeo. Nada te llama la atención especialmente; quizás deberías volver a mirarlo más adelante.' WHERE `entry` = 9062;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Gciudadano:ciudadana;. Te ruego que sigas tu camino si es que no tienes nada que preguntarme.' WHERE `entry` = 9063;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Aléjate de mí, $R! No se sabe cuándo me volverá a tomar la enfermedad.$B$BDéjame solo antes de que vuelva a suceder.' WHERE `entry` = 9067;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Examinando el cadáver, concluyes que este tiene que ser el Caballero de sangre Estrella del Alba, el líder del asalto frustrado a la Ciudad de la Muerte.' WHERE `entry` = 9070;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $C. Me alegro de que te hayas decidido a visitarnos.' WHERE `entry` = 9074;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buenos días, $C. Si deseas aprender el arte del herrero, has venido al lugar idóneo.' WHERE `entry` = 9084;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Oh, no hagas caso de Buddy. No te hará daño, solo le gusta arañar de vez en cuando.$B$BEs solo un gatito grande y adorable.$B$B¿No es así, Buddy? Sí, bonito, cosita. ¡El pequeñín de tu mami, eso es lo que eres!' WHERE `entry` = 9085;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nosotros, los druidas de Cenarius, protegemos el equilibrio de la naturaleza. Lo más importante es el equilibrio. En momentos de necesidad, no deberíamos tardar mucho en tomar acciones decisivas. Que nadie te haga creer lo contrario, ¡sobre todo aquellos de entre nosotros que solo quieren observar!' WHERE `entry` = 9086;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién eres? Déjame en paz.' WHERE `entry` = 9090;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si nuestros enemigos se diesen cuenta de que lo único que queremos es vivir en armonía con ellos, puede que no necesitáramos atacarlos para sobrevivir.' WHERE `entry` = 9106;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Con nuevos enemigos a la vuelta de la esquina, ¡no debemos permitir a nuestros antiguos enemigos que se aprovechen de nuestra situación!' WHERE `entry` = 9108;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Asquerosos gigantes fúngicos! ¿Qué les hemos hecho nosotros?' WHERE `entry` = 9111;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si no me hubiera torcido el tobillo viniendo a este lugar, estaría ahí dentro con mis amigos. Mmmm, ahora que lo pienso, quizás sea mejor que esté aquí fuera, ¿no?' WHERE `entry` = 9112;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Fhwoor gustar pequeños esporinos. Ellos salvar Fhwoor. No comerlos.$B$BFhwoor odiar nagas. ¡Fhwoor destrozar!' WHERE `entry` = 9113;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí yacen los restos del desherbador Pulgaverde, uno de los compañeros druidas del vigía Jhang. Todo indica que fue asesinado rápida y limpiamente mientras intentaba escapar del Recinto de los Esclavos.' WHERE `entry` = 9118;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Casi lo consigo!$B$BVine aquí para demostrar la existencia de Quagmirran, el legendario gigante fúngico. Y lo conseguí; más o menos.$B$B¡Libérame! A cambio, te proporcionaré un beneficio para cuando tengas que tratar con Quagmirran.' WHERE `entry` = 9119;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Gracias al cielo que estás aquí! Zarpa y yo estábamos investigando este lugar cuando Musel\'ek, el señor del pantano, nos saltó encima. ¡Puede controlar a los animales, y hasta a los druidas!$B$BDesafortunadamente, Zarpa fue atrapado en forma de oso en aquel momento, y ahora es la mascota del señor del pantano. ¡No me atrevo a adoptar forma de gato, o me atrapará a mí también!$B$BMe temo que tendrás que luchar contra Zarpa para salvarlo. Sigue dirigiéndote al oeste, no tiene pérdida.' WHERE `entry` = 9121;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como único superviviente de los Tábidos, es un honor y una obligación continuar con las enseñanzas chamanísticas del clarividente Nobundo.' WHERE `entry` = 9122;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No me hagas perder el tiempo. ¿Qué es lo que quieres?', `text0_1_loc7` = 'No me hagas perder el tiempo. ¿Qué es lo que quieres?', `text1_0_loc7` = '¿No ves que estoy ocupado?', `text1_1_loc7` = '¿No ves que estoy ocupado?', `text2_0_loc7` = '¿Sí? ¿Qué quieres?', `text2_1_loc7` = '¿Sí? ¿Qué quieres?', `text3_0_loc7` = '¿Y tú eres...?', `text3_1_loc7` = '¿Y tú eres...?' WHERE `entry` = 9124;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Qué embarazoso, que un cazador te tome como mascota!$B$BTe debo la vida, pero, por favor, no te tomes más molestias por mi bienestar. Ahora que me has liberado de Musel\'ek, podré encontrar la forma de escapar. Quizás recoja a Rayge por el camino.' WHERE `entry` = 9125;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'En la naturaleza, todas las cosas están interconectadas en una red; una ecología. Si procuramos no alterarla, sino solo observarla, podremos tener la suerte de comprender las complejas relaciones que hay entre los organismos que nos rodean.$B$BUna vez que hayamos comprendido esto, podremos tomar las medidas necesarias para el bienestar de todas las criaturas. Intervenir sin ese conocimiento, provocaría su destrucción.$B$BYo estudio las relaciones entre los esporinos y los señores de la marisma.' WHERE `entry` = 9128;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los druidas me protegen de los nagas y de los malvados Perdidos. A cambio, les enseño las plantas y los animales del pantano.' WHERE `entry` = 9131;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ahora que la grosera interrupción ha terminado, como iba diciendo, ¡Quagmirran está cerca!$B$BSegún lo prometido, puedo fortalecerte con un favor mágico que debería ayudarte en tu lucha contra él.' WHERE `entry` = 9144;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ysiel esperaba algo de resistencia enemiga a su incursión en Terrallende. Por eso se nos trajo a los celadores.$B$BNunca esperamos que nuestra resistencia viniera con la fuerza o los números que los nagas han mostrado.' WHERE `entry` = 9167;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; al puesto de mando de los Caballeros de sangre, defensores de Quel\'Thalas, fieles siervos de Lunargenta y verdaderos maestros de la Luz.' WHERE `entry` = 9174;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La naturaleza favorece a aquellos que están dispuestos a conocer sus secretos, $N.' WHERE `entry` = 9176;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La naturaleza es una fuerza poderosa, $R. Aquellos que intentan dominarla nunca lo consiguen de modo permanente.' WHERE `entry` = 9177;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $N.' WHERE `entry` = 9179;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los espíritus están agitados, $R. Debes estar vigilante de tus alrededores mientras estés en Nagrand, no sea que su furia te consuma.' WHERE `entry` = 9180;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Anillo de la Tierra intenta conservar la fuerza y majestuosidad de los elementos.' WHERE `entry` = 9181;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Cómo puede mi pueblo luchar cuando los elementos están en una confusión tal? No ven, que, sin el apoyo de los elementos, el mundo simplemente no puede ser.' WHERE `entry` = 9182;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Untrag del Anillo de la Tierra. Intenta no ofender a los elementos mientras permanezcas en el Trono de los Elementos.' WHERE `entry` = 9183;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tengo tiempo para sermones ahora, $C. Busca tus conocimientos en otra parte.', `text0_1_loc7` = 'No tengo tiempo para sermones ahora, $C. Busca tus conocimientos en otra parte.' WHERE `entry` = 9186;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Parece que te equivocaste, $C.', `text0_1_loc7` = 'Parece que te equivocaste, $C.' WHERE `entry` = 9187;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $N. Confío en que no te hayan seguido hasta aquí.', `text0_1_loc7` = 'Saludos, $N. Confío en que no te hayan seguido hasta aquí.' WHERE `entry` = 9188;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tenemos mucho de qué hablar, $C. Quizás deberías buscar a otros individuos de ideas más similares.', `text0_1_loc7` = 'No tenemos mucho de qué hablar, $C. Quizás deberías buscar a otros individuos de ideas más similares.' WHERE `entry` = 9189;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es un placer ver a alguien cuerdo por aquí. ¿En qué puedo ayudarte?', `text0_1_loc7` = 'Es un placer ver a alguien cuerdo por aquí. ¿En qué puedo ayudarte?', `text1_0_loc7` = 'Ah, compañero forestal. ¿Qué necesitas?', `text1_1_loc7` = 'Ah, compañero forestal. ¿Qué necesitas?' WHERE `entry` = 9190;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Evidentemente careces de la capacidad para comprender las complejidades de lo Arcano, $C. Largo.', `text0_1_loc7` = 'Evidentemente careces de la capacidad para comprender las complejidades de lo Arcano, $C. Largo.' WHERE `entry` = 9191;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué ayuda te puedo brindar en tus estudios?', `text0_1_loc7` = '¿Qué ayuda te puedo brindar en tus estudios?' WHERE `entry` = 9192;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El nuestro es un camino para aquellos con voluntad férrea y determinación firme. No pareces poseer ninguno de estos rasgos, $C.', `text0_1_loc7` = 'El nuestro es un camino para aquellos con voluntad férrea y determinación firme. No pareces poseer ninguno de estos rasgos, $C.' WHERE `entry` = 9193;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta tierra ha sido arrasada por el accidente. Aunque no compartas nuestra filosofía, $C, te ruego que hagas todo lo que puedas para curarla.', `text0_1_loc7` = 'Esta tierra ha sido arrasada por el accidente. Aunque no compartas nuestra filosofía, $C, te ruego que hagas todo lo que puedas para curarla.' WHERE `entry` = 9194;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Que los vientos te guíen, $N.' WHERE `entry` = 9196;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Siempre me ha gustado ordenar y organizar cosas. Supongo que he salido a mi madre.', `text1_1_loc7` = 'Aprender todo lo que se pueda sobre nuevas especies de plantas es muy importante para nuestra expedición. No solo de las comestibles o las medicinales.$B$BLas plantas son la base de otras muchas formas de vida. Comprenderlas mejor significa comprender mejor todo el sistema natural.' WHERE `entry` = 9199;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todavía no sé qué pensar de tu calaña, $R. Algunos de vosotros pueden ser violentos y matar esporinos en cuanto los ven.$B$BPareces agradable... aunque quizás con un aspecto algo extraño, supongo.' WHERE `entry` = 9201;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a Esporaggar, $R. No solemos recibir la visita de gente como tú por aquí, pero tú nos has ayudado. Esta es tu casa.' WHERE `entry` = 9202;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Los señores de la marisma están devorando las bolsas de esporas! ¡Hay que detenerlos!' WHERE `entry` = 9203;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias a ti, nuestros jóvenes estarán a salvo, $N. Ve a Esporaggar y visita a mi gente. Todos han oído hablar muy bien de ti.' WHERE `entry` = 9204;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<La tierra retumba cuando los movimientos de Gordawg se detienen.>$B$BHabla.' WHERE `entry` = 9208;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi padre ha partido hacia lugares más exóticos en busca de mayores presas. Pero no te preocupes, ¡todavía hay mucho que cazar aquí!' WHERE `entry` = 9218;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mantén los ojos bien abiertos, $C. Nunca se sabe cuándo hay alguna bestia acechándote, y no al revés.' WHERE `entry` = 9219;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Escucha, $Gamigo:amiga;. He dejado la expedición en las competentes manos de mi hijo Junior. Cuando ya has cazado las mejores presas de Azeroth, empiezas a pensar en algo más grande.$B$B¿Y has visto las bestias que hay aquí?$B$B¡He venido a abatir la presa más peligrosa que hayas imaginado jamás! ¡Quiero cazar a Colmillo y llevarme su corazón como trofeo!' WHERE `entry` = 9220;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$N! Recuerdo tus proezas de caza. Puede que no empezaras demasiado bien en Tuercespina, pero has seguido a tu ritmo y ahora eres uno de los mejores tras abatir al viejo Bangalash.$B$BPero escucha, $Gamigo:amiga;, las presas de Terrallende ahora son lo máximo. ¡Mira mis cicatrices!' WHERE `entry` = 9221;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a nuestro templo, espíritu.' WHERE `entry` = 9226;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Tratar con los draenei, la gente que nos dio la espalda cuando sufríamos? No lo creo.$B$B¡Un espíritu recto no habla así! Muéstrate, impostor. ¿Quién te envía?' WHERE `entry` = 9227;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Así que debemos confiar en quienes nos insultan de esta manera. ¿Qué imagen tienen los draenei de nosotros como para usar trucos que llamen nuestra atención?$B$BSabemos que están deseando \"redimirnos\", pero mi pueblo no necesita redención alguna. Somos capaces de arreglárnoslas solos y perseguiremos nuestro destino como un pueblo independiente y orgulloso. Transmite este mensaje a tus aliados.' WHERE `entry` = 9229;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bien. Asegúrate de que lo comprenden.$B$BAhora te agradecería que dejaras mi templo en paz.' WHERE `entry` = 9231;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; al Puerto Orebor, $Gamigo:amiga;. Aquí no tienes nada que temer. Somos Tábidos, pero no somos enemigos.$B$BYo soy Ikuti de los Kurenai, un grupo de Tábidos que desean recuperar las relaciones con los hermanos draenei y sus nuevos aliados.' WHERE `entry` = 9235;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Maktu no te reconoce, $R, pero está encantado de verte. Si necesitas comprar o reparar armadura, ven a ver a Maktu.' WHERE `entry` = 9236;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los recuerdos de este templo me envuelven la mente como una bruma espesa, pero no consigo formar una historia coherente con ellos. Este lugar significó algo para mí antaño. Hasta que pueda recuperar ese recuerdo, me sentiré como si faltara parte de mí.$B$BQuizás los draenei puedan ayudarme a recuperar lo que he perdido.' WHERE `entry` = 9237;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Algunos me llaman \"Tim\", pero para mis amigos soy \"el especialista\".' WHERE `entry` = 9238;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si lo preguntas, es que no hay confianza suficiente como para que uses ese apelativo.' WHERE `entry` = 9239;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. ¿Quieres cazar al límite? No busques más.$B$BHemet Nesingwary ha reunido a la mejor expedición jamás vista sobre los restos de este mundo. Ven con nosotros de safari si te atreves.' WHERE `entry` = 9240;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No dejes que las ruinas que nos rodean te engañen, $C. Dalaran es tan poderosa como siempre.' WHERE `entry` = 9241;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Karazhan es más que un simple edificio, $N. Si trazaras las líneas-ley que atraviesan Azeroth, verías que todas convergen justo ahí.$B$B<Alturus señala un punto invisible bajo la torre.>$B$BFascinante, ¿verdad?',  `text1_0_loc7` = 'El archimago Alvareux escribió hace tiempo un tratado en el que desarrollaba una teoría fascinante. Proponía la existencia de una copia exacta de Karazhan en un mundo diferente al nuestro, pero que, de algún modo, compartía nuestro propio espacio.$B$BSe volvió loco poco después. Una lástima.' WHERE `entry` = 9242;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los naaru poseen una sabiduría que no podemos comprender. He aprendido más cosas de A\'dal en los últimos cinco meses que en todos los años que pasé en las bibliotecas de Dalaran.' WHERE `entry` = 9243;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bienvenido a Zabra\'jin, $C. Soy el cazador de las Sombras Denjai, supervisor de esta Avanzada y comandante de las fuerzas de Lanza Negra destacadas en la Marisma de Zangar.' WHERE `entry` = 9248;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ni se os ocurra pensar en intentar huir de mí, bebés múrloc! ¡Tengo ojos de halcón y orejas de... orejas de... de algo con orejas muy poderosas!' WHERE `entry` = 9249;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tor\'gash cree que puede ver el futuro en las entrañas de los pájaros o lanzando los huesos de caña de sus ancestros. En realidad todo lo que hace falta es preguntar a los espíritus. Pero no intentes contarle esto, $N. Es muy terco.' WHERE `entry` = 9250;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Has demostrado ser $Gun cazador:una cazadora; muy capaz, $N. Pero todavía hay una bestia que se aparece en tus sueños... ¡Colmillo!' WHERE `entry` = 9256;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Y ahí estaba, cara a cara con un venado talbuk y armado solo con mi cuchillo... <tose>... y mi inteligencia de cazador curtido que me salvaría el pellejo.$B$BDe repente, ¡veo que un par de vientorrocs cazadores se lanzan sobre mí desde el cielo! Su ataque a traición... <tose> no me ayudó demasiado tampoco.$B$BMe quedé con sus picos de trofeo, pero también tengo una cicatriz infectada de recuerdo. Es muy fea y me duele mucho, pero estoy seguro de que curará en un día o dos.' WHERE `entry` = 9258;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La primera vez que vi setas más altas que árboles, no tenía tanta seguridad en que pudiera llamar a este lugar hogar. Después de un tiempo, sencillamente dejas de darte cuenta.' WHERE `entry` = 9259;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No olvides tomarte tu tiempo para pescar algo mientras estés en la Marisma de Zangar. Nunca sabes cuándo volverás a estar cerca del agua.' WHERE `entry` = 9260;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Telredor era un refugio para sacerdotes draenei y peregrinos. Los sacerdotes residentes casi no reciben visitas.$B$BCuando los orcos lanzaron sus primeros ataques a los asentamientos draenei, los refugiados empezaron a llegar en masa. El anacoreta Ahuurn y los demás transformaron Telredor en un refugio seguro. Muchos draenei, yo incluido, debemos nuestras vidas a sus esfuerzos.' WHERE `entry` = 9261;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Las vistas desde la cima de Telredor son magníficas, ¿verdad?' WHERE `entry` = 9262;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a Telredor, $C. Si necesitas algo, no dudes en preguntar.' WHERE `entry` = 9263;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nunca había visto tantos tipos diferentes de setas en un mismo sitio. Si me dieran el tiempo suficiente, estoy seguro de que podría encontrar un uso para cada una.' WHERE `entry` = 9264;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Telredor ha cambiado más en los últimos veinte años que en los siglos pasados. Yo no me quejo, $N; no es la primera vez que cambia el mundo.$B$BLa Luz me ha traído amigos y compañeros que no habría conocido como monje de clausura. Aprende lo que puedas del pasado mientras estás aquí.' WHERE `entry` = 9265;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El cazador de las Sombras Denjai me ha enviado con un pequeño contingente de albañiles y guerreros para establecerse en esta Avanzada. ¡Apenas hemos terminado de construirla y ya nos pide informes!$B$BSi necesitas material, puedo ofrecerte algunas cosas, pero aún esperamos nuestro primer envío.' WHERE `entry` = 9267;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Si te encuentras con el médico brujo Tor\'gash dile que sus brebajes y sus talismanes no funcionan! Hice exactamente lo que me dijo y Magasha sigue coladita por Surai.$B$B¡No te creas nada de lo que dice ese falso médico brujo!' WHERE `entry` = 9268;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Reavij es tan sutil como un kodo! Pero lo peor es que no se da cuenta de que se nota. Veo la forma en que me mira cuando cree que nadie lo ve.$B$BMe asusta. Desearía que el cazador de las Sombras Denjai no lo hubiera mandado con nosotros. Nos podríamos haber deshecho de él cuando montamos la Avanzada Rata del Pantano.' WHERE `entry` = 9269;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Garadar es un caos, $Gextranjero:extranjera;. Desde que la Abuela enfermó, Garrosh ha caído en una profunda depresión. Desde luego no es el niño de su padre.$B$B<Kurkush suspira.>' WHERE `entry` = 9271;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los anuncios de recompensas por los líderes nagas de las tribus de Cresta Oscura y Escamas de Sangre están enumerados aquí.' WHERE `entry` = 9274;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Han muerto. Todos...$B$BLos Sangreoscura pasaron a través de nosotros y algo que no puedo comprender me poseyó. Dime, ¿alguna vez has experimentado una rabia tan grande que sientes que arde todo tu ser? ¿Un ardor que marchita a los vivos? ¿Un fuego que quema la sangre de tus venas? Eso es lo que yo sentí.$B$B<Los ojos de Colmillosauro brillan con una luz roja durante un segundo.>$B$BYo fui el único que sobrevivió. Me rodearon treinta... Sangreoscura... No recuerdo nada más. Perdí el conocimiento y me desperté al poco tiempo.' WHERE `entry` = 9275;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se avecinan tiempos oscuros, $Gextranjero:extranjera;...' WHERE `entry` = 9276;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los espíritus están muy agitados. Debemos encontrar la causa de tal alteración.' WHERE `entry` = 9277;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemet es el mejor cazador que conozco. Nos topamos en las colinas de Cuna del Invierno, en Azeroth, hace muchas lunas. Él y yo acechábamos a la misma presa: el gran felino conocido como Rak\'shiri.$B$BEn lugar de pelearnos por nuestra presa, hicimos una apuesta; quien consiguiera la piel del Sable de Hielo, podría exigirle algo al otro. Para mi vergüenza eterna, fui el que perdió.$B$B¿Tengo cara de haber ganado?' WHERE `entry` = 9283;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, sí, me siento mejor, pero la verdad es que me gusta estar aquí abajo.' WHERE `entry` = 9286;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Consorcio lleva meses planeando esta operación. Queríamos encontrar el Oshu\'gun, el diamante más grande del universo.$B$BUnos minutos antes de que empezáramos, nos llegó un mensaje del príncipe-nexo Haramad pidiéndonos que esperemos hasta que se resolviera una cuestión política en Tormenta Abisal. He transformado la operación en un mercado negro de gemas mientras esperamos, pero nuestros beneficios se reducen cada minuto.' WHERE `entry` = 9287;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gezhe está aquí para hacer un trabajo. Yo estoy aquí para asegurarme de que nadie lo moleste mientras trabaja.' WHERE `entry` = 9288;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sabes quiénes somos, $Gextranjero:extranjera;? Somos Mag\'har – orcos marrones, según he oído que tu especie nos llama. Los Mag\'har son supervivientes: supervivientes de una dinastía caída; supervivientes de la enfermedad roja; supervivientes de un mundo destrozado...$B$BSoy Jorin, hijo de Kilrogg; el último del linaje de Mortojo: Jefe de Guerra del clan de Foso Sangrante.' WHERE `entry` = 9289;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡No nos habríamos estrellado si Hemet aprendiera a no dispararle a los alerones cuando intenta darle a las presas del suelo!$B$BPero no pasa nada, los arreglaré bastante rápido.' WHERE `entry` = 9290;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Me alegro mucho de que Abusón Puñoinfecto recibiera su merecido! Tengo muchas pieles por si quieres darle alguna a Harold.' WHERE `entry` = 9292;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $N' WHERE `entry` = 9296;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Abuela nos crió a todos. Todos los Mag\'hari que ves han sentido la influencia y el cariño de la Abuela de una forma u otra. La echaremos mucho de menos cuando ya no esté.', `text1_1_loc7` = 'La Abuela dice que me llamo como un gran héroe orco.' WHERE `entry` = 9297;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pareces $Gun:una; $R razonable. Quizás podamos ayudarnos mutuamente.' WHERE `entry` = 9312;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sed fuertes. Kael\'thas nos ha traicionado y lo que sabemos es incierto, pero no os desmoralicéis. Aguantaremos como lo hemos hecho siempre. Arthas no consiguió destrozar nuestro ánimo cuando barrio nuestra tierra y tampoco lo conseguirá Kael\'thas. Aprendamos algo de esto mientras avanzamos hacia un futuro próspero.' WHERE `entry` = 9315;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿A qué lugar de nuestra bella ciudad deseas ir?', `text0_1_loc7` = '¿A qué lugar de nuestra bella ciudad deseas ir?' WHERE `entry` = 9316;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos casas de subastas en Lunargenta. ¿Cuál prefieres?', `text0_1_loc7` = 'Hay dos casas de subastas en Lunargenta. ¿Cuál prefieres?' WHERE `entry` = 9317;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La casa de subastas del oeste está en el medio del Bazar, al oeste del Camino de los Ancestros.', `text0_1_loc7` = 'La casa de subastas del oeste está en el medio del Bazar, al oeste del Camino de los Ancestros.' WHERE `entry` = 9318;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La casa de subastas del Intercambio Real está en la zona este de la ciudad, al sur del área de entrenamiento de Caballeros de sangre y forestales.', `text0_1_loc7` = 'La casa de subastas del Intercambio Real está en la zona este de la ciudad, al sur del área de entrenamiento de Caballeros de sangre y forestales.' WHERE `entry` = 9319;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué camino quieres seguir?', `text0_1_loc7` = '¿Qué camino quieres seguir?' WHERE `entry` = 9320;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Banco de Lunargenta se puede encontrar dentro del Bazar. Búscalo en la esquina sureste.', `text0_1_loc7` = 'El Banco de Lunargenta se puede encontrar dentro del Bazar. Búscalo en la esquina sureste.' WHERE `entry` = 9321;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si buscas un banco en la parte este de la ciudad, no busques más lejos, ve al Intercambio Real. Está en el muro norte.', `text0_1_loc7` = 'Si buscas un banco en la parte este de la ciudad, no busques más lejos, ve al Intercambio Real. Está en el muro norte.' WHERE `entry` = 9322;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La maestra del cielo Anochecer y sus dracohalcones están fuera y hacia el oeste de la Puerta del Pastor.', `text0_1_loc7` = 'La maestra del cielo Anochecer y sus dracohalcones están fuera y hacia el oeste de la Puerta del Pastor.' WHERE `entry` = 9323;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tandrine es la Maestra de la hermandad de Lunargenta. Se encuentra en el Camino de los Ancestros, cerca de la Puerta Magister.', `text0_1_loc7` = 'Tandrine es la Maestra de la hermandad de Lunargenta. Se encuentra en el Camino de los Ancestros, cerca de la Puerta Magister.' WHERE `entry` = 9324;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Posada Ciudad Lunargenta, ubicada en el Intercambio Real, está regentada por una mujer de talento llamada Velandra. Me han dicho que también se puede encontrar allí, a veces, al renombrado maestro vinícola.', `text0_1_loc7` = 'La Posada Ciudad Lunargenta, ubicada en el Intercambio Real, está regentada por una mujer de talento llamada Velandra. Me han dicho que también se puede encontrar allí, a veces, al renombrado maestro vinícola.' WHERE `entry` = 9325;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tenemos una gran abundancia de buzones de correo en nuestra hermosa ciudad. El que se encuentra junto al Banco Real de Cambio debería ajustarse a tus necesidades.', `text0_1_loc7` = 'Tenemos una gran abundancia de buzones de correo en nuestra hermosa ciudad. El que se encuentra junto al Banco Real de Cambio debería ajustarse a tus necesidades.' WHERE `entry` = 9326;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Shalenn se ocupa de los animales en la ciudad y evita que se pongan nerviosos cuando sus dueños están de paseo. Está en la Plaza del Errante, cerca de la sala de los forestales.', `text0_1_loc7` = 'Shalenn se ocupa de los animales en la ciudad y evita que se pongan nerviosos cuando sus dueños están de paseo. Está en la Plaza del Errante, cerca de la sala de los forestales.' WHERE `entry` = 9327;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Caballeros de sangre de la Plaza del Errante ceden generosamente espacio en su sala para Ileda, la maestra de armas de Lunargenta. Su entrenamiento no es barato, pero apreciarás su experiencia.', `text0_1_loc7` = 'Los Caballeros de sangre de la Plaza del Errante ceden generosamente espacio en su sala para Ileda, la maestra de armas de Lunargenta. Su entrenamiento no es barato, pero apreciarás su experiencia.' WHERE `entry` = 9328;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los maestros de batalla descansan detrás de los muros del enclave de los Caballeros de sangre, en la Plaza del Errante. Aunque suelen ser... un poco bruscos, también tienen sus bondades.', `text0_1_loc7` = 'Los maestros de batalla descansan detrás de los muros del enclave de los Caballeros de sangre, en la Plaza del Errante. Aunque suelen ser... un poco bruscos, también tienen sus bondades.' WHERE `entry` = 9329;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Un druida? Nuestra gente no sigue ese camino..., pero estás de suerte. Una tauren, creo que se trata de una hembra, se ha instalado en una modesta casa al norte del Camino de los Ancestros. Se llama Harene Correllanos.', `text0_1_loc7` = '¿Un druida? Nuestra gente no sigue ese camino..., pero estás de suerte. Una tauren, creo que se trata de una hembra, se ha instalado en una modesta casa al norte del Camino de los Ancestros. Se llama Harene Correllanos.' WHERE `entry` = 9330;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué camino quieres seguir?', `text0_1_loc7` = '¿Qué camino quieres seguir?' WHERE `entry` = 9331;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, buscas a uno... de esos. Bueno, si de veras quieres perder tu tiempo hablando con un raro de esos, encontrarás a los forestales en la Plaza del Errante. Zandine podrá atenderte.', `text0_1_loc7` = 'Ah, buscas a uno... de esos. Bueno, si de veras quieres perder tu tiempo hablando con un raro de esos, encontrarás a los forestales en la Plaza del Errante. Zandine podrá atenderte.' WHERE `entry` = 9332;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, ¿buscas a los maestros de lo Arcano? Puedes encontrar a Quithas y a los otros magistri en la Aguja Furia del Sol.', `text0_1_loc7` = 'Ah, ¿buscas a los maestros de lo Arcano? Puedes encontrar a Quithas y a los otros magistri en la Aguja Furia del Sol.' WHERE `entry` = 9333;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lady Liadrin, matriarca de los Caballeros de sangre, tiene el puesto de mando en la Plaza del Errante. Tendrás que hablar con el Campeón Bachi si quieres recibir instrucción. Habla siempre con respeto si aprecias tu cabeza.', `text0_1_loc7` = 'Lady Liadrin, matriarca de los Caballeros de sangre, tiene el puesto de mando en la Plaza del Errante. Tendrás que hablar con el Campeón Bachi si quieres recibir instrucción. Habla siempre con respeto si aprecias tu cabeza.' WHERE `entry` = 9334;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los sacerdotes de nuestra bella ciudad se pueden encontrar en la Aguja Furia del Sol. Busca a la dama Belestra.', `text0_1_loc7` = 'Los sacerdotes de nuestra bella ciudad se pueden encontrar en la Aguja Furia del Sol. Busca a la dama Belestra.' WHERE `entry` = 9335;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los más ágiles de entre nosotros pueden ser difíciles de ver a veces, pero, no obstante, probablemente te encontrarás con el pícaro Zelanis y con su cohorte en las profundidades del Frontal de la Muerte.', `text0_1_loc7` = 'Los más ágiles de entre nosotros pueden ser difíciles de ver a veces, pero, no obstante, probablemente te encontrarás con el pícaro Zelanis y con su cohorte en las profundidades del Frontal de la Muerte.' WHERE `entry` = 9336;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La invocación de demonios es una práctica peligrosa, pero puede proporcionar un poder enorme. Nuestro experto particular es Alamma; se ocupa de los brujos desde el Frontal de la Muerte.', `text0_1_loc7` = 'La invocación de demonios es una práctica peligrosa, pero puede proporcionar un poder enorme. Nuestro experto particular es Alamma; se ocupa de los brujos desde el Frontal de la Muerte.' WHERE `entry` = 9337;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué profesión quieres aprender?', `text0_1_loc7` = '¿Qué profesión quieres aprender?' WHERE `entry` = 9338;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los alquimistas están situados en la Corte del Sol; encontrarás a Camberon ansioso por enseñarte cualquier cosa que quieras saber sobre el arte de elaborar pociones.', `text0_1_loc7` = 'Los alquimistas están situados en la Corte del Sol; encontrarás a Camberon ansioso por enseñarte cualquier cosa que quieras saber sobre el arte de elaborar pociones.' WHERE `entry` = 9339;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bemarrin instruye en el oficio de la herrería en la Plaza del Errante. Aunque es un oficio algo... sucio, supongo que tiene sus ventajas.', `text0_1_loc7` = 'Bemarrin instruye en el oficio de la herrería en la Plaza del Errante. Aunque es un oficio algo... sucio, supongo que tiene sus ventajas.' WHERE `entry` = 9340;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Creo que Sedana la encantadora está reclutando aprendices en estos momentos. La encontrarás en las habitaciones de la Corte del Sol.', `text0_1_loc7` = 'Creo que Sedana la encantadora está reclutando aprendices en estos momentos. La encontrarás en las habitaciones de la Corte del Sol.' WHERE `entry` = 9341;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una elección extraña... pero no soy quién para juzgar, supongo. Danwe es a quien buscas; su puesto de ingeniería está cerca de la Corte del Sol, de camino a la Plaza del Errante.', `text0_1_loc7` = 'Una elección extraña... pero no soy quién para juzgar, supongo. Danwe es a quien buscas; su puesto de ingeniería está cerca de la Corte del Sol, de camino a la Plaza del Errante.' WHERE `entry` = 9342;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Alestus es maestro en... vendar heridas de cualquier manera. Si lo que quieres es aprender primeros auxilios, búscalo en el Camino de los Ancestros, cerca de la entrada al Intercambio Real.', `text0_1_loc7` = 'Alestus es maestro en... vendar heridas de cualquier manera. Si lo que quieres es aprender primeros auxilios, búscalo en el Camino de los Ancestros, cerca de la entrada al Intercambio Real.' WHERE `entry` = 9343;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las aguas de Quel\'thalas rebosan de peces; te ha llamado la atención, ¿verdad? Drathen tiene bastante experiencia en los trucos del oficio de la pesca, y se le puede encontrar en el Camino de los Ancestros, cerca del Intercambio Real.', `text0_1_loc7` = 'Las aguas de Quel\'thalas rebosan de peces; te ha llamado la atención, ¿verdad? Drathen tiene bastante experiencia en los trucos del oficio de la pesca, y se le puede encontrar en el Camino de los Ancestros, cerca del Intercambio Real.' WHERE `entry` = 9344;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La botánica Nathera tiene una gran habilidad para encontrar hierbas y me imagino que puede transmitir sus conocimientos a alguien como tú. La encontrarás en una de las habitaciones de la Corte del Sol.', `text0_1_loc7` = 'La botánica Nathera tiene una gran habilidad para encontrar hierbas y me imagino que puede transmitir sus conocimientos a alguien como tú. La encontrarás en una de las habitaciones de la Corte del Sol.' WHERE `entry` = 9345;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si lo que buscas es una joyería, encontrarás la tienda de Kalinda al extremo sur del Intercambio Real.', `text0_1_loc7` = 'Si lo que buscas es una joyería, encontrarás la tienda de Kalinda al extremo sur del Intercambio Real.' WHERE `entry` = 9346;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En el extremo suroriental del Camino de los Ancestros, Lynalis tiene un buen puesto de peletería. Si pagas, te adiestrará.', `text0_1_loc7` = 'En el extremo suroriental del Camino de los Ancestros, Lynalis tiene un buen puesto de peletería. Si pagas, te adiestrará.' WHERE `entry` = 9347;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si cavar en la suciedad es la clase de pasatiempo que encuentras entretenido, ve a compartir tu locura con Belil en la Plaza del Errante. Seguro que estaría contento al saber que no es la única persona que ha perdido el juicio.', `text0_1_loc7` = 'Si cavar en la suciedad es la clase de pasatiempo que encuentras entretenido, ve a compartir tu locura con Belil en la Plaza del Errante. Seguro que estaría contento al saber que no es la única persona que ha perdido el juicio.' WHERE `entry` = 9348;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tyn es la desolladora de talento; fabrica el mejor cuero de lince que haya visto. Si deseas aprender de ella, la encontrarás en el extremo sureste del Camino de los Ancestros.', `text0_1_loc7` = 'Tyn es la desolladora de talento; fabrica el mejor cuero de lince que haya visto. Si deseas aprender de ella, la encontrarás en el extremo sureste del Camino de los Ancestros.' WHERE `entry` = 9349;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El sastre Keelen siempre está dispuesto a recibir nuevos estudiantes para transmitir su delicado saber. Su tienda es un poco difícil de encontrar al principio: se encuentra justo al norte de la casa de subastas del Bazar. Él está en el piso más bajo del edificio.', `text0_1_loc7` = 'El sastre Keelen siempre está dispuesto a recibir nuevos estudiantes para transmitir su delicado saber. Su tienda es un poco difícil de encontrar al principio: se encuentra justo al norte de la casa de subastas del Bazar. Él está en el piso más bajo del edificio.' WHERE `entry` = 9350;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Por qué ser cruel con Zoquete? Zoquete hambriento, eso es todo.' WHERE `entry` = 9352;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡De acuerdo! Zoquete decirte todo lo que quieras!' WHERE `entry` = 9353;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Puño de Roca hambriento! ¡Necesitar comida! Tribu de ogros Mazo de Guerra echar Puño de Roca del montículo al norte. Muchos más Puños de Roca que alimentar ahora.' WHERE `entry` = 9354;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No cierto. Mí solo querer mordisquear. Yo no comer todo. Bueno, quizás comer los pequeños.$B$B¡Son deliciosos!' WHERE `entry` = 9355;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Zoquete se encoge de hombros.>$B$BVosotros lleváis guerra a los Puño de Roca. Nosotros ponemos en guiso.' WHERE `entry` = 9356;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Sácame de aquí, $R!' WHERE `entry` = 9357;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién es tan valiente como para presentarse ante Lantresor del Filo? ¿Acaso no reconoces la muerte cuando la tienes delante?' WHERE `entry` = 9361;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entonces eres $Gun insensato:una insensata;. ¿Sabes lo que soy?' WHERE `entry` = 9362;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un orco...$B$B<Lantresor se burla.>$B$BSoy un maestro del acero de origen medio orco.' WHERE `entry` = 9363;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Draenei... Por esa razón, no soy ninguno de los dos. Ni orco, ni draenei. Formo parte de una raza poco común. La mayoría hemos sido asesinados hace décadas... Sobrevivo gracias a mi fuerza de voluntad.' WHERE `entry` = 9364;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muérdete la lengua, $R. Yo he visto cosas de las que tú solo has oído hablar en leyendas. He matado a hombres, mujeres y niños sin remordimientos. He servido a las órdenes de dos Jefes de Guerra de la Horda. Y ahora, dirijo a los Puño de Roca.' WHERE `entry` = 9365;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Y, ¿qué quiere tu pueblo de Lantresor, $R?' WHERE `entry` = 9366;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Lantresor ríe ruidosamente.>$B$B¿Sabes dónde te has metido, $Gestúpido:estúpida;? Este es territorio de los ogros. Antes de los orcos y antes de los draenei estaban los ogros. Y sabiendo eso, quieres empezar una guerra que perderás con toda seguridad... Puño de Roca te aplastará. Y si Puño de Roca no te aplasta, entonces será Mazo de Guerra.$B$BPero creo que tus líderes ya lo saben...' WHERE `entry` = 9367;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Lantresor te mira como si viese a través de ti.>$B$BPuede que haya otra solución... Aceptaré lo que pide tu líder, pero solo si haces exactamente lo que diga.$B$BEste trato podría beneficiarnos mucho a los dos.' WHERE `entry` = 9368;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Participarás en una serie de acontecimientos que harán que los orcos Mata\'penas de la Fortaleza Mata\'penas piensen que los ogros de Mazo de Guerra han atacado su base. Después, tendrá lugar otra serie de acontecimientos que harán que los ogros de Mazo de Guerra piensen que los orcos de Mata\'penas están atacando su base.$B$BSi lo haces, resolverás nuestros problemas y los tuyos.$B$BPuño de Roca recuperará su territorio y Mazo de Guerra, el Consejo de la Sombra y Puño de Roca dejarán de atacar a tu gente.' WHERE `entry` = 9369;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ningún bicho superdesarrollado me echará de la marisma de Zangar! Yo les enseñaré de que está hecha la Liga de Expedicionarios...' WHERE `entry` = 9370;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los inscritos tendrán que hablar con el celador Tororroc antes de publicar cualquier tarea.$B$BLos que publiquen anuncios no aprobados serán perseguidos.$B$BNo publiquéis facturas.' WHERE `entry` = 9373;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se exige que los participantes hablen con el celador Moi\'bff Jill antes de anunciar ninguna tarea.$B$BAquellos a los que se halle haciendo anuncios sin aprobar serán procesados.$B$BNo colguéis facturas.' WHERE `entry` = 9374;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si buscas trabajo, mira el tablón de anuncios, $R.' WHERE `entry` = 9375;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tienen respeto por los muertos, $R. Hay que dar una lección a los ogros.' WHERE `entry` = 9376;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debemos buscar supervivientes. Si hay alguien que todavía viva, al menos debemos intentar traerlo de vuelta...' WHERE `entry` = 9377;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién eres? ¿Qué quieres?',  `text1_0_loc7` = '¿Por qué nos has hecho eso?', `text2_0_loc7` = '¡Hay CUATRO luces!', `text3_0_loc7` = '¡Mis teropolillas sañosas me vengarán!', `text4_0_loc7` = '¿Dónde están todos?', `text5_0_loc7` = '¡Los invitados a la cena deberían ser más respetuosos!', `text6_0_loc7` = '¡Oh, qué profanación de este lugar sagrado!', `text7_0_loc7` = 'Unce mi frente con un bálsamo de sanación. ¿Te gusta mi corona de espinas?' WHERE `entry` = 9383;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo adiestrarte en minería. ¿Te interesa?', `text0_1_loc7` = 'Puedo adiestrarte en minería. ¿Te interesa?' WHERE `entry` = 9384;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te puedo instruir en la herboristería. ¿Te interesa?', `text0_1_loc7` = 'Te puedo instruir en la herboristería. ¿Te interesa?' WHERE `entry` = 9385;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Hay que recuperar esas cajas de suministros!' WHERE `entry` = 9389;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ten cuidado al ir al matorral, $C. Hay un aura perjudicial en ese sitio. Debe de ser el residuo de lo que fuera que destruyera a mis compañeros druidas.' WHERE `entry` = 9391;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este bosque ser un lugar espeluznante. Está lleno de más mal Juju que la Alianza, así que ten cuidado.' WHERE `entry` = 9392;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Horda no es el único peligro de este bosque. No te dejes engañar por su aparente tranquilidad.' WHERE `entry` = 9393;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Eres muy fuerte, $R, pero ¿crees que tienes lo que hay que tener para ser un campeón de Mazo de Guerra?' WHERE `entry` = 9394;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aparta de mi camino, $R. ¡No ves que estamos en medio de una batalla!' WHERE `entry` = 9395;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí yace el cadáver de un Tábido. Curiosamente, la causa de muerte no parece ser una polilla mutante.$B$BQuizás tenga algo que ver con el extraño objeto que se encuentra al lado de su mano extendida.' WHERE `entry` = 9401;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muy ocupado... ¡Muy ocupado! ¿Qué?' WHERE `entry` = 9405;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Ahora que me han pegado en todas partes, qué quieres?' WHERE `entry` = 9406;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Averiguaremos lo que ha causado esta masacre aunque sea lo último que hagamos!' WHERE `entry` = 9407;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$GBienvenido:Bienvenida; a las Tabernas del Tiempo!' WHERE `entry` = 9414;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Taberna del Fin del Mundo no tiene nada que ofrecer a los de mi especie. Sin embargo, creo que atrae el tipo de clientela interesada en lo que tengo en venta.$B$BMe especializo en productos de ingeniería exóticos adquiridos en lugares lejanos. De vez en cuando hasta tengo un par de cosas raras en venta.$B$B¿Qué podría interesarte?' WHERE `entry` = 9417;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué ves cuando miras al oeste, mortal?' WHERE `entry` = 9419;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sabes lo que veo yo? Un preludio de la invasión.$B$BSí, el maestro hizo bien en sellar los portales, pero luego se le fue un poco... ¿Cómo podría decirlo...? Digamos que perdió la concentración. Eso. La concentración.$B$BLa Legión supo bien aprovechar esa ventaja. Ahora han conseguido infiltrarse, y cada día que pasa hay más.' WHERE `entry` = 9420;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Legión Ardiente. Demonios. Los veo tan claramente como tú te verías la mano delante de la cara. Bueno, los veía claramente.$B$BLa vuelta de la Legión, ha traído nuevos estratagemas. En circunstancias normales, puedo sentir la presencia de un demonio a miles de leguas.$B$B<Altruis tuerce el gesto.>' WHERE `entry` = 9421;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me evitan. No puedo ver a través de sus defensas. Han colocado guardas para dificultar la detección.$B$B<Altruis señala al oeste.>$B$BNos temen porque saben que no tememos nada. El coste de un poder así es grande; los sacrificios, muchos.' WHERE `entry` = 9422;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Altruis se vuelve hacia ti y sonríe.>$B$BYo no.$B$B<Altruis señala a su corcel, Nethrandamus.>$B$BDebemos detenerlos antes de que esos campamentos Forja terminen lo que quiera que hayan empezado a construir.' WHERE `entry` = 9423;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Así es como se llaman esas estructuras que ensucian el paisaje. La Legión ha empleado a los Mo\'arg y Gan\'arg para construir armas de destrucción masiva en esos campamentos Forja móviles. ¡Que no descubramos lo que intentan construir aquí en Nagrand!$B$BTú serás mis ojos. Tus armas serán mi furia.' WHERE `entry` = 9424;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esto es lo que he sacado de la información que conseguiste con la inspección.$B$BHay dos campamentos Forja principales: Campamento Forja: Odio, al oeste de aquí y Campamento Forja: Miedo, al suroeste. Cada campamento tiene un teletransportador de la Legión, una forja y varias torres de amparo de hechizos. Además los campamentos están custodiados por guardias de cólera que manejan cañones viles. Para terminar, cada campamento tiene un señor del foso como sobrestante.$B$BEsto no debería suponer ningún problema...$B$B< Altruis ríe.>' WHERE `entry` = 9427;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Creo que sacaré algún dinerillo extra mientras los demás deciden si es buena idea ir a Oshu\'gun.$B$B¡El tiempo es oro!' WHERE `entry` = 9429;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Las Ruinas de las Agujas Gemelas no son seguras todavía! Las órdenes son entregar confalones solo cuando las dos balizas estén encendidas y la zona sea segura. ¡Toma el control de las balizas, vamos!' WHERE `entry` = 9431;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las balizas están encendidas, ¡es el momento de atacar! ¡Toma uno de esos estandartes y plántalo en el cementerio por la Alianza!' WHERE `entry` = 9432;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Sal\'salabim te gruñe y se aleja para ingerir su brebaje espumoso.>' WHERE `entry` = 9435;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿¡QUÉ QUERER VOSOTROS!?' WHERE `entry` = 9440;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Fshhhhhhsssfhhhhh?' WHERE `entry` = 9441;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Floon hace una serie de ruidos y chasquidos extraños.>' WHERE `entry` = 9442;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Hablas en serio? ¡Ya me ha matado! ¿Qué más podría querer?' WHERE `entry` = 9443;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mi trabajo consiste en ocuparme de estas pobres almas. Acepto la ayuda de cualquier corazón bondadoso, ya sea Aldor o Arúspice.$B$BNecesitamos a tanta gente que no le diría que no ni a un ogro si supiera cómo hacer un torniquete. La política de la ciudad me importa bien poco.' WHERE `entry` = 9451;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No puedo creerlo. ¡Este etéreo no tiene ni un solo ojo de basilisco!$B$B¡Y su líder tiene la desfachatez de llamarse \'el Príncipe Contrabandista\'!' WHERE `entry` = 9452;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida;, $N. Somos los Aldor, sacerdotes de la luz y siervos de Sha\'tar.$B$BPuedes quedarte con nosotros todo el tiempo que quieras.' WHERE `entry` = 9457;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Sha\'tar significa Nacidos de la luz\'. Es el nombre que dimos a los naaru que recuperaron Shattrath de los ejércitos de Illidan.' WHERE `entry` = 9458;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Shattrath es algo más que un cuartel general para la guerra que libramos contra Illidan y la Legión Ardiente. Es un santuario para los que quieren refugiarse de la violencia que nos rodea.$B$BSi no aceptáramos a los refugiados, seríamos unos hipócritas.$B$BSi preguntas por los Arúspices, te diré que, aunque no me gusten ellos ni su líder, no tenemos la autoridad para cuestionar la sabiduría de los naaru.$B$BSon tiempos difíciles, $N. Necesitamos a todos los aliados que podamos encontrar.' WHERE `entry` = 9459;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; al Bastión Allerian, $C.' WHERE `entry` = 9471;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuidado, $C. El bosque está lleno de peligros ocultos y de enemigos.' WHERE `entry` = 9472;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esto es sencillamente horrible.$B$BNo me importan nada nuestras propias incomodidades aquí, ¡pero debemos hacer algo para aliviar el dolor de los muertos de El Vertedero de Huesos!' WHERE `entry` = 9477;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si esperas algo parecido a una bienvenida oficial, siento decepcionarte, $C; aquí no seguimos demasiado las tradiciones. No nos gustan los artificios.' WHERE `entry` = 9481;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una nota destaca de los demás pedazos de pergamino que están clavados en este cartel de \"Se busca\". Un llamamiento para matar a los Atizahuesos de El Vertedero de Huesos.' WHERE `entry` = 9482;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Atizahuesos... ¡Los detesto a todos!$B$BSi no hubiera perdido mi ojo ni me salieran a veces esos hechizos tan débiles, estaría ahí fuera cargándomelos a todos.' WHERE `entry` = 9485;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He estado observando cómo trabajan los Aldor en las murallas. Van a reconstruir toda la ciudad, ¿sabes?$B$BTrabajan mucho más de prisa que el viejo supervisor Olento en mi Villa del Lago natal. Constantemente perdía las herramientas en el lago, o los gnolls le robaban los suministros.$B$BDebería pedir a los Aldor que le envíen algo de ayuda. Ellos repararían el puente en menos que canta un gallo.' WHERE `entry` = 9486;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hay un gran bosque ahí fuera, $C. Deja que el chamán juegue con los espíritus y el guerrero con las espadas. En Terokkar haré fortuna.' WHERE `entry` = 9487;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Malukaz no para de hablar de la importancia histórica de este bosque, pero yo no puedo permitirme el lujo de preocuparme por la historia hasta que no se arregle el presente.$B$BNo digo que su trabajo aquí no sea importante, pero alguien tiene que preocuparse de que funcionen las defensas y la logística del fuerte.' WHERE `entry` = 9488;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hasta que viajé por Nagrand y llegué al bosque, pensaba que una gran parte de nuestra cultura original se había perdido. Me alegra comprobar que estaba equivocado.$B$B¡Todavía queda mucho por hacer aquí!' WHERE `entry` = 9489;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La apertura del Portal Oscuro, trajo noticias de la suerte que corrió mi pueblo. En cierto modo, mi exilio evitó que compartiese su caída, pero ver a los Errantes darlo todo por Kael\'thas...$B$BNunca imaginé que los que fueron mis hermanos podrían hacer algo así.$B$BLa vuelta a casa con la que tanto soñé nunca tendrá lugar. Este bosque es el único hogar que me queda.' WHERE `entry` = 9490;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Cuado llegué aquí, esperaba estar a la altura de la reputación de la ciudad.$B$B<Ros\'eleth mira a su alrededor.>$B$BLa mayoría de los jóvenes superan la fase de \"quiero ser un Errante\" en cuanto alguien los acepta como aprendices. Entonces pasan a la fase \"yo quiero ser magister\".$B$BPara serte sincera, yo todavía estoy superando lo de \"quiero ser Errante\". Nadie quiere ser costurera de mayor, pero alguien tiene que hacer lo que se ponen los Errantes para no andar desnudos. Eso es lo que solía decirme mi madre.' WHERE `entry` = 9492;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has visto cómo me guiña el ojo Ros\'eleth cada vez que voy a recoger ropa o algún sombrero? Cuando las cosas se calmen por aquí, me la voy a llevar de paseo al bosque.$B$BAhora sí que echo de menos todas esas tabernas de Ventormenta.' WHERE `entry` = 9493;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, ¿qué quieres? ¿No ves que estoy ocupado? ¡El Consejo de la Sombra no me paga por hora de trabajo!', `text0_1_loc7` = 'Sí, ¿qué quieres? ¿No ves que estoy ocupada? ¡El Consejo de la Sombra no me paga por hora de trabajo!' WHERE `entry` = 9497;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué parece que estoy haciendo? Estoy arreglando las cosas para que podamos instalarnos. Y yo solo, además.$B$BCon una base de operaciones en el bosque será más fácil seguir la pista a lo que traman los druidas de Shattrath, Allerian, Rompepedras y Cenarion. Nuestra base en Auchindoun es demasiado poco práctica para eso.$B$BEspera un momento, ¿te conozco?', `text0_1_loc7` = '¿Qué parece que estoy haciendo? Estoy arreglando las cosas para que podamos instalarnos. Y yo sola, además.$B$BCon una base de operaciones en el bosque será más fácil seguir la pista a lo que traman los druidas de Shattrath, Allerian, Rompepedras y Cenarion. Nuestra base en Auchindoun es demasiado poco práctica para eso.$B$BEspera un momento, ¿te conozco?' WHERE `entry` = 9498;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí? Rayos. Ahora voy a tener que empezar desde el principio.', `text0_1_loc7` = '¿Sí? Rayos. Ahora voy a tener que empezar desde el principio.' WHERE `entry` = 9502;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Entrenando! Pronto seré lo suficientemente bueno como para unirme a los otros invocadores y entrar en el Laberignore into de las Sombras de Auchindoun.$B$B¡Espero poder invocar algo grande! ¡Quizás algo tan grande como Murmur, aunque no perdería el control sobre él!', `text0_1_loc7` = '¡Entrenando! Pronto seré lo suficientemente buena como para unirme a los otros invocadores y entrar en el Laberignore into de las Sombras de Auchindoun.$B$B¡Espero poder invocar algo grande! ¡Quizás algo tan grande como Murmur, aunque no perdería el control sobre él!' WHERE `entry` = 9503;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Y qué es exactamente lo que puedo hacer por ti? ¿No te das cuenta de que estoy muy ocupado discutiendo cuestiones importantes con nuestro ilustre líder?$B$BDate prisa en hablar o haré que te den una paliza, gusano.', `text0_1_loc7` = '¿Y qué es exactamente lo que puedo hacer por ti? ¿No te das cuenta de que estoy muy ocupada discutiendo cuestiones importantes con nuestro ilustre líder?$B$BDate prisa en hablar o haré que te den una paliza, gusano.' WHERE `entry` = 9505;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bueno, yo solo estaba informando a nuestro líder sobre los avances y sobre cómo todo sale según lo previsto. Solo tardaremos unas semanas más en tenerlo todo arreglado.$B$BPuede que nos convenga que el Consejo de la Sombra contrate a orcos para el pueblo. Así alegaremos derecho ancestral y podremos trabajar ocultos.$B$BUn momento. ¿Te conozco? ¿Cómo te llamas?', `text0_1_loc7` = 'Bueno, yo solo estaba informando a nuestro líder sobre los avances y sobre como todo sale según lo previsto. Solo tardaremos unas semanas más en tenerlo todo arreglado.$B$BPuede que nos convenga que el Consejo de la Sombra contrate a orcos para el pueblo. Así alegaremos derecho ancestral y podremos trabajar en la sombra.$B$BUn momento. ¿Te conozco? ¿Cómo te llamas?' WHERE `entry` = 9506;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, joven $R.' WHERE `entry` = 9507;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí hay más artefactos incendiarios, $N. Ten cuidado con ellos. Si no los usas adecuadamente, pueden provocar una catástrofe.' WHERE `entry` = 9515;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Voy a averiguar lo que ese siniestro grupo está haciendo el Poblado Grangol\'var!' WHERE `entry` = 9517;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Hay que averiguar lo que ese siniestro grupo está haciendo en el Poblado Grangol\'var!' WHERE `entry` = 9520;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tenemos una misión que cumplir, $Gamigo:amiga;: no dejarnos sorprender por nadie.$B$B¡Y eso significa que hay que actuar antes que ellos!' WHERE `entry` = 9521;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'He tenido que hacer algo mal en algún sitio para que me destinaran aquí.' WHERE `entry` = 9522;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡¿Has oído que han perdido el control total de Murmullo en el Laberignore into de las Sombras?!', `text0_1_loc7` = '¡¿Has oído que han perdido el control total de Murmullo en el Laberignore into de las Sombras?!', `text1_0_loc7` = 'No nos pagan lo suficiente por hacer esto. Pero este pueblo ruinoso sin duda es mejor que merodear dentro de Auchindoun.', `text1_1_loc7` = 'No nos pagan lo suficiente por hacer esto. Pero este pueblo ruinoso sin duda es mejor que merodear dentro de Auchindoun.', `text2_0_loc7` = 'Me enrolé porque dijeron que me pagarían la academia. ¡Mi tío dice que podré ir la próxima temporada!', `text2_1_loc7` = 'Me enrolé porque dijeron que me pagarían la academia. ¡Mi tío dice que podré ir la próxima temporada!', `text3_0_loc7` = '¡Eh, hola! Tú debes de ser $Gel nuevo:la nueva; recluta. Encantado de conocerte.$B$B¿Algún mensaje del Laberignore into de las Sombras de Auchindoun sobre si ya hemos recuperado el control de Murmullo?', `text3_1_loc7` = '¡Eh, hola! Tú debes de ser $Gel nuevo:la nueva; recluta. Encantado de conocerte.$B$B¿Algún mensaje del Laberignore into de las Sombras de Auchindoun sobre si ya hemos recuperado el control de Murmullo?', `text4_0_loc7` = 'Si te interesa, algunos iremos a tomar algo a Shattrath después.', `text4_1_loc7` = 'Si te interesa, algunos iremos a tomar algo a Shattrath después.', `text5_0_loc7` = '¿Te apetece participar con nosotros en la liga de la pelota de cuero? Jugamos de lunes a miércoles casi siempre.$B$BVente si quieres después de este turno. Iremos a dar unas patadas.', `text5_1_loc7` = '¿Te apetece participar con nosotros en la liga de la pelota de cuero? Jugamos de lunes a miércoles casi siempre.$B$BVente si quieres después de este turno. Iremos a dar unas patadas.', `text6_0_loc7` = 'Espero que este lugar sirva como base de la vanguardia de las operaciones del Consejo de la Sombra. Sé que suena raro, pero echo de menos el Laberignore into de las Sombras.', `text6_1_loc7` = 'Espero que este lugar sirva como base de la vanguardia de las operaciones del Consejo de la Sombra. Sé que suena raro, pero echo de menos el Laberignore into de las Sombras.', `text7_0_loc7` = '<Se acerca un poco.>$B$BEntre tú y yo. Estoy pensando en escaquearme una noche de estas, ¿me entiendes?', `text7_1_loc7` = '<Se acerca un poco.>$B$BEntre tú y yo. Estoy pensando en escaquearme una noche de estas, ¿me entiendes?' WHERE `entry` = 9523;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién te crees para aparecer por aquí y hablarme sin más? Soy el líder del Consejo de la Sombra en este pueblo. ¡Fuera de mi vista!$B$BSi te vuelvo a ver, seré la última persona que veas, ¿entendido?$B$BQuizás te gustaría que te enterraran en el laberignore into de Auchindoun, o te sacrificaran a Murmur.' WHERE `entry` = 9524;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontrarás a nuestro instructor alquimista en El Arca de las Luces. Ve con la Luz.', `text0_1_loc7` = 'Encontrarás a nuestro instructor alquimista en El Arca de las Luces. Ve con la Luz.' WHERE `entry` = 9527;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aunque la acumulación de riqueza ya no es el objetivo principal de nuestras vidas, somos conscientes de que el dinero es lo que hace que se mueva el mundo. Encontrarás nuestra casa de subastas en el Trono de los Naaru.', `text0_1_loc7` = 'Aunque la acumulación de riqueza ya no es el objetivo principal de nuestras vidas, somos conscientes de que el dinero es lo que hace que se mueva el mundo. Encontrarás nuestra casa de subastas en el Trono de los Naaru.' WHERE `entry` = 9528;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El banco se encuentra en la cabina central de El Exodar. Ve con la Luz.', `text0_1_loc7` = 'El banco se encuentra en la cabina central de El Exodar. Ve con la Luz.' WHERE `entry` = 9529;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Stephanos es nuestro maestro de grifos. Vive a las afueras de El Exodar, cerca de la entrada principal. Que la Luz te acompañe en tus viajes.', `text0_1_loc7` = 'Stephanos es nuestro maestro de grifos. Vive a las afueras de El Exodar, cerca de la entrada principal. Que la Luz te acompañe en tus viajes.' WHERE `entry` = 9530;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestros maestros de batalla están en el Arca de las Luces. También encontrarás a \'Miglik Borrón\' junto al cuidador Breel; dice ser el representante del círculo de lucha de este mundo.', `text0_1_loc7` = 'Nuestros maestros de batalla están en el Arca de las Luces. También encontrarás a \"Miglik Borrón\" junto al cuidador Breel; dice ser el representante del círculo de lucha de este mundo.' WHERE `entry` = 9531;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Miall es nuestra herrera en jefe en El Exodar. La encontrarás en la parte baja de la Grada de los Mercaderes.', `text0_1_loc7` = 'Miall es nuestra herrera en jefe en El Exodar. La encontrarás en la parte baja de la Grada de los Mercaderes.' WHERE `entry` = 9532;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué instructor buscas?', `text0_1_loc7` = '¿Qué instructor buscas?' WHERE `entry` = 9533;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Druida? Ah... Shalannius. Buscas a Shalannius. Encontrarás a ese elfo de la noche fuera de El Exodar, al lado del atracadero de Valaar.', `text0_1_loc7` = '¿Druida? Ah... Shalannius. Buscas a Shalannius. Encontrarás a ese elfo de la noche fuera de El Exodar, al lado del atracadero de Valaar.' WHERE `entry` = 9534;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestros encantadores están en la Sala de Cristal, enfrente de los joyeros. Qué la luz de acompañe.', `text0_1_loc7` = 'Nuestros encantadores están en la Sala de Cristal, enfrente de los joyeros. Qué la luz de acompañe.' WHERE `entry` = 9535;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Así que quieres ayudarnos a reparar el gran Exodar? He oído que pasarán años antes de que El Exodar vuelva a estar operativo. Tienes dos horas. Es broma. Nuestros ingenieros están en las partes bajas de la Grada de los Mercaderes. Ve con la Luz.', `text0_1_loc7` = '¿Así que quieres ayudarnos a reparar el gran Exodar? He oído que pasarán años antes de que El Exodar vuelva a estar operativo. Tienes dos horas. Es broma. Nuestros ingenieros están en las partes bajas de la Grada de los Mercaderes. Ve con la Luz.' WHERE `entry` = 9536;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Por la Luz! Es una bendición encontrar a alguien que se preocupe por el bienestar de los demás. En un momento como este, necesitamos todas las manos posibles. Nus te puede adiestrar en primeros auxilios. Está cerca de la cueva de cristales de la Sala de Cristal.', `text0_1_loc7` = '¡Por la Luz! Es una bendición encontrar a alguien que se preocupe por el bienestar de los demás. En un momento como este, necesitamos todas las manos posibles. Nus te puede adiestrar en primeros auxilios. Está cerca de la cueva de cristales de la Sala de Cristal.' WHERE `entry` = 9537;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontrarás a Erett el instructor de pesca en la Sala de Cristal, cerca de las aguas bendecidas por la Luz.', `text0_1_loc7` = 'Encontrarás a Erett el instructor de pesca en la Sala de Cristal, cerca de las aguas bendecidas por la Luz.' WHERE `entry` = 9538;
