UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestro herborista, Cemmorhan, está cerca del muro trasero de El Arca de las Luces.', `text0_1_loc7` = 'Nuestro herborista, Cemmorhan, está cerca del muro trasero de El Arca de las Luces.' WHERE `entry` = 9543;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Buscas cazadores? Los encontrarás en el segundo nivel de la Grada de los Mercaderes, en la Sala de los Cazadores.', `text0_1_loc7` = '¿Buscas cazadores? Los encontrarás en el segundo nivel de la Grada de los Mercaderes, en la Sala de los Cazadores.' WHERE `entry` = 9544;
