UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Farii adiestra a los que quieren aprender joyería. Está en la Sala de Cristal, cerca de esa caverna.', `text0_1_loc7` = 'Farii adiestra a los que quieren aprender joyería. Está en la Sala de Cristal, cerca de esa caverna.' WHERE `entry` = 9547;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los peleteros de El Exodar están en la Grada de los Mercaderes. Que la Luz te bendiga.', `text0_1_loc7` = 'Los peleteros de El Exodar están en la Grada de los Mercaderes. Que la Luz te bendiga.' WHERE `entry` = 9549;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestros magos están en el Arca de las Luces. Ve con la Luz.', `text0_1_loc7` = 'Nuestros magos están en el Arca de las Luces. Ve con la Luz.' WHERE `entry` = 9550;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Recibe la bendición de los naaru. ¿En qué puedo ayudarte?', `text0_1_loc7` = 'Recibe la bendición de los naaru. ¿En qué puedo ayudarte?' WHERE `entry` = 9551;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontrarás a nuestros instructores de minería en la parte trasera de la Grada de los Mercaderes.', `text0_1_loc7` = 'Encontrarás a nuestros instructores de minería en la parte trasera de la Grada de los Mercaderes.' WHERE `entry` = 9552;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los instructores de paladines de El Exodar están en la luminosa Capilla de la Luz, en el Trono de los Naaru.', `text0_1_loc7` = 'Los instructores de paladines de El Exodar están en la luminosa Capilla de la Luz, en el Trono de los Naaru.' WHERE `entry` = 9553;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestro instructor de sacerdotes está en la sala de la Orden de la Luz, a la entrada del Trono de los Naaru. Que la luz te acompañe.', `text0_1_loc7` = 'Nuestro instructor de sacerdotes está en la sala de la Orden de la Luz, a la entrada del Trono de los Naaru. Que la luz te acompañe.' WHERE `entry` = 9554;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué instructor profesional estás buscando?', `text0_1_loc7` = '¿Qué instructor profesional estás buscando?' WHERE `entry` = 9555;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay varios instructores de chamanes en la Sala de Cristal. Puede que quieras ver al clarividente Nobundo. Muéstrate $Gcompasivo:compasiva; con los Tábidos. Que la Luz te acompañe.', `text0_1_loc7` = 'Hay varios instructores de chamanes en la Sala de Cristal. Puede que quieras ver al clarividente Nobundo. Muéstrate $Gcompasivo:compasiva; con los Tábidos. Que la Luz te acompañe.' WHERE `entry` = 9556;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los instructores de desuello de El Exodar están en la Grada de los Mercaderes, junto a los peleteros. Que la Luz te bendiga.', `text0_1_loc7` = 'Los instructores de desuello de El Exodar están en la Grada de los Mercaderes, junto a los peleteros. Que la Luz te bendiga.' WHERE `entry` = 9557;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes encontrar a Arthaid, nuestro maestro de establos, fuera del Centro de Cuidadores. Se encuentra en la planta superior de la cabina central, cerca de la entrada principal a El Exodar.', `text0_1_loc7` = 'Puedes encontrar a Arthaid, nuestro maestro de establos, fuera del Centro de Cuidadores. Se encuentra en la planta superior de la cabina central, cerca de la entrada principal a El Exodar.' WHERE `entry` = 9558;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace mucho tiempo, llevábamos monos de una sola pieza. Gracias a nuestros modernos sastres, ya no tenemos que vestir algo tan poco práctico. Puedes encontrar a esos diseñadores de vanguardia en la Grada de los Mercaderes.', `text0_1_loc7` = 'Hace mucho tiempo, llevábamos monos de una sola pieza. Gracias a nuestros modernos sastres, ya no tenemos que vestir algo tan poco práctico. Puedes encontrar a esos diseñadores de vanguardia en la Grada de los Mercaderes.' WHERE `entry` = 9559;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quieres ser un gran guerrero? La guerra no te hace grande. Aun así, encontrarás nuestros instructores de guerreros en el Bancal del Guerrero, por encima del resto de la Grada de los Mercaderes.', `text0_1_loc7` = '¿Quieres ser un gran guerrero? La guerra no te hace grande. Aun así, encontrarás nuestros instructores de guerreros en el Bancal del Guerrero, por encima del resto de la Grada de los Mercaderes.' WHERE `entry` = 9562;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a al Banco Real de Cambio. ¿Qué deseas?', `text0_1_loc7` = '$GBienvenido:Bienvenida; a al Banco Real de Cambio. ¿Qué deseas?', `text1_0_loc7` = 'Saludos. ¿En qué puede ayudarte el Banco del Intercambio Real?', `text1_1_loc7` = 'Saludos. ¿En qué puede ayudarte el Banco del Intercambio Real?' WHERE `entry` = 9563;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias por escoger el Banco de Lunargenta. ¿En qué puedo ayudarte?', `text0_1_loc7` = 'Gracias por escoger el Banco de Lunargenta. ¿En qué puedo ayudarte?' WHERE `entry` = 9564;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quieres mejorar tu habilidad con las armas? El maestro de armas está en nivel más elevado de la Grada de los Mercaderes, en el Bancal del Guerrero.', `text0_1_loc7` = '¿Quieres mejorar tu habilidad con las armas? El maestro de armas está en nivel más elevado de la Grada de los Mercaderes, en el Bancal del Guerrero.' WHERE `entry` = 9565;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, $tciudadano:ciudadana;, ¿qué asunto importante puede precisar mi atención?' WHERE `entry` = 9566;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola. ¿Puedes ayudarme a salir de aquí?' WHERE `entry` = 9568;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí? ¿Qué quieres?' WHERE `entry` = 9574;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Supongo que no traes noticias de las minas, ¿verdad?' WHERE `entry` = 9575;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No hay nada que ver aquí. Circula.', `text0_1_loc7` = 'No hay nada que ver aquí. Circula.', `text1_0_loc7` = 'Esto no te concierne. Sigue tu camino.', `text1_1_loc7` = 'Esto no te concierne. Sigue tu camino.' WHERE `entry` = 9577;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias, amigos; os debo mi libertad. ¿Dónde está Taretha? Tenía la esperanza de volver a verla y darle las gracias antes de partir hacia las montañas.' WHERE `entry` = 9578;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué quieres decir? ¿Taretha está en peligro?' WHERE `entry` = 9579;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No lo haré. No puedo dejar a Taretha en peligro mientras huyo hacia las montañas. No soy un cobarde. ¿Dónde está?' WHERE `entry` = 9580;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Por favor, no me digas nada. No quiero tener más problemas con ellos.', `text0_1_loc7` = 'Por favor, no me digas nada. No quiero tener más problemas con ellos.' WHERE `entry` = 9587;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si no has venido a ayudar a la causa, vete de aquí.' WHERE `entry` = 9595;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Con tu ayuda, ¡quizás podamos ganar!' WHERE `entry` = 9596;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Molino Tarren está más allá de estos árboles. La conmoción de Durnholde debería darnos algo de tiempo para encontrar a Taretha antes de que todo el país esté inundado de soldados humanos. Echemos un vistazo a ese granero que está cerca y luego dirijámonos al pueblo.' WHERE `entry` = 9597;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes comprarme algo utilizando muestras Halaa de batalla e investigación.' WHERE `entry` = 9598;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo instruirte en el oficio del desollador. ¿Te interesa?', `text0_1_loc7` = 'Puedo instruirte en el oficio del desollador. ¿Te interesa?' WHERE `entry` = 9599;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Te refieres a la Posada de la Ciudad de Lunargenta, o a la Taberna del Descanso del Caminante?', `text0_1_loc7` = '¿Te refieres a la Posada de la Ciudad de Lunargenta, o a la Taberna del Descanso del Caminante?' WHERE `entry` = 9602;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Taberna del Descanso del Caminante se encuentra entre el Camino de los Ancestros y el Bazar. Puedes llegar hasta ella desde cualquier dirección.', `text0_1_loc7` = 'La Taberna del Descanso del Caminante se encuentra entre el Camino de los Ancestros y el Bazar. Puedes llegar hasta ella desde cualquier dirección.' WHERE `entry` = 9603;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Gracias por ayudar a Thrall a escapar, amigos. Ahora, espero que también podáis ayudarme a mí. Me desperté esta mañana atrapada en esta prisión mágica. Un extraño zahorí me dijo que me quedaría un tiempo aquí.' WHERE `entry` = 9610;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Sí, amigos. Ese hombre no era un zahorí de Dalaran. Sus ropas y acento no me resultaban familiares. Todo lo que decía es el tiempo esto, el tiempo lo otro... Tendréis que encontrar al zahorí y convencerle para que me suelte.' WHERE `entry` = 9613;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Como representante del Bajo Arrabal, estoy aquí para exponer nuestro caso, por el que Auchindoun debe ser evacuada de su infestación del Consejo de la Sombra. Esto es, ¡si queremos albergar esperanzas de tener un futuro y evitar la posibilidad de un cataclismo mayor que la creación de El Vertedero de Huesos!$B$BTodas nuestras gentes han sufrido ya demasiado. ¡Estoy aquí para encargarme de que ese sufrimiento se detenga!' WHERE `entry` = 9615;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Siéntate, $Ghermano:hermana;. Los devastadores de la Marisma de Zangar pueden acabar con cualquier $Ghombre:mujer;.' WHERE `entry` = 9617;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Si quieres descansar aquí, solo tienes que preguntar!' WHERE `entry` = 9618;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:bienvenida; a Garadar, $Gforastero:forastera;. ¿Buscas un lugar donde descansar?' WHERE `entry` = 9620;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah... ¿Otro chamán que busca la iluminación? ¿Acaso los espíritus no hablan claro? ¿Quieres que te ayude a ver lo que te espera en tu camino?' WHERE `entry` = 9621;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En la Taberna del Descanso del Caminante puedes encontrar a Sylann, uno de los mejores cocineros de la ciudad. Allí también puedes comprarle avituallamientos de cocina a su asistente.', `text0_1_loc7` = 'En la Taberna del Descanso del Caminante puedes encontrar a Sylann, uno de los mejores cocineros de la ciudad. Allí también puedes comprarle avituallamientos de cocina a su asistente.' WHERE `entry` = 9624;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡El Gran Padre Invierno deja juguetes para todos bajo el árbol de Forjaz durante el festín del Festival de Invierno!', `text0_1_loc7` = '¡El Gran Padre Invierno deja juguetes para todos bajo el árbol de Forjaz durante el festín del Festival de Invierno!' WHERE `entry` = 9626;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi hermana Silanna es muy ingenua, no entiende el peligro creciente que habita en estos bosques. Pero de todas formas, en cuanto cargue el carro, nos vamos a la ciudad. $B$BParece ser que tengo más bolsas de las que necesito. Quizás a ti te vengan bien.' WHERE `entry` = 9630;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tengo mucho tiempo para hablar, $R. A menos que quieras ayudarme con un problemilla que tengo.' WHERE `entry` = 9631;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te agradezco muchísimo tu ayuda con las minas. Ahora, solo hay que convencer a los mineros para que vuelvan al trabajo.' WHERE `entry` = 9632;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Isfar suspira.>$B$BUn par de casas en los árboles no hacen un hogar, pero ha sido una gran mejora en las Salas Sethekk.$B$BAunque en Shattrath estaba perfectamente bien, no soy capaz de regresar a mi verdadero hogar en Skettis sin mi hermana. Está atrapada en las Salas Sethekk. La tiene prisionera nuestro hermano mayor.' WHERE `entry` = 9634;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Círculo de los Retos era antaño un lugar de honor y gloria. Pero entonces llegó el Cártel Bonvapor con sus carros llenos de oro y sus plumas más afiladas que hachas de orco...' WHERE `entry` = 9637;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Sethekk se creen los verdaderos y más fieles seguidores del maestro de todos los arakkoa. No me preguntes por el maestro, $R, porque sus misterios son solo para los alados.$B$BLos Sethekk interpretaron la destrucción de Auchindoun como la llegada de nuestro maestro a este planeta. Partieron hacia las ruinas llevándose las reliquias de nuestro gran héroe, Terokk.$B$BSiguen buscando al maestro en las ruinas del templo, pero he conseguido ver la verdad que se esconde tras sus mentiras. Ya no soy uno de ellos.' WHERE `entry` = 9638;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Rey \'Garra Ikiss\' es uno de los líderes de los Sethekk, junto con mi hermano, Tejeoscuro Syth.$B$BFueron ellos quienes guiaron el éxodo de los Sethekk desde Skettis hasta las Ruinas de Auchindoun. Al principio, Ikiss era un líder carismático, pero la locura se ha apoderado de él.$B$B¡Ahora se proclama a sí mismo como la reencarnación de Terokk! Realmente espera que los arakkoa se arrodillen ante él como su verdadero gobernante. ¿Te puedes creer su arrogancia?' WHERE `entry` = 9640;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es el héroe más glorioso entre los arakkoa.$B$BDicen que, en la antigüedad, construyó Skettis con sus propias garras. Era nuestro mayor campeón y defensor, pero nos dejó cuando dejamos de venerar a Rukhmar.$B$BEmpezamos a adorar a un nuevo maestro, uno que nos prometió increíble poder y estatus en el cosmos.$B$BAlgunos dicen que Terokk y Rukhmar son uno y que nos abandonó cuando nosotros le abandonamos a él.' WHERE `entry` = 9641;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Mucho brazo y poco seso! Así son los peones. Trabajan bien cuando eres capaz de motivarlos, pero de vez en cuando necesitan un poco más de \"motivación\", ¿entiendes?' WHERE `entry` = 9642;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Te gusta algo de lo que ves, $C?' WHERE `entry` = 9643;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si tienes dinero, tengo lo que necesitas.' WHERE `entry` = 9644;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Echa un vistazo y avísame si te interesa algo.' WHERE `entry` = 9645;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tómate tu tiempo, mira bien las armas. También reparo armaduras, si necesitas ese servicio.' WHERE `entry` = 9646;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tengo justo lo que necesitas para lanzar tu hechizo.' WHERE `entry` = 9647;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo que se necesita para devolverle la tranquilidad a Nagrand es una espada afilada y una armadura reforzada. ¿Quieres que repare tu equipamiento para que puedas impartir justicia?' WHERE `entry` = 9648;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás buscando algún componente o suministros de veneno? ¡Pues has venido al lugar apropiado!' WHERE `entry` = 9649;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vendo lo que los artesanos necesitan. Solo objetos básicos, nada especial.' WHERE `entry` = 9650;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Sabes dónde estás? La mayoría de los mercaderes están cerca del centro de la ciudad.' WHERE `entry` = 9652;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Lo traigo fino y lo traigo gordo. Bueno, casi todo gordo, más bien.' WHERE `entry` = 9653;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Durante años, Bastión del Honor resistió ante los horrores de la Península del Fuego Infernal. Ahora que el portal está de nuevo abierto y que llega ayuda continua de Azeroth, puede que acabemos de una vez con este sangriento enfrentamiento.' WHERE `entry` = 9656;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Es lamentable que, entre nuestros muchos enemigos en la Península del Fuego Infernal, se encuentren los orcos de la Ciudadela del Fuego Infernal.$B$BPero... ¿quién mejor para desafiar a la Horda que... los orcos?' WHERE `entry` = 9657;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Festival Lunar de los druidas tiene lugar en Claro de la Luna. Si te queda demasiado lejos, puedes usar el transporte que ofrecen en la Poza de la Luna del parque.', `text0_1_loc7` = 'El Festival Lunar de los druidas tiene lugar en Claro de la Luna. Si te queda demasiado lejos, puedes usar el transporte que ofrecen en la Poza de la Luna del parque.' WHERE `entry` = 9662;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si quieres experimentar realmente el festín del Festival de Invierno, deberías ir a Orgrimmar.', `text0_1_loc7` = 'Si quieres experimentar realmente el festín del Festival de Invierno, deberías ir a Orgrimmar.' WHERE `entry` = 9698;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tú... ven aquí. ¿No te parece que estas espadas son de gran calidad? Seguro que quieres comprarme alguna. No encontrarás armas forjadas con tanta artesanía ni dedicación en ningún otro lugar, $R.' WHERE `entry` = 9713;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las agrestes praderas de Nagrand son el mejor lugar para ir a cazar.' WHERE `entry` = 9729;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Te gusta el pescado, $R? Tengo pescado. Si te gusta el pescado fresco, fresquísimo, ven a ver lo que tengo.' WHERE `entry` = 9730;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nunca mires a esos Tábidos directamente a los ojos. ¡Creo que te pueden succionar el alma si quieren!' WHERE `entry` = 9733;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vaya, vaya. Qué grande y fuerte eres ¿Crees que eres lo suficientemente fuerte como para entrar en El Círculo de Sangre y enfrentarte al campeón Mazo de Guerra?$B$B<El luchador del foso se ríe.>$B$BNo lo creo, pero si alguna vez quieres poner a prueba tu fuerza, El Círculo de Sangre está en el norte, justo en las montañas que dividen Nagrand y la Marisma de Zangar. Gurgthock es quien se encarga del espectáculo en las viejas Ruinas Riecráneos y también es quien entrega los premios. Y, hablando de premios, ¡he oído que los de esta temporada son increíbles!' WHERE `entry` = 9735;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ya tenemos el control de las Ruinas de las Agujas Gemelas! Salid ahí afuera y defended lo que es nuestro.' WHERE `entry` = 9749;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este viejo tablón draenei ha sido modificado por los Kurenai y ahora se usa para publicar anuncios y recompensas.' WHERE `entry` = 9751;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Cazador de la Sombra Denjai usa este tablón para publicar anuncios importantes y noticias de recompensas sobre enemigos de Zabra\'jin.' WHERE `entry` = 9752;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida;. ¿Descansarás aquí? ¿Te apetece comer algo?' WHERE `entry` = 9766;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hay que descubrir lo que se esconde tras el misterio del cristal gigante que cayó de los cielos en Cresta Cielo Estrellado.' WHERE `entry` = 9769;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Algunas veces, un enfoque más directo es la solución.' WHERE `entry` = 9770;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si te captura la Legión, diles \"Xar il romath da tidesbi.\" Te matarán enseguida por haber insultado a su dios y te librarás de una tortura horrible o de algo peor...' WHERE `entry` = 9771;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Península del Fuego Infernal es un reino devastado y despiadado con sus habitantes. Repara y haz acopio de víveres y material necesario antes de salir de expedición.' WHERE `entry` = 9775;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Habla bajo y mantén los ojos abiertos, $N. Estamos rodeados de enemigos.' WHERE `entry` = 9777;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tienes que usar las bombas incendiarias en los barriles que están dentro de los pabellones de internamiento del Castillo de Durnholde. Cuando cinco bombas hayan explotado y se haya creado una distracción, encuentra a Thrall en la prisión del sótano de Durnholde y libérale. Síguele y asegúrate de que siga con vida.$B$BInsolente te llevará volando hasta Durnholde cuando quieras.' WHERE `entry` = 9778;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te llevaré a Durnholde directamente si es lo que quieres.' WHERE `entry` = 9779;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Erozion te dará un paquete de bombas incendiarias antes de que partas para Durnholde. No tienes tiempo que perder.' WHERE `entry` = 9780;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Horrible... Es horrible.$B$BNo nos metíamos con nadie y al volver del área 52 a la altura de Tormenta Abisal, ¡nos dispararon! ¡Ya sabía yo que no conviene tomar atajos para llegar al Bosque de Terokkar!' WHERE `entry` = 9781;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Punta de Expedición es la vanguardia de la Alianza en el frente de la Legión. Al norte de aquí no hay más que demonios, demonios y más demonios. No viajes $Gsolo:sola;.' WHERE `entry` = 9784;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los demonios Illidari me tienen aquí por mis conocimientos sobre antiguos cristales y reliquias draenei.$B$BHan esclavizado a muchos de los míos y nos usan para buscar un cristal muy poderoso... Uno que probablemente no esté aquí.' WHERE `entry` = 9787;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Para qué? Si los demonios de Illidan no me capturan y me vuelven a esclavizar, entonces serán los nagas de la Marisma de Zangar quienes lo hagan.$B$BUnirse a Illidan y a sus aliados significa esclavitud para las tribus de Tábidos. A mi pueblo no le queda esperanza.' WHERE `entry` = 9788;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Juro que jamás volveré a ir por un atajo! ¿Cómo se supone que iba a saber que los cielos sobre la Península del Fuego Infernal eran tan peligrosos?' WHERE `entry` = 9789;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Una suave luz te llena según te vas acercando al naaru. El lento repicar de campanas resuena en tu mente y, aunque nadie dice ni la más mínima palabra, sientes una gran seguridad. $GBienvenido:Bienvenida; a la Ciudad de Shattrath.>' WHERE `entry` = 9800;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sigue tu camino; a menos que tengas algo que ver con la flota.' WHERE `entry` = 9801;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Tormenta Abisal\' no ha sido siempre como lo que ves ahora.$B$BHubo un tiempo cuando esta tierra destrozada se conocía con el nombre de las Llanuras de Farahlon. Muy poco de lo que quedaba de esos días ha sobrevivido al mal que se ha instalado aquí.' WHERE `entry` = 9826;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$C, no ataques! Es solo un disfraz. Soy una espía que trabaja para la coalición del Bajo Arrabal fuera de la Ciudad de Shattrath.$B$B¡Espero que estés aquí para ayudar!' WHERE `entry` = 9827;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El archimago Vargoth me confió su bastón cuando me apresuró a huir del ataque de los elfos de sangre a la Torre Violeta. Hice lo que me pidió, esperando poder refugiarme en el viejo asentamiento draenei de Arklon.$B$BPero no fue así. Había dado de pleno con una fuerza de la Legión Ardiente. Los demonios me robaron el bastón, aunque conseguí escapar con vida.$B$BAunque mi maestro nada sabe de mi fracaso, sigue siendo un motivo de gran vergüenza para mí.' WHERE `entry` = 9828;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡¿Es que no descansan nunca?!$B$BSi queremos asegurar un futuro para el pueblo del Bajo Arrabal, debemos ocuparnos del Consejo de la Sombra antes de que provoque un cataclismo aún mayor que El Vertedero de Huesos.$B$BComo el liderazgo de la Ciudad de Shattrath no se moverá rápidamente, nosotros, el pueblo libre del Bajo Arrabal, nos hemos ocupado de este asunto.$B$BPero han descubierto a nuestros espías. Mantendremos a distancia a los agentes del Consejo de la Sombra mientras entras en el laberinto para ver qué descubres.' WHERE `entry` = 9833;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace mucho tiempo, en una dimensión del cosmos inconcebible para la mente humana, nació -si es que se puede usar este verbo- un ser con un poder tal que su entrada en la existencia resquebrajó la realidad a su alrededor.$B$BConocido como Murmur, era la esencia del sonido. ¡Oírlo hablar significaba conocer la muerte eterna!' WHERE `entry` = 9834;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Existir solo para el caos, su menor susurro significaba la destrucción de mundos enteros.$B$BY, aun así, había quienes intentaban adorar a este ser sin conciencia. Había quienes estaban más locos y se atrevían a pensar en controlarlo.' WHERE `entry` = 9835;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las mohosas páginas del códice parecen girar por arte de magia, lo que centra tu atención a un capítulo concreto sobre horrores pasado que ningún ojo mortal debería percibir.$B$BEl libro se abre en una página que, de alguna forma, parece ser la \'correcta\', aunque al mismo tiempo la equivocada.' WHERE `entry` = 9836;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Y cómo los poderosos caían temblando a sus pies, temerosos de que pudiera dirigir sus palabras hacia ellos.$B$BY, aun así, esos charlatanes, esos falsos adoradores de una religión no existente, pretendían negarse a lo inevitable, en un vano intento de controlar a su \'dios\'. Indiferente y, probablemente sin darse cuenta de su presencia, Murmullo bostezó, y ellos cayeron en la inconsciencia.$B$BPero, de alguna forma, uno de ellos consiguió sobrevivir y, en su locura, encontró la forma de traer a Murmullo al mundo.' WHERE `entry` = 9837;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un mortal con un increíble conocimiento arcano y oscuro que nadie podía superar; desarrolló un método para aumentar su invocación mediante la captura y utilización de almas.$B$BCivilizaciones enteras fueron exterminadas mediante este dispositivo de almas para alimentar el ritual mediante el que él y sus conspiradores traerían a Murmullo a su mundo.' WHERE `entry` = 9838;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Y sí, invocaron a Murmullo.$B$BSe utilizaron poderosas magias para contenerle y silenciarle, gracias al constante goteo de almas de las que se alimentaban. Y, ni aun así, fueron capaces de controlar a la bestia, ni de someterla a su voluntad.$B$BEstos mortales comenzaron a pensar en diferentes estrategias que, una tras una, fracasaron. Y, al hacerlo, debilitaron los rituales, dando a Murmullo, sin querer, un atisbo de libertad.$B$BEra todo lo que necesitaba.' WHERE `entry` = 9839;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solo este libro sobrevivió a la catástrofe que destruyó el mundo en el que se encontraba. Aquellos hombres orgullosos e inconscientes pensaron que controlaban a un ser cósmico de poder inimaginable.$B$B¿Acaso tú, dueño del códice, eres como ellos?$B$B¿Has tramado algún plan para invocar a Murmullo o a alguna otra entidad de las mencionadas en esas páginas impías?$B$BYa lo veremos.' WHERE `entry` = 9840;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Eh! Me alegro de ver caras nuevas. No tengo muchas visitas y los técnicos no son muy habladores.$B$BApuesto a que te diriges al Área 52, ¿verdad?' WHERE `entry` = 9841;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Jefe de cohetes Fuselaje, comandante de B.U.M.: Baqueteados Ultraorbitales Mecanizados, a tu servicio.$B$B¿Están todos los sistemas en marcha? Parece que el cohete abisal X-52 todavía necesita mucho trabajo.$B$BVamos, chicos... ¡Estamos a menos CERO segundos!' WHERE `entry` = 9844;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:bienvenida; al Bastión Allerian, $N.' WHERE `entry` = 9845;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Se trata de un trabajo muy delicado, ¿sabes? No puedo estar sometida a presión, o algo acabará rompiéndose.$B$B¡Como la cabeza de Fuselaje!$B$B¿Te estás preguntando sobre mis robots? Siempre estoy trabajando en el nº I. El nº. II y yo conseguimos la capacidad de sentir durante un milisegundo antes de que su condensador cerebral explotase y se volviese loco.$B$BDecidí saltarme el III y IV, y me fui derecha al nº. V. ¡Mi ojito derecho!' WHERE `entry` = 9846;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ten cuidado en las ruinas, $c. Los espíritus del Kirin Tor que encontrarás, no te reconocerán como $gamigo:amiga;.$B$BEl custodio y yo somos los únicos cuyos espíritus mantenían un vínculo tan fuerte con este lugar como para conservar nuestra identidad en la muerte.' WHERE `entry` = 9847;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Kirin\'Var sobrevivió a 20 largos años de incontables asaltos orcos, pero no pudo sobrevivir a las fuerzas de Kael\'thas. Un hombre que se hacía llamar miembro del Kirin Tor nos asesinó a todos, menos a uno.$B$BEl archimago Vargoth sobrevivió al ataque, así que Kael\'thas le encarceló en la Torre Violeta y protegió la Torre con una magia que prohibía el paso a cualquier miembro del Kirin Tor.' WHERE `entry` = 9848;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Lees el cartel de Se busca para Grek?$B$B¡Grek quiere ayudar!' WHERE `entry` = 9853;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola. Esta casa de huéspedes es para los débiles y los cansados, para que se recuperen de sus heridas. Sería imposible aceptar más gente. Si estás buscando un lugar donde descansar, deberías preguntar en el distrito de los Aldor o los Videntes.' WHERE `entry` = 9854;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué quieres? Siéntate y escoge uno.$B$BSoy Botas, mixólogo jefe de B.U.M. y barman extraordinario.$B$BSi lo que necesitas es un toque mágico, el viejo Botas te lo dará.' WHERE `entry` = 9863;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Eres el corresponsal de la Voz de Gadgetzan? Si me sigues, te enseñaré mi nuevo invento...$B$B<Le dices a la maga que no eres el corresponsal que esperaba.> Está bien. Si necesitas componentes, búscalos. Rapidito. Estoy esperando a alguien.' WHERE `entry` = 9868;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡¿Radiaciones?!$B$BNo, eso no tiene que preocuparte. Rayos, hay que ver las cosas que se cree la gente hoy en día.$B$BEntonces, ¿qué te parecen nuestras operaciones ahí abajo? ¡Es una obra de arte! Ver para creer.' WHERE `entry` = 9869;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos notas enormes en el tablón; una es de Papá Ruedas y, la otra, del jefe de cohetes Fuselaje.$B$BLa de Papá parece ser un anuncio en el que solicita la pieza de una máquina. ¡La del jefe de cohetes guarda relación con una recompensa por la cabeza de un enorme gigante de montaña!$B$BLos detalles de ambas están escritos con caligrafía goblin muy pequeña, que, por supuesto, es prácticamente ilegible.' WHERE `entry` = 9872;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡¿Trituradoras?! Ya no sirven. ¡Están muy pasadas de moda!$B$BSi vamos a construir cohetes... si vamos a hacer que el X-52 despegue... ¡necesitamos la mejor tecnología!' WHERE `entry` = 9875;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Khadgar, antiguo aprendiz del guardián caído, Medivh. Seguro que has oído su nombre antes.$B$BYo fui quien descubrió que Medivh había estado poseído por el Maligno, Sargeras, y que había abierto El Portal Oscuro entre este mundo y Azeroth. Si bien honraba a mi maestro, me vi obligado a matarle y desterrar su alma torturada.' WHERE `entry` = 9876;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo luché junto a Lord Anduin Lothar cuando conseguimos expulsar a la Horda de nuestras tierras, en Azeroth. Como heredero del legado de Medivh, no podía hacer otra cosa.$B$BCuando finalmente llegó el momento de invadir este sombrío mundo, y acabar con el mal de los orcos para siempre, di un paso al frente junto a mis amigos Turalyon, Alleria, Danath y Kurdran.' WHERE `entry` = 9877;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mis camaradas y yo nos hicimos llamar los Hijos de Lothar, en honor al hombre más bondadoso y desinteresado que jamás conocimos. Hicimos todo lo que pudimos por derrotar a Ner\'zhul y poner fin a la locura.$B$BCuando este mundo condenado se derrumbó a nuestro alrededor, destruimos El Portal Oscuro para evitar que algún otro mal pudiese caer sobre nuestro amado Azeroth.' WHERE `entry` = 9878;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Atrapados en ese erial, hicimos lo que pudimos por sobrevivir. Con el tiempo, sentimos que una nueva maldad nos rodeaba desde El Vacío Abisal. Peor que los orcos malignos... Era la Legión Ardiente que pretendía tomar... Terrallende.' WHERE `entry` = 9879;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mientras que mis camaradas aguantaban en el límite de la creciente oscuridad, utilicé mi magia para llegar a la Gran Oscuridad del Más Allá. Allí, pude sentir seres con una gran Luz y de insondable pureza.$B$BY encontré a A\'dal y a sus naaru. Fue como... un milagro.' WHERE `entry` = 9880;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Khadgar sonríe para sí mismo >$B$BConvencí a A\'dal y a sus hermanos para que volviesen aquí conmigo. Creo que solo su poder puede expulsar a la Legión de este universo. Pero, como descubrirás, para ello es necesario que su poder viva en nuestros corazones.$B$BPara luchar contra la oscuridad, debemos convertirnos en faros de su eterna Luz.' WHERE `entry` = 9881;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué deseas, $R?' WHERE `entry` = 9886;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué es un \"todólogo\"? Mejor sería preguntar qué no es un \"todólogo.\" ¡Significa que lo estudio todo! De hecho, soy tan experto en \"todo\" que te aseguro que soy el etéreo más inteligente que jamás hayas visto, ¡te apuesto lo que quieras!$B$BPerdona. Está hablando el comerciante en mí...' WHERE `entry` = 9894;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No soy flaco ni oscuro. De hecho, ambas referencias me parecen insultantes.$B$BY ahora, sal de mi vista antes de que te clave el bastón en el culo.' WHERE `entry` = 9895;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah. Veo que eres amigo del Consorcio. ¿Te interesaría hacer una compra?' WHERE `entry` = 9896;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El panel de control cambia y gira sobre sí mismo.' WHERE `entry` = 9899;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<La cabeza de la imagen se vuelve borrosa mientras se funde con lo que la rodea.>$B$BSi hubieras visto las Llanuras de Farahlon, no te creerías que es este mismo lugar. Hace solo unos meses, estos campos no habrían desentonado en los Páramos de Poniente.$B$BYa no queda nada. ¿Cómo ha sido? ¿Y por qué?',  `text1_0_loc7` = 'La tierra está en peor estado de lo que me había imaginado. Si se sigue dañando a este ritmo, Farahlon, eh..., Tormenta Abisal no será más que un erial cósmico.$B$B¿Cuándo parará? Puede que la pregunta correcta sea ¿parará algún día?', `text2_0_loc7` = '<La imagen del archimago parece atravesarte con la mirada en lugar de mirarte.>$B$BGalones y corrientes de energía arcana emanan y flotan como si la zona estuviera hundida en un mar de poder mágico. Ni siquiera el primer cataclismo causó tanto daño.', `text3_0_loc7` = '¡Me vengaré de Kael\'thas por esta traición al Kirin Tor! Una vez caminó entre nosotros como un hermano, pero ya no somos hermanos.$B$BLlegará la hora de la verdad.$B$B<El archimago asiente, sin que le entusiasme el panorama.>$B$BKael\'thas es mucho más que un simple pícaro mago. Cuando esté liberado de esta torre, Ravandwyr y yo ayudaremos a quien quiera que se oponga a él.' WHERE `entry` = 9901;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hubo un tiempo cuando el orbe que ves detrás de mí era capaz de ver lo que sucedía en Azeroth.$B$BLa maldición de Kael\'thas cambió todo eso, por supuesto, pero durante nuestros años de aislamiento, muchos de los magos encontraron alivio en sus imágenes.$B$BAlgunos las miraban para recordar, otros para olvidar.',  `text1_0_loc7` = '<El archimago Vargoth te sonríe irónicamente.>$B$BHay mucho sitio en la Torre Violeta.$B$BAquí me encontrarás en cualquier momento del año.', `text2_0_loc7` = 'La noche en que Kael\'thas arrasó el pueblo, yo no pude hacer nada para ayudar. Su magia ya me había encerrado en la torre, exacerbando mi tormento al obligarme a ver las muertes de los míos.$B$BY todavía, sus voces me llaman en la distancia, despertándome en mitad de la noche.' WHERE `entry` = 9902;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si te han enviado a matarme, coge un número y espera tu turno.' WHERE `entry` = 9903;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Habla bajo! No quiero que me vean aquí.$B$B¡Ooh, esto me pone furioso! ¿Pero quién se creen que son esos etéreos?$B$B¡Ahora me toca a mí!' WHERE `entry` = 9904;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Por favor, déjame sola con mi tristeza.' WHERE `entry` = 9905;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ese niño me da escalofríos. Se queda ahí de pie todo el día, conversando con su abuelo. Después, se irá a contarle a su madre lo que le ha dicho el abuelo, y ella – por supuesto – solo sonríe y asiente.$B$B¡Corderos! ¡Lemmings! ¡Todos ellos!' WHERE `entry` = 9914;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando el jefe de cohetes Fuselaje pedía voluntarios para ayudar a construir el cohete abisal X-52, entregué mi currículum para el departamento de seguridad.$B$BPor desgracia, el único puesto que quedaba era el de jefe de ingenieros. Para mi suerte, tengo aquí a Xyrol.' WHERE `entry` = 9916;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si trabas amistad con el Bajo Arrabal, puede que hagamos negocios.' WHERE `entry` = 9918;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿No te parece increíble esta tecnología etérea?$B$BYo me conformo con que me dejen acercarme hasta aquí. Voy a aprender mucho... ¡Puede que consiga alguno de estos para mí!' WHERE `entry` = 9919;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Dale caña, $Gchico:chica;! Tengo asuntos que atender...' WHERE `entry` = 9920;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se dice que aquellos bendecidos por los espíritus de Auchindoun poseen la habilidad de capturar almas poderosas en forma de fragmentos de espíritus. Me he cruzado con algunos de estos fragmentos de espíritus en mis viajes, pero todavía debo desvelar sus secretos.$B$BLo único que he podido percibir es que parecen originarse en las profundidades de la misma Auchindoun. Si por casualidad te cruzas con alguno de estos fragmentos de espíritus, estoy seguro de que te valdría la pena permitirme que te los quite de las manos...' WHERE `entry` = 9921;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este constructo de cristal zumba con energía. Ante ti, se proyectan extraños glifos que se desplazan arriba y abajo con rapidez.' WHERE `entry` = 9922;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ocupado, ocupado! ¡Tanto por hacer, tan poco tiempo!$B$BBueno, ¿dónde he puesto esa pirollave? Sé que está por aquí... ¡me he quemado los dedos con ella hace solo un momento!' WHERE `entry` = 9923;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace muchos años, mis hombres y yo seguimos al mago Khadgar y al paladín Turalyon hasta este mundo. Nuestro objetivo era aniquilar a toda la horrible raza orca. Pero algo salió mal en el último momento.$B$BAl final, el mundo oscuro estalló en pedazos y mis amigos se perdieron en el vacío.' WHERE `entry` = 9932;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nosotros, los hijos de Lothar, hemos permanecido aquí desde entonces - luchando... muriendo... rogando que llegara un día como el de hoy – un día en el que el Portal se abriera y nuestros hermanos vinieran a llevarnos a casa.$B$BPor más rebosantes de alegría que estemos, el deber sigue llamando. Hay mucho que hacer antes de poder marcharnos de este mundo y descansar.' WHERE `entry` = 9933;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Otro soldado de camino al Bastión del Honor... ¡por la barba de Kurdran, podrán hacer uso de la ayuda!', `text0_1_loc7` = 'Otro soldado de camino al Bastión del Honor... ¡por la barba de Kurdran, podrán hacer uso de la ayuda!' WHERE `entry` = 9935;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡El Consorcio se vengará de los traidores Zaxxis!' WHERE `entry` = 9937;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, un nuevo campeón para nuestra causa. ¿Qué dices, $N?' WHERE `entry` = 9938;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$N, los hijos de Lothar han luchado aquí, en la península del Fuego Infernal, durante dos décadas. Nos vemos acosados por todos lados por orcos indígenas y demonios infernales de la Cruzada Ardiente y, aunque nuestros corazones siguen latiendo con fuerza, años de guerra han reducido nuestros números.$B$B¡Tú, y el influjo de la nueva generación de héroes de Azeroth, sois, sin lugar a dudas, una visión bien acogida!' WHERE `entry` = 9939;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace unos veinte años, la Alianza envió la primera Expedición a este mundo con la esperanza de erradicar a los orcos para siempre. Fue un... conflicto terrible. Muchas almas valientes perecieron para asegurar la seguridad de Azeroth.$B$BPero algunas almas permanecen... en combate eterno.' WHERE `entry` = 9942;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace unos veinte años, la Alianza lanzó la primera expedición a este mundo con la intención de eliminar a los orcos para siempre. Fue... una batalla terrible. Murieron muchos valientes para defender a Azeroth. $B$BGracias, $N, por contribuir al descanso de sus almas.' WHERE `entry` = 9943;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los hombres y mujeres de Bastión del Honor han derramado mucha sangre y han perdido a sus hijos en esta horrible tierra... ¡Y a pesar de eso, siguen luchando con la moral alta!$B$BAhora, tú y tus seguidores de Azeroth podéis ayudar a que cambien las tornas. Gracias a ti ha vuelto la esperanza de regresar a nuestras tierras.' WHERE `entry` = 9944;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como representante del Consorcio, siempre tengo en cuenta la influencia que mis acciones ejercen sobre nuestros negocios.$B$BEs una pena que los elfos de sangre no tengan la delicadeza de hacer lo mismo.' WHERE `entry` = 9947;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi hermano menor es la nueva promesa del Consorcio. Por supuesto, es demasiado modesto para reconocerlo.' WHERE `entry` = 9948;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te saludo, $R. Qué hago aquí es una historia para otro momento. Uf, es una de las malas.$B$BMi hermano y yo estamos atrapados aquí, entre nuestro tiempo y el presente. No osamos marcharnos por miedo de causar una paradoja temporal. Esperamos pacientemente hasta que los dragones nos devuelvan a nuestra auténtica línea temporal.$B$B¿Cuánto tiempo hace, preguntas? En realidad no estoy seguro. Pregúntaselo a mi hermano.$B$B¿Te apetece algo de queso?' WHERE `entry` = 9951;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, has oído bien. Tengo la voz de una joven orca. Pero escucha, $Gchico:chica;, los viajes en el tiempo tienen estas cosas.$B$BPor cierto, son 250 años.' WHERE `entry` = 9953;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, he estado atrapado aquí desde que puedo recordar.$B$BEn mi vida anterior, era armero. ¿Quizás pueda repararte algo?' WHERE `entry` = 9954;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Al fin, alguien ha bajado a hablar conmigo! ¿Quieres oír alguno de mis poemas?' WHERE `entry` = 9955;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Bajando la calle! ¡Mirando el valle! ¡Observando las hojas caer en la calle!' WHERE `entry` = 9956;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta plataforma de teletransporte etérea está cubierta de botones e interruptores de colores vivos, pero parece haber caído en desuso.' WHERE `entry` = 9958;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Todo en venta, $Gamigo:amiga;! Grande y pequeño, sea lo que sea lo que necesitas, Hazzin te lo proporcionará.$B$BSi valoras tu vida, no te acerques a la forja de maná Ultris sin reabastecerte. Hazzin es la última oportunidad que tienes.' WHERE `entry` = 9959;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pastos de Bosquehumeante ha abierto establecimientos en Orgrimmar y Forjaz. ¿Qué prepararán este año?', `text0_1_loc7` = 'Los Pastos de Bosquehumeante han abierto establecimientos en Orgrimmar y Forjaz. ¿Qué prepararán este año?' WHERE `entry` = 9962;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Nunca había visto nada igual a esos dracos abisales. Parece que han heredado el temperamento de su señor, pero, al mismo tiempo, son algo totalmente diferente.' WHERE `entry` = 9968;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El suelo de este lugar todavía está caliente de sangre recién derramada. ¡Ah, habría dado mi propio ojo para estar aquí cuando los demonios entraron por ese portal y se enfrentaron a los héroes de Azeroth! ¡Menuda batalla!$B$B¡Pero ahora, al otro lado hay una batalla aún más feroz!' WHERE `entry` = 9969;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si has venido hasta aquí, debe de ser porque osas aventurarte cerca de la forja de maná Ara.$B$B¿Quieres comprar o vender antes de seguir hasta allí?' WHERE `entry` = 9970;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solo hay tres tipos de personas en este mundo: competidores, clientes y empleados. Los competidores se deben aplastar implacablemente, a los clientes se les debe satisfacer, y a los empleados, supervisar estrictamente.$B$BLos contratistas independientes son un mito. Quieren que se les pague mejor que a los empleados, pero que se les trate como a clientes. Nunca confíes en nadie que use ese título para sí.' WHERE `entry` = 9971;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuidado, mortal. tras esta sala hay peligros desconocidos en tu mundo. Camina con cuidado...' WHERE `entry` = 9977;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegro de verte de nuevo, $N. ¿Quieres que te lleve a la guarida del maestro?' WHERE `entry` = 9978;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegro de ver a un amigo de Aldor por estos recónditos rincones. Me temo que no soportaré mucha más cháchara de esos elfos insolentes.' WHERE `entry` = 9980;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El bosque canta con las semillas del cambio. Nuestros nuevos amigos, los draenei, están aquí, y los ayudaremos a construir un lugar para ellos.' WHERE `entry` = 9986;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué es lo que quieres? No me van las charlas.' WHERE `entry` = 9988;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Otro soldado de camino al Bastión del Honor... ¡por la barba de Kurdran, podrán hacer uso de la ayuda!', `text0_1_loc7` = 'Otro soldado de camino al Bastión del Honor... ¡por la barba de Kurdran, podrán hacer uso de la ayuda!' WHERE `entry` = 9991;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a la Flecha de la Tormenta, $C.$B$BQue no te disuada que esté presente a través de este dispositivo de imágenes. Tengo muchos enemigos a los que les gustaría verme muerto, así que es solo por precaución.$B$BTe aseguro que estás perfectamente $Gseguro:segura; aquí.' WHERE `entry` = 9993;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Hay algo que desees comprar? ¿Hay algo que desees vender?$B$BPor eso estamos aquí, $Gamigo:amiga;.$B$BPor favor, dime qué deseas.' WHERE `entry` = 9994;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Así que acabas de llegar de Azeroth, ¿eh? Será mejor que te armes de valor, $C – en esta tierra hay peligros como ninguno al que se haya enfrentado antes la Horda. Soy Nazgrel – la mano derecha de nuestro poderoso Jefe de Guerra, Thrall. Me honró con la tarea de salvaguardar a su valiente expedición y – por todos los espíritus – ¡lo haré!' WHERE `entry` = 9995;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Throm\'ka, $Ghermano:hermana;! $GBienvenido:Bienvenida; a Terrallende. La Horda necesita toda la fuerza que pueda reunir en este mundo destrozado. Te prometo que, si es una batalla gloriosa lo que andas buscando, aquí tendrás hasta hartarte.' WHERE `entry` = 9996;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hazlo rápido, $C – tenemos una guerra que librar aquí, ¿o no te habías dado cuenta?' WHERE `entry` = 9997;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te saludo, $Gaventurero:aventurera;. Sigue mis advertencias antes de aventurarte en La Ciénaga Negra.' WHERE `entry` = 10000;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, bienvenido de nuevo, héroe. ¿Has vuelto para volver a proteger al Guardián? ¡No olvides llevarte una cronobengala contigo!' WHERE `entry` = 10001;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando nuestro Jefe de Guerra oyó que el Portal Oscuro se había vuelto a abrir, reunió de inmediato a sus consejeros para planear esta osada expedición. Aunque varios de sus concejales tienen sus dudas, Thrall mantiene la esperanza de que podríamos descubrir cosas nuevas sobre la historia de nuestro pueblo. Cree que, en algún lugar en medio de las ruinas de este mundo, se encuentra escondida la auténtica historia del pueblo orco.$B$BDebo admitir que en mis momentos más oscuros... temo que la historia debería permanecer sin contar.' WHERE `entry` = 10003;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Horda ha arriesgado mucho para aventurarse en Terrallende - recursos, mano de obra – incluso la defensa táctica de nuestras tierras está en juego. A pesar de todo, Thrall cree en la promesa de esta tierra devastada. Hay una luz extraña en sus ojos estos días – como si sintiera alguna revelación conmovedora que los demás no sentimos.$B$BCon todo, es mi Jefe de Guerra y mi hermano de sangre. Lo seguiría a través de las mismas puertas del abismo.' WHERE `entry` = 10004;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Throm\'ka, $C. Debes de ser $Gnuevo:nueva;. Pareces joven y no tienes aspecto de haber vivido los horrores de estas tierras.$B$B<El sargento Trizacráneo escupe en el suelo>$B$BEso va a cambiar.' WHERE `entry` = 10005;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Creo que, con algo de ayuda, Taerix puede estar cerca de un avance.' WHERE `entry` = 10008;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mis hermanos de Ventormenta me dijeron que no me uniera al ejército... ¡me alegro de no haberlos escuchado!' WHERE `entry` = 10009;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Puesto Devastación es un gran lugar para un enano Martillo Salvaje. ¡Nunca he pasado tanto tiempo en el aire!', `text0_1_loc7` = 'El Puesto Devastación es un gran lugar para un enano Martillo Salvaje. ¡Nunca he pasado tanto tiempo en el aire!' WHERE `entry` = 10010;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los halcones zancudos son criaturas nobles. Tratad bien al vuestro; hay pocas cosas más peligrosas que un halcón zancudo enfadado.' WHERE `entry` = 10011;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Estoy segura de que, con tu ayuda, la botánica Taerix podrá hallar soluciones a nuestros problemas.' WHERE `entry` = 10012;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Te apetece ayudarme a destruir el portal de distorsión del campamento que la Legión Ardiente tiene hacia el sur?' WHERE `entry` = 10019;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todavía no eres lo suficientemente $Gpoderoso:poderosa; como para luchar en el Ojo de la Tormenta. Vuelve cuando tengas más experiencia.', `text0_1_loc7` = 'Todavía no eres lo suficientemente $Gpoderoso:poderosa; como para luchar en el Ojo de la Tormenta. Vuelve cuando tengas más experiencia.' WHERE `entry` = 10024;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Horda no debe permitir que los draenei y su entrometida Alianza controlen las células de maná de Terrallende. ¿Quieres acompañar a los elfos de sangre en su ataque al Ojo de la Tormenta?', `text0_1_loc7` = 'La Horda no debe permitir que los draenei y su entrometida Alianza controlen las células de maná de Terrallende. ¿Quieres acompañar a los elfos de sangre en su ataque al Ojo de la Tormenta?' WHERE `entry` = 10026;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola, $C! ¿Vienes por la misión de vuelo?' WHERE `entry` = 10027;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si has estado en la marisma de Zangar, puede que te hayas encontrado con un tipo llamado Daniels. Ya sabes – lleva un casco extraño, solo responde al nombre de \"el Especialista.\"$B$BPor supuesto, le enseñé todo lo que sabe.$B$BNo te dejes engañar por la ropa elegante. En el fondo sigo siendo un pícaro. Daniels y yo pasamos mucho tiempo juntos en las trincheras, pero eso fue antes de que yo me retirara. Por lo que sé, sigue ahí fuera, desatando el infierno.' WHERE `entry` = 10031;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Día tras día, lo único que hacemos es matarnos trabajando en esta mina. El elfo de sangre no fue hecho para tales tareas manuales.' WHERE `entry` = 10032;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$N, comunica a Gahruj nuestro éxito.' WHERE `entry` = 10039;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El emisario Taluun me trajo consigo esperando que pudiera enseñar a nuestros nuevos aliados cosas sobre los elementos de este mundo... pero nadie escucha.', `text0_1_loc7` = 'El emisario Taluun me trajo consigo esperando que pudiera enseñar a nuestros nuevos aliados cosas sobre los elementos de este mundo... pero nadie escucha.' WHERE `entry` = 10040;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Aunque la atención de los Sha\'tar permanezca en el valle Sombraluna e Illidan, no podemos ignorar la amenaza que Kael\'thas el Caminante del Sol supone.' WHERE `entry` = 10042;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El emisario Taluun me trajo consigo esperando que enseñara a nuestros nuevos aliados cosas acerca de los elementos de este mundo. Hasta ahora, se niegan a escuchar, pero, ¿quizás tú lo harías?' WHERE `entry` = 10043;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A medida que te acercas a la consola, el cristal de acceso que tienes contigo comienza a zumbar suavemente y a emitir una luz parpadeante.$B$BUn torrente de información fluye hasta tu mente y, de repente, eres consciente de los niveles de producción de energía de la forja de maná, de su estabilidad y de su producción a diferentes tuberías. La consola aguarda tu orden.' WHERE `entry` = 10045;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Celador de la Luz tiene una tarea doble. Proteger a la hermandad a cualquier precio en primer lugar. Y en segundo lugar, destruir a los enemigos de la Luz dondequiera que estén.' WHERE `entry` = 10051;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Por la barba de Muradin, $C. Esta tierra está al borde de la aniquilación. No me puedo imaginar que alguien sobreviviera aquí desde el final de la segunda guerra.', `text0_1_loc7` = 'Por la barba de Muradin, $C. Esta tierra está al borde de la aniquilación. No me puedo imaginar que alguien sobreviviera aquí desde el final de la segunda guerra.' WHERE `entry` = 10052;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Throm\'ka, $C. ¿Es esta tierra destrozada lo que esperabas ver? No se parece en nada a las historias que me han contado. Es difícil creer que mi pueblo llegara a prosperar en este lugar desierto.', `text0_1_loc7` = 'Throm\'ka, $C. ¿Es esta tierra destrozada lo que esperabas ver? No se parece en nada a las historias que me han contado. Es difícil creer que mi pueblo llegara a prosperar en este lugar desierto.' WHERE `entry` = 10053;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Busco alguien que me ayude con una tarea que debería haberse acabado hace tiempo.$B$BPareces capaz.' WHERE `entry` = 10054;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Legión está por todas partes... ¡hay que ocuparse de ellos!' WHERE `entry` = 10056;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gracias a ti, podremos seguir con nuestros planes en las Ruinas de Farahlon.' WHERE `entry` = 10057;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué sabes de los etéreos, ser de carne?$B$BSin duda nos ves iguales a todos, e ignoras los conflictos y luchas entre las diferentes facciones.$B$BQuizás sea el momento para presentaciones. Soy el comandante Ameer del Protectorado – también tu única esperanza de resolver la infestación de criaturas del vacío y El Etereum en esta zona.' WHERE `entry` = 10058;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Etereum era la clase gobernante de etéreos en mi mundo de K\'aresh. Trágicamente, nos vimos obligados a evacuar K\'aresh poco antes de que el señor del vacío, Dimensius, liberara sus ejércitos del vacío sobre el mundo y diezmara por completo todo lo que se hallaba a su paso. Por aquel acto, El Etereum juró venganza. Lleva siguiéndole la pista a Dimensius desde entonces...$B$BEl Protectorado, sin embargo, no fue una reacción a Dimensius. Fuimos una reacción a El Etereum.' WHERE `entry` = 10059;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Etereum se ha vuelto oscuro... retorcido. La venganza se ha convertido en su único recurso y, cualquiera que se interponga en su camino o no se una a su causa se considera enemigo. Muchos vieron que El Etereum caía en picado hacia la locura y se marcharon. Varios de los príncipes-nexo del consejo Etereum los abandonaron para formar otros grupos y facciones con diferentes objetivos. El Consorcio es un ejemplo de un grupo disidente. El Protectorado es otro...' WHERE `entry` = 10060;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pensamos que El Etereum es una amenaza tan grande para nuestra existencia como Dimensius. Ambos deben ser detenidos...' WHERE `entry` = 10061;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola. Soy Vixton Silbapellizco, vendedor oficial de la Arena para el circuito de lucha de Bonvapor. Si tienes al representante, tengo los artículos que te harán la boca agua.' WHERE `entry` = 10062;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si estás $Gmareado:mareada;, habla con Pestle. Puede que tenga algo para los mareos.' WHERE `entry` = 10063;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El aprendiz mira de vez en cuando por encima del hombro, aparentemente reaccionando ante sonidos que tú no puedes oír.>$B$BBuenos días, $N. Discúlpame si parezco distraído, pero me encuentro en medio de un trabajo para el granjero Natin. Quiero poner herraduras a todos sus caballos para cuando vuelva del concejo. ¿En qué puedo ayudarte?' WHERE `entry` = 10064;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Al agente Ya-six y a mí nos asignaron una misión de prioridad alta desde el mando del Protectorado: erradicar toda señal de infestación de criaturas del vacío de Ultris. Tenían que ser ataques tácticos. Nuestro último ataque antes de volver a la Atalaya del Protectorado tenía que caer sobre un señor del vacío llamado Arconus, que había estado engordando gracias a los seres de carne de la zona.$B$BPor desgracia, llegamos un poco tarde. Ya-six y yo nos separamos en el tiroteo con las bestias de carne. Ahora está atrapado dentro, inmovilizado tras líneas enemigas.' WHERE `entry` = 10065;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si la Luz no puede guiarte, $Gamigo:amiga;, quizás yo pueda ayudarte. ¿Qué necesitas?', `text0_1_loc7` = 'Si la Luz no puede guiarte, $Gamigo:amiga;, quizás yo pueda ayudarte. ¿Qué necesitas?', `text1_0_loc7` = 'Esta isla puede ser extraña, ¿verdad? ¿Hay algo que pueda hacer por ti?', `text1_1_loc7` = 'Esta isla puede ser extraña, ¿verdad? ¿Hay algo que pueda hacer por ti?' WHERE `entry` = 10066;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Buscas almacenamiento? Entonces querrás dirigirte a la seguridad de El Exodar... los banqueros estarán muy contentos de ayudarte.', `text0_1_loc7` = '¿Buscas almacenamiento? Entonces querrás dirigirte a la seguridad de El Exodar... los banqueros estarán muy contentos de ayudarte.' WHERE `entry` = 10067;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los hipogrifos están bien cuidados cerca del mismo Exodar. Deberías viajar hasta allí si deseas volar hasta la Avanzada de Sangre.', `text0_1_loc7` = 'Los hipogrifos están bien cuidados cerca del mismo Exodar. Deberías viajar hasta allí si deseas volar hasta la Avanzada de Sangre.' WHERE `entry` = 10071;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El suministro de tejidos de El Exodar, antaño tan vasto, ha disminuido peligrosamente. Tu reputación con los draenei aumentaría si nos ayudaras donando tela.' WHERE `entry` = 10072;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Funaam es nuestro registrador del gremio. Su oficina se ha establecido dentro de El Exodar. Sigue el camino; la encontrarás enseguida.', `text0_1_loc7` = 'Funaam es nuestro registrador del gremio. Su oficina se ha establecido dentro de El Exodar. Sigue el camino; la encontrarás enseguida.' WHERE `entry` = 10073;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestra humilde posada está situada al norte de la Avanzada Azur; Chellan cuidará bien de ti.', `text0_1_loc7` = 'Nuestra humilde posada está situada al norte de la Avanzada Azur; Chellan cuidará bien de ti.' WHERE `entry` = 10074;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esbina estará contenta de cuidar de tu compañero animal, $Gamigo:amiga;. Te espera fuera de la posada, dentro de la Avanzada Azur.', `text0_1_loc7` = 'Esbina estará contenta de cuidar de tu compañero animal, $Gamigo:amiga;. Te espera fuera de la posada, dentro de la Avanzada Azur.' WHERE `entry` = 10075;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, buscas instrucción, ¿verdad? Hay varios instructores que acampan en la Avanzada Azur... ¿a quién buscas?', `text0_1_loc7` = 'Ah, buscas instrucción, ¿verdad? Hay varios instructores que acampan en la Avanzada Azur... ¿a quién buscas?' WHERE `entry` = 10076;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Druida? Nadie aquí sigue ese camino... pero creo haber oído de alguien que coincide con esa descripción cerca de El Exodar. Shalannius, creo.', `text0_1_loc7` = '¿Druida? Nadie aquí sigue ese camino... pero creo haber oído de alguien que coincide con esa descripción cerca de El Exodar. Shalannius, creo.' WHERE `entry` = 10077;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Acteon es nuestro rastreador residente, y es de los buenos, además – las pieles que viste son prueba de su destreza. Su campamento está al sureste de la Avanzada Azur.', `text0_1_loc7` = 'Acteon es nuestro rastreador residente, y es de los buenos, además – las pieles que viste son prueba de su destreza. Su campamento está al sureste de la Avanzada Azur.' WHERE `entry` = 10078;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué? ¿Quieres donar ropa a Lunargenta?$B$B¿Vas detrás de un jinete de halcón, verdad? Muy bien, muy bien... haz este favor a la ciudad, y me aseguraré de que tu reputación mejore.' WHERE `entry` = 10079;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Para el dominio de lo Arcano, no necesitas buscar más lejos que Semid. Instruye en el extremo oriental de la Avanzada Azur, junto con Ruada.', `text0_1_loc7` = 'Para el dominio de lo Arcano, no necesitas buscar más lejos que Semid. Instruye en el extremo oriental de la Avanzada Azur, junto con Ruada.' WHERE `entry` = 10081;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La buena Tullas es una mujer noble y una defensora incondicional de la Luz. Sus lecciones se instruyen en la misma posada.', `text0_1_loc7` = 'La buena Tullas es una mujer noble y una defensora incondicional de la Luz. Sus lecciones se instruyen en la misma posada.' WHERE `entry` = 10083;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En la pequeña, pero buena posada de Chellan, puedes encontrar al sacerdote Guvan.', `text0_1_loc7` = 'En la pequeña, pero buena posada de Chellan, puedes encontrar al sacerdote Guvan.' WHERE `entry` = 10084;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Eres uno de los chamanes? Mm. Supongo que eres libre de escoger tu propio camino... sea cual sea. Tuluun tiene un pequeño campamento aislado al noroeste de la Avanzada Azur.', `text0_1_loc7` = '¿Eres uno de los chamanes? Mm. Supongo que eres libre de escoger tu propio camino... sea cual sea. Tuluun tiene un pequeño campamento aislado al noroeste de la Avanzada Azur.' WHERE `entry` = 10085;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ruada instruye a jóvenes combatientes en la parte oriental de la Avanzada Azur. Si lo que buscas es instrucción en el arte práctico de la guerra, ella te ayudará.', `text0_1_loc7` = 'Ruada instruye a jóvenes combatientes en la parte oriental de la Avanzada Azur. Si lo que buscas es instrucción en el arte práctico de la guerra, ella te ayudará.' WHERE `entry` = 10086;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay varios mercaderes diestros esparcidos por Bruma Azur. ¿A quién buscas?', `text0_1_loc7` = 'Hay varios mercaderes diestros esparcidos por Bruma Azur. ¿A quién buscas?' WHERE `entry` = 10087;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Daedal conoce el arte de elaborar pociones. Trabaja estrechamente con Fateema en el extremo oriental de la Avanzada Azur.', `text0_1_loc7` = 'Daedal conoce el arte de elaborar pociones. Trabaja estrechamente con Fateema en el extremo oriental de la Avanzada Azur.' WHERE `entry` = 10088;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tenemos herrería establecida en la Avanzada Azur. Sin embargo, he visto a varios herreros jóvenes venir de aquel campamento extraño en la costa sur. Quizás deberías investigar.', `text0_1_loc7` = 'No tenemos herrería establecida en la Avanzada Azur. Sin embargo, he visto a varios herreros jóvenes venir de aquel campamento extraño en la costa sur. Quizás deberías investigar.' WHERE `entry` = 10089;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No teníamos cocineros de sobras, pero es un poco raro... hay algunos aromas deliciosos en el viento del sur. Sigue tu nariz, ¿eh?', `text0_1_loc7` = 'No teníamos cocineros de sobras, pero es un poco raro... hay algunos aromas deliciosos en el viento del sur. Sigue tu nariz, ¿eh?' WHERE `entry` = 10090;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestros encantadores se pasan el día en El Exodar, trabajando en varios proyectos. Nahogg iniciará tu instrucción.', `text0_1_loc7` = 'Nuestros encantadores se pasan el día en El Exodar, trabajando en varios proyectos. Nahogg iniciará tu instrucción.' WHERE `entry` = 10091;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El artificiero local, Daelo, puede enseñarte las bases de la ingeniería. ¡Quizás podrías ayudar a nuestro técnico a reparar el dispositivo de comunicaciones que funciona mal!', `text0_1_loc7` = 'El artificiero local, Daelo, puede enseñarte las bases de la ingeniería. ¡Quizás podrías ayudar a nuestro técnico a reparar el dispositivo de comunicaciones que funciona mal!' WHERE `entry` = 10092;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Fateema es nuestra anacoreta, y ha sido inestimable para cuidar a las víctimas del accidente. Practica su oficio en el extremo occidental de la Avanzada.', `text0_1_loc7` = 'Fateema es nuestra anacoreta, y ha sido inestimable para cuidar a las víctimas del accidente. Practica su oficio en el extremo occidental de la Avanzada.' WHERE `entry` = 10093;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GUn:Una; aspirante a pescador... bueno, Diktynna vive en la costa del Vado Ammen, y usa su red y su palo para mantenernos abastecidos a todos. Estoy seguro de que tu ayuda le iría bien.', `text0_1_loc7` = '$GUn:Una; aspirante a pescador... bueno, Diktynna vive en la costa del vado Ammen, y usa su red y su palo para mantenernos abastecidos a todos. Estoy segura de que tu ayuda le iría bien.' WHERE `entry` = 10094;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Heur ha estudiado la flora local desde nuestro aterrizaje. Podrá guiarte. Puedes encontrarle trabajando con Daedal en el margen occidental de la Avanzada Azur.', `text0_1_loc7` = 'Heur ha estudiado la flora local desde nuestro aterrizaje. Podrá guiarte. Puedes encontrarle trabajando con Daedal en el margen occidental de la Avanzada Azur.' WHERE `entry` = 10095;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestros nuevos aliados, los fúrbolgs de Semprepino, han mostrado su interés en llevarse todas las pieles que encontremos. Viven al norte; yo acudiría a ellos en busca de conocimiento.', `text0_1_loc7` = 'Nuestros nuevos aliados, los fúrbolgs de Semprepino, han mostrado su interés en llevarse todas las pieles que encontremos. Viven al norte; yo acudiría a ellos en busca de conocimiento.' WHERE `entry` = 10096;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Dulvi es la mujer que quieres. Instruye a nuevas posibilidades en medio de la Avanzada.', `text0_1_loc7` = 'Dulvi es la mujer que quieres. Instruye a los nuevos en medio de la Avanzada.' WHERE `entry` = 10097;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Viaja al Bastión Semprepino, $Gamigo:amiga;. Los fúrbolgs que hay allí han sido muy útiles, y puede que tengan justo la pericia que necesitas.', `text0_1_loc7` = 'Viaja al Bastión Semprepino, $Gamigo:amiga;. Los fúrbolgs que hay allí han sido muy útiles, y puede que tengan justo la pericia que necesitas.' WHERE `entry` = 10098;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tenemos sastres en la Avanzada Azur. Pero quizás quieras probar en el campamento de la playa – parecen tener varios mercaderes del otro lado del mar.', `text0_1_loc7` = 'No tenemos sastres en la Avanzada Azur. Pero quizás quieras probar en el campamento de la playa – parecen tener varios mercaderes del otro lado del mar.' WHERE `entry` = 10099;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Buscas un lapidario? Nuestros joyeros están en El Exodar, utilizando los materiales que han descubierto para hacer maravillas. Habla con Farii cuando llegues.', `text0_1_loc7` = '¿Buscas un lapidario? Nuestros joyeros están en El Exodar, utilizando los materiales que han descubierto para hacer maravillas. Habla con Farii cuando llegues.' WHERE `entry` = 10100;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡¿Que quieres qué?! He oído cosas sobre uno de esos extraños draenei en el Valle de los Héroes. Puede que ella sepa algo; yo no sé nada.', `text0_1_loc7` = '¡¿Que quieres qué?! He oído cosas sobre uno de esos extraños draenei en el Valle de los Héroes. Puede que ella sepa algo; yo no sé nada.' WHERE `entry` = 10101;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El espíritu de agua nos ha dejado. Nuestra tribu está condenada.' WHERE `entry` = 10103;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los vientos ya no hablan con nuestra tribu, $N. Mi pueblo está perdido sin ancianos que les traigan los espíritus.' WHERE `entry` = 10104;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estas cadenas que me atan me impiden llevar el espíritu del fuego a mi pueblo, $N. Las almas sin fuego son almas que no se pueden rebelar contra sus maestros.' WHERE `entry` = 10105;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No sé qué mosca te ha picado para que andes buscando un chamán..., pero conozco a alguien que se ajusta al perfil. Hay una mujer que se hace llamar clarividente en la orilla del Valle de los Héroes.', `text0_1_loc7` = 'No sé qué mosca te ha picado para que andes buscando un chamán..., pero conozco a alguien que se ajusta al perfil. Hay una mujer que se hace llamar clarividente en la orilla del Valle de los Héroes.' WHERE `entry` = 10106;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Paladín? ¿Has estado tomando pociones raras del boticario o algo así? He oído rumores de que un destacamento elfo ha visitado a la Dama Oscura en Entrañas. Puede que alguno de ellos sepa algo.', `text0_1_loc7` = '¿Paladín? ¿Has estado tomando pociones raras del boticario o algo así? He oído rumores de que un destacamento elfo ha visitado a la Dama Oscura en Entrañas. Puede que alguno de ellos sepa algo.' WHERE `entry` = 10108;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tu ayuda ha sido inestimable, $Gamigo:amiga;.$B$BTe aseguro que la expedición Cenarion hará lo que pueda para luchar contra cualquier cristal máculo que caiga de los cielos enviado por Illidan.' WHERE `entry` = 10109;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde aquí, la torre de entrada, puedes ir en tres direcciones. Las escaleras que hay tras de mí conducen al gran salón de baile, donde ahora mismo los invitados se entretienen. Estoy seguro de que hueles el establo, que está cerca, y la entrada a las Alcobas de los Sirvientes queda al lado de la puerta de la torre de entrada.' WHERE `entry` = 10115;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las malditas arañas, los murciélagos, que chillan sin parar y esas malditas bestias demoníacas han tomado residencia en las Alcobas de los Sirvientes. Ya no es seguro pasearse, especialmente cuando los grandes salen de su escondite.' WHERE `entry` = 10118;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, sí, personalmente nunca los he visto, pero a veces los oigo de lejos. Ten cautela si te aventuras a entrar.' WHERE `entry` = 10119;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, ser de carne. Soy Navuud, jefe de investigación de la Avanzada del Protectorado.' WHERE `entry` = 10152;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Dejar a Kael\'thas fue lo mejor que pudimos hacer. Sus seguidores están demasiado ciegos o demasiado asustados para darse cuenta de adónde les está conduciendo.$B$BSin embargo, nosotros no.' WHERE `entry` = 10153;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Están aquellos que intentan mentir y tergiversar la realidad. Los profetas, por otra parte, llevan la verdad a la luz.$B$BEl fin de esta biblioteca, $N, es convertir a todo el mundo en profeta.' WHERE `entry` = 10178;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entre los etéreos, hay quienes tratan con carne y con seres de carne; son conocidos como los tratantes de carne.' WHERE `entry` = 10179;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Buscas algo en nuestras bellas tierras?', `text0_1_loc7` = '¿Buscas algo en nuestras bellas tierras?' WHERE `entry` = 10180;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La maestra del cielo Anochecer está fuera de la Puerta Magister cerca de Lunargenta. Se ocupa de los dracohalcones.', `text0_1_loc7` = 'La maestra del cielo Anochecer está fuera de la Puerta Magister cerca de Lunargenta. Se ocupa de los dracohalcones.' WHERE `entry` = 10181;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si quieres registrar una hermandad, te sugiero que busques en Lunargenta.', `text0_1_loc7` = 'Si quieres registrar una hermandad, te sugiero que busques en Lunargenta.' WHERE `entry` = 10182;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La posada de Delaniel está en la esquina suroriental de la Plaza Alalcón. Buen vino, buena comida, camas limpias... ¿Qué más se puede pedir?', `text0_1_loc7` = 'La posada de Delaniel está en la esquina suroriental de la Plaza Alalcón. Buen vino, buena comida, camas limpias... ¿Qué más se puede pedir?' WHERE `entry` = 10183;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Algunos forestales son incapaces de cuidar de su material. Lo mejor es dejarlo en algún sitio en la ciudad. Anathos se ocupa de esas cosas. Vive y trabaja en la Plaza Alalcón, al lado de la posada.', `text0_1_loc7` = 'Algunos forestales son incapaces de cuidar de su material. Lo mejor es dejarlo en algún sitio en la ciudad. Anathos se ocupa de esas cosas. Vive y trabaja en la Plaza Alalcón, al lado de la posada.' WHERE `entry` = 10184;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se ha instalado en Lunargenta una vaca que dice ser druida. Lo más probable es que en estos momentos esté paciendo en nuestros jardines.', `text0_1_loc7` = 'Se ha instalado en Lunargenta una vaca que dice ser druida. Lo más probable es que en estos momentos esté paciendo en nuestros jardines.' WHERE `entry` = 10185;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hannovia se ocupa de adiestrar a los forestales. Se encuentra en el edificio nororiental de la Plaza Alalcón.', `text0_1_loc7` = 'Hannovia se ocupa de adiestrar a los forestales. Se encuentra en el edificio nororiental de la Plaza Alalcón.' WHERE `entry` = 10186;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Garridel enseña las artes Arcanas en la Plaza Alalcón. Se encuentra en la segunda planta de la posada.', `text0_1_loc7` = 'Garridel enseña las artes Arcanas en la Plaza Alalcón. Se encuentra en la segunda planta de la posada.' WHERE `entry` = 10187;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Caballero de sangre Noellene te espera en el edificio nororiental de la plaza.', `text0_1_loc7` = 'La Caballero de sangre Noellene te espera en el edificio nororiental de la plaza.' WHERE `entry` = 10189;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si buscas la sabiduría de un sacerdote, habla con Ponaris. Está con los demás en la segunda planta de la posada de Delaniel.', `text0_1_loc7` = 'Si buscas la sabiduría de un sacerdote, habla con Ponaris. Está con los demás en la segunda planta de la posada de Delaniel.' WHERE `entry` = 10190;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay un edificio al noreste de la Plaza Alalcón. En él, es muy probable que encuentres a Tannaria. Su especialidad es permanecer oculta.', `text0_1_loc7` = 'Hay un edificio al noreste de la Plaza Alalcón. En él, es muy probable que encuentres a Tannaria. Su especialidad es permanecer oculta.' WHERE `entry` = 10191;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Celoenus es nuestro experto residente en invocación de demonios. Está en la posada de la Plaza Alalcón, en la segunda planta.', `text0_1_loc7` = 'Celoenus es nuestro experto residente en invocación de demonios. Está en la posada de la Plaza Alalcón, en la segunda planta.' WHERE `entry` = 10192;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Conozco a un alquimista local. Creo que se llama Sheynathren. La última vez que lo vi, se dirigía al Refugio de Saltheril. Seguramente para unirse a esas ridículas fiestas.', `text0_1_loc7` = 'Conozco a un alquimista local. Creo que se llama Sheynathren. La última vez que lo vi, se dirigía al Refugio de Saltheril. Seguramente para unirse a uno de esos ridículos grupos.' WHERE `entry` = 10193;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Errantes tienen un herrero en su retiro al este de Canción Eterna. Arathel podrá ayudarte.', `text0_1_loc7` = 'Los Errantes tienen un herrero en su retiro al este de Canción Eterna. Arathel podrá ayudarte.' WHERE `entry` = 10194;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Delaniel ha contratado a una cocinera excelente. Se llama Quarelestra. Seguro que puedes aprender mucho de ella. Está en la posada de la Plaza de Alalcón, claro.', `text0_1_loc7` = 'Delaniel ha contratado a una cocinera excelente. Se llama Quarelestra. Seguro que puedes aprender mucho de ella. Está en la posada de la Plaza de Alalcón, claro.' WHERE `entry` = 10195;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si buscas a Eredania la encantadora, ve a la pequeña fiesta de Saltheril. Recibió su invitación hace unos días.', `text0_1_loc7` = 'Si buscas a Eredania la encantadora, ve a la pequeña fiesta de Saltheril. Recibió su invitación hace unos días.' WHERE `entry` = 10196;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una profesión tan impredecible y peligrosa no puede desempeñarse en nuestros tranquilos bosques sin consecuencias desastrosas. Inténtalo en Lunargenta. Sus muros son lo suficientemente resistentes como para aguantar los impactos.', `text0_1_loc7` = 'Una profesión tan impredecible y peligrosa no puede desempeñarse en nuestros tranquilos bosques sin consecuencias desastrosas. Inténtalo en Lunargenta. Sus muros son lo suficientemente resistentes como para aguantar los impactos.' WHERE `entry` = 10197;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Para los que prefieran métodos más caseros de curar las heridas, Kanaria tiene un puesto en el interior de la posada de la Plaza Alalcón.', `text0_1_loc7` = 'Para los que prefieran métodos más caseros de curar las heridas, Kanaria tiene un puesto en el interior de la posada de la Plaza Alalcón.' WHERE `entry` = 10198;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay un pescador en nuestra gran ciudad. Está bien si te gusta pasar la tarde con repugnantes cosas resbaladizas y húmedas.', `text0_1_loc7` = 'Hay un pescador en nuestra gran ciudad. Está bien si te gusta pasar la tarde con repugnantes cosas resbaladizas y húmedas.' WHERE `entry` = 10199;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién mejor que el gran botánico Tyniarrel para los arreglos florales de una fiesta? Estoy seguro de que Saltheril se lo paga en vino.', `text0_1_loc7` = '¿Quién mejor que el gran botánico Tyniarrel para los arreglos florales de una fiesta? Estoy segura de que Saltheril se lo paga en vino.' WHERE `entry` = 10200;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ameer debe de estar preocupado por las actividades de El Etereum, pero yo debo concentrarme en la verdadera amenaza: Dimensius.' WHERE `entry` = 10202;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestra joyera residente, Aleinia, está en el establecimiento de Delaniel en la Plaza Alalcón. Puede que busque aprendiz.', `text0_1_loc7` = 'Nuestra joyera residente, Aleinia, está en el establecimiento de Delaniel en la Plaza Alalcón. Puede que busque aprendiz.' WHERE `entry` = 10203;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sathein cree que los pellejos de lince son perfectos para sus creaciones. Deberías hablar con él. Está con Mathreyn al suroeste de la Puerta Magister.', `text0_1_loc7` = 'Sathein cree que los pellejos de lince son perfectos para sus creaciones. Deberías hablar con él. Está con Mathreyn al suroeste de la Puerta Magister.' WHERE `entry` = 10204;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay mineros en Lunargenta. Con toda seguridad, estarán cerca de las apestosas forjas en las que funden lo que encuentran.', `text0_1_loc7` = 'Hay mineros en Lunargenta. Con toda seguridad, estarán cerca de las apestosas forjas en las que funden lo que encuentran.' WHERE `entry` = 10205;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mathreyn es competente en la técnica bárbara del desuello, si es lo que te interesa. Es el proveedor de Sathein. Los encontrarás a ambos al suroeste de la Puerta Magister.', `text0_1_loc7` = 'Mathreyn es competente en la técnica bárbara del desuello, si es lo que te interesa. Es el proveedor de Sathein. Los encontrarás a ambos al suroeste de la Puerta Magister.' WHERE `entry` = 10206;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La buena costurera está muy ocupada últimamente en el Refugio de Saltheril. Su habilidad con la aguja y el hilo es muy apreciada allí.', `text0_1_loc7` = 'La buena costurera está muy ocupada últimamente en el Refugio de Saltheril. Su habilidad con la aguja y el hilo es muy apreciada allí.' WHERE `entry` = 10207;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué buscas? Habla.', `text0_1_loc7` = '¿Qué buscas? Habla.' WHERE `entry` = 10208;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En otro tiempo y en otro lugar habría sido considerado como un cachorro. Y aquí estoy ahora; un luchador con experiencia y listo para morir por la gloria de los naaru.$B$BDiez de entre nosotros llegaron a Forja de Maná Ara. Los demás cayeron ante la Legión.' WHERE `entry` = 10209;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Habla rápido. No tengo tiempo que perder en charlas. Los Renegados son ya una fuerza considerable, pero todavía hay demasiadas cosas por hacer. Esta ciudad no se gestiona sola.$B$B¿Qué quieres?' WHERE `entry` = 10212;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tienes lo que hace falta para luchar en el Círculo de Lucha Bonvapor, $C. Deja de hacerme perder el tiempo.', `text0_1_loc7` = 'No tienes lo que hace falta para luchar en el Círculo de Lucha Bonvapor, $C. Deja de hacerme perder el tiempo.' WHERE `entry` = 10214;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Está bien, $C. ¿Crees que tienes lo que hay que tener para entrar en las arenas del Círculo de Lucha Bonvapor?', `text0_1_loc7` = 'Está bien, $C. ¿Crees que tienes lo que hay que tener para entrar en las arenas del Círculo de Lucha Bonvapor?' WHERE `entry` = 10215;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los maestros de batalla están reunidos en el Bancal del Guerrero.' WHERE `entry` = 10217;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los maestros de batalla están en la sala de guerra del Castillo de Ventormenta.', `text0_1_loc7` = 'Los maestros de batalla están en la sala de guerra del Castillo de Ventormenta.' WHERE `entry` = 10218;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ve al alto de los cazadores. Allí encontrarás a los maestros de batalla.', `text0_1_loc7` = 'Ve al alto de los cazadores. Allí encontrarás a los maestros de batalla.' WHERE `entry` = 10220;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontrarás a los maestros de batalla en el Bastión de los Valientes en el Valle del Honor.', `text0_1_loc7` = 'Encontrarás a los maestros de batalla en el Bastión de los Valientes en el Valle del Honor.' WHERE `entry` = 10221;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo encontrarás en el centro de cuidadores, en el Trono de los Naaru. Que la Luz sea contigo.', `text0_1_loc7` = 'Lo encontrarás en el centro de cuidadores, en el Trono de los Naaru. Que la Luz sea contigo.' WHERE `entry` = 10223;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ser de carne, soy el capitán Saeed del protectorado y estos soldados que están a mi lado son mis vengadores. Esperamos órdenes del ser de carne que destruyó el conducto del vacío. Cuando él lo ordene, nos adentraremos en la guarida de Dimensius y esperaremos para el ataque final.$B$BHa pasado demasiado tiempo... ¡El señor del vacío probará nuestras fuerzas combinadas!' WHERE `entry` = 10229;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El draco abisal te dirige una mirada de aprobación.' WHERE `entry` = 10230;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ya está, $N. ¡Atacamos cuando tú digas! ¡Da la orden y mis hombres y yo cargaremos!' WHERE `entry` = 10232;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al Ecodomo de la Tierra Media, $Gviajero:viajera;. Tengo que pedirte que tengas cuidado al tratar con ese goblin, Shauly. Es la única persona que conozca que usa la palabra \"colega\" como signo de puntuación.' WHERE `entry` = 10233;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Bastión Allerian está rodeado de enemigos por todas partes. Es hora de plantarles cara.' WHERE `entry` = 10234;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si vas a venir aquí fuera, haz algo útil, porque el acechasombras de ahí no está haciendo mucho.' WHERE `entry` = 10236;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Batalla del Monte Hyjal es uno de los acontecimientos más protegidos de esta línea temporal. Si algún intruso modificara su resultado, se reflejaría en todos los momentos de la historia dependientes.$B$BComo mano derecha del Aspecto, soy más sensible al flujo del tiempo que otros dragones de bronce. He sentido una perturbación en el tiempo de los acontecimientos de la Batalla del Monte Hyjal. Alguien o algo está intentando cambiar esa línea temporal y hay que impedirlo.' WHERE `entry` = 10237;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué buscas? Habla. No voy a venderte un elekk hasta que demuestres amistad y dedicación a mi pueblo.', `text0_1_loc7` = '¿Qué buscas? Habla. No voy a venderte un elekk hasta que demuestres amistad y dedicación a mi pueblo.' WHERE `entry` = 10239;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Nuestro trabajo en la Arboleda Viviente no debe ponerse en peligro. Nuestras propias vidas dependen de su crecimiento y su salud.' WHERE `entry` = 10245;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí fue donde Gul\'dan cortó el vínculo entre los orcos y los espíritus elementales. Su insaciable sed de poder no se conformaba con la aniquilación de los draenei. Tenía que destruir Draenor; arrasar la tierra y absorber su energía para usarla en la guerra.$B$BAhora todo lo que queda son jirones de su locura.$B$BMira el altar, $r. Esta tierra quedará marcada para siempre...' WHERE `entry` = 10250;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué te parece la guerra, $N? Tú recuerda por qué estamos aquí... Eso hace que luchar y morir sea mucho más fácil.' WHERE `entry` = 10253;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos buzones de correo en el Trono de los Naaru: uno al lado del banco y otro en el exterior de la casa de subastas.', `text0_1_loc7` = 'Hay dos buzones de correo en el Trono de los Naaru: uno al lado del banco y otro en el exterior de la casa de subastas.' WHERE `entry` = 10254;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Punta de Expedición es uno de los peores lugares donde te pueden destinar, pero es vital para la defensa de Bastión del Honor. La información que conseguimos aquí y los combates que libran nuestros soldados contra la Legión y los orcos de Zeth\'Gor son muy útiles para la campaña de Fuego Infernal de la Alianza.' WHERE `entry` = 10255;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegro de ver a $Gun:una; $R como tú ayudando para que Sylvanaar sea un lugar mejor. Hemos hecho mucho en muy poco tiempo, pero las fuerzas que nos rodean amenazan nuestra forma de vida.$B$BMe gustaría escuchar lo que tienes que decir. Habla, $C.' WHERE `entry` = 10264;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida; al Refugio Cenarion, $N. Tengo órdenes de ofrecerte material básico si lo necesitas.' WHERE `entry` = 10266;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como representante de la Liga de Expedicionarios, Bronwyn y yo estamos aquí por petición de la Alianza para estudiar estas montañas.$B$BEntre tú y yo..., cuando volvimos de Silithus necesitábamos unas vacaciones. Este parece un buen sitio para relajarse.' WHERE `entry` = 10270;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Puede que Borgrim tenga razón; es posible que un descanso nos venga bien.$B$BPero ahora que estamos aquí, estoy dispuesta a mancharme las manos y estudiar lo que puede ofrecernos esta tierra.' WHERE `entry` = 10271;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Hijos de Lothar llevan mucho tiempo luchando en una guerra que nunca termina. Es asombroso que nuestro equipo se haya conservado tan bien.' WHERE `entry` = 10275;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $gviajero:viajera; Soy Oronok. Siéntate al lado de mi fuego y caliéntate.$B$B¡Ten cuidado con las hambrientas criaturas que hay por debajo de nosotros!' WHERE `entry` = 10280;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Es un gato gigante con armadura, riendas y silla de montar. ¿No te parece ya lo suficientemente gracioso? ¿También quieres que hable?>' WHERE `entry` = 10290;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Solo un mago puede aprender la magia de los portales, $C.' WHERE `entry` = 10291;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Un mago... ¿Quieres aprender la magia de los portales?' WHERE `entry` = 10292;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Sociedad Real de Boticarios de Entrañas está dispuesta a ayudar a nuestros aliados orcos aquí, en Terrallende. Es una oportunidad de estrechar nuestros lazos y de encontrar nuevos especímenes y compuestos para nuestros estudios.$B$BCompuestos como... venenos.' WHERE `entry` = 10293;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Eh, no abras las jaulas!$B$BEstos ogros me tienen justo donde quiero.' WHERE `entry` = 10300;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tengo entendido que el polvo y el calor de la Península del Fuego Infernal son muy irritantes... No estoy seguro, pero confiaré en mis queridos y todavía vivos aliados.' WHERE `entry` = 10301;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Estos orcos deben de tener muchas ganas de hacer suyas estas tierras de Terrallende! Si te dijera cuánto pagan por mi lealtad, me matarías de la envidia.' WHERE `entry` = 10306;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si tienes dinero suficiente, puedo enseñarte a montar un halcón zancudo.' WHERE `entry` = 10308;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás $glisto:lista; para escuchar la historia de la Clave de Condenación, $N?' WHERE `entry` = 10312;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muy bien. Las palabras que buscas tienen un nombre, $N. Son la Clave de Condenación. Son las palabras pronunciadas por Gul\'dan para destruir este mundo. El mismo hechizo que cortó el último vínculo que unía a los orcos con los elementos de Draenor.$B$B<Oronok asiente con la cabeza.>$B$BSí, esa clave es lo que debes reconstruir y son sus palabras las que debes pronunciar.' WHERE `entry` = 10313;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Clave de Condenación permaneció en los aposentos privados de Gul\'dan en Karabor durante veinte años. Ah, claro... Tú no debes de saber lo que es Karabor, ¿verdad?$B$BEl Templo Oscuro...$B$BSí; ahora es propiedad de Illidan Tempestira.$B$BCuando Illidan tomó el control del Templo Oscuro, descubrió la clave. Se dio cuenta de que el daño a este mundo ya estaba hecho, pero pensó que sería mejor guardarla en un lugar seguro para usarlo en el futuro.' WHERE `entry` = 10314;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde El Altar de Condenación ves los espíritus de los condenados, arrodillados, orando. Lo que no ves, soy yo al lado de Gul\'dan.$B$B<Oronok asiente con la cabeza.>$B$BTe dije que en mi vida he sido muchas cosas, ¿verdad? Una de ellas era lugarteniente de Gul\'dan.$B$BHe hecho cosas terribles en mi vida. He matado a hombres, mujeres y niños. He bailado sobre la sangre derramada de draenei... pero...$B$B<Oronok se atraganta.>' WHERE `entry` = 10315;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nunca olvidaré ese día, $r. Nunca. Aunque hacía tiempo que había abandonado las vías del chamanismo para tomar las de la magia oscura, sentí cómo se cortó el vínculo. Un dolor muy profundo... No podría explicártelo con palabras.$B$BCuando se cortó el vínculo con los elementos, todos los orcos de Draenor cambiaron. Si alguien todavía dudaba del poder de las sombras, esas dudas desaparecieron con los ríos de lava que recorrían la tierra destrozada.' WHERE `entry` = 10316;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me sacudió una náusea irresistible. Tras el ritual, volví corriendo a ver a mis hijos. Recogí lo que pude y me fui con ellos. Nos escapamos a las colinas y nos escondimos. No teníamos elección...$B$BNo pasa un día sin que recuerde el corte. Hemos tardado veinte años en volver a conectar con los elementos. Han sido necesarios veinte años para que los elementos empezaran a curarse.' WHERE `entry` = 10317;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como dije, Illidan encontró la clave cuando tomó el Templo Oscuro. Dividió el tomo en tres partes y se lo entregó a sus esbirros para que lo protegieran.$B$BMis hijos... Mis hijos han crecido. Cuando supe lo que había hecho Illidan con el código, les pedí que fuesen a recuperarlo.$B$BEncuéntralos, $N. Encuéntralos y recupera la Clave de Condenación.' WHERE `entry` = 10318;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a Shattrath, sede de los Sha\'tar en Terrallende. ¿Necesitas información?', `text0_1_loc7` = '$GBienvenido:Bienvenida; a Shattrath, sede de los Sha\'tar en Terrallende. ¿Necesitas información?' WHERE `entry` = 10321;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El cuerpo sin vida de Ar\'tor permanece suspendido en el aire por la magia demoníaca. Siguen absorbiéndolo... incluso muerto.' WHERE `entry` = 10323;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He... muerto.$B$B¿Por qué sigo aquí? ¿No hay ningún terreno de caza en el que pueda pasar la eternidad?$B$BNo... Antes debo terminar mi tarea. Antes de dejar este mundo, debo recuperar la segunda parte de la Clave de Condenación.', `text0_1_loc7` = 'He... muerto.$B$B¿Por qué sigo aquí? ¿No hay ningún terreno de caza en el que pueda pasar la eternidad?$B$BNo... Antes debo terminar mi tarea. Antes de dejar este mundo, debo recuperar la segunda parte de la Clave de Condenación.' WHERE `entry` = 10327;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He pasado mi tiempo en las trincheras y muchos años entrenando a los cachorros. Me alegro de estar en casa. No importa en lo que se haya convertido, este es mi hogar.',  `text1_0_loc7` = 'Ocuparme de esas bestias intrépidas, que se interponen entre mis compañeros de clan y la muerte, es un honor.' WHERE `entry` = 10339;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Nuainn Alatormenta niega con la cabeza.>$B$BCon la Legión a un lado y los esbirros de Illidan al otro, vamos a necesitar todo lo posible para defender el bastión, ¡y lo haremos! Todo auténtico Martillo Salvaje preferiría morir a renunciar a nuestra tierra.' WHERE `entry` = 10349;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ponte $Gderecho:derecha;, $Gbruto:bruta;! Ahora estás bajo las órdenes de la Guardia de Kor\'kron.' WHERE `entry` = 10350;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pisa con suavidad, $R. Más allá de este puente se encuentra el infierno.' WHERE `entry` = 10356;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡La Estación de Toshley es la mejor! ¡La soledad y el suministro de fuentes de poder extrañas de Terrallende son un marco ideal para experimentos extremos!' WHERE `entry` = 10359;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has hablado con mi clon –quiero decir... mi gemelo?' WHERE `entry` = 10360;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No puedo creer que me hicieran trasladar mi transportador fuera de la ciudad! La mayoría de los gnomos afectados por el accidente recobraron algún tipo de forma humanoide a las pocas semanas, y debería haber sido un honor ser un participante de tan importante experimento gnómico!$B$B¡Además, necesitamos los huevos!' WHERE `entry` = 10363;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Gracias por sacarme de ahí, te debo una!',  `text1_0_loc7` = 'Ahora debería poder salir de aquí. ¡Gracias!', `text2_0_loc7` = '¡Este lugar es una locura! ¡Podría pasarme años aquí, investigando toda esta tecnología!' WHERE `entry` = 10373;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solo un pequeño libro de canciones que pensé que te gustaría tener. Contiene la letra de una canción conocida como El lamento de los Altonato; la que Lady Sylvanas conjuró del aire.' WHERE `entry` = 10378;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos bancos en Shattrath, ambos situados a lo largo del centro de la ciudad. Uno está dirigido por los draenei Aldor, y el otro por los elfos de sangre Arúspices.', `text0_1_loc7` = 'Hay dos bancos en Shattrath, ambos situados a lo largo del centro de la ciudad. Uno está dirigido por los draenei Aldor, y el otro por los elfos de sangre Arúspices.' WHERE `entry` = 10379;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El banco de los Aldor está situado al noroeste del centro de la ciudad.', `text0_1_loc7` = 'El banco de los Aldor está situado al noroeste del centro de la ciudad.' WHERE `entry` = 10380;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los maestros de guerra de Shattrath a menudo se congregan en los huecos de las escaleras entre la parte alta y baja de la ciudad. Los maestros de guerra de la Alianza se reúnen en un lugar, y los de la Horda en otro, con los maestros de batalla de arena entre ellos y en el extremo contrario.', `text0_1_loc7` = 'Los maestros de guerra de Shattrath a menudo se congregan en los huecos de las escaleras entre la parte alta y baja de la ciudad. Los maestros de guerra de la Alianza se reúnen en un lugar, y los de la Horda en otro, con los maestros de batalla de arena entre ellos y en el extremo contrario.' WHERE `entry` = 10388;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué tipo de instructor buscabas?', `text0_1_loc7` = '¿Qué tipo de instructor buscabas?' WHERE `entry` = 10391;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Taberna del Fin del Mundo se puede encontrar en la parte más oriental de la ciudad baja.', `text0_1_loc7` = 'La Taberna del Fin del Mundo se puede encontrar en la parte más oriental de la ciudad baja.' WHERE `entry` = 10394;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién osa interrumpir al Gran Comandante?' WHERE `entry` = 10401;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Escucho.' WHERE `entry` = 10405;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Oh? Por supuesto, tienes documentos que documentan esta solicitud.' WHERE `entry` = 10406;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Escúchame, peón. Tenemos un proceso para la entrega y custodia del fragmento de la Clave. El enviado entrega la misiva, que ha sido sellada por Lord Illidan. Sin misiva, no hay traslado.' WHERE `entry` = 10407;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Ruusk traga saliva.>$B$BYo... ¿Qué quieres decir?' WHERE `entry` = 10408;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espera. ¿Dónde vas? Necesitabas un traslado, ¿no es correcto? Pues acabemos con ello, ¿de acuerdo? ¿Zuluhed en Faucedraco? Así se hará. Haz saber a Lord Illidan que sus órdenes se llevaron a cabo con la entera colaboración de los eclipsianos.$B$B¡Enviaré a Ruul el Ensombrecedor para que entregue personalmente el fragmento!$B$BMe has oído bien - Ruul...' WHERE `entry` = 10409;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Con la ayuda del ingeniero Lapforge, de Gadgetzan, he construido un potente transportador gnómico aquí, en la Estación de Toshley! Ayudaría de buena gana a cualquiera ingeniero gnómico con la pericia suficiente a construir su propio localizador de transporte personal.' WHERE `entry` = 10410;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lucharemos cuando estés $gpreparado:preparada;, $N.' WHERE `entry` = 10421;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'DOMINO MÁS DE DOS IDIOMAS: ENÁNICO, GNOMÓTICO Y LA LENGUA COMÚN DE LOS HUMANOS. ¡PERO NO TENGO NI IDEA DE QUÉ ESTÁS DICIENDO!$B$B¡HABLA MÁS ALTO: NO TE OIGO CON EL CHIRRIDO DE MIS ENGRANAJES!' WHERE `entry` = 10422;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, sí... $gbienvenido:bienvenida; a mi estación.$B$BEntonces, ¿qué te parece? Es bastante ingenioso lo que hemos hecho aquí, ¿no crees?$B$BMe alegro de haberme librado de esos elfos estirados de Sylvanaar, de cualquier modo.$B$BBusca algo útil que hacer por aquí; hay muchos experimentos y todas esas cosas con las que ayudar.' WHERE `entry` = 10423;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Gloria a los naaru, $N. La Luz triunfará pronto sobre sus enemigos.' WHERE `entry` = 10424;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El enano parece haber sido descubierto y asesinado en medio de una misión de espionaje. Los guardas de la Forja Muerta han dejado el cadáver al aire libre, quizás como aviso para otros entrometidos en potencia.' WHERE `entry` = 10427;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El guardián Arcano parece mirar hacia ti, pero no dice nada.>' WHERE `entry` = 10430;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Soy un científico que estudia el tiempo y el sonido, así que no estoy muy seguro de cómo se supone que tengo que pensar o encontrar el tiempo para hacer nada de trabajo aquí, con todo este ruido!$B$B¿Por qué no van esos estúpidos a volarse a otra parte?$B$BAh, bueno, al menos tengo buen aspecto. Dime, $gmuchacho:preciosa;, ¿te he visto antes por aquí?' WHERE `entry` = 10444;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El cadáver del orco se ha apartado a un lado para no ensuciar el camino.' WHERE `entry` = 10446;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gran parte de mi pueblo me conoce como un traidor... como el perrito faldero de Illidan. Es mejor que lo sigan pensando por ahora.' WHERE `entry` = 10447;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todas las recompensas por servicios prestados son administradas por el Clamaguerras Sardon Cortecertero.$B$BNo colguéis facturas.' WHERE `entry` = 10448;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todas las recompensas por servicios prestados son administradas por Clamaguerras Birramorro.$B$BNo colguéis facturas.' WHERE `entry` = 10449;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$gBienvenido:Bienvenida; a la posada de la Estación de Toshley. ¿Qué puedo hacer por ti?' WHERE `entry` = 10450;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Te interesa aprender las complejidades de la joyería?' WHERE `entry` = 10451;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡La prospección es el método principal para encontrar piedras preciosas! Simplemente usa la habilidad de prospección (que se encuentra en la pestaña general de tu libro de hechizos) en un montón de cinco minerales para extraerle las piedras preciosas. ¡Cualquier tipo de mineral no precioso servirá! $B$BLos minerales de mejor calidad proporcionarán piedras preciosas más valiosas, así que encontrarás piedras preciosas de mayor calidad en el mineral de hierro que en el mineral de cobre.', `text0_1_loc7` = '¡La prospección es el método principal para encontrar piedras preciosas! Simplemente usa la habilidad de prospección (que se encuentra en la pestaña general de tu libro de hechizos) en un montón de cinco minerales para extraerle las piedras preciosas. ¡Cualquier tipo de mineral no precioso servirá! $B$BLos minerales de mejor calidad proporcionarán piedras preciosas más valiosas, así que encontrarás piedras preciosas de mayor calidad en el mineral de hierro que en el mineral de cobre.' WHERE `entry` = 10452;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué deseas saber acerca de la joyería?' WHERE `entry` = 10454;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hasta que no domines el nivel de aprendiz de joyería no volveremos a hablar.' WHERE `entry` = 10455;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Compartiré contigo mis conocimientos de joyería.' WHERE `entry` = 10456;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La creación de joyas y gemas es para lo que vivo. ¿Puedo compartir mi pasión contigo?', `text0_1_loc7` = 'La creación de joyas y gemas es para lo que vivo. ¿Puedo compartir mi pasión contigo?' WHERE `entry` = 10457;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Desearía tener más tiempo para enseñar a todos los que se dirigieron a mí, pero así no es como funcionan las cosas. Hasta que no hayas aprendido todo lo que Driann tiene que enseñarte, no puedo ayudarte.' WHERE `entry` = 10458;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Estoy muy ocupada, $R.$B$B¡Tenemos un problema terrible en nuestras manos del que debemos ocuparnos de inmediato!' WHERE `entry` = 10460;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El alcance de Illidan sobre esta tierra se deteriora. ¿Lo notas, $N?' WHERE `entry` = 10465;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Cuando corras con los Jinetes Férreos de Razak, aprenderás un par de cosas sobre seguir órdenes.$B$BLo primero es que luchas hasta la muerte. Lo segundo es ver lo primero.' WHERE `entry` = 10466;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Necesitas que te afilen la espada, o quizás un arma nueva? Más vale prevenir que curar...' WHERE `entry` = 10467;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si buscas un martillo, has venido al lugar apropiado.' WHERE `entry` = 10468;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$g Hijo : Jovencita;, no voy a mentirte, las cosas no van bien por aquí. ¡Estamos bajo ataques constantes de los bichos y nuestra misma forma de vida, nuestra libertad, está en juego!$B$B¿Vas a comportarte de forma responsable y ayudar?' WHERE `entry` = 10469;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Teron Sanguino? Sí... he oído hablar de él.' WHERE `entry` = 10470;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Cuando era joven me formé con dureza para convertirme en una anacoreta en el sacerdocio. Prácticamente no se había oído de las exarcas femeninas.$B$BHoy en día, el sexo importa poco – es su coraje y convicción lo que marca la diferencia.' WHERE `entry` = 10473;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si estás $Gpreparado:preparada; y deseas volver rápidamente a la corriente normal del tiempo, puedo transportarte a Andormu, en las Cavernas del Tiempo.' WHERE `entry` = 10474;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Dar forma al flujo del tiempo es una tarea muy difícil y bastante delicada, pero hoy has realizado una actuación admirable.' WHERE `entry` = 10475;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás aquí para ayudar con los disturbios?' WHERE `entry` = 10491;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como recompensa por sus años de servicio, Illidan permitió a Kael\'thas que le enviara cinco guerreros para instruirlos bajo su mando. Sería la primera vez que se permitía a elfos de sangre instruirse como cazademonios.$B$BLa instrucción fue brutal. Illidan hizo pasar a los guerreros de Kael por pruebas que vencerían a los cazademonios más expertos.$B$BTres de los elfos de Kael murieron durante la instrucción – otro se volvió loco.$B$BUno lo logró.' WHERE `entry` = 10492;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Su nombre era Varedis. Para sorpresa de Illidan, no solo tuvo éxito en los atroces ritos de paso que formaban parte de la instrucción; Varedis ya exhibía un mejor dominio de sus poderes que muchos cazademonios veteranos.$B$BIllidan presintió el potencial que había en Varedis y asignó a tres maestros cazademonios para que lo siguieran instruyendo en sus hábitos.' WHERE `entry` = 10493;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tras un año de instrucción bajo el mando de los tres mejores cazademonios de Illidan, Varedis los había superado.$B$BAl no estar ya satisfecho con el entrenamiento que podían ofrecerle, Varedis se infiltró en el Consejo de la Sombra, donde conoció la existencia de una reliquia poderosa conocida como el Libro de Nombres Viles. Leyó este libro en secreto, memorizando cada pasaje.' WHERE `entry` = 10494;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se rumorea que saber el verdadero nombre de un demonio proporciona cierto poder sobre ese demonio.$B$BSe dice que el Libro de Nombres Viles contiene el verdadero nombre de cada demonio que ha existido. Al memorizar los pasajes del libro, Varedis había encontrado una fuente de poder que rivalizaba con el acto de beber del cráneo de Gul\'dan del propio Illidan.$B$BPor petición de Illidan, Varedis dirige ahora el patio de armas de las Ruinas de Karabor – junto con los tres maestros que instruyeron a Varedis.' WHERE `entry` = 10495;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Vete, mortal! Tu calaña no es bienvenida aquí...' WHERE `entry` = 10496;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No es sorprendente que el potencial de poder como cazademonios de un elfo sea tan alto. Después de todo, los sin\'dorei no poseen la aversión natural a la magia demoníaca que tienen los elfos de la noche.$B$BSin embargo, no espero que muchos de ellos lo logren. La mayoría, incluido Varedis, buscan el poder en sí. Ese poder los consumirá, pues no les impulsa el fuego puro que arde en el interior del corazón de un auténtico cazademonios.' WHERE `entry` = 10497;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes alejar a un hombre de los hijos de Lothar, pero no puedes sacarle a los hijos de su interior.$B$B<El herrero se encoge de hombros.>$B$BEs difícil de explicar, pero no siento que deba estar en el Bastión del Honor ahora mismo, eso es todo.$B$BQuizás esté destinado a formar parte de algo más grande.' WHERE `entry` = 10500;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mumman ha establecido su humilde tienda cerca de la entrada principal de la ciudad, justo después de pasar por la posada. Es un cocinero excelente.', `text0_1_loc7` = 'Mumman ha establecido su humilde tienda cerca de la entrada principal de la ciudad, justo después de pasar por la posada. Es un cocinero excelente.' WHERE `entry` = 10501;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La sastrería de Keelen, en la esquina noroeste del Bazar, tiene un telar como el que describes.', `text0_1_loc7` = 'La sastrería de Keelen, en la esquina noroeste del Bazar, tiene un telar como el que describes.' WHERE `entry` = 10502;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los detalles de este anuncio exigen la ejecución de una loba enorme. Parece que tiene su guarida al sur de la Cresta Dentada, cerca de la fortaleza de la Horda.' WHERE `entry` = 10509;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las Montañas Filospada son un lugar de grandes oportunidades. Qué mala suerte que algunos consideren estar por encima de los demás en asuntos que se refieren a aprovecharse de tales oportunidades.$B$B¿Quién se cree que es la Expedición de Cenarion para decirnos lo que podemos o no podemos hacer aquí?' WHERE `entry` = 10532;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Negociaciones? ¡NEGOCIACIONES!$B$BPermíteme dejar totalmente clara nuestra postura. Somos un pueblo libre, capaz de hacer lo que queramos y movernos como nos plazca. La Expedición de Cenarion no es dueña de esta cadena montañosa.$B$B¡No habrá negociaciones! Ahora vuelve con los druidas y diles que seguiremos haciendo lo que nos plazca.' WHERE `entry` = 10533;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos venido a la Foresta Ruuan para ver si podemos restablecer el equilibrio de la naturaleza en estas montañas.$B$BSin embargo, la intromisión de los arakkoa y el Culto Vermis está dificultando la tarea.$B$BAfortunadamente, hay gente como tú y Samia, dispuesta a echarnos una mano.' WHERE `entry` = 10534;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Detecto una carga en tu región paranarbulátrica! Debes de haber utilizado el Cefirium Capacitorium últimamente – no puedes volver a usarlo hasta que se agote la carga...' WHERE `entry` = 10537;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Estoy aquí a instancias de mi maestro, el barón Sablecrín, para ayudar a la Expedición de Cenarion con sus problemas con el Culto Vermis.$B$BSoy bastante experta en estas cosas.' WHERE `entry` = 10538;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuaar, ¿te encuentras bien? Hoy no pareces tú.' WHERE `entry` = 10539;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cierto, la reunión. Vayamos al grano, entonces.$B$BEl hecho es que la madera y el ganado que se está reuniendo de la Foresta Ruuan, de lo que tú eres responsable, ha disminuido sobremanera.$B$B¡Necesitamos esos recursos, pero has permitido que los druidas de la Expedición de Cenarion se interpongan en nuestro camino!' WHERE `entry` = 10541;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No quiero excusas, exijo resultados!$B$B¿Crees que lo estamos haciendo aquí es una broma? Si no lo hacemos bien, no solo vendrán los llamados bienhechores, sino que también se nos considerará indignos para la elevación en las filas de Alanegra.$B$B¿Es eso lo que quieres?' WHERE `entry` = 10542;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Al menos podrías intentar sonar algo convincente.$B$BMira, Nuaar, no iba a decirte esto, pero quizás sea lo mejor, porque también va a involucrarte a ti. Maxnar planea un ataque total a los druidas de la Foresta Ruuan. Y pretende aniquilarlos.$B$BYa es lo malo que hayamos estado luchando contra los ogros Peña\'mok, así que no podemos permitirnos tener que encargarnos de otro frente. He organizado una tregua temporal con los arakkoa.$B$BBueno, ¿qué opinas?' WHERE `entry` = 10543;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estamos organizando los preparativos definitivos mientras hablamos. El resto dependerá de con qué rapidez puedas organizar tus tropas en la Foresta Ruuan.$B$B¿Crees que puedes ocuparte de eso y tenerlo hecho rápidamente?' WHERE `entry` = 10544;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Esa es la actitud!$B$BMe alegro de que hayamos tenido esta breve reunión. Me siento mucho mejor con respecto al ataque ahora. Como líderes como tú en el frente, ¿cómo vamos a perder?$B$BBueno, ya tienes tus órdenes de marcha. ¡Ahora vuelve a la Foresta Ruuan y haz que suceda!' WHERE `entry` = 10545;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Karazhan no fue siempre tan... siniestro. El oscurecimiento de Karazhan llegó solo con la muerte de Medivh. O con lo que creemos que fue la muerte de Medivh.' WHERE `entry` = 10546;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Has llegado hasta aquí, bruto. Ya no puedes volver atrás.' WHERE `entry` = 10549;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, sobrestante. ¿$gPreparado:Preparada; para el ataque?', `text0_1_loc7` = 'Hola, sobrestante. ¿$gPreparado:Preparada; para el ataque?', `text1_0_loc7` = '¿Hay algo que necesite, señor?', `text1_1_loc7` = '¿Hay algo que necesite, señor?', `text2_0_loc7` = 'No tienes buen aspecto, sobrestante.', `text2_1_loc7` = 'No tienes buen aspecto, sobrestante.', `text3_0_loc7` = 'He oído que Kolphis Rangoscuro te ha estado buscando.', `text3_1_loc7` = 'He oído que Kolphis Rangoscuro te ha estado buscando.' WHERE `entry` = 10553;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Esos druidas han explotado una tremenda fuente de energía! Se pueden convertir en animales... proyectar relámpagos y luz lunar intensa... ¡Debo descubrir cómo lo hacen!$B$BQuizás llevan cargas bajo la toga...' WHERE `entry` = 10554;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El arakkoa te dedica una mirada fija y encantada, y alterna entre mirarte y mirar a través de ti.>$B$B¿Qué han provocado nuestros esfuerzos, aparte de una abominación mayor que la que intentábamos evitar?' WHERE `entry` = 10556;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las células de maná que hay en el interior del Ojo de la Tormenta son muy valiosas. ¡No podemos permitirnos perderlas y que los draenei las encuentren! ¡Eh, tú! ¡Haz algo útil y ayúdanos en la lucha!', `text0_1_loc7` = 'Las celdas de maná que hay en el interior del Ojo de la Tormenta mana son muy valiosas. ¡No podemos permitirnos perderlas y que los draenei las encuentren! ¡Eh, tú! ¡Haz algo útil y ayúdanos en la lucha!' WHERE `entry` = 10559;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este vuelo te lleva al punto remoto de Cresta Canto. ¡Muy peligroso!$B$BPor esto, voy a tener que necesitar una renuncia firmada...' WHERE `entry` = 10561;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Entonces, qué es? ¿No ves que soy un enano ocupado?' WHERE `entry` = 10563;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay un viejo dicho, \"Dale un pescado a un hombre; lo has alimentado un día. Enseña a pescar a un hombre y lo habrás alimentado toda una vida.\" Por supuesto, querrás cocinar ese pescado, y ahí es donde entro yo.' WHERE `entry` = 10568;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola, $n! ¿Cómo van tus aventuras? Mis experimentos progresan bien...$B$B¡Asegúrate de volver conmigo más adelante! ¡Quizás te tenga más oportunidades de probar vuelos!' WHERE `entry` = 10569;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Fuerza y honor, $C. Necesitamos soldados con ganas de aprender como tú en el Valle de Alterac; ¡el territorio de los Lobo Gélido debe ser defendido! ¿Volverás a Azeroth para prestar tu ayuda?', `text0_1_loc7` = 'Fuerza y honor, $C. Necesitamos soldados con ganas de aprender como tú en el Valle de Alterac; ¡el territorio de los Lobo Gélido debe ser defendido! ¿Volverás a Azeroth para prestar tu ayuda?' WHERE `entry` = 10570;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Mi aversión a las interrupciones solo se ve eclipsada por mi odio hacia Gruul el Asesino de Dragones y sus siete hijos!' WHERE `entry` = 10603;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Debes... liberar... a mis hijos...' WHERE `entry` = 10605;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No lo pueden mantener arriba! Al final, sus alas cederán...' WHERE `entry` = 10606;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, $Gotro:otra; $C que quiere ser $Ggladiador:gladiadora;, ¿eh? ¡Bien!$B$BVendo los estatutos de equipos de arena que necesitarás para participar en los combates de clasificación del Círculo de Lucha de Bonvapor.', `text0_1_loc7` = 'Ah, $Gotro:otra; $C que quiere ser $Ggladiador:gladiadora;, ¿eh? ¡Bien!$B$BVendo los estatutos de equipos de arena que necesitarás para participar en los combates de clasificación del Círculo de Lucha de Bonvapor.' WHERE `entry` = 10608;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡La Avanzada Rompeloma es un lugar genial en el que estar destinado! ¡Las aberraciones del Foso Sangrante están tan cerca que se puede oler su infecta sangre! ¿Qué mejor lugar para vengarse de un clan que ha traicionado a sus hermanos y ha hecho un pacto con demonios?' WHERE `entry` = 10610;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buentiempo y yo estamos aquí representando los intereses de Sylvanaar.$B$BLos druidas han sido más que acogedores, pero permitir que la Horda pase por aquí me pone los pelos de punta.' WHERE `entry` = 10612;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, mortal.' WHERE `entry` = 10613;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En los tramos orientales de los Campos de Ala Abisal se encuentra la Fortaleza Faucedraco. Es allí donde el cruel clan de orcos Faucedraco tortura a los dracos que capturan intentando doblegarlos a su voluntad. Muchos han perdido la esperanza y ceden a la voluntad de los Faucedraco. Los demás... se niegan a desembarcar por miedo a que las partidas de guerra Faucedraco los capturen y los lleven de vuelta a la fortaleza.' WHERE `entry` = 10614;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo... No tenemos ningún poder. Illidan encontró y entregó a los Faucedraco a su líder - Zuluhed. Este orco era el único responsable de la captura y encarcelamiento de Alexstrasza. ¿Cómo podríamos luchar contra tal poder? ¡Capturó a un Aspecto!$B$BY con igual facilidad, se llevó a mi compañero.' WHERE `entry` = 10615;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Neltharaku asiente.>$B$BKarynaku... está prisionera en lo alto de la Fortaleza Faucedraco. He intentado liberarla muchas veces. He fracasado todas ellas... Has luchado antes contra esas bestias, ¿no es cierto?' WHERE `entry` = 10616;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En este lugar hay misterios que nosotros los orcos estamos desperados por descubrir. Misterios sobre nuestro pasado, misterios sobre nuestro futuro y misterios que ofrecen fugaces vistazos de nuestro interior.$B$BMi esperanza es penetrar en esos misterios... ¿andarás ese camino conmigo?' WHERE `entry` = 10636;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay problemas en el bosque. ¡Debemos descubrir su origen antes de que sea demasiado tarde!' WHERE `entry` = 10637;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pronto echaré raíces y dormiré. Antes de descansar hay cosas que se deben hacer.' WHERE `entry` = 10640;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Éxito! Me he ajustado a la Ciudadela del Fuego Infernal y a la magia que hay en su interior. ¡Ah, tanta energía!$B$BHay innumerables orcos viles en aquel lugar... ¡pero eso no es todo! También siento poder demoníaco dentro de esos muros...$B$BSi te abres, podré comprender tu mente -- brevemente --, tomar pluma y pergamino y registrar mis hallazgos para ti...' WHERE `entry` = 10643;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo instruirte en encantamiento. ¿Te interesa?', `text0_1_loc7` = 'Puedo instruirte en encantamiento. ¿Te interesa?' WHERE `entry` = 10651;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo instruirte en sastrería. ¿Te interesa?', `text0_1_loc7` = 'Puedo instruirte en sastrería. ¿Te interesa?' WHERE `entry` = 10652;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo instruirte en peletería. ¿Te interesa?', `text0_1_loc7` = 'Puedo instruirte en peletería. ¿Te interesa?' WHERE `entry` = 10653;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo instruirte en herrería. ¿Te interesa?', `text0_1_loc7` = 'Puedo instruirte en herrería. ¿Te interesa?' WHERE `entry` = 10654;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegro de volver a verte, $gamigo:amiga;.' WHERE `entry` = 10655;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es agradable caminar y estirar las piernas. ¿Hay algo en lo que pueda ayudarte, $c?' WHERE `entry` = 10657;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Si se me permite el atrevimiento, te recuerdo que, mientras me ajusto a la magia de la Ciudadela del Fuego Infernal, sin duda habrá celadores del Fuego Infernal pisándome los talones.$B$BAsí que te pido que seas un miembro honesto de tu Horda y actúes como el héroe... ¡y me protejas!' WHERE `entry` = 10659;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Auchindoun es el hogar de los espíritus de mis ancestros. Quizás no lo parezcamos, pero todos los Tábidos fuimos Draenei antaño... nuestros espíritus son los mismos.' WHERE `entry` = 10662;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Forja Muerta está dirigida por el Consejo de la Sombra, bajo el ojo vigilante de la Legión.' WHERE `entry` = 10664;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Abajo se encuentra la Forja Muerta, que tiene un nombre acertado. Si se le dejara hacer lo que pretende, el Consejo de la Sombra suministraría a la Legión con una multitud interminable de infernales.' WHERE `entry` = 10666;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sabemos que los cultores del Culto Vermis se congregan al otro lado del túnel. Resistiremos aquí.' WHERE `entry` = 10667;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida;, $N. Tengo pericia en el arte de trabajar con tela lunar primigenia. Los sastres interesados en ser especialistas en tejido lunar me piden a menudo que les enseñe nuestras artes.' WHERE `entry` = 10669;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Encantada de conocerte! Soy Gidge, la experta en sastrería con fuego de hechizo residente en Shattrath. No es que haya competencia, pero si te interesa aprender a ser $Gun sastre:una sastra; fuego de hechizo y no te has decidido por una especialidad, puedo ayudarte.' WHERE `entry` = 10671;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si buscas patrones o conocimientos sobre la sastrería con tejido de sombra, te ayudaré con mucho gusto.' WHERE `entry` = 10673;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Temen mis palabras. Hacen que sea un ejemplo. Una parodia del único dios verdadero, Neptulon.$B$B<Skar\'this escupe.>$B$BSe acerca el jusssto castigo, mortal. La gloriosa mano del Cazamareas se extenderá hasta los no creyentes como una guadaña contra una nueva cosecha.' WHERE `entry` = 10676;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estas ruinas están marcadas por una magia terrible. Debemos hacer lo posible por curar esta gran herida.' WHERE `entry` = 10677;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Puerta de la Muerte es un portal que se abre al mundo que la Legión Ardiente utiliza para alimentar a sus canes manáfagos y a otros horrores de tipo canino. Cuando veas a alguna de esas criaturas, de ahí es de donde proviene, y primero pasa por aquí.$B$BÚltimamente nos hemos dado cuenta de que la Legión Ardiente ha aumentado sus convocatorias de estas criaturas a través del portal.$B$B¡Debes cerrar la Puerta de la Muerte antes de que se convierta en una marea inexorable y todo esté perdido!', `text0_1_loc7` = 'La Puerta de la Muerte es un portal que se abre al mundo que la Legión Ardiente utiliza para alimentar a sus canes manáfagos y a otros horrores de tipo canino. Cuando veas a alguna de esas criaturas, de ahí es de donde proviene, y primero pasa por aquí.$B$BÚltimamente nos hemos dado cuenta de que la Legión Ardiente ha aumentado sus convocatorias de estas criaturas a través del portal.$B$B¡Debes cerrar la Puerta de la Muerte antes de que se convierta en una marea inexorable y todo esté perdido!' WHERE `entry` = 10679;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vine a esta tierra para purificar espíritus purificados, pero, después del ataque, me temo que mi misión ha fracasado. Se han perdido tantos... por favor, debes ayudarlos.' WHERE `entry` = 10680;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Que la Luz te guíe, $N.' WHERE `entry` = 10682;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Llevo a cabo mis servicios al Bastión del Honor con gran orgullo.' WHERE `entry` = 10683;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Cuando establecí mi academia para pícaros en Shattrath, no estaba tan abarrotada. Entonces, los refugiados comenzaron a venir en avalancha y los naaru necesitaron todo edificio disponible para alojarlos.$B$BHe cerrado las puertas de mi academia por ahora, pero no es permanente. Buscaré un nuevo lugar en el que establecerme.$B$BEntonces, una nueva generación de pícaros aprenderá cosas tan básicas como gubia y sonreír, los combates de saqueos, y culpar a otro por romper polimorfos. Y quién podría olvidar la regla principal de la picardía: los pícaros de verdad no amagan.', `text1_1_loc7` = 'El truco para utilizar la evasión es darse cuenta de que los mejores quince segundos de tu vida a menudo son los últimos.', `text2_1_loc7` = 'Espadas, garras, dagas, por supuesto – he instruido a pícaros en el uso de todas ellas. Pero las mazas no las pienso tocar, ni instruiré a ningún pícaro al que se le meta en la cabeza blandir una.$B$BNo hay nada sigiloso en una maza. Si vas por ese camino habrá una muerte ruidosa y una limpieza larga.', `text3_1_loc7` = 'Mis primeras clases siempre están llenas de chicos con ojos brillantes. Pero pocos aprueban la primera prueba.$B$BMe he cansado de gritar \"cuando grito desaparecer, tenéis que desaparecer\". No escuchan nunca. Ahora, la prueba simplemente mata al inepto.', `text4_1_loc7` = 'Nunca salgo de casa sin suministro abundante de partículas explosivas, especialmente cuando trabajo con otros. Te garantizo que a alguien se le acabará cada vez.$B$BCuando las opciones son repartir material o dejar que el pobre inocente muera, ayudo... casi siempre.' WHERE `entry` = 10686;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Oh, no! ¡Mi Fei Fei nunca cogería las cosas de otra persona! Es un buen perro... solo necesita un poco de atención a veces. ¡Si le das un regalito, verás!' WHERE `entry` = 10689;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solía divertirme aceptando retos de cualquiera que me los ofreciera, pero hace tiempo que me he cansado de victorias tan fáciles.$B$BPor ahora, me estoy tomando algo de tiempo para que mi negocio de componentes despegue y para escribir un libro sobre mis experiencias en Martillo Crepuscular.' WHERE `entry` = 10691;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos mercaderes de gemas residentes: uno trabaja estrechamente con los Arúspices, y el otro es leal a los Aldor. ¿A quién buscas?', `text0_1_loc7` = 'Hay dos mercaderes de gemas residentes: uno trabaja estrechamente con los Arúspices, y el otro es leal a los Aldor. ¿A quién buscas?' WHERE `entry` = 10697;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No te voy a instruir hasta que seas $Gexaltado:exaltada; a ojos de los draenei.', `text0_1_loc7` = 'No te voy a instruir hasta que seas $Gexaltado:exaltada; a ojos de los draenei.' WHERE `entry` = 10705;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Oh, gracias! ¡La negrura que había en mi interior se ha disipado! Veo de nuevo... ¡y veo la cara de quien me ha salvado!' WHERE `entry` = 10706;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Vete, $C! ¡Déjanos en paz o entraremos en ti y te mostraremos lo que es la auténtica desesperación!' WHERE `entry` = 10707;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No te preocupes. ¡El Puesto Devastación es de lo más sólido! Pero, por si acaso, prepárate para saltar sobre un grifo si la plataforma comienza a romperse...' WHERE `entry` = 10717;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Deseas hablar conmigo, $R?' WHERE `entry` = 10720;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Morthis observa el cielo.>$B$BEn parte, envidio la libertad que disfrutan Grajo y Corvax. En efecto es una gran bendición estar libre de ataduras terrenales.$B$BEl futuro me verá volar de nuevo, pero antes me debo ocupar de una tarea.' WHERE `entry` = 10735;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El arte de las ballestas te lo puede enseñar Woo Ping, el maestro de armas humano del Arsenal de Weller en Ventormenta; Handiir, el maestro de armas draenei de la Grada de los Mercaderes dentro de El Exodar; o Bixi Tambaleapié, el maestro de armas gnomo que está dentro de Armas Mil Maderas en Forjaz.', `text0_1_loc7` = 'El arte de las ballestas te lo puede enseñar Woo Ping, el maestro de armas humano del Arsenal de Weller en Ventormenta; Handiir, el maestro de armas draenei de la Grada de los Mercaderes dentro de El Exodar; o Bixi Tambaleapié, el maestro de armas gnomo que está dentro de Armas Mil Maderas en Forjaz.' WHERE `entry` = 10747;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ilyenia Fuegolunar, elfa de la noche maestra de armas que reside en el Bancal del Guerrero en Darnassus podrá entrenarte en el uso de los arcos.', `text0_1_loc7` = 'Ilyenia Fuegolunar, elfa de la noche maestra de armas que reside en el Bancal del Guerrero en Darnassus podrá entrenarte en el uso de los arcos.' WHERE `entry` = 10748;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ilyenia Fuegolunar, elfa de la noche maestra de armas del Bancal del Guerrero en Darnassus, o Buliwyf, el enano maestro de armas de Armas Mil Maderas en Forjaz podrán entrenarte en el uso de las armas de puño.', `text0_1_loc7` = 'Ilyenia Fuegolunar, elfa de la noche maestra de armas del Bancal del Guerrero en Darnassus, o Buliwyf, el enano maestro de armas de Armas Mil Maderas en Forjaz podrán entrenarte en el uso de las armas de puño.' WHERE `entry` = 10750;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si quieres hablar de armas de fuego, debes dirigirte a Buliwyf. Lo encontrarás en Armas Mil Maderas en Forjaz.', `text0_1_loc7` = 'Si quieres hablar de armas de fuego, debes dirigirte a Buliwyf. Lo encontrarás en Armas Mil Maderas en Forjaz.' WHERE `entry` = 10751;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estoy a quí a instancias del príncipe-nexo Haramad. Están ocurriendo cosas extrañas en La Flecha de la Tormenta.' WHERE `entry` = 10752;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El maestro de armas enano Buliwyf te puede entrenar tanto con hachas de una mano como de dos manos. Lo encontrarás en Armas Mil Maderas en Forjaz.', `text0_1_loc7` = 'El maestro de armas enano Buliwyf te puede entrenar tanto con hachas de una mano como de dos manos. Lo encontrarás en Armas Mil Maderas en Forjaz.' WHERE `entry` = 10754;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tanto Handiir como Buliwyf pueden enseñarte a usar una maza correctamente, sea del tamaño que sea. Encontrarás a Buliwyf dentro de Armas Mil Maderas en Forjaz. Handiir pasa los días en El Exodar formando a draenei en la Grada de los Mercaderes.', `text0_1_loc7` = 'Tanto Handiir como Buliwyf pueden enseñarte a usar una maza correctamente, sea del tamaño que sea. Encontrarás a Buliwyf dentro de Armas Mil Maderas en Forjaz. Handiir pasa los días en El Exodar formando a draenei en la Grada de los Mercaderes.' WHERE `entry` = 10755;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Woo Ping es el maestro de las armas de asta. Entrena a sus pupilos en el Arsenal de Weller en Ventormenta.', `text0_1_loc7` = 'Woo Ping es el maestro de las armas de asta. Entrena a sus pupilos en el Arsenal de Weller en Ventormenta.' WHERE `entry` = 10756;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos maestros de la espada: Handiir y Woo Ping. El primero se encuentra en la Grada de los Mercaderes en El Exodar, y el segundo en el Arsenal de Weller en Ventormenta.', `text0_1_loc7` = 'Hay dos maestros de la espada: Handiir y Woo Ping. El primero se encuentra en la Grada de los Mercaderes en El Exodar, y el segundo en el Arsenal de Weller en Ventormenta.' WHERE `entry` = 10758;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tanto Ilyenia Fuegolunar en el Bancal del Guerrero en Darnassus como Bixi Tambaleapié de Armas Mil Maderas en Forjaz te podrán enseñar a lanzar las armas arrojadizas correctamente.', `text0_1_loc7` = 'Tanto Ilyenia Fuegolunar en el Bancal del Guerrero en Darnassus como Bixi Tambaleapié de Armas Mil Maderas en Forjaz te podrán enseñar a lanzar las armas arrojadizas correctamente.' WHERE `entry` = 10759;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tanto Hanashi como Sayoc podrán entrenarte en el uso de las hachas de una y dos manos. Se encuentran en el Valle del Honor en Orgrimmar.', `text0_1_loc7` = 'Tanto Hanashi como Sayoc podrán entrenarte en el uso de las hachas de una y dos manos. Se encuentran en el Valle del Honor en Orgrimmar.' WHERE `entry` = 10770;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Archibald es el maestro de la ballesta. Es el maestro de armas de Entrañas y se encuentra en el Barrio de la Guerra.', `text0_1_loc7` = 'Archibald es el maestro de la ballesta. Es el maestro de armas de Entrañas y se encuentra en el Barrio de la Guerra.' WHERE `entry` = 10772;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sayoc, el maestro de armas de Orgrimmar te enseñará cómo usar las armas de puño. Pasa sus días en el Valle del Honor.', `text0_1_loc7` = 'Sayoc, el maestro de armas de Orgrimmar te enseñará cómo usar las armas de puño. Pasa sus días en el Valle del Honor.' WHERE `entry` = 10774;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestro instructor de armas de fuego es Ansekhwa, el maestro de armas tauren que se encuentran en la elevación media central de Cima del Trueno.', `text0_1_loc7` = 'Nuestro instructor de armas de fuego es Ansekhwa, el maestro de armas tauren que se encuentran en la elevación media central de Cima del Trueno.' WHERE `entry` = 10775;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ansekhwa enseña sobre las mazas y se encuentra en la elevación menor central de Cima del Trueno.', `text0_1_loc7` = 'Ansekhwa enseña sobre las mazas y se encuentra en la elevación menor central de Cima del Trueno.' WHERE `entry` = 10776;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tanto Hanashi, el maestro de armas trol del Valle del Honor de Orgrimmar como Ansekhwa, el maestro de armas tauren que entrena en la elevación menor central de Cima del Trueno podrán formarte en el arte de los bastones.', `text0_1_loc7` = 'Tanto Hanashi, el maestro de armas trol del Valle del Honor de Orgrimmar como Ansekhwa, el maestro de armas tauren que entrena en la elevación menor central de Cima del Trueno podrán formarte en el arte de los bastones.' WHERE `entry` = 10778;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ansekhwa enseña de mazas y está en la elevación menor central de Cima del Trueno.', `text0_1_loc7` = 'Ansekhwa enseña de mazas y está en la elevación menor central de Cima del Trueno.' WHERE `entry` = 10785;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si no estás aquí para entrenar, vete y déjame trabajar.' WHERE `entry` = 10789;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No entiendo por qué quieres aprender otra cosa... pero supongo que puedo indicarte adónde ir. ¿Qué tipo de entrenamiento te interesa?' WHERE `entry` = 10790;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ven y yo te mostraré cómo montar las mejores bestias que jamás hayas visto. Tú... tendrás suficiente oro para pagar por mis servicios... Supongo.', `text0_1_loc7` = 'Ven y yo te mostraré cómo montar las mejores bestias que jamás hayas visto. Tú... tendrás suficiente oro para pagar por mis servicios... Supongo.' WHERE `entry` = 10792;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Bienvenido a Soto Eterno, $c. Timeon y yo llevamos algún tiempo estudiando a los arakkoa de las Montañas Filospada y hemos encontrado algunas diferencias alarmantes entre ellos y sus primos de Terokkar.' WHERE `entry` = 10793;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Grok busca ayuda. ¡$GPequeño:Pequeña; $R ve a las montañas y ayuda al Machacasangre!' WHERE `entry` = 10797;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Los Hijos de Gruul han de ser derrotados si pretendemos librarnos de nuestros grilletes y volver a vivir como ogros libres! $B$BDebemos trabajar silenciosamente pero rápido si queremos que nuestra resistencia clandestina tenga alguna posibilidad de éxito.' WHERE `entry` = 10800;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Temo que solo sea posible salvar al Coronel Jules mediante un peligroso ritual: un exorcismo.' WHERE `entry` = 10801;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué es lo primero que te viene en mente cuando piensas en los Martillo Salvaje?¡Jinetes de Grifos si estás en tus cabales! Sombraluna no es Pico Nidal, pero también tenemos buenas mercancías para ti, eso es, buenas mercancías. ¡Echa un vistazo a mis bellezas a ver si alguna te llama la atención!' WHERE `entry` = 10809;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Parece que sabes manejarte con una bestia que corre por la tierra pero si quieres mejorar, necesitarás entrenamiento y dedicación. Y oro. Tienes oro, ¿verdad?' WHERE `entry` = 10817;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Son buenas bestias, ¿verdad? Los dracoleones que criamos aquí están entrenados para soportar las condiciones climatológicas extremas y duras de Draenor. Verás que son fiables y robustos.' WHERE `entry` = 10818;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No me importa quién eres ni qué haces fuera de la Guardia del cielo, pero mientras estés aquí harás lo que se te diga.' WHERE `entry` = 10819;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Cuando sea mayor quiero ser curtidor!' WHERE `entry` = 10821;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si consigues completar una baraja de Tormentas, te recompensaremos con un naipe superior de la Luna Negra de la Cólera. Esto aumentará tus posibilidades de asestar un impacto crítico con hechizos o ataques físicos hasta que tú recibas uno.' WHERE `entry` = 10831;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si consigues completar una baraja de Locuras, te recompensaremos con un naipe superior de la Luna Negra de la Demencia. El poder de la demencia te llenará cada vez que mates a un enemigo.' WHERE `entry` = 10832;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si consigues completar una baraja de Bendiciones, te recompensaremos con un naipe superior de la Luna Negra de la Cruzada. Esto aumentará el daño que ocasiones con tus hechizos y ataques a medida que sigas luchando.' WHERE `entry` = 10833;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si consigues completar una baraja de Furias, te recompensaremos con un naipe superior de la Luna Negra de la Venganza. Mientras poseas este naipe cualquier jugador que ataque tendrá una posibilidad de sufrir Venganza Sagrada.' WHERE `entry` = 10834;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Shhh! Habla más bajo. Los bebés están durmiendo.' WHERE `entry` = 10837;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes encontrar al maestro Pyreanor entre los consejeros de Thrall en la fortaleza Grummash, en el Valle de la Sabiduría.', `text0_1_loc7` = 'Puedes encontrar al maestro Pyreanor entre los consejeros de Thrall en la fortaleza Grummash, en el Valle de la Sabiduría.' WHERE `entry` = 10843;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo enseñarte joyería. ¿Te interesa?', `text0_1_loc7` = 'Puedo enseñarte joyería. ¿Te interesa?' WHERE `entry` = 10846;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tú no sentir como en tu casa. ¿Quizá querer comer algo?' WHERE `entry` = 10848;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A veces esos detestables elfos de sangre vienen por aquí y necesitan algún reactivo inane o algún veneno. Tengo que mantener mis reservas de esos productos llenas todo el tiempo para que no le lleguen rumores a Lord Illidan de que los Faucedraco no están colaborando...$B$B<Rumpus traga con dificultad>$B$BNo queremos que pase eso...', `text0_1_loc7` = 'A veces esos detestables elfos de sangre vienen por aquí y necesitan algún reactivo inane o algún veneno. Tengo que mantener mis reservas de esos productos llenas todo el tiempo para que no le lleguen rumores a Lord Illidan de que los Faucedraco no están colaborando...$B$B<Rumpus traga con dificultad>$B$BNo queremos que pase eso...' WHERE `entry` = 10849;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<La luz que rodea a Xi\'ri parpadea en azul. El naaru te da la bienvenida como aliado en el campo de batalla.>' WHERE `entry` = 10851;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$G Hermano:Hermana;, a partir de ahora se te considera $g unido:unida; por la sangre al Ala Abisal. ¡El Ala Abisal al completo está detrás de ti ahora! ¡Ha llegado el momento de atacar al corazón de nuestros enemigos! Debemos reclamar nuestra herencia perdida.' WHERE `entry` = 10854;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debes haberte preguntado por qué hablo con tanta claridad para ser un ogro. Es un efecto secundario de los cristales que crecen en el bancal que rodea Ogri\'la. Los ogros de allí me han bendecido con esta vara, y su cristal ha bendecido mi mente.$B$BA través de tus continuas acciones arriba en Ogri\'la, espero poder llevar esta bendición a mis hermanos ogros, algún día. Te agradezco todo lo que has hecho y harás, mi $g rey: reina;.' WHERE `entry` = 10860;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Otro, eh? Nunca lo conseguirás...' WHERE `entry` = 10862;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Recuerda que solo puedes escoger un trabajo de recolección cada día. Además de eso, siempre buscamos cristales. Hasta el peón más insignificante puede traerlos al campamento.' WHERE `entry` = 10863;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tengo nada nuevo para ti, chico. Las labores manuales se te han quedado pequeñas...' WHERE `entry` = 10864;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Manos a la obra, $N. Te enseñaré lo que sé, pero sigue el ritmo.' WHERE `entry` = 10872;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Guarda silencio sobre lo que oigas y veas aquí, $r.' WHERE `entry` = 10877;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a la Sastrería Fiable de Keelen. Creo que mi tienda satisfará tus necesidades. Si deseas entrenar conmigo, solo tienes que pedirlo; será un placer enseñarte.' WHERE `entry` = 10886;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este montón de calaveras apesta a vileza. Temes que los arakkoa hayan añadido algo al montón recientemente.' WHERE `entry` = 10888;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me resultas familiar...' WHERE `entry` = 10892;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mis visiones están llenas de eventos que suceden por el mundo y de algunos que pueden llegar a suceder. Muchos de ellos son desalentadores, pero no debemos creer que no puedan cambiar.' WHERE `entry` = 10893;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si necesitas reactivos, has venido al lugar adecuado. No encontrarás una selección mejor en todo Shattrath.' WHERE `entry` = 10894;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El sobrestante jefe Limo limpia sus botas.>' WHERE `entry` = 10896;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Firme, soldado!' WHERE `entry` = 10901;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Te maldigo mil veces, $R! ¡Esta prisión no me retendrá durante mucho tiempo!' WHERE `entry` = 10903;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Piérdete antes de que te dé una paliza con mi botarang.' WHERE `entry` = 10904;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La forja de cristal vil puede usarse para transformar tus fragmentos de ápices en frascos inestables de la bestia. Estos frascos vuelven al que los bebe más ágil, fuerte y feliz.$B$BElige, abajo.' WHERE `entry` = 10910;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Al colocar los fragmentos de ápices en la forja de cristal vil y tirar de la palanca, el aparato hace un ruido de otro mundo mientras los convierte en polvo. Unos instantes después tu frasco sale de su \"boca\".>' WHERE `entry` = 10911;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La forja de cristal Bash\'ir puede usarse para transformar tus fragmentos de ápices en frascos inestables del hechicero. Estos frascos vuelven al que los bebe más ágil y feliz y aumentan la efectividad de sus hechizos de ataque y sanación.$B$BElige debajo.' WHERE `entry` = 10913;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Cuando colocas tus fragmentos de ápices en el hueco de la forja de cristal Bash\'ir y tiras de la palanca, el aparato, literalmente, los desintegra. Unos momentos después tu frasco aparece a tus pies.>' WHERE `entry` = 10914;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esperamos que puedas librarnos de nuestros dolores de cabeza. ¡Intenta tener diez hijos, los diez intentando ser algo para lo que no están destinados!$B$BAun así, como padre, hacemos lo que debemos.' WHERE `entry` = 10916;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Tienes hijos, $c?$B$BDeja que te digamos algo, creemos que éramos mucho más felices antes de venir a Ogri\'la y que los cristales nos hicieran más conscientes. Antes los habríamos golpeado en la cabeza para callarlos. Ahora, todo lo que hacen es darnos la lata con el Guardia del cielo Sha\'tari.$B$B¡La ignorancia es felicidad!' WHERE `entry` = 10917;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ya que eres relativamente nuevo en Ogri\'la, no has estado expuesto a los cristales el tiempo suficiente para obtener todos sus beneficios. Además, solo tienes una cabeza.$B$BSin embargo, nosotros, como puedes imaginar, sabemos mucho de bastantes cosas.$B$BÚltimamente, nos hemos interesado en los demonios de los Campamentos Forja. En concreto, hemos diseñado una forma de deshacernos de ellos para siempre.$B$BAl menos en teoría.' WHERE `entry` = 10919;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La forja de cristal Bash\'ir puede usarse para transformar tus fragmentos de ápices en frascos inestables del hechicero. Estos frascos vuelven al que los bebe más ágil y feliz y aumentan la efectividad de sus hechizos de ataque y sanación.$B$BSin embargo, parece que no tienes los 10 fragmentos necesarios para comprar un frasco.' WHERE `entry` = 10921;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La forja de cristal vil puede usarse para transformar tus fragmentos de ápices en frascos de la bestia. Estos frascos vuelven al que los bebe más ágil, fuerte y feliz.$B$BSin embargo, parece que no tienes los 10 fragmentos necesarios para comprar un frasco.' WHERE `entry` = 10922;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gahk nuevo en Ogri\'la como $gpequeño:pequeña; $r. Ser como el cielo del que hablan los ogros abajo. Pero a Gahk le duelen las cabezas porque el cristal hacer a Gahk más listo.$B$B<Las dos cabezas de Gahk asienten.>$B$BNosotros aprender a hacer una runaoscura de cristal forjado. ¡Eso ayudar a Ogros a machacar a los demonios!' WHERE `entry` = 10923;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$gBienvenido:Bienvenida; a Ogri\'la, $c.$B$BLos ogros aquí hemos conseguido gran agudeza mental gracias a las emanaciones mágicas de los cristales que nos rodean. Aunque no acabamos de entender su funcionamiento, hemos logrado apreciar nuestro nuevo hogar y solo queremos vivir en paz.$B$BPor desgracia, los demonios y etéreos y, en especial, el Vuelo Negro, no nos dejan alcanzar esa paz.' WHERE `entry` = 10926;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'De parte de todo Ogri\'la, agradecemos tus esfuerzos, $N.$B$B<La cabeza izquierda de Chu\'a\'lor asiente, mientras que su cabeza derecha te sonríe.>$B$BY no olvides que al ayudarnos aquí, también ayudas a proteger a nuestros hermanos allá abajo, para quienes eres como $g un rey : una reina;.' WHERE `entry` = 10927;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tú no. No eres lo suficientemente fuerte para participar en partidas de clasificación. Puedes buscar a un maestro de batalla de arena y practicar. Nunca es demasiado pronto para empezar a entrenar.', `text0_1_loc7` = 'Tú no. No eres lo suficientemente fuerte para participar en partidas de clasificación. Puedes buscar a un maestro de batalla de arena y practicar. Nunca es demasiado pronto para empezar a entrenar.' WHERE `entry` = 10929;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has venido a hacerme daño? No tengo nada que ver con la guerra, $R.$B$B¡Solo soy un erudito enfermizo, por favor déjame tranquilo!' WHERE `entry` = 10930;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El huevo parece fuerte tal y como debería ser un huevo del Vuelo Negro. ¿Quizá si colocaras muchos fragmentos de ápices cerca, sus vibraciones harían que el huevo se rompiera?' WHERE `entry` = 10931;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El huevo parece fuerte tal y como debería ser un huevo del Vuelo Negro. ¿Quizá si colocaras muchos fragmentos de ápices cerca, sus vibraciones harían que el huevo se rompiera?$B$BUna lástima que no tengas los 35 fragmentos necesarios para llevar a cabo la tarea.' WHERE `entry` = 10932;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Expedición Cenarion me salvo de una muerte segura dentro de las minas. ¡Les debo mi vida! Ofrezco mis servicios de reparación y venta de reactivos a todos los aliados de la Expedición Cenarion.' WHERE `entry` = 10936;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí encontrarás mucho que hacer, $N.' WHERE `entry` = 10937;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Debemos restaurar nuestro poder en estas minas!' WHERE `entry` = 10939;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El sobrestante Sangreoscura gruñe.>' WHERE `entry` = 10940;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La reliquia de ápices brilla, revelando la inteligencia oculta en su interior.' WHERE `entry` = 10948;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El monumento de ápices amenaza desde arriba.' WHERE `entry` = 10949;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Eres una pequeña cría, ¿verdad? Mis ojos me decepcionan.$B$B¿Supongo que estás aquí para comprar alguno de mis escritos exóticos?' WHERE `entry` = 10953;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $N. ¿Necesitas llegar rápido al Alto de los Vientonegro, nuestra base en las Montañas Skethyl? Tengo una linda raya abisal preparada.$B$B¿Quieres ir ahora? ¡Tan solo di la palabra $gchico grande: nena;!' WHERE `entry` = 10978;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buenos días. Cuando hayas subido un poco más en las filas de la Guardia del cielo Sha’tari, podré ofrecerte un vuelo directo a la Avanzada de la Guardia del Cielo, nuestro campamento en lo alto de las Montañas Filospada.' WHERE `entry` = 10980;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Bebe o vete!' WHERE `entry` = 10987;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ese es el espíritu, $gchico:chica;! ¡Estamos muy orgullosos del trabajo que estás haciendo para nosotros!$B$BY ya sabes, si quieres un viaje rápido a las Montañas Skethyl, baja a hablar con la cuidadora de la Guardia del cielo Irena. Ella se encargará.' WHERE `entry` = 10989;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Veo que has estado invirtiendo tu tiempo por aquí, $c.$B$BInvierte más, aún quedan demonios, novato.' WHERE `entry` = 10992;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ya sabes, $N, es un gran honor trabajar contigo.$B$BCuando no estés domando, quizá podamos $gsalir a cenar : juntarnos para una noche de chicas;.$B$B¡Oh! ¿He mencionado que ahora podemos llevarte a nuestro campamento en las Montañas Skethyl realmente rápido? Si quieres, hablaré con la cuidadora de la Guardia del cielo Irena acerca de eso.' WHERE `entry` = 11001;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las reliquias son, en verdad, un gran misterio. Quizá si las estudias de forma regular puedas adquirir un mejor conocimiento de sus capacidades.' WHERE `entry` = 11007;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Tienes acero que necesite ser moldeado?' WHERE `entry` = 11017;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué quieres?' WHERE `entry` = 11030;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El señor supremo Mor’ghor saluda.>' WHERE `entry` = 11032;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Qué misterios del comercio puedo ayudarte a desvelar hoy?' WHERE `entry` = 11045;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ven, $R. ¿Qué fórmula te interesa hoy?', `text0_1_loc7` = 'Ven, $R. ¿Qué fórmula te interesa hoy?' WHERE `entry` = 11055;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La pila de cráneos apesta a insensatez. Temes que los arakkoa hayan añadido algún cráneo a la pila recientemente. $B$BNecesitarás 10 pergaminos perdidos en el Tiempo para invocar a los descendientes de los adversarios de Terokk.' WHERE `entry` = 11057;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Saludos $Gviajero:viajera;!', `text0_1_loc7` = '¡Saludos $Gviajero:viajera;!' WHERE `entry` = 11064;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestra técnico de éter de la Guardia del cielo y su compañía están estudiando la forja de cristal Bash’ir. ¡Necesita ayuda! ¡Ve al Alto Bash’ir, encuentra a la técnico y mantenla a salvo!' WHERE `entry` = 11066;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Siento que has despejado el camino para mis hermanos. Mi conexión con ellos y con el templo aún es fuerte. ¿Quieres adentrarte más?' WHERE `entry` = 11081;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Arakkoa invisibles... están alrededor nuestro... observándonos... esperando!' WHERE `entry` = 11089;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los dracos son mi negocio, $N. tengo autorización para vendérselos a nuestros oficiales de mayor rango.' WHERE `entry` = 11090;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Los focos de cristal? Te lo explicaré, ya que solo tienes la mitad del cerebro necesario para imaginarlo.$B$BUn foco de cristal mermado puede combinarse con diez fragmentos de ápices, creando un foco de cristal cargado. Estos focos cargados tienen propiedades curativas, pero lo más importante es que pueden usarse con los demonios poseídos.$B$BMira, el foco cargado puede usarse para permitir que un demonio poseído presente poderes especiales. Cuando pase algo de tiempo, Gahk tendrá una misión para ti que consistirá en eso.' WHERE `entry` = 11094;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este crimen no puede quedar sin resolver. La situación de la posada y la violencia de su destrucción lo convierten en un asunto muy delicado. Inspecciona el lugar con el máximo respeto.' WHERE `entry` = 11126;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pareces audaz, $C. ¿Recuerdas los días gloriosos de Theramore, antes de que Jaina nos hiciera abandonar la lucha contra la Horda? ¿Estás harto de tener que guardar tu espada mientras los orcos siguen provocándonos?' WHERE `entry` = 11136;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber llegado a Orgrimmar hace poco.', `text0_1_loc7` = 'El zepelín debería haber llegado a Orgrimmar hace poco.' WHERE `entry` = 11165;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber llegado a Grom\'gol hace poco...', `text0_1_loc7` = 'El zepelín debería haber llegado a Grom\'gol hace poco...' WHERE `entry` = 11167;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber llegado a Orgrimmar hace poco...', `text0_1_loc7` = 'El zepelín debería haber llegado a Orgrimmar hace poco...' WHERE `entry` = 11169;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber salido de Orgrimmar hace poco...', `text0_1_loc7` = 'El zepelín debería haber salido de Orgrimmar hace poco...' WHERE `entry` = 11170;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber salido de Grom\'gol hace poco...', `text0_1_loc7` = 'El zepelín debería haber salido de Grom\'gol hace poco...' WHERE `entry` = 11172;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber llegado a Entrañas hace poco.', `text0_1_loc7` = 'El zepelín debería haber llegado a Entrañas hace nada.' WHERE `entry` = 11173;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber salido de Orgrimmar hace poco.', `text0_1_loc7` = 'El zepelín debería haber salido de Orgrimmar hace nada.' WHERE `entry` = 11174;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber salido de Entrañas hace poco.', `text0_1_loc7` = 'El zepelín debería haber salido de Entrañas hace nada.' WHERE `entry` = 11175;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El zepelín debería haber llegado a Entrañas hace poco.', `text0_1_loc7` = 'El zepelín debería haber llegado a Entrañas hace nada.' WHERE `entry` = 11179;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me paso el día buscando piezas para el zepelín y Frezza no tiene ni la decencia de pagarme por horas. Pero tú no has venido por eso, ¿verdad? Apuesto a que quieres saber dónde está ahora el zepelín. ¡Estás de suerte, tengo mi dispositivo de seguimiento de zepelines aquí!', `text0_1_loc7` = 'Me paso el día buscando piezas para el zepelín y Frezza no tiene ni la decencia de pagarme por horas. Pero tú no has venido por eso, ¿verdad? Apuesto a que quieres saber dónde está ahora el zepelín. ¡Estás de suerte, tengo mi dispositivo de seguimiento de zepelines aquí!' WHERE `entry` = 11189;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Guau! ¿Realmente quieres comprarnos a nosotros?' WHERE `entry` = 11211;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No puedo más que dar por hecho que sabes quien soy. ¿Y aun así insistes en molestarme?$B$B¿Qué puedes querer que requiera mi atención?' WHERE `entry` = 11217;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vamos, ¡no seas ridículo! ¡Yo soy el único Budd Magallenas!$B$BHe recorrido toda esta tierra, desde lo alto del Monte Hyjal hasta las profundidades del Templo Sumergido. ¡De hecho, yo fui el primero en llamarlo el Templo Sumergido!$B$BSí, fui yo. Pero no lo pienses demasiado.' WHERE `entry` = 11219;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, bien, escucha $C. Soy mayor de lo que aparento y muy sabio para mi edad. Harías bien en no hacerme demasiadas preguntas.$B$BMira, no tienes que creerme. Lee lo publicado en cualquier tablón de anuncios. ¡Estoy en todas partes!$B$BPero hazte un favor e ignora esas en las que pone \"SE BUSCA\" en el título. Leves malentendidos, te lo aseguro.' WHERE `entry` = 11221;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '$GBienvenido:Bienvenida;, $C. Estoy aprendiendo el arte de manejar grifos con Baldruc. Espero tener mi propio establo de grifos algún día.' WHERE `entry` = 11224;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Qué quieres, cariño?' WHERE `entry` = 11230;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Oh, ¿yo cocinera? Supongo que es cierto. Una camarera tiene que trabajar en la cocina de vez en cuando y yo he aprendido alguna que otra receta a lo largo de los años. Nada especial, pero son lo suficientemente abundantes como para llenar la considerable barriga de Grok.' WHERE `entry` = 11231;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi hermano Krixx cree que trabajar en Orgrimmar es malo. Nada se parece a la peste de este lugar. ¡Al menos el jefe me paga un plus de peligrosidad!$B$B¿Qué? ¿Quieres saber dónde está ahora el zepelín? ¡Estás de suerte, tengo mi dispositivo de seguimiento de zepelines aquí!', `text0_1_loc7` = 'Mi hermano Krixx cree que trabajar en Orgrimmar es malo. Nada se parece a la peste de este lugar. ¡Al menos el jefe me paga un plus de peligrosidad!$B$B¿Qué? ¿Quieres saber dónde está ahora el zepelín? ¡Estás de suerte, tengo mi dispositivo de seguimiento de zepelines aquí!' WHERE `entry` = 11234;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Manteneos en guardia. Somos la primera línea de defensa de Theramore contra sus numerosos enemigos de la marisma.' WHERE `entry` = 11243;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ahora que los desertores han tomado Punta Perdida, Torre de la Punta Norte es la más lejana de las avanzadas de Theramore.' WHERE `entry` = 11245;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué puede hacer un tipo como yo por $Gun:una; $R como tú en un momento como este?',  `text1_0_loc7` = '¿Puedo hacer algo por ti, $C?', `text2_0_loc7` = '¡Si estas aquí para tomar parte en los grandes desechos, ponte en la cola!$B$BMi parte viene con retraso.' WHERE `entry` = 11256;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me supera, chico. Soy nuevo en el equipo.$B$BPuedo decirte que he matado montones de trols y bebido mucho grog. Lo único que me falta por ver es el tesoro que Budd está venga a prometer...' WHERE `entry` = 11257;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Te refieres al equipo de Budd? Sí, no respeta a las personas. Cogería a cualquiera que supiera usar una pala.$B$BAcepta no-muertos. Budd los odia, es probable que les tenga miedo.',  `text1_0_loc7` = 'Sí, nunca se sabe quien va a ser el siguiente en unirse, o el siguiente en morir.$B$B¿Mi consejo? No te encariñes con nadie.', `text2_0_loc7` = 'De acuerdo. Budd ha estado recogiendo gente por todo el mundo.$B$BParece que cuando hay bebida gratis la gente está dispuesta a dejar sus diferencias de lado.' WHERE `entry` = 11258;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Se huele a sí mismo y hace una mueca de asco>$B$B¡Ese es el olor de la aventura, $C!',  `text1_0_loc7` = '<hip>Esscucha esto, $R. Passha un par de díassh luchando con troles salvajes y desssspuésh cuéntame como hueles', `text2_0_loc7` = 'Oh, eso. Verás, últimamente no ha habido más que grog orco por el campamento.$B$BEl grog orco no parece sentarme bien, tú me entiendes...' WHERE `entry` = 11259;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; al Alto del Centinela, $C.' WHERE `entry` = 11264;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Brogg se alegra de que los goblins de Piñón de Barro le dejen quedarse, pero a veces cree que los oye reírse de él.' WHERE `entry` = 11281;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Nunca he experimentado un frío como este, $r. Incluso sin hielo ni nieve, los fríos baldíos de Rasganorte son extremadamente agotadores.' WHERE `entry` = 11285;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Cuando sea mayor quiero ser mozo de establos!' WHERE `entry` = 11291;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Esta posada fue construida antes de que se despertaran los salvajes del norte. Demos gracias a la Luz por eso.$B$B¿Te quedarás mucho?' WHERE `entry` = 11293;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si necesitas hablar con el aprendiz Garion, lo encontrarás dentro de la casita con Tabetha. Por lo menos allí no puede robar los componentes.' WHERE `entry` = 11309;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desearía que tuviéramos un sacerdote o un druida aquí. No puedo continuar mis estudios hasta que me haya recuperado de mis heridas.' WHERE `entry` = 11311;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola! Esos inútiles piratas nos están bloqueando en la bahía.$B$B¿Crees que puedes ayudarnos lanzándoles unas cuantas bombas?$B$BNuestro zepelín robado está aquí, ¡salta a bordo antes de que zarpe!' WHERE `entry` = 11320;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola! Esos inútiles piratas nos están bloqueando en la bahía.$B$B¿Crees que puedes ayudarnos lanzándoles unas cuantas bombas?$B$BNuestro zepelín robado está a menos de $3078w minutos de aquí. ¡Asegúrate de estar a bordo cuando zarpe!' WHERE `entry` = 11322;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Soy un prisionero!$B$BMe derribaron junto a mi precioso zepelín. ¿Y por qué? ¿Por transportar hombres y material para la Horda? ¡Eso no es nuevo!$B$BAhora me obligan a dar vueltas a la bahía con él para bombardear a los piratas. ¡No puedo seguir así! ¡Apenas lo puedo mantener íntegro a base de saliva y alambre!$B$BSi estás seguro de que quieres ayudarles, volverá a atracar en el puerto en menos de un minuto.$B$B¡Te digo que es un crimen!' WHERE `entry` = 11330;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Paso el día y la noche aquí sentado haciendo munición y encordando ballestas.$B$B¿Supongo que tu también quieres que haga algo para ti?' WHERE `entry` = 11352;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Durante miles de años estos bosques han ardido. Se alimentan de una antigua magia y conforman la zona de apareamiento de la madre de linaje y sus consortes protodraco.$B$BPero ahora un mal hace tiempo olvidado se ha despertado y quiere esclavizar a la prole de la madre de linaje para sus propios fines.$B$BEstos vrykuls han causado una alteración que se está extendiendo más allá de los límites del Encierro y amenaza a tu gente.' WHERE `entry` = 11362;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los representantes de Bonvapor de Piñón de Barro usan este tablón para colgar información sobre trabajo y recompensas.' WHERE `entry` = 11365;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puede que quieras apartarte. Limpiar peces es un trabajo sucio.' WHERE `entry` = 11376;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Sociedad Real de Boticarios te da la bienvenida a Rasganorte, $C. Y bien, ¿qué vas a hacer para colaborar a la causa de Sylvanas?' WHERE `entry` = 11384;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡La situación allí es peor de lo que pensaba! ¡El zepelín es poco más que un amasijo de hierros retorcidos y combustible ardiendo!' WHERE `entry` = 11403;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te doy la bienvenida a Theramore, $C.' WHERE `entry` = 11406;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tanto disparar a Skorn y a otros lugares en el horizonte y ¿qué crees que me ha tocado?$B$B¡Recuperar bolas de cañón!$B$BYo no me apunté para esto, ¿sabes?' WHERE `entry` = 11418;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si no es urgente, no tengo demasiado tiempo para hablar. Las puertas de Theramore son mi mayor responsabilidad.' WHERE `entry` = 11424;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El teniente Aden siempre se ofrece para encargarse de las puertas. A mí me parece bien.$B$BTengo un puesto que me ofrece, al mismo tiempo, una buena vista de mis guardias Y de las hijas de los mercaderes.' WHERE `entry` = 11426;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Gracias a la Luz que los Defias no tienen ni idea de bucear!' WHERE `entry` = 11432;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El capitán Adams me envía a investigar qué está ocurriendo en Haltorboll. Pero esto es lo más cerca que he conseguido acercarme, la torre vrykul abandonada me otorga cierta protección ante ojos entrometidos.$B$BLos Renegados están tramando algo allí. ¡Puedo sentirlo!' WHERE `entry` = 11436;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a Theramore.' WHERE `entry` = 11457;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué encantamientos te interesan hoy, $C?' WHERE `entry` = 11458;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encantado de conocerte, $C.' WHERE `entry` = 11469;
UPDATE `locales_npc_text` SET `text1_0_loc7` = 'Existen ocho tipos de cartas de la Luna Negra: Bestias, Bendiciones, Elementales, Furias, Locuras, Tormentas, Portales y Señores de la Guerra, con ocho cartas cada palo que van del as al ocho. Si reúnes las ocho cartas de un mismo palo, puedes convertirlas en una baraja que podrás entregar al profesor Thaddeus Paleo a cambio de un poderoso abalorio.' WHERE `entry` = 11472;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a Theramore, $C.', `text0_1_loc7` = '$GBienvenido:Bienvenida; a Theramore, $C.' WHERE `entry` = 11492;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No deberíamos haber dejado a tantos atrás, en Valgarde. ¡El almirante Keller, ese insufrible ególatra!$B$B¿Cómo voy a cumplir mis órdenes con tan pocos? ¡Necesito hombres suficientes para que se queden aquí en la fortaleza y aun así poder asignar las tropas necesarias al Fuerte Vildervar cuando esté listo!$B$BAl menos nos queda algo de esperanza de poder resolver nuestros problemas ahora que estás aquí.' WHERE `entry` = 11494;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aconsejo al Capitán en los asuntos que decide compartir conmigo, pero tengo la sensación de que preferiría escucharte a ti.' WHERE `entry` = 11496;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Todas estas bocas que alimentar y ¿quién tiene que hacerlo? ¡Yo!$B$B¡Cocinar, limpiar y todo lo demás!' WHERE `entry` = 11498;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Jamás en mi vida creí que vería una frontera tan espectacular.$B$BOjalá no estuviera contaminada por el mal y por un saber que está mejor oculto.' WHERE `entry` = 11502;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He visto muchas cosas en mi vida, pero el cambio sufrido por la Liga de Expedicionarios en Garganta Susurro las supera a todas.$B$B¡Me alegro de haber podido escapar de allí con la piel intacta!' WHERE `entry` = 11504;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La tierra... está helada. Somos presas fáciles aquí. Estos carros de despojos no van a ir a ningún sitio.' WHERE `entry` = 11562;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué parece? ¡El suelo está helado como un témpano!$B$BLas ruedas de estos carros de despojos fueron construidas para avanzar sobre barro como mucho. Vamos a necesitar una abominación y algunas cadenas para moverlos.' WHERE `entry` = 11564;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los marineros varados de la Alianza saben que van a morir, pero aun así luchan contra nosotros con todas sus fuerzas.$B$BImpresionante y patético al mismo tiempo.', `text1_1_loc7` = 'Es cuestión de horas que aplasten a los Alianzas de Playa Derelicta. Están rodeados y son inferiores en número.', `text2_1_loc7` = 'Por la voluntad de Sylvanas, nuestros enemigos caerán.' WHERE `entry` = 11586;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Esos dos cañones de la pared son lo que nos está ralentizando. Sin su protección, nuestras fuerzas podrían asaltar su fortificación sin sufrir ninguna baja.$B$BO eliminamos esos cañones o esperamos a que se queden sin munición. Elección de Anselm, supongo.' WHERE `entry` = 11588;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ven, resguárdate del frío y descansa tus agotados huesos un rato. Nuestra hospitalidad es tuya.', `text0_1_loc7` = 'Ven, resguárdate del frío y descansa tus agotados huesos un rato. Nuestra hospitalidad es tuya.' WHERE `entry` = 11607;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Camina en la Luz, $ghijo:hija;.' WHERE `entry` = 11614;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$G Señor : Señora; estoy aquí para $gayudarle y defenderle:ayudarla y defenderla;.$B$B¿Sus órdenes?' WHERE `entry` = 11622;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Date prisa, $c!$B$BEsta crisis está de todo menos bajo control.' WHERE `entry` = 11655;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Buscas transporte a algún lugar, $N? Mis murciélagos te llevarán allí en un abrir y cerrar de ojos.' WHERE `entry` = 11662;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Camina con cuidado, $R.$B$BEstos cadáveres son todo lo que queda de mis mejores hombres.' WHERE `entry` = 11667;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Estos soplagaitas de la Alianza son duros! Embarcarse en destructores tripulados por Renegados... eso sí que es comportamiento suicida.' WHERE `entry` = 11671;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mis hermanas y yo te damos la bienvenida, $R.' WHERE `entry` = 11673;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mantente alerta, $n.$B$BAún no estamos a salvo...' WHERE `entry` = 11676;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $Gforastero:forastera;. Si buscas un lugar necesitado de tu ayuda, no cabe duda de que lo has encontrado.$B$BLos muertos se levantan, caminan por la tierra, llegan en barcos desde el otro lado del gran océano. El fiordo se vuelve demasiado peligroso incluso para nosotros y pronto tendremos que ir hacia el norte en busca de tierras más seguras.' WHERE `entry` = 11688;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estoy aquí para ayudar y defender.$B$B¿Cómo vamos a atacar al enemigo?' WHERE `entry` = 11689;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Acércate, joven, mis oídos ya no son lo que eran y no me gusta levantar la voz.' WHERE `entry` = 11691;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a mi taberna, $Gfatigado viajero:fatigada viajera;. ¿En qué puedo ayudarte?', `text0_1_loc7` = 'Te doy la bienvenida a mi taberna, $Gfatigado viajero:fatigada viajera;. ¿En qué puedo ayudarte?' WHERE `entry` = 11714;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Cómo me alegro de verte!$B$BHay trabajo que hacer y no tenemos demasiado tiempo.' WHERE `entry` = 11738;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gorth no tiene miedo a morir. Los Renegados me volverán a montar.',  `text1_0_loc7` = 'A veces Gorth está confuso. A Gorth se le da bien matar gente, así que a Gorth lo hacen sargento. ¡Pero ahora Gorth ya no puede matar a nadie!', `text2_0_loc7` = 'Vrykuls son más grandes y fuertes que nos. ¡Pero nos es más guapo!' WHERE `entry` = 11742;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mantén la voz baja, $c. No queremos que nos oigan.' WHERE `entry` = 11746;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He pasado mi vida entera en el ejército. He luchado en muchas guerras, bajo muchos estandartes, pero siempre han sido las guerras de otros.$B$BEsta vez no es así. Cada soldado Renegado que ves aquí ha venido por un único motivo. Arthas ha de morir.' WHERE `entry` = 11796;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Cómo permití que Petrov me convenciera para venir aquí con estos enanos incompetentes?' WHERE `entry` = 11808;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Fue todo un detalle por parte de la Liga de Expedicionarios permitirme ir con ellos. De todas formas, con los riesgos de El Bosque Hendido y alrededores, parece que venir sin ayuda habría sido una locura.' WHERE `entry` = 11858;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Voy a echar de menos el Campamento Pezuña Invernal, pero comprendo por qué ha tomado esta decisión el Cabecilla Tótem de Ceniza. El hogar es una comodidad que no siempre podemos permitirnos.' WHERE `entry` = 11874;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Oye, ¿no vessh que esshtoy intentando beber?' WHERE `entry` = 11880;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí tienes, $gchico:chica;. ¡Haz que valga la pena!' WHERE `entry` = 11898;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Claro. Lo encontré en el cadáver de un enano aquí cerca.' WHERE `entry` = 11910;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hey! Que sea capaz de montar en carnero más rápido que nadie, no quiere decir que no vaya a ayudar a los destiladores. Puedes ayudar si quieres.$B$B¡Pero espera que aún hay más!$B$BAyudar en la Fiesta de la cerveza te da la posibilidad de montar en algunos de los carneros más rápidos de los reinos!' WHERE `entry` = 11945;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Jaja, ¿ves esto? ¡Griftah ha VUELTO! ¡De nuevo en mi puesto, con mis antiguas mercancías y listo para hacer tratos contigo!$B$B¿Quieres ser mejor, más rápido, más fuerte? Cada uno de estos amuletos hace lo que te estoy contando. ¡Anímate y compra uno de estos adornos milagrosos! ¡A precio de ganga!' WHERE `entry` = 11947;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muchos son objetos poco comunes y muy preciados que busca mi clientela.$B$BTú podrías traérmelos, $C. A cambio te ofreceré lo que codicies.$B$B¿Hablamos de eso que me gustaría poder obtener hoy?' WHERE `entry` = 12044;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una orden es susurrada por los vientos abisales y Mah\'duun escucha.$B$B$G Un hombre:Una mujer; se presenta ante Mah\'duun y hablan sobre aquellos a los que el viento quiere que maten hoy.' WHERE `entry` = 12046;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Comprendo que mucha gente busca el amor en las tabernas y los festivales como este, pero el alcohol nubla los sentidos y hace que calcular las dificultades de la vida sea difícil.$B$B¡Pero he encontrado una solución! Mediante muchas pruebas y tribulaciones, he creado un aparato extraordinario que te proporciona todos los beneficios del alcohol sin ninguna de sus desventajas. ¡Con estas gafas puestas, TODO EL MUNDO parece atractivo!$B$B¡Mmh... incluida tú, nena! ¿Sabes qué? Te haré un descuento.', `text0_1_loc7` = 'Comprendo que mucha gente busca el amor en las tabernas y los festivales como este, pero el alcohol nubla los sentidos y hace que calcular las dificultades de la vida sea difícil.$B$B¡Pero he encontrado una solución! Mediante muchas pruebas y tribulaciones, he creado un aparato extraordinario que te proporciona todos los beneficios del alcohol sin ninguna de sus desventajas. ¡Con estas gafas puestas, TODO EL MUNDO parece atractivo!$B$B¡Mmh... incluida tú, nena! ¿Sabes qué? Te haré un descuento.' WHERE `entry` = 12080;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿No es preciosa? ¿Quieres llevarla a dar una vuelta?' WHERE `entry` = 12082;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola! ¿Te interesa aprender más sobre los carneros de carreras y las carreras de carneros?', `text0_1_loc7` = '¡Hola! ¿Te interesa aprender más sobre los carneros de carreras y las carreras de carneros?' WHERE `entry` = 12083;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando aceptes una de estas tareas, te prestaremos un carnero de carreras. Es un préstamo, así que no durará para siempre. El carnero puede parecer lento, pero dale con las riendas y cogerá el ritmo.$B$BA nuestros carneros les encantan las manzanas. Hemos colocado algunos contenedores con manzanas por el camino, así que asegúrate de parar junto a ellos. Comer manzanas hará que tu carnero olvide lo cansado que está.', `text0_1_loc7` = 'Cuando aceptes una de estas tareas, te prestaremos un carnero de carreras. Es un préstamo, así que no durará para siempre. El carnero puede parecer lento, pero dale con las riendas y cogerá el ritmo.$B$BA nuestros carneros les encantan las manzanas. Hemos colocado algunos contenedores con manzanas por el camino, así que asegúrate de parar junto a ellos. Comer manzanas hará que tu carnero olvide lo cansado que está.' WHERE `entry` = 12084;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando te dan un carnero, también te entregan las riendas. Pero estas no son las riendas de la abuelita, estas tienes que usarlas.$B$BUsa las riendas para conseguir velocidad. Sigue usándolas y ¡arre! ¡Estarás volando!$B', `text0_1_loc7` = 'Cuando te dan un carnero, también te entregan las riendas. Pero estas no son las riendas de la abuelita, estas tienes que usarlas.$B$BUsa las riendas para conseguir velocidad. Sigue usándolas y ¡arre! ¡Estarás volando!$B' WHERE `entry` = 12085;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una vez que empieces a usar las riendas con el carnero, verás su velocidad cambiar. Comienza andando. Tira un poco de las riendas y se pondrá a trotar. Usa las riendas mientras trota para que vaya a medio galope. Tirar un poco más de las riendas hará que galope.$B', `text0_1_loc7` = 'Una vez que empieces a usar las riendas con el carnero, verás su velocidad cambiar. Comienza andando. Tira un poco de las riendas y se pondrá a trotar. Usa las riendas mientras trota para que vaya a medio galope. Tirar un poco más de las riendas hará que galope.$B' WHERE `entry` = 12086;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mantener al carnero a medio galope o galope durante demasiado tiempo hará que se canse. Si quieres que tu carnero se recupere un poco, deja que ande o trote. Si hay contenedores de manzanas cerca, llévalo junto a ellos.$B', `text0_1_loc7` = 'Mantener al carnero a medio galope o galope durante demasiado tiempo hará que se canse. Si quieres que tu carnero se recupere un poco, deja que ande o trote. Si hay contenedores de manzanas cerca, llévalo junto a ellos.$B' WHERE `entry` = 12087;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Montar en carnero es más fácil de lo que parece, pero hace falta habilidad para ser un experto. Usa las riendas, come manzanas si puedes y evita que tu carnero se canse.$B', `text0_1_loc7` = 'Montar en carnero es más fácil de lo que parece, pero hace falta habilidad para ser un experto. Usa las riendas, come manzanas si puedes y evita que tu carnero se canse.$B' WHERE `entry` = 12088;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Sí? ¿Qué ocurre?$B$B¡El tiempo no espera a nadie!' WHERE `entry` = 12090;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Construimos este lugar con nuestras manos desnudas! Sangre y lágrimas.$B$BCréeme cuando te digo esto: no tenemos intención de abandonar el control de Valgarde.' WHERE `entry` = 12097;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Amigo, lo que tengo aquí es una novedad sin precedentes! ¡Y está en venta a muy buen precio!$B$BVerás, estaba harto de gastar dinero en las tabernas buscando a mi media naranja, así que hice estas gafas y ahora TODO EL MUNDO es la viva imagen de la perfección. ¡No hay posibilidad de error!$B$BSi quieres probarlas, tendrás que acolchar mis bolsillos. ¡Ya que tienes tan buen aspecto te haré un descuento!', `text0_1_loc7` = '¡Amigo, lo que tengo aquí es una novedad sin precedentes! ¡Y está en venta a muy buen precio!$B$BVerás, estaba harto de gastar dinero en las tabernas buscando a mi media naranja, así que hice estas gafas y ahora TODO EL MUNDO es la viva imagen de la perfección. ¡No hay posibilidad de error!$B$BSi quieres probarlas, tendrás que acolchar mis bolsillos. ¡Ya que tienes tan buen aspecto te haré un descuento!' WHERE `entry` = 12098;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te vi por el camino y esperaba que te pasaras por aquí. ¿Qué puedo hacer por ti, cielo?' WHERE `entry` = 12100;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Actualmente, los perros normales ni se me acercan. Me quedé hecho polvo cuando lo descubrí... Me encantaban las bestias.$B$BPero, de todos modos, los canes de peste son buenos sustitutos. No son tan... bonitos. Pero bueno... ¡yo tampoco!' WHERE `entry` = 12103;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No encontrarás una cerveza mejor en todo Azeroth.' WHERE `entry` = 12104;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Ulkang aúlla y te huele, y tú puedes entender lo que dice.>$B$BEl extraño huargo blanco del norte, Garwal, se ha apoderado de mi manada. Debemos rajarle la garganta para que yo vuelva a ser el huargo alfa.' WHERE `entry` = 12112;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡El accidente de Cerrotajo está entorpeciendo la llegada de la cerveza! ¡Y los ataques de los enanos Hierro Negro cada hora no están ayudando!$B$BAsí que si quieres ayudar, este es el trato: te prestaré unas riendas y uno de mis carneros.$B$BPor cada barril que me traigas te daré unas fichas. Hasta dejaré que te quedes con el carnero un ratito más por cada barril$B$B¡PERO una vez que empieces, no podrás repetirlo hasta mañana.$B$B¿Estás seguro de que estás preparado?', `text0_1_loc7` = '¡El accidente de Cerrotajo está entorpeciendo la llegada de la cerveza! ¡Y los ataques de los enanos Hierro Negro cada hora no están ayudando!$B$BAsí que si quieres ayudar, este es el trato: te prestaré unas riendas y uno de mis carneros.$B$BPor cada barril que me traigas te daré unas fichas. Hasta dejaré que te quedes con el carnero un ratito más por cada barril$B$B¡PERO una vez que empieces, no podrás repetirlo hasta mañana.$B$B¿Estás seguro de que estás preparado?' WHERE `entry` = 12113;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Veo que inspeccionas los carneros que hemos obtenido de forma totalmente legal... Sé que quieres descubrir la sensación de montar en un carnero... Pero antes de jugar hay que trabajar.$B$BBueno, cuando se trata de estos carneros jugar y trabajar son lo mismo.' WHERE `entry` = 12114;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ah del barco, asere! ¿Qué ocurre, perro de agua salada?$B$B¡Este lugal es todo extraño, cheche!',  `text1_0_loc7` = '¡Tienes buen aspecto, compay! ¿Robaste a algún masetero? Será mejor que repartas las riquesas... ¡no seas un sucio perro agarrao, camarada!', `text2_0_loc7` = '¡Mira tú allá, compay! ¡Ese tío con escorbuto está cabreando al guajiro! El muy terco va a tragar sal, yunta...  lo van a pasar pol debajo de la quilla y se va a quedar hecho un trapo, asere.', `text3_0_loc7` = '¡Es la verdad, compay! ¡No hay marinero más duro que los piratas del Mar del Norte! Mejor no enfadar a nadie, camarada. ¡Fíjate tú! ¡Todos los marineros estúpidos acaban siendo asotaos, compay!' WHERE `entry` = 12120;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Y ahora te pones cheche! ¿Intentas ofenderme, pulgoso chucho de tierra firme?$B$bApártate, cobarde pillastre. ¡Voy en pira, t\'aluego!' WHERE `entry` = 12121;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Así que eres la novedad en la ciudad, ¿eh?$B$B<Zorek escupe al suelo.>$B$BSí...' WHERE `entry` = 12122;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡En ninguna otra fiesta se bebe y celebra como en la Fiesta de la cerveza!', `text0_1_loc7` = '¡En ninguna otra fiesta se bebe y celebra como en la Fiesta de la cerveza!' WHERE `entry` = 12134;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sip? ¿Quieres volar? Nop... ¡No-no-no de eso nada!' WHERE `entry` = 12144;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Te doy la bienvenida a la zona de la Fiesta de la cerveza, colega! Hay mucho de lo que disfrutar aquí mientras celebramos el espíritu de la competición amistosa entre destilerías rivales. ¡Avísame si tienes alguna pregunta!' WHERE `entry` = 12152;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $r. Soy Hidalgo, maestro de la caza y uno de los mejores halconeros del mundo. Esta máquina de matar que ves posada sobre mi hombro es Excelsior.$B$BPuede que estés pensando: \"¿Qué diablos hace un halconero?\" Yo te lo diré: ¡cazamos! La halconería es el arte de usar un ave rapaz para cazar y recoger la presa. ¿Quizá te interese aprender a cazar con un ave entrenada? ¡Hidalgo puede enseñarte!' WHERE `entry` = 12153;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Fiesta de la cerveza consiste en disfrutar de la mejor comida y bebida de la zona ¡y no exagero! Prueba las cervezas caseras vigorizantes, las salchichas, los pretzels y los quesos. ¡No encontrarás nada tan bueno por tan poco dinero!' WHERE `entry` = 12154;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Después de la cerveza y las salchichas, la parte más popular de la Fiesta de la cerveza son los souvenirs. Desde las vestimentas tradicionales hasta mini-barriles de sabrosa cerveza, Belbi Tiratrastos tiene toda clase de objetos basados en la Fiesta de la cerveza. ¡Pero asegúrate de que tienes suficientes fichas de la Fiesta de la cerveza, ya que es la única forma de comprarlos! Puedes conseguir fichas ayudando en la zona de la Fiesta.' WHERE `entry` = 12155;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las carreras de carneros son una de las grandes tradiciones de la Fiesta de la cerveza. Si quieres más información, habla con Pol Semperámbar junto al establo de carneros. ¡Es muy divertido y puede ser una buena manera de conseguir fichas para la Fiesta de la cerveza!' WHERE `entry` = 12156;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola, te doy la bienvenida a la Fiesta de la cerveza! Hay muchas formas de divertirse aquí, así que si tienes alguna pregunta, avísame. Si no, ve a comprar una cerveza y un pretzel.' WHERE `entry` = 12157;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La comida y la bebida lo son todo en la Fiesta de la cerveza y no hay nada mejor que lo que estamos sirviendo. ¿Crees que miento? Prueba las sabrosas cervezas, salchichas, galletas saladas y los quesos que tenemos. Ya lo verás: ¡lo mejor y lo más barato!' WHERE `entry` = 12158;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A todo el mundo le gustan esos carneros de carreras de la Fiesta de la cerveza. ¿Quieres saber más? Habla con Driz Pataplaf abajo en el establo de carneros. Pásalo pipa y gana algunas fichas para la Fiesta de la cerveza.' WHERE `entry` = 12159;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Oye, no te olvides de llevarte algún souvenir de la Fiesta de la cerveza! Tenemos de todo: desde las vestimentas tradicionales hasta mini-barriles de sabrosa cerveza. Habla con Blix Chirimbolo, pero asegúrate de que tienes suficientes fichas de la Fiesta de la cerveza... ¡es la única manera de comprarlos! Puedes conseguir fichas ayudando en la zona de la Fiesta.' WHERE `entry` = 12160;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Como oyes, la única manera de cazar es estando bien borracho!' WHERE `entry` = 12165;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<La criatura picotea tu mano.>' WHERE `entry` = 12167;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El falcón grazna hambriento pidiendo una larva.>' WHERE `entry` = 12168;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Será mejor que mantengas la cabesa gacha, \"asere\". Si entiendes lo que quiero desir.$B$B¡Yarrrrr!' WHERE `entry` = 12170;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo llevo las peleas aquí. Si quieres apostar, haslo con confiansa; si encontramos a alguien amañando las peleas, lo pasamos pol la quilla.$B$B¡D\'eso m\'aseguro yo, cheche!' WHERE `entry` = 12173;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tesoros de los elfos de sangre sin igual. No es solo por el oro, la seda o las gemas que usan como materiales; la artesanía sin\'dorei tiene valor por sí misma.' WHERE `entry` = 12174;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Pagaré! ¡Pagaré! ¡Aaayyyyy! ¡No me hagas daño!' WHERE `entry` = 12175;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Lo único que elll Loco Jonah Sterling quiele que haga es fregalll la\'h cubielllta\'h todo\' los días.$B$B¿Fregalll las cubielta\'h?$B$B¡Yo soy maga, yo NO friego cubiellltas! Al menos no de la manela mundana.' WHERE `entry` = 12176;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Es evidente que este pirata ha bebido demasiado. Parece estar inconsciente por el momento.>' WHERE `entry` = 12178;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Entonces, ¿qué va a ser? ¿Ron? ¿Cerveza?$B$B¿O quizá otra cosa?' WHERE `entry` = 12179;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Vas a hacerle una de las tuyas a Jack delante de sus chicos?$B$BEso requiere mucha... estupidez. Bueno, será tu problema. Yo no voy a arriesgar mi vida por menos de un oro. ¡Y será mejor que esperes a que me haya ido antes de hacer nada!' WHERE `entry` = 12180;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Siempre hay una buena razón para beber!' WHERE `entry` = 12181;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los enanos Hierro negro están irritados por algo. Vienen casi cada media hora, rompen todos los barriles y se van.$B$BTe estarás preguntando \"¿y a mí qué?\"$B$BTe lo explicare... ¡Los barriles que rompen evitan que regalemos cerveza gratis! Me has oído bien: cerveza gratis.$B$BAsí que, si estás por aquí cuando aparezcan esos enanos, dedica un minutos a ayudar a proteger los barriles.' WHERE `entry` = 12182;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡La Fiesta de la cerveza es la festividad perfecta! Pero tiene un inconveniente: los ataques de los enanos Hierro Negro. Tienen lugar casi cada media hora.$B$BLos atacantes se ceban con los barriles que dan cerveza GRATUITA. Y es una cerveza muy buena que regalan los cerveceros.$B$BSi estás por aquí cuando aparezcan los enanos Hierro Negro, echa una mano y protege la cerveza GRATUITA.' WHERE `entry` = 12183;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida de nuevo, $N.' WHERE `entry` = 12185;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Este maldito Jonah Sterling nos llevará a la perdición. Si pasara más tiempo fuera de su cueva, vería que atacar a armadas profesionales en tiempos de guerra es una locura.' WHERE `entry` = 12186;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muchas cosas cambian tanto en tierra como en el agua: tú, ellos... muchas cosas cambian.$B$BLa forma de vida de Komawa se ve amenazada por estos cambios. ¿Qué harás?' WHERE `entry` = 12188;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estoy fuera de mi elemento, ¿eh? ¡Walt no sabe de qué está hablando! Yo solito saqué el hierro de esos enanos y acampé para observar lo que ocurría en la excavación.' WHERE `entry` = 12189;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El tipo aquel, está muy disgustado por su alfombra, ¿sabes?' WHERE `entry` = 12190;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buenas, colega.', `text0_1_loc7` = 'Buenas, colega.' WHERE `entry` = 12191;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Su... su... supongo que quieres volver a la avanzada. ¿Yarp?' WHERE `entry` = 12193;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El banco de trabajo de Walt está listo para ser usado.' WHERE `entry` = 12194;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Dos veces al día. A las seis y cuarto de la mañana y a las seis y cuarto de la tarde, el propio Manitas Mayor Mekkatorque viene a la zona de la Fiesta de la cerveza para la ceremonia de apertura del barril. ¡No te la pierdas!' WHERE `entry` = 12195;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Dos veces al día. A las seis y cuarto de la mañana y a las seis y cuarto de la tarde, el propio cazador de las sombras Vol\'jin viene a la zona de la Fiesta de la cerveza para la ceremonia de apertura del barril. ¡No te la puedes perder!' WHERE `entry` = 12196;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los tiempos cambian, $N. La guerra ha llegado.$B$B¿Se alzará nuestra gente en esta ocasión y ayudará a nuestros aliados... o nos quedaremos al margen?' WHERE `entry` = 12208;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No tengo tiempo para hablar, lo siento.' WHERE `entry` = 12209;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿No ves que estoy trabajando $Gchico:chica;? Lárgate antes de que Kowalski me vea charlando.' WHERE `entry` = 12210;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Torgoley nos hace trabajar duro, pero es un buen capitán.' WHERE `entry` = 12212;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No quedamos muchos, $N. El regreso de nuestro viejo enemigo es motivo de gran preocupación.' WHERE `entry` = 12213;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debo prestar atención al barco y a la tripulación, $Gbuen hombre:buena mujer;. Perdona que no me pare a hablar contigo.' WHERE `entry` = 12214;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Lady Mehley pasa tanto tiempo en Theramore que nos han encargado que cuidemos de sus pasajeros.', `text0_1_loc7` = 'El Lady Mehley pasa tanto tiempo en Theramore que nos han encargado que cuidemos de sus pasajeros.' WHERE `entry` = 12215;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al majestuoso Lady Mehley. Soy el capitán Torgoley.$B$BSi tienes preguntas acerca del barco formúlalas, si no, disfruta del viaje como yo siempre hago... Después de todo, citando a uno de mis escribas favoritos: Mi experiencia con los barcos es que en ellos uno realiza interesantes descubrimientos acerca del mundo. Descubre que puede vivir sin él.' WHERE `entry` = 12216;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, ese es su nombre y es un buen barco.$B$BHay mucho que aprender de ese tipo de naves, ya sabes. Un gran poeta dijo una vez: Los barcos pasan en la noche y se comunican con solo pasar, solo una señal y una voz lejana en la oscuridad. Así que, en la vida en el océano, pasamos y nos comunicamos con solo una mirada y una voz, la oscuridad de nuevo y el silencio.' WHERE `entry` = 12217;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si deseas sustento, la jefa de galera Grace puede ayudarte.' WHERE `entry` = 12218;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El señor Combs, el ingeniero e intendente del barco puede proporcionarte cualquier suministro que necesites. También es un gran manitas si necesitas reparar tu equipo.' WHERE `entry` = 12219;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, sí, eso... el nombre. Bueno... dicen que un buen nombre, como la buena voluntad, se consigue mediante muchas acciones y se pierde con solo una. No vale la pena entrar en detalles de cómo o por qué, digamos, sin más, que así es como me llaman algunos.' WHERE `entry` = 12220;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los muertos se levantan y buscan vengarse de cualquiera que se atreva a pasar por sus dominios. ¡Volved atrás!' WHERE `entry` = 12221;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tuve suerte. ¡No muchos tauren llegan a moso de camarote en una fragata como esta! Y Grezzix me deja encargarme de estas increíbles canoas de batalla.' WHERE `entry` = 12222;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este portal no es como los demás que llevan de vuelta a Azeroth. Para mantenerlo debemos conseguir más células de maná.$B$BO corremos ese riesgo o perdemos nuestro acceso a la Isla de Quel\'Danas y a La Fuente del Sol.' WHERE `entry` = 12227;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Este es el portal que usaron los ejércitos de Kael para retirarse a La Fuente del Sol. Algunos de sus perritos falderos continúan aquí, la Legión los ha recompensado de una forma muy... poco habitual.' WHERE `entry` = 12239;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Que la Luz guíe tu camino y te asista en tus viajes. Si necesitas ayuda para encontrar lo que buscas, estaré justo allí.', `text0_1_loc7` = 'Hemos logrado tomar el Sagrario. Podemos seguir convirtiendo a los centinelas y usarlos como guardias en otras posiciones tácticas.' WHERE `entry` = 12241;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ah del barco! ¿Tú quieres un boleto pa subir a bordo del Hermana Misericordia? Viniste al lugal adecuao...' WHERE `entry` = 12242;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debemos asegurarnos de no perder más almas a manos del mar.', `text0_1_loc7` = 'Debemos asegurarnos de no perder más almas a manos del mar.' WHERE `entry` = 12243;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Perdóname $C, pero he de concentrarme en mi trabajo.', `text0_1_loc7` = 'Perdóname $C, pero he de concentrarme en mi trabajo.' WHERE `entry` = 12244;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muchas almas se han perdido a manos de los mares. Yo y los fuegos fatuos tenemos la misión de encontrarlas.', `text0_1_loc7` = 'Muchas almas se han perdido a manos de los mares. Yo y los fuegos fatuos tenemos la misión de encontrarlas.' WHERE `entry` = 12245;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los fuegos fatuos y yo vigilamos los mares en busca de almas perdidas.', `text0_1_loc7` = 'Los fuegos fatuos y yo vigilamos los mares en busca de almas perdidas.' WHERE `entry` = 12246;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los mares son un lugar peligroso, $C. Debemos estar alerta.', `text0_1_loc7` = 'Los mares son un lugar peligroso, $C. Debemos estar alerta.' WHERE `entry` = 12247;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me temo que no puedo hablar contigo ahora.', `text0_1_loc7` = 'Me temo que no puedo hablar contigo ahora.' WHERE `entry` = 12248;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestros esfuerzos han dado su fruto. Hemos capturado el Sagrario de Tramo del Sol, $N. Ve a ver si hay alguien a quien puedas ayudar.' WHERE `entry` = 12259;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La tripulación de El Valentía hará todo lo que esté en sus manos para que tu viaje sea tan rápido y agradable como sea posible.' WHERE `entry` = 12262;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo siento, pero tendremos que hablar en otro momento. Adiós.' WHERE `entry` = 12263;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a El Valentía, $Gseñor:señora;. Ahora, si me disculpa...', `text0_1_loc7` = '$GBienvenido:Bienvenida; a El Valentía, $Gseñor:señora;. Ahora, si me disculpa...' WHERE `entry` = 12264;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un nombre poco apropiado para un marinero, ¿no te parece?', `text0_1_loc7` = 'Un nombre poco apropiado para un marinero, ¿no te parece?' WHERE `entry` = 12267;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puede que este canoso perro marino sea el más viejo de la tripulación, pero también es el más duro del grupo. ¡Mira! ¡Mira! ¡Sus brazos parecen los de un ogro!' WHERE `entry` = 12268;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos jurado hacer todo lo que esté en nuestras manos para proteger a los pasajeros de El Valentía. La línea marítima entre Auberdine y el Puerto de Ventormenta ha de ser segura.', `text0_1_loc7` = 'Hemos jurado hacer todo lo que esté en nuestras manos para proteger a los pasajeros de El Valentía. La línea marítima entre Auberdine y el Puerto de Ventormenta ha de ser segura.' WHERE `entry` = 12270;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola. Estás a bordo de La Fantasía de la Doncella. Yo soy el capitán Cánula y tienes el privilegio de navegar en el orgullo de los Asaltantes Aguasnegras. Los Velasangre no se nos acercan gracias a mi astuta tripulación y a la gran cantidad de pólvora que llevamos en la bodega. El seguro, ya sabes.$B$BSi tienes alguna pregunta, yo te daré respuestas.' WHERE `entry` = 12271;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a bordo del Transbordador de Plumaluna. Viajamos entre el Bastión Plumaluna y La Costa Olvidada buscando a quienes se perdieron en el mar. Este mar puede ser un lugar peligroso si no estás preparado.$B$BSi necesitas prepararte, habla con nuestros proveedores. Están más que dispuestos a ayudarte.' WHERE `entry` = 12275;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En nombre de la tripulación del Espuma de la Luna, te doy la bienvenida $C. Espero que hagas uso de nuestras provisiones antes de partir.' WHERE `entry` = 12276;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Elune nos ha bendecido muchas veces con los descubrimientos que hemos realizado. Pero debemos permanecer atentos, $C.', `text0_1_loc7` = 'Elune nos ha bendecido muchas veces con los descubrimientos que hemos realizado. Pero debemos permanecer atentos, $C.' WHERE `entry` = 12278;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a bordo de La Fantasía de la Doncella. Debería ser una travesía tranquila hasta el puerto.' WHERE `entry` = 12281;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es increíble lo que puedes aprender de un gnomo si lo estrujas con fuerza suficiente: trucos astutos sobre cómo librarse de unas ataduras, hacer galletas deliciosas e, incluso, cómo construir una máquina voladora.' WHERE `entry` = 12282;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Siempre que puedo. Es un ejercicio magnífico y me mantiene informado sobre lo que traman, que sé por experiencia, que nunca es nada bueno.' WHERE `entry` = 12283;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Te doy la bienvenida a bordo de El invocador del trueno!$B$BNo prestes atención a esa gente que se queja sobre explosiones y cosas así. Lo digo en serio, todo el mundo comete errores. Hay que tener un poco de confianza.$B$BMira, lo entendería si un capitán hubiera hundido su barco 22 o 23 veces... pero menos que eso no es digno de mención.' WHERE `entry` = 12288;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Refunfuña> Creo que esas montañas han aparecido de la nada... ¡las cinco veces!',  `text1_0_loc7` = '<Refunfuña> ¿Crees que estrellar un zepelín contra el océano es difícil? ¡Más difícil sería hacerlo 4 veces!', `text2_0_loc7` = '<Refunfuña> Me gustaría poder decir que nunca nos hemos estrellado contra un lago. Me gustaría poder decir que solo nos hemos estrellado contra un lago una vez... ¡Pero nos hemos estrellado en 4 lagos diferentes, incluido el Lago Lordamere!', `text3_0_loc7` = 'Me impresiona que las ramas de los pinos no dañen demasiado nuestra nave.$B$BClaro que eso no es excusa para las 3 veces que nos hemos estrellado contra un bosque.', `text4_0_loc7` = 'Jamás pensé que llegaría a ver Teldrassil... Y te juro que jamás pensé que nos estrellaríamos contra Teldrassil.', `text5_0_loc7` = '¡Hemos conseguido atravesar Cima del Trueno dos veces!', `text6_0_loc7` = '<Refunfuña> ¡Aún no sé cómo hemos logrado atravesar Las Mil Agujas sin un arañazo, para acabar estrellándonos y ardiendo en El Desierto de Sal!' WHERE `entry` = 12289;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Si te gustan las emociones fuertes, nuestro Capitán es el mejor! ¡Adoro este trabajo!' WHERE `entry` = 12290;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No preguntes...' WHERE `entry` = 12291;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No puedes culparme por estos contratiempos... ¡Ni siquiera puedo ver por dónde vamos!$B$BAunque quizá eso signifique que sí puedes culparme...$B$B¡No! ¡Espera! Puedo atracar esta nave perfectamente... ¡Deben de ser las órdenes del Capitán! Quizá esté forzándonos al límite y examinando nuestra capacidad de supervivencia y reparación.' WHERE `entry` = 12292;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solían llamarme Iolol hasta que se hizo una votación secreta en la ciudad que dio como resultado que tuviera que cambiarme el nombre. Era eso o seguir los pasos de Corki. Tú habrías hecho lo mismo. ¿Qué te parece mi nuevo nombre? Un gran avance, ¿verdad?$B$BPor cierto, mira el tablón de anuncios si buscas trabajo.' WHERE `entry` = 12294;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si Kil\'jaeden atraviesa La Fuente del Sol, nuestro mundo se verá envuelto en una guerra sin precedentes en los últimos 10000 años.' WHERE `entry` = 12309;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿No es precioso el monumento? Es importante recordar los momentos de nuestra historia en los que se hicieron grandes sacrificios para no olvidar que todas las guerras tienen un precio, incluso las que se ganan.' WHERE `entry` = 12323;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te doy la bienvenida a bordo, $C. Relájate y disfruta del viaje. A bordo del \"Princesa púrpura\" queremos que te relajes. Tenemos máquinas de refrescos y comida abajo por si necesitas algo mientras estas a bordo.' WHERE `entry` = 12327;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Déjame dormir cinco minutitos más...' WHERE `entry` = 12331;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El vuelo desde Grom\'gol a Entrañas es precioso, pero muy tranquilo. Aunque eso no es excusa para quedarse dormido en el trabajo.' WHERE `entry` = 12333;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Conseguiste ver su espectáculo en el Bosque de Terokkar? ¡Fue fantástico!', `text0_1_loc7` = '¿Conseguiste ver su espectáculo en el Bosque de Terokkar? ¡Fue fantástico!', `text1_0_loc7` = '¡Los Tauren Chieftains son los juglares más famosos de todo Azeroth!', `text1_1_loc7` = '¡Los Tauren Chieftains son los juglares más famosos de todo Azeroth!', `text2_0_loc7` = 'Me pregunto cuánto tiempo más se quedarán en Lunargenta. ¡Espero que toquen ahí!', `text2_1_loc7` = 'Me pregunto cuánto tiempo más se quedarán en Lunargenta. ¡Espero que toquen ahí!', `text3_0_loc7` = 'Daría lo que fuera por poder hablar con ellos...', `text3_1_loc7` = 'Daría lo que fuera por poder hablar con ellos...' WHERE `entry` = 12336;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No luches contra el enemigo sin estar preparado, $N. Puedo proporcionarte objetos muy poderosos... siempre y cuando nos hayas demostrado lo que vales.' WHERE `entry` = 12338;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<A Ahlurglgr le sorprende poder entenderte.>' WHERE `entry` = 12344;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Has aprendido un truco para hablar con nosotros, $R, pero no me impresiona.$B$BEn el agua hay traidores Aleta Invernal que matar.' WHERE `entry` = 12350;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No estoy ciego, ¿sabes? ¡Veo lo que está pasando aquí!' WHERE `entry` = 12352;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace poco vine aquí para estudiar a los múrlocs Aleta Invernal. Disfrazado como uno de ellos, mantuve las distancias y tomé notas sobre su vida diaria.$B$BSin embargo, poco después de mi llegada todo cambió.$B$BUn terremoto en Gelidar causó un corrimiento de tierras que dejó al descubierto las cavernas cercanas. De ahí salió un extraño makrura que robó los renacuajos de los Aleta Invernal. Cuando los múrlocs entraron en las cavernas para recuperar a sus crías, volvieron a salir brillando y bajo la influencia del makrura.$B$BAhora los que no han sido afectados se han refugiado aquí y yo he tomado el mando a regañadientes.' WHERE `entry` = 12357;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Mis hermanos y hermanas se han vuelto locos! ¿Crees que puedes ayudarme a salir de aquí?' WHERE `entry` = 12361;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No tengo tiempo para charlas, hay trabajo que hacer!',  `text1_0_loc7` = '¿Tú puedes entenderme? ¡Yo puedo entenderte!', `text2_0_loc7` = '$R, ¿por qué iba a querer hablar contigo? ¡Probablemente fue tu gente la que provocó este desastre!', `text3_0_loc7` = 'Hay almejas que recoger y orcas que matar. ¡Ayuda o apártate!', `text4_0_loc7` = 'Blurglgr murbrgl... es broma. Te entiendo.', `text5_0_loc7` = '¿Ya has averiguado por qué se han vuelto locos nuestros hermanos y hermanas del poblado?', `text6_0_loc7` = 'Sabía que no deberíamos haber entrado en esas cuevas. ¡No es natural!', `text7_0_loc7` = 'Algo me huele a chamusquina en el nuevo rey Mrgl-Mrgl, pero no consigo averiguar el qué.' WHERE `entry` = 12363;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Atuik, cabecilla de los Kalu\'ak de Komawa.' WHERE `entry` = 12364;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Malditos sean Kael\'thas y sus sangrevil! Han destruido todo por lo que hemos trabajado, pero están locos si creen que no contraatacaremos. Por el momento, Lady Liadrin busca una nueva fuente de poder para nuestra orden. No nos destruirán tan fácilmente.' WHERE `entry` = 12370;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Al arrasar Orgrimmar no solo atacaron a los orcos, sino a toda la Horda. ¡Fue un ataque contra nuestra existencia!$B$BEl Rey Exánime pretendía sembrar el miedo.$B$BEsto...$B$B<Garrosh señala hacia las paredes fortificadas que le rodean.>$B$BEsto es el resultado de la campaña de Arthas.$B$BLa Horda no teme a nada...' WHERE `entry` = 12373;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Todo el mundo puede encontrar algo con lo que disfrutar en el Festival de Fuego del Solsticio de Verano!', `text0_1_loc7` = '¡Todo el mundo puede encontrar algo con lo que disfrutar en el Festival de Fuego del Solsticio de Verano!', `text1_0_loc7` = '¡Nuestras llamas arden fuertes y leales a través del territorio!', `text1_1_loc7` = '¡Nuestras llamas arden fuertes y leales a través del territorio!', `text2_0_loc7` = '¿Has bailado ya en el poste de las cintas?', `text2_1_loc7` = '¿Has bailado ya en el poste de las cintas?', `text3_0_loc7` = 'Admiro la habilidad de los malabaristas de antorchas.', `text3_1_loc7` = 'Admiro la habilidad de los malabaristas de antorchas.' WHERE `entry` = 12376;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Has llegado a una hoguera de la Alianza! ¡Profana esta hoguera y acaba con ella!' WHERE `entry` = 12377;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El miedo siempre ha sido nuestra arma más eficaz. Se han ganado y perdido guerras enteras apoyándose en los temblorosos hombros de la duda y la turbación. Entonces, ¿cómo se lucha contra un enemigo que no conoce el miedo?$B$B<Colmillosauro agita la cabeza.>$B$BEn Rasganorte no solo luchamos contra la Plaga, luchamos por mantener el control de nuestros sentidos antes de que nos destrocen...' WHERE `entry` = 12381;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí yace el cadáver de uno de los trabajadores de caravana de Quijacero.', `text0_1_loc7` = 'Aquí yace el cadáver de uno de los trabajadores de caravana de Quijacero.' WHERE `entry` = 12389;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Culto Crepuscular intenta deshacer todo lo que luchamos para preservar. Hemos jurado evitar que ocurra esta calamidad, $R.', `text0_1_loc7` = 'El Culto Crepuscular intenta deshacer todo lo que luchamos para preservar. Hemos jurado evitar que ocurra esta calamidad, $R.' WHERE `entry` = 12390;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos perdido a muchos de nuestros jinetes del viento en misiones de exploración al oeste. La Plaga no tiene piedad en sus ataques contra los posibles espías.$B$BPor suerte, he encontrado una solución que solo pone en peligro la vida del jinete. La tasa de supervivencia entre los que han sido transportados hasta las Llanuras de Nasam es del 30 ¡Eso es un 30más que cuando estábamos usando jinetes del viento!' WHERE `entry` = 12417;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tanto Desembarco de Garrosh como Pal\'ea fueron barridos por la niebla. ¡Somos los únicos suficientemente locos como para quedarnos aquí, colega!' WHERE `entry` = 12422;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Cómo quieres que trabaje sin mis cosas y con Palanqueta resoplándome en el cogote?' WHERE `entry` = 12423;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo que ves en esta costa no es fruto de la Plaga. De hecho, creemos que la Plaga también teme a esta niebla... puede que ese sea el motivo por el que no vienen aquí.$B$BVerás, la niebla está viva, colega. Dentro de esos zarcillos de aire yace la muerte...$B$BSi entras en sus dominios, te desvanecerás. ¡La niebla te llevará!$B$BNunca hemos visto a nadie regresar de la niebla con vida.' WHERE `entry` = 12424;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde que llegué aquí he visto horrores que antes solo habitaban en mis peores pesadillas, pero nada podría haberme preparado para lo que se oculta tras la niebla.$B$B<Mootoo asiente.>$B$BAl otro lado de esa niebla, a solo unos metros de aquí, se encuentran unas criaturas sedientas de sangre conocidas como Kvaldir. Nadie ha regresado de la niebla y pocos de los que habitaban aquí cuando se formó la niebla lograron huir. Desde entonces la zona ha sido declarada no habitable por Grito Infernal. Entra bajo tu propia responsabilidad...' WHERE `entry` = 12426;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Me alegro de verte! Estoy demasiado débil para romper los eslabones de esta cadena.$B$B¡Bueno no te quedes ahí! ¡Libérame!' WHERE `entry` = 12433;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ahora mismo estoy muy ocupado, $C.$B$B¿Qué puedo hacer por ti?' WHERE `entry` = 12435;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Habla despacio, $Gniño:niña;, tu extraño idioma es nuevo para mí.' WHERE `entry` = 12440;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Baja la voz, $R. Hamat es mayor y necesita descansar.' WHERE `entry` = 12441;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vale, siéntate entonces. Esto puede llevarnos un rato.$B$BCuando llegamos a este infierno congelado, el general Arlos, en la Fortaleza Denuedo, nos encomendó a mí y a mi grupo de temerarios pilotos una tarea especial: debíamos establecer una base aérea avanzada para las operaciones de la Alianza.$B$BEsta pista de aterrizaje es el resultado. Permíteme que te diga que no fue fácil con todas esas terribles bestias y el suelo durísimo por su estado de congelación. ¡Pero lo logramos y lo hicimos rápido!' WHERE `entry` = 12456;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'De todas formas, tuvimos que bombear mucha arena y aceite para la maquinaria. Las charcas cercanas resultaron perfectas para ello.$B$BIncluso parte de la arena tenía propiedades mágicas... ¿podríamos hablar de beneficios? Pero esa es otra historia.$B$BAsí que drenamos la mayoría del agua y construimos la estación de bombeo justo en el medio. Todo iba como la seda hasta que un día la tubería de succión principal se atascó.' WHERE `entry` = 12457;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ten en cuenta que esta parte la descubrí posteriormente, porque yo no estaba allí entonces.$B$BCuando la desmontaron, encontraron las piezas de un robot que parecía un gnomo. Por supuesto los muy insensatos trabajaron día y noche para reconstruirlo sin avisarnos.$B$BEs entonces cuando perdimos el contacto con la estación de bombeo.' WHERE `entry` = 12458;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tras un par de días sin noticias, envié a una partida de reconocimiento a la plataforma. Jamás regresaron.$B$BEnvié a otro grupo al día siguiente con el mismo resultado y perdí un par de máquinas voladoras durante el reconocimiento aéreo.$B$BLlegados a ese punto, envié a alguien en busca de ayuda al sur y nos preparamos para lo peor. Concentramos todo nuestro esfuerzo en hacer armaduras, armas y robots para dirigirnos hacia allí con toda nuestra fuerza.$B$BCuando lo hicimos, hace un par de días, no podíamos creer lo que encontramos.' WHERE `entry` = 12459;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como te iba contando, no era posible explicar lo que vimos allí.$B$BMi gente había desaparecido y en su lugar había un auténtico ejército de robots y androides campando a sus anchas.$B$BLos androides tenían aspecto de gnomos y dijeron que habían estado esperándonos. De hecho, a su modo, actuaban como si nos conocieran. Nos rodearon y rápidamente nos llevaron a lo más alto de la estación de bombeo.$B$B¡Fue entonces cuando vimos a su líder y lo que estaba haciendo con los gnomos supervivientes!' WHERE `entry` = 12460;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se hace llamar el maestro de palancas Mecazod. ¡Cuando llegamos, estaba ocupado trasvasando los cerebros de los supervivientes a cuerpos mecánicos!$B$BNos saludó con afecto y nos explicó que él era uno de los primeros gnomos creados por algo llamado \"el gran arquitecto\", un vigilante titán del legendario Ulduar.$B$BPor lo visto, él era el obstáculo que mi equipo había absorbido accidentalmente del lugar en el que se había estropeado miles de años atrás. ¡Y nosotros tuvimos que construir nuestra estación de bombeo justo sobre él!' WHERE `entry` = 12461;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los mecánicos de la estación lo habían reconstruido, devolviéndolo a la \"vida\".$B$BY ahora, como agradecimiento, nos iba a devolver el favor curándonos del mal que él denomina \"la maldición de la carne\".$B$BSegún Mecazod, es una dolencia que acaba afectando a todas las creaciones de los titanes. Dicho de otra forma, todos empezamos siendo robots de algún tipo y con el paso de los milenios poco a poco nos vamos volviendo seres de carne.$B$BLo sé, es absurdo.' WHERE `entry` = 12462;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A mí no me parecía una \"cura\" y no estaba dispuesto a quedarme parado escuchando a un robot demente mientras masacraba a mi gente.$B$BHicimos lo que cualquier gnomo en su sano juicio hubiera hecho... ¡corrimos! Bueno, de hecho, la mayoría de nosotros nos lanzamos en paracaídas desde la plataforma, pero te haces una idea.$B$BSolo unos pocos conseguimos regresar aquí y aún estamos bastante mermados, pues también nos tenemos que encargar de todo lo demás: el magnatauro al norte y la Plaga al este. ¡Por no mencionar que la Horda se ha establecido al noroeste!' WHERE `entry` = 12463;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Eso explica nuestro estado actual, $N.$B$BNo podemos encargarnos de esto nosotros solos y, para serte sincero, me siento un poco perdido en este asunto. Jinky sabe lo que hay que hacer. Deberías ir a verla ahora.$B$BSolo espero que seas capaz de sacarnos de este lío con nuestros cuerpos intactos.' WHERE `entry` = 12464;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las acciones de estos desertores nos ponen a todos en evidencia. Solo los que son testigos de su magnitud entienden la vergüenza que supone. Por eso Ug\'thor está aquí… Debe aprender.' WHERE `entry` = 12472;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He jurado bajo ley colmillarr que vengaría a mi pueblo... estos asesinos redujeron Pal\'ea a cenizas.' WHERE `entry` = 12477;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He tratado a campesinos, nobles, hijos de mercaderes y pordioseros. Permíteme que comparta algo de sabiduría contigo: cuando miramos a los ojos de la muerte, todos somos iguales.' WHERE `entry` = 12478;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Es justa tu causa y fiel tu corazón? Si es así, los Druidas por la Ética y Humanidad en el Trato de Animales te damos la bienvenida. Mientras no pasees por el oscuro camino de la depravación y hagas daño a los animales que nos han permitido coexistir en su mundo, D.E.H.T.A te considerará un aliado.' WHERE `entry` = 12485;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este piloto de reconocimiento no sobrevivió al accidente de su escuadrón.' WHERE `entry` = 12489;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entonces, $N, ¿en qué objeto estás interesado?' WHERE `entry` = 12494;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ah, rarezas y singularidades. Estamos especializados en objetos totalmente únicos de Azeroth para los aventureros más exigentes.$B$BYa que estás hablando conmigo, supongo que ya sabes qué buscas.' WHERE `entry` = 12495;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. ¿Has venido a honrar esta llama?$B$BRecuerda: las hogueras de nuestro festival arden en todas las regiones con un asentamiento de la Alianza. Saca tiempo para honrarlas a todas si puedes.$B$BLa Horda también tiene sus propios fuegos. Harías bien en profanar esos indignos esfuerzos cuando los veas.', `text0_1_loc7` = 'Saludos, $C. ¿Has venido a honrar esta llama?$B$BRecuerda: las hogueras de nuestro festival arden en todas las regiones con un asentamiento de la Alianza. Saca tiempo para honrarlas a todas si puedes.$B$BLa Horda también tiene sus propios fuegos. Harías bien en profanar esos indignos esfuerzos cuando los veas.' WHERE `entry` = 12504;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Observad las llamas del verano!', `text0_1_loc7` = '¡Observad las llamas del verano!', `text1_0_loc7` = '¡Cuidado, no te quemes!', `text1_1_loc7` = '¡Cuidado, no te quemes!', `text2_0_loc7` = '¿Disfrutando del Festival del Fuego?', `text2_1_loc7` = '¿Disfrutando del Festival del Fuego?', `text3_0_loc7` = '¡Te doy la bienvenida! ¿Has honrado ya la llama de esta tierra?', `text3_1_loc7` = '¡Te doy la bienvenida! ¿Has honrado ya la llama de esta tierra?' WHERE `entry` = 12506;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Este mundo ha sido dañado más allá de lo que nuestros ojos pueden ver. Incluso el paso del tiempo parece diferente en cierto modo.' WHERE `entry` = 12507;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Solo me faltaba que la maldita Plaga nos vaya a echar de aquí! Los colonos lo dimos todo para comenzar nuevas vidas en Rasganorte.$B$BSerá mejor que creas que vamos a luchar con uñas y dientes.' WHERE `entry` = 12521;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $N. Soy Durkot Hermano Lobo, protector de la manada.' WHERE `entry` = 12523;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡La Plaga está por todas partes! Esto es... una pesadilla.' WHERE `entry` = 12525;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Arthas cree que algunos de sus gusanos no-muertos van a hacer que volvamos corriendo a los Reinos del Este... está muy equivocado.' WHERE `entry` = 12526;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En esta tierra están actuando fuerzas ocultas, $N. Mantén los ojos abiertos y el valor firme.' WHERE `entry` = 12527;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Algunos intentamos evitar las tareas militares. Un buen pescador siempre viene bien... incluso los generales necesitan comer.' WHERE `entry` = 12528;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Otro día más trabajando en la cocina. Después de un tiempo se vuelve un rollo.' WHERE `entry` = 12529;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi turno casi ha terminado. No puedo esperar para plegar la oreja, ¿sabes a qué me refiero?' WHERE `entry` = 12530;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hace tiempo Arthas fue un príncipe humano. El ejército que guió hasta Rasganorte sufrió un fatídico destino.$B$BMi hermano Thassarian era miembro de aquel ejército.' WHERE `entry` = 12534;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Somos mineros, granjeros y artesanos, no soldados. Pero defenderemos nuestra tierra con los puños desnudos si es necesario.' WHERE `entry` = 12538;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Puedo traerte algo? Mi surtido es un poco limitado, pero era de esperar... teniendo en cuenta la situación.' WHERE `entry` = 12553;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Eh?' WHERE `entry` = 12554;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Morir por su país es el deber de un soldado. Pero si decir que esta guerra es una locura es traición, entonces soy un traidor.' WHERE `entry` = 12555;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Concéntrate, mortal. Esta es la línea de frente de una guerra como jamás has visto.$B$BNecesitarás el control total de tus facultades si quieres servirnos de algo.' WHERE `entry` = 12566;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Esta región está muy alborotada!$B$BLos oscuros cabalgan de nuevo junto con sus San\'layn, los dragones luchan en su gran guerra de El Nexo y los vrykuls han vuelto a despertar.$B$BTu gente navega hacia el norte en sus barcos de madera y luchan unos contra otros y nosotros, los Kalu\'ak, estamos atrapados en medio.$B$BY ahora, mediante alguna temible maldad, la tierra explota con la energía mágica del mundo. Tenemos que encontrar un refugio seguro donde tengamos una posibilidad de sobrevivir. Estos dos embajadores me dan dolor de cabeza... quizá tú puedas ayudar a mi gente, $R.' WHERE `entry` = 12575;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Kvaldir no tienen piedad, $N. Pero no puedo abandonar la sagrada Kashala. Si no abandonamos a los espíritus, ellos tampoco nos abandonarán.' WHERE `entry` = 12578;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Están atacando Kashala! Debemos mantenerlos a distancia hasta que podamos escapar.' WHERE `entry` = 12579;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Malditos hombres-gigante! ¡Si quieren lucha, lucha tendrán!' WHERE `entry` = 12580;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. ¿Has venido a honrar esta llama?$B$BRecuerda: las hogueras de nuestro festival arden en todas las regiones con un asentamiento de la Horda. Saca tiempo para honrarlas a todas si puedes.$B$BLa Alianza también tiene sus propios fuegos. Harías bien en profanar esos indignos esfuerzos cuando los veas.', `text0_1_loc7` = 'Saludos, $C. ¿Has venido a honrar esta llama?$B$BRecuerda: las hogueras de nuestro festival arden en todas las regiones con un asentamiento de la Horda. Saca tiempo para honrarlas a todas si puedes.$B$BLa Alianza también tiene sus propios fuegos. Harías bien en profanar esos indignos esfuerzos cuando los veas.' WHERE `entry` = 12582;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este joven colmillarr parece haber muerto en combate en vez de ahogado.' WHERE `entry` = 12585;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En una sola palabra: magia.$B$BLos irresponsables mortales con sus cortas vidas no piensan en los efectos a largo plazo de sus acciones.$B$BLas energías Arcanas han sido liberadas de forma demasiado liberal, desbaratando el equilibrio del mundo. Si esto sigue así, Azeroth pronto será aniquilado.$B$BAmbas partes están de acuerdo en esto. Pero antes de que se pudiera planear un concilio, Malygos puso en marcha su plan.$B$BNo hemos tenido más elección que enfrentarnos a él.' WHERE `entry` = 12590;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estamos progresando, $R, pero demasiado despacio.' WHERE `entry` = 12591;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tenemos que lograrlo! El destino de Azeroth depende de ello...' WHERE `entry` = 12592;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Malygos el Tejehechizos, el Señor de la Magia, maestro del Vuelo Azul. Es el aspecto dragón azul, una de las criaturas más antiguas de Azeroth.$B$BDicen que se está recuperando de una locura que ha durado milenios, pero sus acciones recientes me hacen pensar que aún no está cuerdo.$B$BLa solución que propone para nuestro problema es buscar y matar a todos los mortales de Azeroth que usen la magia.$B$BAl ser el guardián de la vida, el Vuelo Rojo se ha visto obligado a entrar en acción.$B$BAhora un dragón lucha contra otro y el destino de nuestro frágil mundo depende del resultado.' WHERE `entry` = 12593;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Me alegra que estés aquí, $C. La situación militar en la Meseta de La Fuente del Sol es bastante desalentadora.' WHERE `entry` = 12596;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La capitana Selana me ha ordenado que colabore con tus esfuerzos aquí, en la Meseta de La Fuente del Sol.' WHERE `entry` = 12597;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Ofensiva Sol Devastado ha realizado un gran progreso en las zonas exteriores de la Isla de Quel\'Danas. Están rechazando a las tropas de Kil\'jaeden y, en medio del caos, he logrado guiar a una pequeña fuerza de exploradores dentro de la Meseta de La Fuente del Sol. Mis tropas avanzaron a través de la Plaza del Parhelio, avistaron a un grupo de elfos de sangre Filosol y se enzarzaron en una lucha contra el enemigo. Esta lucha no tardó en convertirse en un desastre.' WHERE `entry` = 12598;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los defensores de Kil\'jaeden aquí, en La Fuente del Sol, son mucho más fuertes que cualquier cosa a la que nos hayamos enfrentado fuera de la isla. La Legión ha distorsionado nuestra tecnología de los guardianes Arcanos con energía vil para crear algo mucho más siniestro. Uno solo de esos protectores Filosol diezmó mis tropas y tuve que ordenar una retirada rápida. Esperaba que expulsaran a mi escuadrón de la isla, pero me sorprendieron dejando de perseguirnos.' WHERE `entry` = 12599;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No sé, pero me alegra que lo hicieran. Este respiro ha permitido al anacoreta Elbadon salvar a muchos de los hombres y mujeres que están bajo mi mando. He enviado a algunos de mis exploradores más sigilosos y me han informado de algo interesante. ¡Casi todos los protectores Filosol se han quedado sin energía! Parece que las tropas de Kil\'jaeden están conservando su energía y parecen satisfechos conteniéndonos con un solo protector activo. Si tú y tus compañeros queréis entrar por la fuerza, permíteme que te sugiera un plan de ataque.' WHERE `entry` = 12600;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Aprovechaos de sus protectores Filosol desactivados. Si alguno de sus exploradores Filosol os ve, seguro que intentará reactivar un protector Filosol cercano. Matadlo rápido para evitar eso. Tras eliminar a los elfos de sangre, siempre y cuando no activéis los sensores de proximidad de los protectores, podréis pasar sin tener que luchar. Eso os ahorrará tiempo y recursos.' WHERE `entry` = 12601;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todas las barreras que impedían que llegaras a La Fuente del Sol han sido destruidas. ¡Entra en La Fuente del Sol y evita que Kil\'jaeden entre en este mundo!' WHERE `entry` = 12604;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás disfrutando del festival, $C?$B$BMientras los vigilantes de la Llama se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...', `text0_1_loc7` = '¿Estás disfrutando del festival, $C?$B$BMientras los vigilantes de la Llama se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...' WHERE `entry` = 12609;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Budd se queda muy quieto, haciendo como que no te escucha. Inspeccionándolo de cerca puedes ver un poco de baba caer bajo su máscara.>',  `text1_0_loc7` = '<Budd se balancea en cuclillas, farfullando algo que parece ser lengua trol.>', `text2_0_loc7` = 'Pírate, colega.', `text3_0_loc7` = 'No es momento para hablar, colega. Necesito descansar...', `text4_0_loc7` = '¡Poder trol, colega!', `text5_0_loc7` = '¿Qué te pasa, colega? ¿Nunca has visto un trol?' WHERE `entry` = 12617;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tralladón nunca volverá a su antigua gloria, pero... ¡Juro que esas escorias marineras pagarán por lo que han hecho!' WHERE `entry` = 12626;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué miras tú, asere?' WHERE `entry` = 12634;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Jamás comprendí pol qué algunos piratas entierran sus tesoros. A mí me gusta tenerlo a mano.' WHERE `entry` = 12636;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Picanorte, el rompehielos de vapor de la Alianza, navega desde aquí hasta el Puerto de Menethil, en Los Humedales, a través del mar de los Reinos del Este.' WHERE `entry` = 12638;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puerto Moa\'ki ha sido bendecido con dos grandes tortugas marinas: Caminante de las Olas e Isla Verde. Nadan entre Unu\'pe y Komawa.' WHERE `entry` = 12640;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tienes ganas de ir a Rasganorte, ¿verdad? El Kraken a vapor navega desde aquí hasta la Fortaleza Denuedo, en la Tundra Boreal.' WHERE `entry` = 12641;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola! Echas de menos los Reinos del Este, ¿verdad? Bueno, si realmente quieres volver, el Kraken navega de vuelta a Ventormenta. ¡Yo agradecería un clima más cálido!' WHERE `entry` = 12644;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestra bendita gran tortuga marina, Isla Verde, viaja desde aquí hasta Puerto Moa\'ki, en el Cementerio de Dragones.' WHERE `entry` = 12645;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ey. ¿Quieres volver al viejo mundo? Vuela hasta los Claros de Tirisfal con estilo a bordo del Besanubes.' WHERE `entry` = 12646;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola. Si quieres llegar a Rasganorte, El Viento Poderoso es la mejor manera de ir. Te llevará directamente al Bastión Grito de Guerra, en la Tundra Boreal.' WHERE `entry` = 12647;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Rasganorte aguarda, $R. El majestuoso Besanubes te llevará a través del mar hasta Campo Venganza en el Fiordo Aquilonal.' WHERE `entry` = 12649;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Vas a casa, $C? El Fuerte Viento te llevará hasta allí si tu hogar está en Kalimdor. Vuela directamente desde aquí hasta Durotar.' WHERE `entry` = 12650;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola! Los pilotos de Palanqueta son lo mejorcito de lo mejorcito. Lo conseguimos a base de formación continua. Eso incluye formación en paracaidismo. Nunca sabes cuándo vas a tener que saltar, pero sabes que algún día tendrás que hacerlo.$B$BAsí que esto de aquí es un teletransportador para ir a lo alto de esta aguja. Una vez arriba, la única forma de la que puede bajar un verdadero soldado es con un paracaídas. Tenemos otro soldado estacionado allí arriba con los paracaídas.$B$BSi te apetece probar, $C, daré el visto bueno para que uses el teletransportador. Solo tendrás que entrar en él.', `text0_1_loc7` = '¡Hola! Los pilotos de Palanqueta son lo mejorcito de lo mejorcito. Lo conseguimos a base de formación continua. Eso incluye formación en paracaidismo. Nunca sabes cuándo vas a tener que saltar, pero sabes que algún día tendrás que hacerlo.$B$BAsí que esto de aquí es un teletransportador para ir a lo alto de esta aguja. Una vez arriba, la única forma de la que puede bajar un verdadero soldado es con un paracaídas. Tenemos otro soldado estacionado allí arriba con los paracaídas.$B$BSi te apetece probar, $C, daré el visto bueno para que uses el teletransportador. Solo tendrás que entrar en él.' WHERE `entry` = 12658;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Buenas, colega! Siéntate y echa un trago.' WHERE `entry` = 12659;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todo lo que deseaba era volver a servir a mi rey. Por la humanidad, contra un enemigo común.$B$BPero sin embargo descubro que hay inmundos cultores infiltrados entre nosotros... y todas las señales apuntan hacia Naxxanar como origen de esta podredumbre.' WHERE `entry` = 12663;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'De aquí es de donde vienen las órdenes de los cultores. Su líder aparecerá pronto.$B$BNo dejemos pasar esta oportunidad.' WHERE `entry` = 12664;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Eh? ¿Qué? Lárgate.' WHERE `entry` = 12666;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si consigo crear una nueva máquina de guerra, sé que conseguiré un buen dinerito por ella en la Puerta de Cólera.$B$BAlgunas de nuestras anteriores acciones no han sido del todo... estables, pero mi máquina voladora debería ser suficiente si consigo desarrollar un sistema de bombardeo efectivo para ella.' WHERE `entry` = 12667;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tenemos dinero que conseguir! Ya lo decía papi: los mejores tiempos para hacer fortuna son los tiempos de guerra.' WHERE `entry` = 12668;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sientes una extraña presencia mientras permaneces frente al ídolo.' WHERE `entry` = 12669;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Te doy la bienvenida! Si necesitas un lugar para descansar, puedo solucionarlo.', `text0_1_loc7` = '¡Te doy la bienvenida! Si necesitas un lugar para descansar, puedo solucionarlo.' WHERE `entry` = 12673;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Será mejor que te marches de aquí si no quieres acabar dentro de una de esas jaulas...' WHERE `entry` = 12694;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Haz lo que puedas para mantener a la Plaga a raya, soldado! Informa de cualquier actividad sospechosa y mata primero, ¡ya preguntarás después!' WHERE `entry` = 12698;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Recuerda mis palabras, estos insensatos Renegados nos conducirán a la muerte a todos.',  `text1_0_loc7` = 'Si me preguntas a mí, y lo has hecho, creo que deberíamos matar a todos los boticarios antes de que sea demasiado tarde.', `text2_0_loc7` = 'La Horda da mucha importancia al honor y a la guerra. Estoy seguro de que no hay nada de honorable en lo que están haciendo los boticarios.', `text3_0_loc7` = 'Estoy vigilando a esos idiotas Renegados de ahí abajo. ¡Ninguna precaución está de más con esa gente!', `text4_0_loc7` = 'Si tienes tratos con el doctor de allí, ten cuidado. Los que se traen entre manos no es natural.' WHERE `entry` = 12699;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Colega, los baldíos al este de este lugar están llenos de cuerpos. ¡Aléjate de esta zona si quieres vivir!' WHERE `entry` = 12701;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Impresionantes, ¿verdad? Estos gigantes serían un forra… estoooo... unos aliados perfectos. Este es su líder, pero sigue negándose a hablar conmigo.' WHERE `entry` = 12706;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $R. Puedes compartir este cañón con nosotros para refugiarte de los dragones. Pero ten cuidado con el renacuajo goblin... los de su calaña no son de fiar.' WHERE `entry` = 12707;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si tuviera dinero suficiente, podría conquistar el mundo...' WHERE `entry` = 12708;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta consola se encuentra delane de unos escombros envueltos en vapor. Debajo se oye el sonido de un motor.', `text0_1_loc7` = 'Esta consola se encuentra delane de unos escombros envueltos en vapor. Debajo se oye el sonido de un motor.' WHERE `entry` = 12709;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; al Templo del Reposo del Dragón, caminante.$B$BAquí los Vuelos conviven en paz unos con otros entre los huesos de sus honrados ancestros.$B$BAl menos, había paz hasta que el Vuelo Azul inició un ataque sorpresa contra el Templo.$B$BEntra con nuestra bendición, pero ten en cuenta que no toleraremos ninguna agresión en este lugar sagrado.' WHERE `entry` = 12713;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Quieres volver abajo, $C?$B$BPuedo hacer que uno de estos dracos te lleve a salvo hasta donde quieras ir.' WHERE `entry` = 12714;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No serás $Gun verdadero:una verdadera; colega hasta que hayas matado un dragón... colega.' WHERE `entry` = 12725;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No hay ninguna emoción como la de una caza de dragones.' WHERE `entry` = 12726;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, $R. Siento que la primera vez que veas nuestra tierra sagrada sea en este estado.' WHERE `entry` = 12727;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Por qué estás aquí?' WHERE `entry` = 12730;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aberraciones de la Plaga... Muy pronto recibirán su merecido.' WHERE `entry` = 12733;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Acércate al fuego y descansa, $R. Espero que no te moleste el olor a pescado.', `text0_1_loc7` = 'Acércate al fuego y descansa, $R. Espero que no te moleste el olor a pescado.' WHERE `entry` = 12738;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos luchado a través de los valles y cimas del Monte Hyjal, a través de las arenas movedizas de Silithus, contra los temibles ejércitos de la Legión. Somos los hijos sin nombre ni rostro de la Alianza. Por la Luz y por el poder de la Alianza, el primer golpe nos pertenece y el último golpe es todo lo que ven nuestros enemigos.$B$BSomos la Séptima Legión.' WHERE `entry` = 12739;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Saludos! ¿Necesitas algo?' WHERE `entry` = 12740;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi G.U.A.O. es más que un objetivo al que lanzarle jarras, es un recurso muy valioso para los entrenamientos. ¡Si puedes acertar al G.U.A.O., podrás acertar a cualquier cosa!$B$BPero procura no golpearme a mí.' WHERE `entry` = 12744;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Joe el Cojo, ¿eres tú? ¡Eres tú!' WHERE `entry` = 12752;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué te trae por aquí, $Gpequeño:pequeña;?' WHERE `entry` = 12753;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El cielo no es seguro, colega. ¡Mantente alerta!' WHERE `entry` = 12756;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ten cuidado por dónde pisas en el jardín de Ysera, mortal. Las cosas no siempre son lo que parecen.' WHERE `entry` = 12758;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Necesitas algo? ¿Quizá que reparen tu arma o armadura? ¿Ehmmm?' WHERE `entry` = 12759;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La belleza natural de estas Colinas oculta una gran oscuridad bajo ellas. Ten cuidado, colega.' WHERE `entry` = 12773;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi padre me ha regalado esta armadura de batalla y esta hacha. Pronto les daré buen uso.' WHERE `entry` = 12777;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Por suerte, la letrina está vacía.' WHERE `entry` = 12779;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tenemos poca comida y bebida. Me temo que no tengo nada que ofrecerte.' WHERE `entry` = 12780;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Gracias por tu ayuda, $N. Estos gelifructus son, literalmente, el fruto de tu trabajo.' WHERE `entry` = 12781;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando descubrí que tenía parientes aquí, en el Norte, vine a averiguar más cosas. Lo que encontré fueron tribus en lucha peleando contra nuestra propia naturaleza.$B$BNo me queda esperanza, $R. Este lugar maldito ha logrado corromper incluso al gran Ursoc.$B$BHe perdido toda la esperanza.' WHERE `entry` = 12785;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí... Siento la magia de Vordrassil... y no puedo percibir ningún tipo de corrupción.$B$B¡Por favor! Tienes que permitir que te ayude. El destino me ha traído aquí, ¡quizá aún tenga algún propósito!' WHERE `entry` = 12787;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Zybarus te da las gracias desde el fondo de su corazón, héroe. Sin ti, Zybarus habría muerto, o peor, se habría convertido en uno de ellos...' WHERE `entry` = 12790;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace tiempo caminaba por las Colinas Pardas, pues era hijo del gran Ursoc, quien reinaba en esta tierra.$B$BPero ahora... ahora estoy condenado a pasar el resto de mi vida en esta cueva.' WHERE `entry` = 12793;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No lo entiendes. Si quisiera, podría salir de esta cueva luchando.$B$BEs mi padre, Ursoc. Ha regresado de entre los muertos... pero ya no es el mismo.$B$BDio su vida durante la Guerra de los Ancestros hace miles de años. Durante siglos, los fúrbolgs que le rendían pleitesía intentaron encontrar un modo de resucitarlo.$B$BFinalmente, decidieron que iban a intentar que el fallido árbol del mundo, Vordrassil, volviera a crecer y usar su magia para resucitar a mi padre.' WHERE `entry` = 12794;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Habla en voz baja, $C. Soy una espía de Rencor Venenoso disfrazada.' WHERE `entry` = 12795;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El esqueje que crearon tenía un fruto. Al principio pareció funcionar. Ursoc volvió y, durante un breve instante, me llené de alegría.$B$BA medida que se iba recuperando, era evidente que no era el mismo. Tampoco lo eran los fúrbolgs.$B$BEl mal que hizo que los druidas destrozaran a Vordrassil volvió a manifestarse en el nuevo árbol y los convirtió a todos en monstruos sedientos de sangre.$B$BEsa cosa que camina ahora por las Colinas Pardas no es mi padre y ha de ser devuelto a su descanso eterno.' WHERE `entry` = 12796;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Kodian, hija de Ursoc. Vigilo este santuario que en otro tiempo fue sagrado.$B$BNo he visto a ningún extraño venir aquí desde hace mucho tiempo.' WHERE `entry` = 12797;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un fúrbolg llamado Tur Patafuria. No es uno de los nuestros.$B$BÉl viene de las tierras del sur. Pertenecía a la tribu Fauces de Madera.$B$BQuería ver a Ursoc. Parecía creer que la corrupción de Vordrassil podía ser purificada y utilizada para salvar a mi padre.$B$BEs una locura albergar tales esperanzas. Estarás de acuerdo conmigo cuando veas la oscuridad que le ha afectado.' WHERE `entry` = 12798;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Saludos, $C.$B$B¿Cómo puedo ayudarte?' WHERE `entry` = 12810;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El temperamental protodraco del señor feudal Torvald Eriksson está encadenado aquí arriba.' WHERE `entry` = 12811;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí? ¿Qué ocurre?' WHERE `entry` = 12812;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'En este baldío helado, estamos aprendiendo rápidamente el significado de la muerte, $N. El poder del Rey Exánime se extiende por cada centímetro de Rasganorte, penetrando en la propia tierra. Él vuelve a nuestros caídos contra nosotros.$B$BDesde el cielo, jinetes de grifos resucitados nos atacan con sus vengativos martillos de tormenta mientras los muertos andantes nos atacan en tierra.' WHERE `entry` = 12823;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Supongo que Krenna duda de mis habilidades. Mientras nuestros guerreros de élite caminan para enfrentarse a la Alianza y sus aliados tramperos, o se dedican a causar estragos entre los trols, a mí me han ordenado eliminar a esos molestos vrykuls Desuelladragones.$B$BQuizá si unimos nuestros esfuerzos, podamos mostrarle lo que valemos a la conquistadora.' WHERE `entry` = 12824;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; al Refugio Pino Ámbar, $C.' WHERE `entry` = 12825;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué? ¿No ves que estoy ocupado?$B$B¡¿Crees que esta transmateria va a arreglarse sola?!' WHERE `entry` = 12842;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El alto señor Bolvar Fordragón lucha por todos nosotros. Si no fuera por él, Arthas estaría sentado sobre el trono de Ventormenta en este preciso instante.' WHERE `entry` = 12843;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estoy a punto de descifrar esta mena. ¡En cualquier momento tendré la solución!' WHERE `entry` = 12844;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí, $Ghijo mío:hija mía;?' WHERE `entry` = 12846;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El sumo abad te estudia un instante y sonríe.>$B$BMuy bien, conozco un lugar, $Ghijo:hija;. Tú besa mi anillo y avísame cuando te sientas en condiciones.' WHERE `entry` = 12848;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entonces, únete a mí en la entrada de la abadía. Estaré allí en seguida.' WHERE `entry` = 12849;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He jugado mi papel destruyendo civilizaciones enteras... pero tener que rebajarme a tratar con los desdichados miembros del Culto de los Malditos me repugna.' WHERE `entry` = 12850;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Habla en voz baja. ¡No queremos llamar la atención de estas bestias!' WHERE `entry` = 12852;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No me molestes con nimiedades!' WHERE `entry` = 12853;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No ves que estoy ocupado! ¡Nunca conseguiremos que esta base esté lista cuando regrese el Príncipe!',  `text1_0_loc7` = 'Si esas bestias salvajes no hubieran quemado nuestros barcos, ya estaríamos en casa. ¡Ahora déjame en paz para que pueda seguir trabajando!' WHERE `entry` = 12856;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Cruzada Argenta tiene el control aquí, colega. ¡No íbamos a dejar que te llevaras toda la gloria!' WHERE `entry` = 12857;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Esos malditos campesinos tienen que trabajar más deprisa! Quiero volver a casa, a Forjaz. Echo de menos a mi mujer e hijos.',  `text1_0_loc7` = 'El Capitán dice que en cuanto el príncipe Arthas regrese, volveremos a Lordaeron.', `text2_0_loc7` = 'No sé por qué está tardando tanto el joven Príncipe, pero no creo que podamos aguantar mucho más. Algunos de los soldados están empezando a notar el frío.' WHERE `entry` = 12858;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando vimos que esas bestias salvajes habían quemado nuestros barcos, nos quedamos con el corazón en un puño. Jamás me había sentido tan indefenso...',  `text1_0_loc7` = 'Rabia. Eso resume mis sentimientos hacia este desastre. Nos dejamos la piel intentando atravesar los bosques y llegar a la orilla, solo para descubrir que era demasiado tarde. Nuestra única forma de volver a casa había sido destruida.', `text2_0_loc7` = 'A pesar de todos los apuros a los que hemos tenido que hacer frente en estos baldíos, creo que el joven Príncipe nos guiará hasta la victoria. Rezo cada noche para que esté a salvo.' WHERE `entry` = 12859;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El lacayo olvidado agita la cabeza.>$B$BNo podemos continuar así, colega. Si el príncipe Arthas no regresa pronto, moriremos todos.',  `text1_0_loc7` = 'Nunca tendremos estos barcos listos a tiempo. El invierno se acerca y con él nuestra perdición. Temo que el joven Príncipe nos ha condenado a todos.', `text2_0_loc7` = 'Hace mucho que Arthas y Muradin se han ido. Espero que no estén en peligro.' WHERE `entry` = 12860;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Dónde puedo enviarte?' WHERE `entry` = 12862;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un buen club puede marcar la diferencia en el mundo.', `text0_1_loc7` = 'Un buen club puede marcar la diferencia en el mundo.' WHERE `entry` = 12863;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola, colega! Veo que tienes algunas fichas de la Fiesta de la cerveza. Resulta que yo aún tengo algunos objetos de la Fiesta de la cerveza a la venta.', `text0_1_loc7` = '¡Hola, colega! Veo que tienes algunas fichas de la Fiesta de la cerveza. Resulta que yo aún tengo algunos objetos de la Fiesta de la cerveza a la venta.' WHERE `entry` = 12864;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mola ser miembro del Club de la \"Cerveza del Mes\".', `text0_1_loc7` = 'Mola ser miembro del Club de la \"Cerveza del Mes\".' WHERE `entry` = 12869;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Con suficientes fichas puedes unirte al \"Club de la Cerveza del Mes\". ¡Formar parte del Club de la Cerveza del Mes es como celebrar la Fiesta de la cerveza durante todo el año!$B$BCada mes recibirás un correo con una nueva cerveza para degustar. Si te gusta esa cerveza, puedes ir a la taberna Roca de Fuego de Forjaz y comprar más a los vendedores de allí.' WHERE `entry` = 12873;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿No has oído hablar del Club \"de la Cerveza del Mes?\" A esos tipos les encanta estar de fiesta. ¡Si fuera por ellos, la Fiesta de la cerveza nunca acabaría! A veces, la del año pasado parecía no haber acabado.$B$BPero ahora han llegado a la conclusión de que la mejor manera de mantener vivo el espíritu de la Fiesta de la cerveza es permitir que la gente se una a su club.$B$B¡Puedes unirte por solo unas fichas! Todos los meses se te enviará cerveza y podrás comprar esa misma cerveza si te gusta. Se trata de material de máxima calidad y es una forma de asegurarse de que el espíritu de la Fiesta de la cerveza no se vaya nunca.', `text0_1_loc7` = '¿No has oído hablar del Club \"de la Cerveza del Mes?\" A esos tipos les encanta estar de fiesta. ¡Si fuera por ellos, la Fiesta de la cerveza nunca acabaría! A veces, la del año pasado parecía no haber acabado.$B$BPero ahora han llegado a la conclusión de que la mejor manera de mantener vivo el espíritu de la Fiesta de la cerveza es permitir que la gente se una a su club.$B$B¡Puedes unirte por solo unas fichas! Todos los meses se te enviará cerveza y podrás comprar esa misma cerveza si te gusta. Se trata de material de máxima calidad y es una forma de asegurarse de que el espíritu de la Fiesta de la cerveza no se vaya nunca.' WHERE `entry` = 12878;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿No has oído hablar del Club \"de la Cerveza del Mes\"? ¡Son un montón de tipos muy guapos que quieren celebrar la Fiesta de la cerveza durante todo el año! Intentaron volver a traer la Fiesta de la cerveza en el pasado, pero nunca duró demasiado.$B$BCuando te hayas unido al Club, se te enviará por correo una cerveza diferente cada mes. Si te gusta esa cerveza, puedes hablar con los vendedores de Forjaz para conseguir más.$B$BCreo que tienes belleza suficiente para unirte al Club. Tú me das unas cuantas fichas y yo te daré los formularios. ¡Quizá podamos celebrar la Fiesta de la cerveza juntos durante todo el año!', `text0_1_loc7` = '¿No has oído hablar del Club \"de la Cerveza del Mes\"? ¡Son un montón de tipos muy guapos que quieren celebrar la Fiesta de la cerveza durante todo el año! Intentaron volver a traer la Fiesta de la cerveza en el pasado, pero nunca duró demasiado.$B$BCuando te hayas unido al Club, se te enviará por correo una cerveza diferente cada mes. Si te gusta esa cerveza, puedes hablar con los vendedores de Forjaz para conseguir más.$B$BCreo que tienes belleza suficiente para unirte al Club. Tú me das unas cuantas fichas y yo te daré los formularios. ¡Quizá podamos celebrar la Fiesta de la cerveza juntos durante todo el año!' WHERE `entry` = 12880;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Los muertos se levantan de nuestra propia cripta!' WHERE `entry` = 12881;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Lo lograste, $G chico:chica;! ¡Pensé que estabas en las últimas!' WHERE `entry` = 12882;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La reina Alexstrasza me ha encargado la defensa del Templo del Reposo del Dragón.$B$BComo ya sabes, Malygos ordenó a sus tropas que destruyeran el ancla de una línea Ley cercana. Entonces, las fuerzas del Vuelo redirigieron ese poder hacia el Santuario de Dragones Azur.$B$BLos dragones azules están usando ese poder para fortalecerse; ¡tanto que se atreven a atacar el mismísimo templo! Están empezando a tener ventaja.$B$B¿Estás aquí para ayudarnos, $R?' WHERE `entry` = 12887;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $R.$B$BTe estaba esperando.' WHERE `entry` = 12899;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muy bien. ¡Vamos!' WHERE `entry` = 12900;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los grifos de las cimas de Rasganorte son los más fuertes de la especie.' WHERE `entry` = 12904;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No pienses ni por un momento que tus logros pasados te dan derecho a hacer lo que sea, $N. Porque no es así.$B$BSi quieres mi respeto, tendrás que ganártelo. Tendrás que seguir mis órdenes sin cuestionarlas y cumplirlas... sin excepción.' WHERE `entry` = 12906;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola. El escudero Walter y yo somos responsables del cuidado de la vieja Blanchy y de las demás monturas de aquí.' WHERE `entry` = 12909;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al Campamento Oneqwah.' WHERE `entry` = 12912;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'G.U.A.O. es más que un objetivo al que lanzarle jarras, es un recurso muy valioso para los entrenamientos. ¡Si puedes acertar al G.U.A.O., podrás acertar a cualquier cosa!$B$BPero procura no golpearme a mí.' WHERE `entry` = 12913;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Belgaristrasz dice la verdad; es cierto que la situación es grave. $B$BPuedo otorgarte el poder de invocar un draco del Vuelo Verde. Habla con Belgaristrasz o Eternos si prefieres aprovechar el poder del Rojo o el Bronce.' WHERE `entry` = 12915;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Varos Zancanubes y sus guardianes del anillo protegen el segundo anillo. Tus dracos pueden apañárselas contra los guardianes del anillo, pero Varos se encuentra tras un escudo impenetrable creado con la energía del propio Oculus. Diez ensamblajes de centrifugadora dan potencia al escudo desde el anillo y las plataformas de encima. Destrúyelos y Varos será vulnerable.$B$BPuede otorgarte el poder de invocar a un draco del Vuelo Rojo. Habla con Eternos o Verdisa si prefieres aprovechar el poder del Vuelo Bronze o del Vuelo Verde.' WHERE `entry` = 12916;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Belgaristrasz dice la verdad; ¡el tiempo se agota!$B$BPuedo otorgarte el poder de invocar un draco del Vuelo Bronze. Habla con Belgaristrasz o Verdisa si prefieres aprovechar el poder del Rojo o el Verde.' WHERE `entry` = 12917;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mi enseñanzas solo son útiles para los magos. No puedo ayudarte, $C.' WHERE `entry` = 12932;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Fiesta de la cerveza ha sufrido algunos cambios desde el primer año. Ahora entregamos fichas de la Fiesta de la cerveza en vez de vales. Si tienes vales viejos, puedo cambiártelos.', `text0_1_loc7` = 'La Fiesta de la cerveza ha sufrido algunos cambios desde el primer año. Ahora entregamos fichas de la Fiesta de la cerveza en vez de vales. Si tienes vales viejos, puedo cambiártelos.' WHERE `entry` = 12934;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Vienes para viajar, $R?$B$B¿O te preocupa algo más?' WHERE `entry` = 12937;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te doy la bienvenida, $Gaventurero:aventurera;. Llegas justo a tiempo.' WHERE `entry` = 12939;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Thel\'zan debe morir, ¡aunque solo sea para detener el incesante parloteo!' WHERE `entry` = 12940;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Publicaciones oficiales de la ciudadanía y oficiales de Hibergarde.' WHERE `entry` = 12941;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Te inquieta estar ante la presencia de un dragón negro?$B$BBien. Debería. También inquieta a estos dragones. Pero se aferran a sus ancestrales virtudes, no osarían atacarme aquí.$B$BInsensatos.' WHERE `entry` = 12942;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí?' WHERE `entry` = 12946;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El Vuelo Infinito está tratando de alterar el destino del príncipe Arthas Menethil. Ahora lo conoces como el Rey Exánime, pero aquí y en este momento aún era un príncipe de Lordaeron que trataba de hacer lo que era mejor para su reino. Hoy Arthas toma una decisión que marcará el destino, llevando a lo que los Vigilantes del Tiempo llamamos un punto de inflexión en la línea temporal.' WHERE `entry` = 12949;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Arthas ordenó arrasar Stratholme, matando a todos los humanos que hubiera dentro de sus murallas. Descubrió pruebas de que la Plaga había infectado la ciudad con su insidiosa peste y la enfermedad no solo mata a la gente. Los que mueren debido a esta peste se vuelven a despertar convertidos en zombis descerebrados que alimentan la máquina de guerra de la Plaga.' WHERE `entry` = 12950;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los Infinitos están intentando algo con una perspicacia poco común en ellos. Están tratando de ocultar las pruebas que llevarán a Arthas a tomar la decisión de arrasar Stratholme. Sus agentes han usado magia ilusionista para que los cargamentos de grano de los campos cercanos parezcan normales. Necesito que encuentres el grano y uses este disruptor Arcano en los envíos. Los hombres de Arthas están buscando grano apestado y lo encontrarán rápidamente cuando desaparezca la ilusión.' WHERE `entry` = 12952;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No creo que podamos permitirnos dejar pasar la oportunidad de congraciarnos con el Vuelo Rojo. Con las batallas que se avecinan, tenerlos como aliados tendrá un valor incalculable.' WHERE `entry` = 12953;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace años, el brujo Nekros Trituracráneos abusó de un poderoso artefacto para encerrar a Alexstrasza y a la mayoría del Vuelo Rojo. Zuluhed y Nekros están muertas, pero la deuda que tenemos con los Rojos aún sigue pendiente.' WHERE `entry` = 12954;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Alianza ha mantenido este remoto hospedaje durante algún tiempo. Nuestros cazadores y leñadores le han dado algo de uso.$B$BPero, con la guerra en curso, será mejor que te creas que ahora manda el ejército.' WHERE `entry` = 12955;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El Comandante detiene la clase y se gira: sus ojos te evalúan, te juzgan.>$B$BCreo que alguien como tú me sería de ayuda, $C.' WHERE `entry` = 12958;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, $C, ¿qué ocurre?' WHERE `entry` = 12960;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegro de verte de nuevo, $N.$B$B¿Has venido a poner al día tus conocimientos sobre tácticas de la Plaga?' WHERE `entry` = 12961;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Esperando órdenes!' WHERE `entry` = 12965;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al Manantial de Granito, colega.$B$BSi consigues atrapar a alguno de esos trols de hielo vivo, avísame. Pagamos bien por ellos.' WHERE `entry` = 12966;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegro de volver a verte, $N. Espero que nuestro club te esté tratando bien.', `text0_1_loc7` = 'Me alegro de volver a verte, $N. Espero que nuestro club te esté tratando bien.' WHERE `entry` = 12971;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$N, me alegro de verte!$B$B¿O es la primera vez que nos encontramos?$B$BAhora mismo estoy en tantos lugares y momentos que a veces es difícil llevar la cuenta.' WHERE `entry` = 12976;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Buen trabajo! Ahora Arthas está al corriente del grano apestado de Stratholme y está a punto de comenzar la matanza. Pero algo no va bien. Siento una presencia extraña en esta línea temporal además de nosotros y eso debe de significar que el Vuelo Infinito anda por aquí.' WHERE `entry` = 12992;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Drakuru extiende su ensangrentada mano a través de la jaula y te mira expectante.>' WHERE `entry` = 12998;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hubo un tiempo en el que podría haberte arrancado el corazón solo por ver la expresión de tu rostro...' WHERE `entry` = 12999;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Eh, colega!$B$BMe alegro de volver a verte... $N, ¿verdad? Sí, sí, claro.$B$BTú y yo vamos a ser muy buenos amigos. Drakuru sabe de estas cosas.$B$B<Una sonrisa comienza a extenderse por la cara pintada del trol.>' WHERE `entry` = 13001;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde este trono comunico la voluntad de Grito Infernal. Su palabra es vida y su palabra es muerte.' WHERE `entry` = 13003;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Que Elune te proteja, $r.' WHERE `entry` = 13004;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Cómo puedo ayudarte, $Gseñor:señora;?' WHERE `entry` = 13005;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Esta es la única manera de que Harry hable conmigo! Tengo que llevar puesta esta estúpida máscara todo el tiempo...' WHERE `entry` = 13009;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Uy, colega!$B$BEsto no es lo que parece. Bueno, igual...$B$BEs complicado.' WHERE `entry` = 13011;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $R.$B$BSi te interesa colaborar en la defensa del templo, habla con Lord Afrasastrasz en el interior.' WHERE `entry` = 13018;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a mi campamento, $R.$B$BNo parece gran cosa, pero por ahora es un hogar.' WHERE `entry` = 13019;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Cómo puedo ayudarte, $Gniño:niña;?', `text0_1_loc7` = '¿Cómo puedo ayudarte, $Gniño:niña;?' WHERE `entry` = 13022;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Floppy y yo salimos a dar un paseo, pero nos perdimos y yo tenía mucho miedo porque había lobos, ya sabes... y nos perseguían, pero entonces vimos esta torre y Floppy y yo nos escondimos aquí y después de un rato los lobos se marcharon y ha sido terrible y no sabemos volver a casa.' WHERE `entry` = 13023;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al Templo del Reposo del Dragón, $R. Disculpa la conmoción de fuera. Con los recientes actos de Malygos, nos hemos visto asediados. De todas formas, podrás descansar aquí bajo nuestra protección si te hace falta.' WHERE `entry` = 13025;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Saludos, $C!$B$BEspero que no te moleste el trabajo duro. Nos vendría bien un poco de ayuda.' WHERE `entry` = 13030;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los vientos y los árboles hablan en susurros de nuevos problemas. La tierra parece saludable, pero está sucediendo algo que no se ve a primera vista.' WHERE `entry` = 13031;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Pellejos de la Horda! ¡Para nuestras tropas, solo lo mejor!' WHERE `entry` = 13033;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Suministros para el camino? ¿Quizá algo para tomar mientras observas las luchas?' WHERE `entry` = 13034;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los enemigos se acercan por todos los flancos y todos los ancestros hablan de abandonar este puesto. Yo digo que deberíamos quedarnos y luchar como los guerreros que somos.' WHERE `entry` = 13035;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La llamaré el espíritu de Gnomeregan. ¿No es hermosa?' WHERE `entry` = 13042;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a El Confín Argenta, $C.$B$BEstás en tu casa, pero no te acomodes demasiado. Aún no estamos a salvo... y lo que nos queda.$B$BSi queremos salir de aquí de una pieza, tendremos que ayudar todos... tú también.' WHERE `entry` = 13043;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tenemos un serio problema.' WHERE `entry` = 13045;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La tierra ha sido profanada por la presencia de la Plaga por un lado y por los rituales de los Drakkari por el otro.$B$BNosotros nos mantenemos firmes entre ambos.' WHERE `entry` = 13046;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Demos gracias a la Luz!', `text0_1_loc7` = '¡Demos gracias a la Luz!', `text1_0_loc7` = 'Eh, tú, $C. Estamos en plena batalla.', `text1_1_loc7` = 'Eh, tú, $C. Estamos en plena batalla.', `text2_0_loc7` = '¿Qué haces aquí?', `text2_1_loc7` = '¿Qué haces aquí?', `text3_0_loc7` = '¿Tú no eres $N? Había oído que habías muerto.', `text3_1_loc7` = '¿Tú no eres $N? Había oído que habías muerto.', `text4_0_loc7` = 'Que sea rápido... Luchar aquí es macabro y necesito mantenerme fuerte.', `text4_1_loc7` = 'Que sea rápido... Luchar aquí es macabro y necesito mantenerme fuerte.', `text5_0_loc7` = '¿Eres un nuevo recluta? No llevas la armadura estándar.', `text5_1_loc7` = '¿Eres un nuevo recluta? No llevas la armadura estándar.', `text6_0_loc7` = '¡Oh, bien! ¡Refuerzos!', `text6_1_loc7` = '¡Oh, bien! ¡Refuerzos!', `text7_0_loc7` = '¿Tienes un mensaje para mí?', `text7_1_loc7` = '¿Tienes un mensaje para mí?' WHERE `entry` = 13047;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Tengo más pelo del que esperabas, $R?$B$BNo puedo decir que me sorprenda. Me lo dicen a menudo todos los que llegan tan lejos...' WHERE `entry` = 13048;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Soy Ubungo. Represento a la tribu Zandalari.$B$BSe han puesto en marcha cosas malas, $R. Los Drakkari van a cavar su propia tumba con lo que planean.$B$BAlgunos trols no aprenden nunca.' WHERE `entry` = 13050;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es verdad que los horrores aquí presentes superan a los que hemos soportado en las Tierras de la Peste.$B$BAhora la Plaga es más poderosa que nunca.' WHERE `entry` = 13068;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No lo soporto más...$B$B¡Te matan y luego te comen! ¡Eso si tienes suerte!', `text0_1_loc7` = 'No lo soporto más...$B$B¡Te matan y luego te comen! ¡Eso si tienes suerte!', `text1_0_loc7` = '¡Tienes que ayudarme a salir de aquí, $R!$B$B¡No quiero convertirme en uno de ellos!', `text1_1_loc7` = '¡Tienes que ayudarme a salir de aquí, $R!$B$B¡No quiero convertirme en uno de ellos!', `text2_0_loc7` = 'Desde que llegué aquí no he logrado pegar ojo ni comer.$B$BNo estoy hecho para la Cruzada...', `text2_1_loc7` = 'Desde que llegué aquí no he logrado pegar ojo ni comer.$B$BNo estoy hecha para la Cruzada...', `text3_0_loc7` = '<sollozo> Mi tío sirvió en El Alba Argenta.$B$B¿Cómo logró enfrentarse a estos seres inmundos?', `text3_1_loc7` = '<sollozo> Mi tío sirvió en El Alba Argenta.$B$B¿Cómo logró enfrentarse a estos seres inmundos?', `text4_0_loc7` = '¡Cada vez que cierro los ojos veo a los miembros de la Plaga rodeándome!$B$BTengo que salir de aquí...', `text4_1_loc7` = '¡Cada vez que cierro los ojos veo a los miembros de la Plaga rodeándome!$B$BTengo que salir de aquí...', `text5_0_loc7` = 'Voy a morir aquí... Lo sé.', `text5_1_loc7` = 'Voy a morir aquí... Lo sé.', `text6_0_loc7` = '¡¡Estamos condenados!!', `text6_1_loc7` = '¡¡Estamos condenados!!', `text7_0_loc7` = '¡Sal de ahí mientras puedas, $C!$B$BNo podemos ganar...', `text7_1_loc7` = '¡Sal de ahí mientras puedas, $C!$B$BNo podemos ganar...' WHERE `entry` = 13069;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Menudo desastre.' WHERE `entry` = 13070;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡BUM!$b$bEs lo último que recuerdo...' WHERE `entry` = 13077;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<suspiro>...$B$B¿Qué tal un poco de ayuda?' WHERE `entry` = 13078;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ohh, el dolor es... insoportable...' WHERE `entry` = 13080;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Date prisa, $r.$b$bNo me queda mucho tiempo.' WHERE `entry` = 13081;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Rápido, $ghermano:hermana;. Estoy goteando...' WHERE `entry` = 13082;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<suspiro>... ¿Crees que podríamos irnos, $C?$B$B¡Pierdo fluido con rapidez!' WHERE `entry` = 13083;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué miras?$B$BTe informo de que los Mortero somos una familia con mucha historia y una gran tradición trabajando con morteros.$B$B¡Sí, eso es!$B$BNo encontrarás mejores alquimistas en todo Azeroth, ni en ningún otro lugar, ¡te lo aseguro!$B$B¡Puede que yo aún tenga el título de aprendiz, pero mis habilidades no tienen par! Uno tiene que ser humilde para llegar a ser algo en la vida.$B$BClaro que quizá estés aquí para ayudarme.$B$B<El diminuto gnomo sonríe de forma arrogante.>' WHERE `entry` = 13092;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $R.$B$BMe temo que tendrás que disculparme, pero estoy terriblemente ocupado en este momento.' WHERE `entry` = 13097;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Bueno, me sorprende ver a otros aventureros en un lugar como éste! Si hubiera sabido que veníais, habría venido con vosotros en lugar de volar en este cacharro gnómico infernal. ¡Ahora que estáis aquí, podéis ayudarme a llegar al Tribunal! Si mis teorías e investigaciones sobre estos salones son correctas, ¡aquí yace la información sobre la creación de toda la raza enana!' WHERE `entry` = 13100;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Contempladlo, camaradas! ¡Sin duda es una visión maravillosa! Ojala mis hermanos pudieran estar conmigo en estos momentos. ¡No se creerían el descubrimiento que iba a ser esto! El Tribunal son los bibliotecarios de los discos de los titanes, que están guardados en esta sala. ¡Mirad! ¡Se pueden ver los discos bajo el suelo! Debemos apresurarnos, ¡el mayor descubrimiento de la raza enana está justo ahí!' WHERE `entry` = 13101;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo último que necesitábamos era que esos locos trols Drakkari derribaran una necrópolis de la Plaga en nuestro patio trasero.$B$B¿Cómo han conseguido hacer eso?' WHERE `entry` = 13117;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Agh, no debí haberme ofrecido como especialista. ¡Siempre nos piden que hagamos lo imposible!' WHERE `entry` = 13118;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El crocolisco cautivo emite un gruñido grave cuando te acercas.>' WHERE `entry` = 13120;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Un día excelente para una incursión en el campo, ¿no le parece, $Gseñor:señora;?' WHERE `entry` = 13121;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El Oráculo Hablalluvia herido gime por el dolor.>' WHERE `entry` = 13124;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tú debes de ser uno de los valientes soldados que han estado luchando contra los ejércitos de la Plaga de Mal\'ganis. Bien hecho.' WHERE `entry` = 13125;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sin duda. Mal\'ganis está dirigiendo a la Plaga desde la Plaza de los Cruzados, pero las verjas que llevan allí desde la entrada de la ciudad están cerradas. Lo que no sabe es que un pequeño grupo todavía puede llegar a la Plaza pasando por el Ayuntamiento. ¿Te unirás a mí en este ataque?' WHERE `entry` = 13126;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Si tienes que ir a algún sitio, puedo llevarte hasta allí.' WHERE `entry` = 13132;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Sácame de aquí, $R!' WHERE `entry` = 13135;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Asqueroso amigo de lenguas-grandes, este no ser tu lugar. ¡Irte de tierra Corazón Frenético o yo llamar cosas mágicas para matarte!' WHERE `entry` = 13137;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Corazón Frenético nunca ser amigos de lenguas-grandes. ¡Tú $Gtonto:tonta; por volver aquí, cosa traidora!' WHERE `entry` = 13138;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Tú serás mi salvación.$B$BAunque también puedes ser mi perdición.' WHERE `entry` = 13139;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Alguna vez has visto dracos como los de aquí? Son perfectos para cazar.' WHERE `entry` = 13147;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo siento, pero nuestras cervezas son exclusivamente para los miembros de \"El Club de la Cerveza del Mes.\"', `text0_1_loc7` = 'Lo siento, pero nuestras cervezas son exclusivamente para los miembros de \"El Club de la Cerveza del Mes.\"' WHERE `entry` = 13155;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este puesto es seguro, $R.$B$BHace ya algún tiempo que se eliminaron a los enemigos de esta zona.' WHERE `entry` = 13156;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Gracias por liberarme, $R.$B$BAún estoy un poco mareada por el veneno. Creo que me quedaré aquí hasta que recupere las fuerzas.' WHERE `entry` = 13157;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡El poder de los impíos nos permite mandar sobre la propia muerte!' WHERE `entry` = 13172;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La sangre es vida... Y la vida de nuestros enemigos es tan fácil de controlar como el flujo y reflujo de la sangre.' WHERE `entry` = 13173;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nosotros somos lo que viaja en el frío viento. El gélido escalofrío de la desesperación... El helado abrazo de la muerte...' WHERE `entry` = 13174;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'A todos nos apena ver a los Drakkari destruyéndose a sí mismos de esta forma.$B$BEl poder que obtienen matando a los dioses naturales del territorio es transitorio.$B$BLo que ganen a corto plazo lo perderán para toda la eternidad.' WHERE `entry` = 13176;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Recupérate deprisa, debemos seguir adelante.' WHERE `entry` = 13177;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esto no se está volviendo más fácil. Stratholme está ardiendo. Debemos enfrentarnos a la Plaga y a las llamas para llegar hasta Mal\'Ganis. Preparaos.' WHERE `entry` = 13179;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Zim\'Torga puede proporcionarnos cierto nivel de protección, pero la presencia de la Plaga y las acciones de los Drakkari la han debilitado mucho.$B$BAhunae y yo nos ocuparemos del resto de nuestras defensas.' WHERE `entry` = 13180;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El hacha de Ahunae acabará con los Drakkari.$B$B¡Y tú te encargarás de todo lo que escape a mi hacha!' WHERE `entry` = 13234;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El tramposo Señor de la Magia Urom protege el tercer anillo. Aparecerá solo e indefenso, ¡pero que no te engañen las apariencias! Urom es un poderoso conjurador que dirige un grupo de criaturas fantasmales. Búscale arriba.$BPuedo otorgarte el poder de invocar a un draco del Vuelo Rojo. Habla con Eternos o Verdisa si prefieres aprovechar el poder del Bronce o del Verde.' WHERE `entry` = 13237;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El mayor de tus desafíos aún está por llegar. El Guardián-Ley Eregos es un dragón azul de inmenso poder. Lo encontrarás sobrevolando el anillo superior.$B$BSe ha desbloqueado el poder total de tus dracos. ¡Usa su poder para derrotar a Eregos!' WHERE `entry` = 13238;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los dracos rubí destacan en aguantar daño y proteger a sus aliados.$B$BAl utilizar Cólera abrasadora pueden lanzar fuego líquido que salta de objetivo en objetivo, haciendo cada vez más daño.$B$BCada vez que un draco rubí es alcanzado por un ataque enemigo, gana una Carga evasiva. Estas cargas sirven para realizar una serie de maniobras evasivas, que permiten al draco esquivar los ataques y hechizos durante un tiempo.' WHERE `entry` = 13254;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida al campamento, $C.' WHERE `entry` = 13261;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué necesitas de Corvus?' WHERE `entry` = 13262;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Medita sobre lo que has visto hoy, $N.$B$BEl futuro ya está escrito. Solo te queda decidir qué papel vas a jugar en él, tron.' WHERE `entry` = 13265;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los agentes más poderosos de Malygos protegen los anillos superiores. En solitario, no tienes posibilidades de derrotarlos. Podemos otorgarte el poder de invocar a los dracos de nuestros Vuelos. Montado a lomos de un draco, podrás volar dentro de El Oculus y controlar la poderosa magia de los dragones.' WHERE `entry` = 13267;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Belgaristrasz dice la verdad; ¡el tiempo se agota!$B$BEl poder completo de tus dracos ha sido desbloqueado. ¡Usa su poder para derrotar a Eregos!' WHERE `entry` = 13268;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Belgaristrasz dice la verdad, la situación es en efecto grave.$B$BSe ha desbloqueado todo el poder de tus dracos. ¡Úsalo para derrotar a Eregos!' WHERE `entry` = 13269;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nadie conoce a la Plaga mejor que nosotros, los caballeros de la Muerte.$B$BCréeme, $R, no hay nada más eficaz que usar el fuego contra el fuego.' WHERE `entry` = 13270;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No le pidas demasiado al viejo Nass. Ya no es tan joven y fuerte como antaño.' WHERE `entry` = 13271;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aunque a mis compatriotas les gustaría que jugaras un papel activo en lo que está pasando en Zul\'Drak, nosotros, los Zandalari, estamos aquí principalmente para ser testigos de lo que ocurre y redactar la crónica del final de un imperio.' WHERE `entry` = 13282;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás $Gpreparado:preparada; para enfrentarte a Mal\'Ganis junto a mí?' WHERE `entry` = 13287;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todo está preparado y listo para empezar.' WHERE `entry` = 13288;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Muévete. No hay nada que ver aquí.' WHERE `entry` = 13289;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '<Rosangre te observa con curiosidad.>$B$BNo puedo evitar preguntarme por qué te mereces tanta atención.$B$BNo te ofendas...' WHERE `entry` = 13290;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Drakkari están manteniendo mi cuerpo próximo a la muerte en el altar. Pero no pueden ver mi espíritu.$B$BDebemos trabajar juntos para evitar que consigan mi poder.' WHERE `entry` = 13291;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Oooh... midda al pequeño necrófago.$B$B¡Zalta, pequeñño! ¡Zalta!$B$BJua, jua jua... Buen pequeñño necrófago.' WHERE `entry` = 13300;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Plaga es una mancha en estas prístinas tierras. Por el poder de los creadores, aplastaré hasta el último de sus decrépitos huesos bajo mi talón.' WHERE `entry` = 13303;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los titanes no crearon la vida de la noche a la mañana. El proceso fue gradual y necesitó muchos experimentos e iteración.$B$BPor eso existen lugares tan protegidos como la Cuenca de Sholazar.$B$BPara mantener la seguridad de sus lugares de experimentación, los titanes crearon un mecanismo de defensa. Su fuerza destructiva no tiene igual y no te contaré nada más salvo que jures lealtad a mi causa.' WHERE `entry` = 13304;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Revelar estos secretos a un mortal como tú roza el sacrilegio, pero no tengo elección.$B$BDesde que Freya, hija de los titanes, me dejó aquí, solo he tenido una tarea en la que no puedo fracasar. La vida ha de ser protegida cueste lo que cueste.$B$BHay una puerta que une este lugar con otro muy parecido. Allí es donde se encuentra el arma que buscamos.' WHERE `entry` = 13305;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta avanzada es una zona libre de engranajes. Las personas que usen engranajes en la construcción de dispositivos mecánicos serán ridiculizadas constantemente.' WHERE `entry` = 13306;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Parece que has tenido la misma idea que yo. Cazando fruta, ¿no?', `text0_1_loc7` = 'Parece que has tenido la misma idea que yo. Cazando fruta, ¿no?' WHERE `entry` = 13307;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡¿Loken?! Ya me estoy hartando... puede que hayamos neutralizado a los enanos férreos, ¡pero apostaría a que en otra parte hay otra máquina escupiendo un montón de esos vrykuls férreos!' WHERE `entry` = 13308;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos torres del zepelín a las afueras de Orgrimmar. La del este da servicio a los Claros de Tirisfal y a la Vega de Tuercespina, mientras que la del oeste da servicio a Rasganorte y a Cima del trueno.', `text0_1_loc7` = 'Hay dos torres del zepelín a las afueras de Orgrimmar. La del este da servicio a los Claros de Tirisfal y a la Vega de Tuercespina, mientras que la del oeste da servicio a Rasganorte y a Cima del trueno.' WHERE `entry` = 13310;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Busca la torre de aterrizaje de zepelines que está fuera de la ciudad en el sudeste.', `text0_1_loc7` = 'Busca la torre de aterrizaje de zepelines que está fuera de la ciudad en el sudeste.' WHERE `entry` = 13311;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Caminamoho parece gravemente herido.' WHERE `entry` = 13318;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde este muelle, El Valentía hace el viaje de ida y vuelta entre Ventormenta y Auberdine.', `text0_1_loc7` = 'Desde este muelle, El Valentía hace el viaje de ida y vuelta entre Ventormenta y Auberdine.' WHERE `entry` = 13321;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mi pobre máquina voladora. ¡No sobrevivió al golpe y sin piezas de repuesto nunca podré arreglarla!' WHERE `entry` = 13322;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Pronto, $N!$B$BPronto probarás el auténtico poder. Pronto recibirás la recompensa por tu dedicación y servicio.' WHERE `entry` = 13324;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Mphmm rmphhimm rrhumghph?',  `text1_0_loc7` = '¡Rmmurphum mmph hmrphum!', `text2_0_loc7` = 'Umphrum mmphur, murruphru...', `text3_0_loc7` = '¡Mhrrumph rummrhum phurr!' WHERE `entry` = 13326;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida. ¿Puedo ofrecerte descanso o sustento?', `text0_1_loc7` = 'Te doy la bienvenida. ¿Puedo ofrecerte descanso o sustento?' WHERE `entry` = 13328;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has venido para comprarle algo a Hargus?' WHERE `entry` = 13342;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Otra persona suplicando los favores del Rey Exánime?$B$BSolo los dignos obtienen los dones oscuros...' WHERE `entry` = 13348;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a las oficinas del Registro de hermandades de Dalaran. ¿En qué puedo ayudarte hoy?', `text0_1_loc7` = 'Te doy la bienvenida a las oficinas del Registro de hermandades de Dalaran. ¿En qué puedo ayudarte hoy?' WHERE `entry` = 13349;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espero que el archimago se dé cuenta de lo que he tenido que hacer para cumplir la parte del trato de Hemet.' WHERE `entry` = 13352;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Deberías pegarte a Arthas ahora mismo!' WHERE `entry` = 13353;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se lo arrebataremos todo, caballero de la Muerte.' WHERE `entry` = 13355;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Corazón Frenético se quedará la tierra. Leguas-grandes marcharse o nosotros matar del todo.' WHERE `entry` = 13359;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Corazón Frenético pueden ser despiadados, pero nosotros buena gente. Tú verlo si quedarte un tiempo, nosotros amigos con inteligentes y fuertes.$B$BNo gustarnos los tontos.' WHERE `entry` = 13360;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta ser la Gran Piedra de Lluvia. La mantenemos a salvo y le traemos cositas brillantes y ella cuidarnos.$B$B¿Igual tener más cositas brillantes para piedra?' WHERE `entry` = 13361;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo que faltarme en altura compensarlo con magia. Yo cuidar bien de la Gran Piedra de Niebla... la mayoría del tiempo.$B$BEstúpidos hombres cachorro.' WHERE `entry` = 13362;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los hombres cachorro llevan días atacándonos. Pero la Gran Piedra de Rayos aún ser segura.$B$BSoo-brada haber venido a ayudarnos muchas veces... Hombres cachorro no tener ninguna posibilidad.' WHERE `entry` = 13363;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Kartak venir a por carne de lenguas-grandes. Corazón Frenético asegurarse de que haya bastante preparada.' WHERE `entry` = 13364;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, piel seca.',  `text1_0_loc7` = '¿Qué miras?', `text2_0_loc7` = '¿Hay alguna razón para que aún estemos aquí?', `text3_0_loc7` = 'Supongo que quieres algo.' WHERE `entry` = 13365;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Todo bien?',  `text1_0_loc7` = '¿Sí?', `text2_0_loc7` = 'Es agradable tener un buen compañero de caza.', `text3_0_loc7` = '¿Ocurre algo?' WHERE `entry` = 13366;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola! ¿Cómo estar? Nosotros bien... sí, bien bien. ¡Hola!',  `text1_0_loc7` = '¿Alguna vez tropezar con tus pies? Nosotros sí. Doler un poco. Si no hacer, no divertido. Nop.', `text2_0_loc7` = '¡Hola! Eres fuerte y $Gguapo:guapa; y esas cosas. Me gustas. Seamos amigos, ¿vale?', `text3_0_loc7` = '¿Quieres que ayudemos en algo? ¿Con qué ayudar? Nosotros buenos ayudando.' WHERE `entry` = 13367;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Un buen día para volar, ¿verdad?' WHERE `entry` = 13375;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Reconozco a un as en cuanto lo veo. ¡Gracias de nuevo por salvarme la vida!' WHERE `entry` = 13376;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Mira lo que has hecho, caballero de la Muerte! ¡Has otorgado gloria a la Plaga!' WHERE `entry` = 13382;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Déjame salir de aquí, tron!',  `text1_0_loc7` = 'Tú no eres de la Plaga. ¿Qué haces aquí, tron?', `text2_0_loc7` = '¿Por qué te deja caminar por aquí la plaga, tron?', `text3_0_loc7` = 'Creo que estás perdido, tron...' WHERE `entry` = 13390;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tu Plaga no ser bienvenida en Zul\'Drak!',  `text1_0_loc7` = 'Zul\'Drak pertenece a los Drakkari, tron. ¡Siempre lo hará!', `text2_0_loc7` = 'Tu Plaga va a fracasar aquí, tron.', `text3_0_loc7` = '¡Nuestra gente va a matar hasta al último de vosotros, miembros de la Plaga!', `text4_0_loc7` = '¡Aléjate de mí, asqueroso miembro de la Plaga!' WHERE `entry` = 13391;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ahora puede parecer una plantación de calabazas normal, ¡pero cuando coloquemos la caldera de peste, se convertirá en una fuente de enfermedad, podredumbre y peste!' WHERE `entry` = 13395;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Cómo te atreves? ¡Desaparece de mi vista!',  `text1_0_loc7` = 'Ese zoquete probablemente esté practicando ballet en su cuarto.', `text2_0_loc7` = '¡JAMÁS menciones ese nombre delante de mí! ¡Considera este el primer y último aviso!', `text3_0_loc7` = 'Puedo decirte donde no está...', `text4_0_loc7` = '¡Heigan está llevando a cabo el mantenimiento de ese estudio de baile lleno de babas y mugre que tiene!' WHERE `entry` = 13396;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Que la peste llegue a Nuevo Avalon solo es cuestión de tiempo.' WHERE `entry` = 13397;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Fineous tiene lo que necesitas, colega.' WHERE `entry` = 13402;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cruzados...$B$B<Osahendido agita la cabeza.>$B$BEstoy convencido de que son un grupo único entre los humanos. Mi teoría es que no poseen las funciones cerebrales superiores, por lo que no pueden sopesar una situación y calcular un resultado de forma lógica. Uno solo tiene que mencionar la Luz para conseguir que echen espumarajos y se preparen para la batalla. Llegados a ese punto, irán de cabeza hacia su compromiso sin preocupación alguna por su propio bienestar.' WHERE `entry` = 13419;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$N! He estado esperando tu vuelta. ¿Qué te ha retenido tanto tiempo, tron?$B$BEs hora de completar la conversión de los Sangreañublo.$B$B¡Únete a mí y contempla la creación de nuestro ejército!' WHERE `entry` = 13422;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estoy malherido, pero haré lo que pueda para ayudar. Intenta mantenerte dentro de mi barrera antimagia o la magia de los acólitos te destruirá.' WHERE `entry` = 13425;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué puedo hacer por ti, $R?' WHERE `entry` = 13426;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Koltira gruñe.>' WHERE `entry` = 13430;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Yo no me alisté para esto, pero oye... al menos aquí hace calor.' WHERE `entry` = 13431;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Cruzada Escarlata servirá a un nuevo maestro... en la muerte...' WHERE `entry` = 13432;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Me estás retando, $Ghermano:hermana;?', `text0_1_loc7` = '¿Me estás retando, $Ghermano:hermana;?' WHERE `entry` = 13433;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Algún día, Rejek ser poderoso guerrero.' WHERE `entry` = 13438;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás tratando de llegar a Auberdine o a la Fortaleza Denuedo? Si vas por los canales, encontrarás el Puerto de Ventormenta entre el Distrito de la Catedral y el Parque.', `text0_1_loc7` = '¿Estás tratando de llegar a Auberdine o a la Fortaleza Denuedo? Si vas por los canales, encontrarás el Puerto de Ventormenta entre el Distrito de la Catedral y el Parque.', `text1_0_loc7` = 'El Puerto de Ventormenta es la forma más rápida de llegar a Auberdine o a la Fortaleza Denuedo. Puedes encontrarlo entre el Distrito de la Catedral y el Parque.', `text1_1_loc7` = 'El Puerto de Ventormenta es la forma más rápida de llegar a Auberdine o a la Fortaleza Denuedo. Puedes encontrarlo entre el Distrito de la Catedral y el Parque.' WHERE `entry` = 13439;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Espero que me traigas buenas noticias sobre la Worldwide Invitational. Si es así, significa que tienes un código secreto para entregarme. A cambio del código, te daré un regalo, la empuñadura de Tyrael.$B$BSimplemente susúrramelo al oído cuando estés $Glisto:lista;.', `text0_1_loc7` = 'Saludos, $C. Espero que me traigas buenas noticias sobre la Worldwide Invitational. Si es así, significa que tienes un código secreto para entregarme. A cambio del código, te daré un regalo, la empuñadura de Tyrael.$B$BSimplemente susúrramelo al oído cuando estés $Glisto:lista;.' WHERE `entry` = 13441;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Somos los elegidos, $N. Caballeros de la Muerte de la Plaga, enviados para sembrar las semillas de la destrucción del maestro.' WHERE `entry` = 13444;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Estos monstruos mataron a mi padre y secuestraron a mi hermana. ¡Pagarán por ello!' WHERE `entry` = 13445;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$GBienvenido:Bienvenida; a mi cocina! Aquí preparamos algunos de los mejores platos de Dalaran.' WHERE `entry` = 13449;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espero que les des su merecido a los lenguas-grandes. Ser hora de que ellos irse.' WHERE `entry` = 13451;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gran batalla tener aún lugar en Retiro Pavesa.$B$B¿Quizá tú sacar tiempo para ayudar en algún momento?' WHERE `entry` = 13452;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Saludos, $C! El Puerto de Ventormenta es digno de ver. Es incluso mejor verlo desde el aire. ¿Te gustaría obtener una vista de pájaro del puerto?' WHERE `entry` = 13454;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No creo que poseas la agudeza mental necesaria para comprender la naturaleza de la magia de portales.$B$BSin embargo, puedes usar el cristal tras de mí para teletransportarte a las ruinas que se encuentran bajo Dalaran en el Bosque Canto de Cristal.' WHERE `entry` = 13455;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Tienes un momento? ¡Soo-nata enseñarte bonita canción!' WHERE `entry` = 13457;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C.', `text0_1_loc7` = 'Saludos, $C.' WHERE `entry` = 13458;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$GBienvenido:Bienvenida; a la barbería, colega! ¿Has venido a cortarte el pelo? ¿A teñirte? ¿Quizá algo distinto?$B$BPodemos cepillar el vello facial, poner pendientes, hacer tatuajes de elfo de la noche e incluso dar forma a los cuernos y zarcillos de los draenei.$B$BSiéntate y nos pondremos manos a la obra. Solo unas pocas monedas y pronto encontrarás al nuevo y más atractivo tú...', `text0_1_loc7` = '¡$GBienvenido:Bienvenida; a la barbería, colega! ¿Has venido a cortarte el pelo? ¿A teñirte? ¿Quizá algo distinto?$B$BPodemos cepillar el vello facial, poner pendientes, hacer tatuajes de elfo de la noche e incluso dar forma a los cuernos y zarcillos de los draenei.$B$BSiéntate y nos pondremos manos a la obra. Solo unas pocas monedas y pronto encontrarás al nuevo y más atractivo tú...' WHERE `entry` = 13459;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$GBienvenido:Bienvenida; a la barbería, colega! ¿Has venido a cortarte el pelo? ¿A teñirte? ¿Quizá algo distinto?$B$BPodemos cepillar el vello facial, poner pendientes, dar forma a colmillos y cuernos, ¡e incluso modificar las características de los no-muertos!$B$BSiéntate y nos pondremos manos a la obra. Solo unas pocas monedas y pronto encontrarás al nuevo y más atractivo tú...', `text0_1_loc7` = '¡$GBienvenido:Bienvenida; a la barbería, colega! ¿Has venido a cortarte el pelo? ¿A teñirte? ¿Quizá algo distinto?$B$BPodemos cepillar el vello facial, poner pendientes, dar forma a colmillos y cuernos, ¡e incluso modificar las características de los no-muertos!$B$BSiéntate y nos pondremos manos a la obra. Solo unas pocas monedas y pronto encontrarás al nuevo y más atractivo tú...' WHERE `entry` = 13461;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Coge una pinta!' WHERE `entry` = 13462;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Varos Zancanubes y sus guardianes del anillo protegen el segundo anillo. Tus dracos pueden apañárselas contra los guardianes del anillo, pero Varos se encuentra tras un escudo impenetrable creado con la energía del propio Oculus. Diez ensamblajes de centrifugadora dan potencia al escudo desde el anillo y las plataformas de encima. Destrúyelos y Varos será vulnerable.', `text0_1_loc7` = 'Varos Zancanubes y sus guardianes del anillo protegen el segundo anillo. Tus dracos pueden apañárselas contra los guardianes del anillo, pero Varos se encuentra tras un escudo impenetrable creado con la energía del propio Oculus. Diez ensamblajes de centrifugadora dan potencia al escudo desde el anillo y las plataformas de encima. Destrúyelos y Varos será vulnerable.' WHERE `entry` = 13466;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Qué haces aquí? ¡Tienes que volver a Stratholme inmediatamente! ¿Te teletransporto a Stratholme?' WHERE `entry` = 13470;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Busca esas cajas con grano plagado y usa tu Disruptor arcano en ellas! Entonces Arthas tendrá las pruebas que necesita para comenzar la purga de Stratholme.' WHERE `entry` = 13471;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Qué haces aquí? ¡Arthas está en la entrada de Stratholme ahora mismo y tú deberías estar con él! ¡Vamos!' WHERE `entry` = 13472;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay tres clases de familias de mascotas. Las mascotas feroces, como los felinos, pueden hacer daño, pero no lo reciben tan bien. Las mascotas tenaces, como las tortugas, son más defensivas y pueden mantener la atención de un enemigo para que puedas mantenerte a distancia. Las mascotas astutas, como las arañas, tienen una mezcla de facultades ofensivas y defensivas que son particularmente útiles en campos de batalla y arenas.$B$BA medida que tu mascota gane experiencia, podrá aprender nuevos talentos, al igual que tú, pero a un ritmo más lento. Si alguna vez quieres cambiar los talentos de tu mascota, ven a hablar conmigo.', `text0_1_loc7` = 'Hay tres clases de familias de mascotas. Las mascotas feroces, como los felinos, pueden hacer daño, pero no lo reciben tan bien. Las mascotas tenaces, como las tortugas, son más defensivas y pueden mantener la atención de un enemigo para que puedas mantenerte a distancia. Las mascotas astutas, como las arañas, tienen una mezcla de facultades ofensivas y defensivas que son particularmente útiles en campos de batalla y arenas.$B$BA medida que tu mascota gane experiencia, podrá aprender nuevos talentos, al igual que tú, pero a un ritmo más lento. Si alguna vez quieres cambiar los talentos de tu mascota, ven a hablar conmigo.' WHERE `entry` = 13474;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás $Gseguro:segura;?$B$BUna vez que haya eliminado el conocimiento de los talentos de tu mente, no habrá vuelta atrás.', `text0_1_loc7` = '¿Estás $Gseguro:segura;?$B$BUna vez que haya eliminado el conocimiento de los talentos de tu mente, no habrá vuelta atrás.' WHERE `entry` = 13475;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muy bien. Ya está.', `text0_1_loc7` = 'Muy bien. Ya está.', `text1_0_loc7` = 'Muy bien. Ya está.' WHERE `entry` = 13476;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí es donde lo sacrifiqué todo para liberar a mi padre. Ahora vengo a destruirlo...' WHERE `entry` = 13482;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí es donde lo sacrifiqué todo para liberar a mi padre. Ahora vengo a destruirlo...' WHERE `entry` = 13485;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí estás, $N.$B$BAhora vuelve a concentrarte en tu tarea...' WHERE `entry` = 13501;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nuestros almacenes están vacíos. Hasta que no lleguen más suministros, no puedo vender nada.' WHERE `entry` = 13502;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Cuidado, $R.$B$B¡Ahí fuera es a vida o muerte!' WHERE `entry` = 13504;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Por qué me miran todos como si hubiera estrellado la nave?' WHERE `entry` = 13506;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$N! Apenas te reconozco con esas ropas nuevas. ¿Todavía tienes esa túnica... o escogiste las albarcas... o fue el bastón? No lo recuerdo, fue hace tanto tiempo...$B$BBueno, me alegro de que estés aquí para ayudar de nuevo. Necesitamos toda la ayuda posible. $N, si actúas tal y como lo hiciste allá en los Páramos de Poniente con la Milicia del Pueblo, tendremos una oportunidad.$B$BEstos son tiempos duros y si no ganamos esta batalla, puede que no haya unos Páramos de Poniente a los que volver.' WHERE `entry` = 13511;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Con la bendición de la Luz y tu ayuda, $N, los Defias dejaron de ser una amenaza.$B$BPero ahora estamos muy lejos de los Páramos de Poniente y si no ganamos esta batalla, puede que no tengamos un hogar al que volver.' WHERE `entry` = 13512;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué puede hacer Korg hoy por ti?' WHERE `entry` = 13517;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Gamigo:amiga;.' WHERE `entry` = 13519;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sí, el Rey Exánime caerá ante nuestro poder. Obtendremos nuestra venganza final.$B$B¡Pero primero nos aseguraremos de que la peste conocida como El Embate Escarlata conozca su final de una vez por todas!' WHERE `entry` = 13525;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muy bien, pero si yo fuera tú, prestaría atención y no asentiría ciegamente a todo lo que escuchase.$B$BEmpieza, tal y como empiezan todas las buenas historias, con una traición. No es que fuésemos traicionados... en absoluto. El Rey Exánime nos ascendió a los caballeros de la Muerte por encima de los demás.$B$BMás bien fuimos nosotros los que le traicionamos con la ayuda del alto señor Darion Mograine.$B$BEn la batalla final en la Capilla de la Esperanza de la Luz, cuando esos infelices estaban a punto de ser aniquilados, Tirion Fordring ayudó a Mograine a romper el control que el Rey Exánime tenía sobre él y de todos los que estábamos allí presentes.' WHERE `entry` = 13526;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Con el Rey Exánime huyendo aterrado de Vadín, que estaba armado con la Crematoria, pudimos arrebatarle El Bastión de Ébano al resto de fuerzas de la Plaga.$B$BPero eso no arregló las cosas entre nosotros y la Cruzada Escarlata.$B$BTal y como los Renegados aprendieron de la forma más difícil, los Escarlatas no parecían hacer diferencias entre nosotros y la Plaga.$B$BPensamos por nosotros mismos, tenemos nuestras propias motivaciones, tenemos casi todas las cualidades de los vivos y aun así, para ellos no somos distintos de los esclavos sin mente del Rey Exánime.$B$BPiensan erróneamente que nos tumbaremos y nos haremos los muertos.' WHERE `entry` = 13527;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El lord comandante te clava la mirada antes de continuar.>$B$BA eso iba. Cuando la Alta General Abbendis trajo a sus fuerzas a Rasganorte, cambió su nombre por el de El Embate Escarlata.$B$BEl almirante Barean Viento Oeste no tardó mucho en presentarse ante ella en Nueva Vega del Amparo en el Cementerio de Dragones. Por lo que parece, ella no le esperaba. Y tampoco se esperaba que minase su autoridad y le arrebatase las riendas de mando.$B$BProbablemente estuviste $Ginvolucrado:involucrada; en su caída, pero da igual. Una vez que ella murió, el almirante se fue de forma misteriosa y le hemos perdido el rastro.' WHERE `entry` = 13528;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como he dicho, le perdimos el rastro, pero sin duda es algo de lo que podrás encargarte por mí. Sospecho que está ahí abajo, en alguna parte, pero los espías que han vuelto no han sido capaces de localizarlo en la isla.$B$BLos Renegados en Rencor Venenoso hicieron un buen trabajo saboteando la Nueva Vega del Amparo, pero las fuerzas del Embate que están presentes ahí abajo son los veteranos de ese lugar. Por desgracia han conseguido mejor equipo por el camino y unos grifos que les habrán quitado a algunos idiotas.$B$BLo que intento decir es que esto no va a ser un paseo por el campo, $N, ni siquiera para ti.' WHERE `entry` = 13529;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Empezaremos en un momento.$B$BSolo quiero ser muy claro: no estamos aquí para ablandarlos. No estamos aquí para hacerles mella. Estamos aquí para poner fin a esa enfermedad que se autodenomina El Embate Escarlata.$B$BSi no quieres formar parte de esto, si no tienes las agallas necesarias, vete volando ya. Quiero a alguien que esté con nosotros todos los días hasta terminar el trabajo.$B$B¿Eres tú esa persona, $N?' WHERE `entry` = 13530;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El lord comandante hace una mueca, mirándote de arriba abajo con respeto.>$B$BEso es lo que quería oír.' WHERE `entry` = 13531;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Piensa en todo el sufrimiento que han causado los Escarlatas a lo largo de los años. Las torturas, los asesinatos, el fanatismo.$B$BNo permitiré que eso continúe. ¿Y tú?' WHERE `entry` = 13534;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El encuentro Hyldnir es sagrado. Quien obtiene una victoria se asegura toda una vida junto a Thorim.' WHERE `entry` = 13540;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $N.' WHERE `entry` = 13548;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C. Puedo ayudarte a guardar a tus mascotas o recuperar a compañeros perdidos.', `text0_1_loc7` = 'Saludos, $C. Puedo ayudarte a guardar a tus mascotas o recuperar a compañeros perdidos.' WHERE `entry` = 13557;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Colmillo de Hielo me ha llevado en incontables batallas.$B$BNo dejes que su falta de armadura te engañe. Es la osa más dura que conozco.', `text1_1_loc7` = '¡No te retrases, moza!$B$B¡Hay que luchar!', `text2_1_loc7` = '¡Luchamos por la gloria de Brunnhildar!', `text3_1_loc7` = 'Colmillo de Hielo demuestra un gran valor al entrar en el campo de batalla sin armadura.$B$BQuizás su ejemplo pueda infundirte algo de valor, $N.' WHERE `entry` = 13565;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $C, si has perdido una llave puede que yo pueda ayudarte.', `text0_1_loc7` = 'Hola, $C, si has perdido una llave puede que yo pueda ayudarte.' WHERE `entry` = 13572;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ante ti se alza un señor de lo Arcano. Desafiarme sería una necedad. A mi paso he dejado muchos cadáveres en llamas.' WHERE `entry` = 13573;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mi vida y mi alma han estado llenas con recuerdos de la insufrible Horda. Tiempos de dolor, desesperación y sacrificio. ¡La furia de la batalla jamás podría abandonar mi corazón!' WHERE `entry` = 13574;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este es un portal para el campo de batalla de la Cuenca de Arathi.' WHERE `entry` = 13575;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este es un portal para el campo de batalla del Valle de Alterac.' WHERE `entry` = 13576;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este es un portal para el campo de batalla del Ojo de la Tormenta.' WHERE `entry` = 13577;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todavía no eres lo suficientemente $Gpoderoso:poderosa; como para luchar en el Ojo de la Tormenta. Vuelve cuando tengas más experiencia.', `text0_1_loc7` = 'Todavía no eres lo suficientemente $Gpoderoso:poderosa; como para luchar en el Ojo de la Tormenta. Vuelve cuando tengas más experiencia.' WHERE `entry` = 13578;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este es un portal para el campo de batalla de la Garganta Grito de Guerra.' WHERE `entry` = 13579;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué pasa?$B$B¿Has venido a comer, tron? ¿O quizás a aprender del mejor?' WHERE `entry` = 13581;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Buscas un tabardo?', `text0_1_loc7` = '¿Buscas un tabardo?' WHERE `entry` = 13583;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C, yo puedo ayudarte a recuperar a tus compañeros perdidos.', `text0_1_loc7` = 'Saludos, $C, yo puedo ayudarte a recuperar a tus compañeros perdidos.' WHERE `entry` = 13584;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Qué hay! ¿Quieres comprar suministros de pesca o recibir una lección o dos?' WHERE `entry` = 13591;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He estado ausente durante demasiado tiempo, luchando por Azeroth para redescubrir mi pasado y reclamar mi trono. Con todos los desafíos que ha tenido que superar la Alianza en mi ausencia, ver que hay héroes como tú defendiendo nuestra coalición llena de gozo mi corazón.' WHERE `entry` = 13593;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estás perdiendo el tiempo. ¡Ve a matar a los lacayos del Rey Exánime, $C!' WHERE `entry` = 13607;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me alegro de volver a verte, $N. ¿Cómo podría olvidar a la persona que salvó la vida de mi hermana?' WHERE `entry` = 13609;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El explorador Natoescarcha intenta ponerse en pie mientras te acercas, pero vuelve a caer al suelo mientras se agarra una herida sin duda dolorosa en el costado.>' WHERE `entry` = 13611;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No pensaba que esto fuera a suceder, $Gzagal:zagala;. He perdido mucha sangre... es imposible que pueda volver.' WHERE `entry` = 13612;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una manada de arpías llegó desde el valle. Las lideraba una grande llamada Sirana... No tenía ningún lugar al que escapar. Hacía mucho tiempo que no eran agresivas... No me lo esperaba... Bajé la guardia... Ha sido culpa mía.' WHERE `entry` = 13613;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es un poco tarde... para eso... $Gmuchacho:muchacha;.$B$B<El explorador Natoescarcha tose algo de sangre sobre la nieve.>$B$BCoge... al joven que está al mando por mí.$B$B<El explorador Natoescarcha se desploma mientras exhala su último aliento.>' WHERE `entry` = 13614;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mira a tu alrededor y deja que la devastación cause su efecto, $C. Hasta el último trol que vivía aquí está muerto o ha huido a los niveles superiores.$B$BY solo empeora.$B$BEn un acto de desesperación, los trols Drakkari vivos sacrificaron a sus propios dioses animales para poder beber la sangre de los dioses y conseguir un poder increíble. Aunque tuvieron éxito al mantener a raya a la Plaga con su nueva fuerza, los resultados fueron catastróficos. La prueba es que el antiguo imperio de Zul\'Drak yace en ruinas.' WHERE `entry` = 13615;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aléjate un poco, ¿quieres? ¡Estoy intentando simular que sigo bajo el control del Rey Exánime!' WHERE `entry` = 13616;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Gerk, Burr, Dargath: todos se han ido... Solo quedamos MacKellar y yo. Espero que hayas traído algunas buenas noticias.' WHERE `entry` = 13619;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Silencio por favor! ¡Cachorros durmiendo!' WHERE `entry` = 13628;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Sé $Gbienvenido:bienvenida; entre nosotros, $C.' WHERE `entry` = 13637;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Con mi libertad llega una tormenta de venganza!' WHERE `entry` = 13639;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Escucha lo que tengo que contarte sobre nuestro enemigo antes de que empecemos. Querrás estar $Gpreparado:preparada; cuando estemos machacando a la Plaga, ¿verdad?' WHERE `entry` = 13640;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Imagina todo el conocimiento y sabiduría que hay encerrados en Ulduar. ¡Allí podrían estar los mismos secretos de la vida!' WHERE `entry` = 13642;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Señor de batalla Ymirjar, Algar el Elegido reúne a sus ejércitos de vargul en el Relicario de Dolor, al noreste de esta jaula.$B$BEl príncipe Navarius está al otro lado de los Campos Muertos, al este de aquí, cerca de los grandes escalones que llevan al segundo nivel de Zul\'Drak.$B$BNo se conoce la posición actual de Thrym, algo curioso teniendo en cuenta su tamaño.$B$B¡Hay que ocuparse de todos!' WHERE `entry` = 13647;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Veneno... me arden las entrañas...', `text0_1_loc7` = 'Veneno... me arden las entrañas...' WHERE `entry` = 13650;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debemos atacar Jotunheim continuamente. ¡No darles ninguna oportunidad de recobrar el aliento, ni de dormir, ni de aceptar la derrota!$B$BTirion Vadín no podrá conseguir lo que estás haciendo ni con todo el poder de la Crematoria, ni con todo el apoyo de la Cruzada Argenta.$B$BAhora, date prisa con lo tuyo, $N. Están ocurriendo demasiadas cosas como para pararse a charlar.' WHERE `entry` = 13654;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando estés $Glisto:lista; te colocaré sobre mi cabeza y marcharemos hacia el corazón de la Plaga en Zul\'Drak. ¡Deberás dirigirme para aplastar y destruir a nuestros enemigos!' WHERE `entry` = 13656;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Estamos desbordados, $R! ¡Estate $Gatento:atenta;!' WHERE `entry` = 13658;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Espada de Ébano está en deuda contigo por tu ayuda en la toma de La Cámara de las Sombras. Tenemos suerte de tenerte entre nosotros.$B$BMientras la Alianza y la Horda luchan entre ellos, haciendo el trabajo del Rey Exánime, nosotros nos mantendremos centrados en la tarea que nos ocupa.$B$BDonde la Cruzada Argenta puede dudar y pensar dos veces en la mejor manera de aplastar al enemigo, nosotros no tendremos dificultades morales.$B$BTú serás nuestro instrumento de venganza, $N.' WHERE `entry` = 13659;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Te arrancaré todas las extremidades.', `text1_1_loc7` = '¿Me desafías?', `text2_1_loc7` = 'No pareces tan duro.', `text3_1_loc7` = '¿Crees que tienes lo que hace falta?' WHERE `entry` = 13660;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Bienvenida, compañera de batallas. Puedo ayudarte a recuperar compañeros perdidos.' WHERE `entry` = 13661;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Si no puedes ayudar de otro modo, mata a estas cosas!' WHERE `entry` = 13671;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedo ocuparme de mí mismo, $N.' WHERE `entry` = 13673;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué haces TÚ aquí?$B$BAsegúrate de mantenerte fuera de mi camino. ¡No abandoné El Embate Escarlata para que me distrajeran cosas como tú!' WHERE `entry` = 13674;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No me molestes, estorbo. Estoy estudiando el combate de ineptos de estos llamados luchadores.$B$BYo les ganaré a todos y después ascenderé para servir al Rey Exánime.' WHERE `entry` = 13678;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Parece que podrías hacernos ganar algo de dinero. ¿Qué tal una vuelta por dentro del Anfiteatro de la Angustia, colega?' WHERE `entry` = 13679;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ahora eres $Gel campeón:la campeona;, colega! ¿Crees que tienes lo que hay que tener para defender tu título?' WHERE `entry` = 13680;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta copia del Cuerno de Hodir se alza en memoria del Rey Arngrim y todos los demás que perecieron en la batalla de Truenotoño.' WHERE `entry` = 13685;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué puedo hacer por ti?' WHERE `entry` = 13690;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Este patético cachorro verde era inútil en las minas. Da igual, servirá de calentamiento para la batalla.' WHERE `entry` = 13691;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Tú también estás aquí para demostrarle tu valía al Rey Exánime?$B$BEs algo arcaico, ¿no crees? ¿Tener que luchar y sobrevivir?' WHERE `entry` = 13694;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Oye, yo te conozco! Supongo que debo darte las gracias por estar aquí. Ese accidente en el teletransporte ha sido lo mejor que me ha pasado.$B$BSi vuelves a la Tundra Boreal, dile a mi copia tonta y santurrona que he ascendido un poco.' WHERE `entry` = 13695;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Yunque de Fjorn se alza de forma prominente en Dun Niffelem como un símbolo de su victoria sobre Fjorn y sus ejércitos.$B$BNo puedes evitar sonreír al saber que tus acciones han tenido este efecto duradero en el mundo.' WHERE `entry` = 13697;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Ya no entreno a caballeros de la Muerte, me cansé de iniciados cretinos y de sus constantes preguntas.$B$BAhora, largo.' WHERE `entry` = 13699;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ono\'zun aquí para desafiar a los vrykul y subir en el escalafón.$B$BDespués de que Ono\'zun luche en Valhalas y gane, Ono\'zun trabajar para el Rey Exánime' WHERE `entry` = 13701;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Cómo puedo ayudarte?', `text0_1_loc7` = '¿Cómo puedo ayudarte?' WHERE `entry` = 13702;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una vez lo tuve todo. Fama, fortuna, el mejor equipo completo de gladiador... y lo eché todo a perder.$B$BCuando el Cártel me descubrió amañando peleas... comerciando con victorias... todo se acabó.$B$BAhora no tengo nada aparte de estos harapos y un hígado enfermo.' WHERE `entry` = 13707;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién eres?$B$BLargo... estoy agotado de tanto trabajar en la carretera del puerto. Es hora de beber cerveza enana, dar gritos y echarse la siesta.' WHERE `entry` = 13709;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Una vez tuve una casa en los frondosos bosques de Quel\'Thalas. Una vez tuve una familia, un marido... hijos. Una vez, estaba viva y era una con el mundo. El Rey Exánime me arrebató todo eso en un instante.$B$B¡Aunque no tengo poder para atacarle directamente, haré que pague tres veces por ello!' WHERE `entry` = 13715;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Allá donde están los Natoescarcha hay águilas. Han sido nuestras aliadas durante más tiempo del que llevo caminando por la tierra... y ya no soy lo que se dice joven, $Gzagal:zagala;.' WHERE `entry` = 13733;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué quieres?' WHERE `entry` = 13738;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Sé $Gbienvenido:bienvenida; aquí, como $Gamigo:amiga; y $Galiado:aliada; de los terráneos.' WHERE `entry` = 13740;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Se te permite visitar nuestra fortaleza, $Gpequeño:pequeña; $R.$B$B¡No nos deshonres!' WHERE `entry` = 13747;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No confío en ti lo suficiente como para hablar de tales cosas.$B$BDeberás respetar las pruebas que ha soportado nuestro pueblo, las cargas que hemos aguantado...$B$BQuizá si acabas siendo un gran honor para Dun Niffelem estaría dispuesto a compartir contigo el saber de nuestro pueblo.' WHERE `entry` = 13748;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Muy bien, $Gpequeño:pequeña; $C.$B$BAunque aún te queda mucho por pulir en nuestra relación con Thorim, me veo obligado a compartir contigo este secreto.$B$BVuelve a hablar conmigo cuando estés $Glisto:lista; para conocer lo que buscas.' WHERE `entry` = 13749;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $R. ¿En qué puedo ayudarte hoy?', `text0_1_loc7` = 'Hola, $R. ¿En qué puedo ayudarte hoy?' WHERE `entry` = 13759;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo siento, colega. Solo oficiales certificados de la Horda pueden autorizar la compra de un vehículo.', `text0_1_loc7` = 'Lo siento, colega. Solo oficiales certificados de la Horda pueden autorizar la compra de un vehículo.' WHERE `entry` = 13761;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Míralos... todo lo que les importa es ser mejor que el otro.' WHERE `entry` = 13777;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Verás, Thorim no es como esas arpías. Es un ser digno de estatura parecida a la de los dioses.$B$BSirvió a los titanes como guardián de Ulduar y el Templo de los Creadores.$B$BEso fue hasta que su hermano asesinó a su mujer, Sif, en un acto de cobardía.' WHERE `entry` = 13778;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Thorim se volvió loco. Arremetió contra sus más amados aliados, los gigantes de escarcha.$B$BEsa es una de las razones por las que hoy, esta tierra está dividida por la guerra entre los Hyldnir y Los Hijos de Hodir... y por la que Thorim se ha exiliado al Templo de las Tormentas.' WHERE `entry` = 13779;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Y a mí qué me importa si estas lobas y los gigantes de escarcha se matan entre ellos cada día?$B$BNo, no... Quiero que Thorim salga de su estupor y reúna a sus aliados para retomar Ulduar.$B$B¡El mundo se partirá en dos si fracasa!$B$BY por supuesto, te recompensaré apropiadamente.' WHERE `entry` = 13780;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Déjame con mi vergüenza, $R.' WHERE `entry` = 13782;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hombres... no puedes vivir con ellos y no puedes vivir sin ellos. Bueno, sí que puedes... pero es muy aburrido.' WHERE `entry` = 13783;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Estas minas son un lugar terrorífico. Por suerte, no tengo intención de quedarme mucho tiempo.' WHERE `entry` = 13784;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Los osos son criaturas hermosas. Salvajes, fuertes y feroces en la batalla... muy parecidos a nosotras, hermana.' WHERE `entry` = 13785;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo siento, colega. Me temo que hemos usado todas las piezas de las que podíamos prescindir por el momento.', `text0_1_loc7` = 'Lo siento, colega. Me temo que hemos usado todas las piezas de las que podíamos prescindir por el momento.' WHERE `entry` = 13786;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola, hola!' WHERE `entry` = 13790;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, encanto. ¡Me alegra tener compañía!' WHERE `entry` = 13792;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encantado de conocerte, $C.' WHERE `entry` = 13793;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Las montañas tienen mucha historia... No olvides pararte a aprenderla, $C.' WHERE `entry` = 13794;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, encanto. ¡Me alegra tener compañía!' WHERE `entry` = 13795;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos encontrado estos protodracos en el Santuario de Dragones Bronce, en el Cementerio de Dragones. Desde el principio supimos que eran de nuestro linaje, nuestra sangre en el pasado.$B$BDiscúlpame, $C. supongo que estás $Gconfuso:confusa;. Soy Penumbrius, del Vuelo Bronce. He adoptado esta forma por conveniencia. Es mucho más fácil convivir y comunicarse con los mortales cuando tienes aspecto de mortal.$B$B¿Cómo puedo ayudarte?' WHERE `entry` = 13797;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Hola, $N! ¿Cómo puedo ayudarte hoy?', `text0_1_loc7` = '¡Hola, $N! ¿Cómo puedo ayudarte hoy?' WHERE `entry` = 13798;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Habla, $C.' WHERE `entry` = 13799;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ya he evitado mi destino durante bastante tiempo. Nunca he visto tan claro lo que debo hacer.$B$BDebo destruir a Loken yo mismo.' WHERE `entry` = 13800;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Por un instante he creído que eras mi amada Sif... Ese mechón de pelo... ¡pero no! No puede ser... Sif está muerta...$B$B¡Mi hermano... la mató!' WHERE `entry` = 13801;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ulduar... juré proteger Ulduar.$B$BJuré proteger el Templo de los Creadores.$B$BJuré proteger los pactos entre las razas de escarcha.$B$B¡Pero fracasé! Dejé que mi hermano me engañara. Dejé que me pusiera en contra de mis aliados. Me engañó para que culpara de la muerte de Sif a un estimado amigo, Arngrim, el rey de los gigantes de escarcha.$B$BCuando me di cuenta de lo que había hecho... caí en la desesperación.' WHERE `entry` = 13802;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Quién eres para venir a decir esto al poderoso Thorim, $Gpequeño:pequeña;?$B$B¡Debería... aplastarte!$B$BPero me has sacado de mi estupor. ¡Tu presencia aquí es una señal!$B$BNo ha sido un accidente que te confundiera con Sif. ¿Qué pensaría ella de mí? Debo prepararme para la batalla.$B$B¡Debo vengar su muerte y liberar la tierra sagrada de los titanes de la locura de mi hermano!' WHERE `entry` = 13803;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te doy la bienvenida a Las Cumbres Tormentosas, $R.' WHERE `entry` = 13804;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Mrfrrmrfrmrrr!$B$B<El geist se aclara la garganta.>$B$B¿Sí?' WHERE `entry` = 13831;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aún no posees fuerza suficiente para luchar en la Playa de los Ancestros. Vuelve cuando hayas obtenido más experiencia.', `text0_1_loc7` = 'Aún no posees fuerza suficiente para luchar en la Playa de los Ancestros. Vuelve cuando hayas obtenido más experiencia.' WHERE `entry` = 13832;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No podemos permitir que la Horda utilice los secretos ocultos de la Playa de los Ancestros contra nosotros. ¿Te unirás a nuestros valientes soldados allí?', `text0_1_loc7` = 'No podemos permitir que la Horda utilice los secretos ocultos de la Playa de los Ancestros contra nosotros. ¿Te unirás a nuestros valientes soldados allí?' WHERE `entry` = 13833;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No podemos permitir que la Alianza utilice los secretos ocultos de la Playa de los Ancestros contra nosotros. ¿Te unirás a nuestros valientes guerreros allí?', `text0_1_loc7` = 'No podemos permitir que la Alianza utilice los secretos ocultos de la Playa de los Ancestros contra nosotros. ¿Te unirás a nuestros valientes guerreros allí?' WHERE `entry` = 13834;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Factoría de Carne es el corazón de la producción de necrófagos, abominaciones y gigantes de carne de la Plaga. Si podemos destruirla, debilitaremos al Rey Exánime de forma significativa.' WHERE `entry` = 13838;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Más reclutas hambrientos?' WHERE `entry` = 13840;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué necesitas?' WHERE `entry` = 13841;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Servimos a Thorim, hermana.' WHERE `entry` = 13843;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Siempre es un placer conocer a personajes de tu talla en esta tierra abandonada. ¿Puedo ofrecerte algún tipo de ayuda?' WHERE `entry` = 13844;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $C. Mi consejo para ti es este: cuando viajes por el mundo, ten cuidado con la magia porque puede acabar con el profano.', `text0_1_loc7` = 'Hola, $C. Mi consejo para ti es este: cuando viajes por el mundo, ten cuidado con la magia porque puede acabar con el profano.' WHERE `entry` = 13846;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Por supuesto! Ve con la bendición de Elune.' WHERE `entry` = 13847;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es un día aciago en la historia de los trols, colega.',  `text1_0_loc7` = 'Es horrible, colega. No puedo ni mirar.' WHERE `entry` = 13848;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Mi deber es recoger estos trágicos eventos para el bien de las generaciones venideras.' WHERE `entry` = 13850;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No debemos descentrarnos. Si dejamos que la situación nos confunda, podríamos ponernos en peligro. Los soldados necesitan descansar de toda esta locura.$B$BDisfruta de la victoria, $N. De no ser por ti, hubiéramos perdido la Vanguardia.' WHERE `entry` = 13851;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Al fin, refuerzos! ¡Los guardias de la prisión no podrán plantar cara a esta invasión mucho más tiempo! Tendréis que relevar a mis hombres e intentar aguantar todo lo que podáis. Aún queda algo de energía disponible en el sistema de defensa. Usad los cristales de activación situados alrededor de la prisión para lanzar una descarga si la situación empeora.' WHERE `entry` = 13853;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Ya están listos para continuar todos los miembros de tu grupo? Lanzaré una descarga con este cristal de activación para cubrir la retirada de mis guardias.' WHERE `entry` = 13854;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo que hago por la Cruzada Argenta no debería considerarse el cumplimiento de una obligación. Más bien un pago, si lo prefieres. El Alto Señor me liberó del Rey Exánime y puso ante nosotros el sendero a la salvación. Dicho esto, hay que reconocer que nuestros métodos difieren... enormemente. Los Caballeros de la Espada de Ébano y yo ayudaremos a la Cruzada Argenta a sentar las bases en Corona de Hielo y luego separaremos nuestros caminos. Así, el pago quedará satisfecho.$B$BA nosotros nos aguarda otro destino.' WHERE `entry` = 13856;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No cabe duda de que esos cruzados están causando muchos quebraderos de cabeza, $G hermano:hermana;. Pero ¿qué es el sacrificio sin dolor? Tú estuviste conmigo en la Capilla de la Esperanza de la Luz. Viste muchos sacrificios. Y ahora, tenemos que pagar nuestra deuda con el Alto Señor. Pagarle por su sacrificio.$B$BCuando la Cruzada Argenta haya establecido un punto de apoyo dentro de las murallas de Corona de Hielo, partiremos. A nosotros nos aguarda otro destino.' WHERE `entry` = 13857;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En el interior del ojo se agita una magia ancestral. Da sensación de vértigo, como si pudieras caerte en el iris por accidente para no regresar.' WHERE `entry` = 13869;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El vrykul descansa pacíficamente en su sueño mágico.' WHERE `entry` = 13871;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<hip>$B$BSin mis colegas te sentirías $Gsolo:sola; aquí, $G chico:chica;.',  `text1_0_loc7` = '¡Yarr! ¡Alguien tiene que enseñarles a estos pedorros para qué!$B$B<hic>', `text2_0_loc7` = '¡Es hora de patear culos y beber un ron doble!$B$BY casi se me ha acabado el ron doble...' WHERE `entry` = 13873;
