UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tomar el Pináculo de los Cruzados solo es el primer paso de un viaje largo y peligroso. Aún queda mucho por hacer antes de que podamos asaltar la Ciudadela de la Corona de Hielo.$B$BSolo será posible con tu ayuda y la de otros como tú, $Ghéroe:heroína;.' WHERE `entry` = 13896;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida:c;, $C.' WHERE `entry` = 13897;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Rompecielos patrulla por Corona de Hielo. No encontrarás un barco mejor en ningún reino.' WHERE `entry` = 13901;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Martillo de Orgrim está preparado para atacar Corona de Hielo. No encontrarás una nave mejor.' WHERE `entry` = 13902;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El alto señor Darion Mograine asiente.>' WHERE `entry` = 13903;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La única razón por la que sigues con vida es porque tengo curiosidad.' WHERE `entry` = 13905;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El líquido rojo sangre de dentro del ojo se mueve, mostrando imágenes de un masivo ejército de la Plaga. El ojo parece tirar de ti, abriéndose camino hacia tu mente y haciendo que te apartes instintivamente. Te sientes muy $Gincómodo:incómoda; en presencia de este objeto.' WHERE `entry` = 13906;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Sea lo que sea lo que vas a pedir, hazlo deprisa.' WHERE `entry` = 13908;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'A partir de ahora nos encargaremos nosotros. Os habéis ganado un respiro después de esa batalla.' WHERE `entry` = 13910;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué pasa?' WHERE `entry` = 13911;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Muchos han caído dentro de Valhalas y sus cadáveres son el testamento de la fuerza de nuestros métodos.$B$B¿Quedarán también tus huesos esparcidos en este lugar de honor y batalla, $N?' WHERE `entry` = 13918;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Debería matarte aquí mismo, $R!$B$B¡Continúa con tu estúpido balbuceo y acabaré contigo!' WHERE `entry` = 13919;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Todavía no has demostrado ser $Gdigno:digna; de participar en un desafío dentro de Valhalas, $N.$B$BRegresa cuando te hayas labrado un nombre.' WHERE `entry` = 13921;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Muchos han caído dentro de Valhalas y sus cadáveres son el testamento de la fuerza de nuestros métodos.$B$B¿Quedarán también tus huesos esparcidos en este lugar de honor y batalla, $N?' WHERE `entry` = 13922;
