UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Reuniendo... información... sobre la Plaga.$B$BLlegaron tropas de la Alianza... $B$BNos escondimos... esperamos hasta que atacaron la puerta... entonces, atacamos por detrás.$B$BAtrapados entre nosotros... y la Plaga... demasiado para ellos...$B$B$GHermano:Hermana;... ha sido glorioso.', `text0_1_loc7` = 'Reuniendo... información... sobre la Plaga.$B$BLlegaron tropas de la Alianza... $B$BNos escondimos... esperamos hasta que atacaron la puerta... entonces, atacamos por detrás.$B$BAtrapados entre nosotros... y la Plaga... demasiado para ellos...$B$B$GHermano:Hermana;... ha sido glorioso.' WHERE `entry` = 13931;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El día del juicio se acerca. En el fondo de tu corazón, sabes que la justicia es el único camino verdadero.' WHERE `entry` = 13932;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo he dado todo... por la Horda...', `text0_1_loc7` = 'Lo he dado todo... por la Horda...' WHERE `entry` = 13935;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Espera, $C...', `text0_1_loc7` = 'Espera, $C...' WHERE `entry` = 13938;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aarrgh... Es demasiado tarde para mí, $Ghermano:hermana;.$B$BEstoy... acabado...', `text0_1_loc7` = 'Aarrgh... Es demasiado tarde para mí, $Ghermano:hermana;.$B$BEstoy... acabada...' WHERE `entry` = 13940;
