UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los miembros de la Horda que nos visitan suelen quedarse en el Santuario de los Atracasol, en la zona noreste de Dalaran.$B$BAllí hay una posada, además de portales a las diversas ciudades de la Horda y a los campos de batalla.', `text0_1_loc7` = 'Los miembros de la Horda que nos visitan suelen quedarse en el Santuario de los Atracasol, en la zona noreste de Dalaran.$B$BAllí hay una posada, además de portales a las diversas ciudades de la Horda y a los campos de batalla.' WHERE `entry` = 13972;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los miembros de la Alianza que nos visitan suelen quedarse en El Enclave de Plata, en la zona sudoeste de Dalaran.$B$BAllí hay una posada, además de portales a las diversas ciudades de la Alianza y a los campos de batalla.', `text0_1_loc7` = 'Los miembros de la Alianza que nos visitan suelen quedarse en El Enclave de Plata, en la zona sudoeste de Dalaran.$B$BAllí hay una posada, además de portales a las diversas ciudades de la Alianza y a los campos de batalla.' WHERE `entry` = 13973;
