UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La arena de Dalaran y quienes se encargan de ella se encuentran bajo la ciudad, en las cloacas.$B$BHay almenos tres formas de entrar: cloacas en al este y el oeste, y un pozo al norte.', `text0_1_loc7` = 'La arena de Dalaran y quienes se encargan de ella se encuentran bajo la ciudad, en las cloacas.$B$BHay almenos tres formas de entrar: cloacas en al este y el oeste, y un pozo al norte.' WHERE `entry` = 13976;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puedes encontrar portales a las diversas capitales y a los campos de batalla en los cuarteles de la Alianza y la Horda: El Enclave de Plata y el Santuario de los Atracasol.', `text0_1_loc7` = 'Puedes encontrar portales a las diversas capitales y a los campos de batalla en los cuarteles de la Alianza y la Horda: El Enclave de Plata y el Santuario de los Atracasol.' WHERE `entry` = 13977;
