UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay tres maneras de salir de Los Bajos Fondos: rampas que suben hacia el este y el oeste y el túnel que sale desde la parte de debajo de Dalaran.$B$BAsegúrate de que sabes volar antes de meterte por el túnel.', `text0_1_loc7` = 'Hay tres maneras de salir de Los Bajos Fondos: rampas que suben hacia el este y el oeste y el túnel que sale desde la parte de debajo de Dalaran.$B$BAsegúrate de que sabes volar antes de meterte por el túnel.' WHERE `entry` = 14026;
