UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Lo logramos! ¿Puedes creerte lo que estamos oyendo? ¡Es increíble!' WHERE `entry` = 14176;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace un día magnífico, ¿verdad? Espero que podamos ver unos cuantos fuegos artificiales antes de que se acabe.' WHERE `entry` = 14178;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $Gpequeño:pequeña; $R.' WHERE `entry` = 14179;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Solo porque algo no esté roto no quiere decir que no puedas arreglarlo. ¿Buscas algo más, cariño?' WHERE `entry` = 14180;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El suministrador de D.E.D.I. te ruge expectante.' WHERE `entry` = 14182;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cazar tesoros y cerveza suculenta... no hay nada mejor, $Gmuchacho:muchacha;.' WHERE `entry` = 14197;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los martillos de los terráneos se mantienen unidos tras Pedruscón. Nos opondremos a los esbirros de Loken hasta el final.' WHERE `entry` = 14203;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hazlo rápido, $C.' WHERE `entry` = 14204;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<El motor ruge con fuerza>' WHERE `entry` = 14205;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Fuertes vientos rodean el altar. Hay una ranura que parece haber albergado alguna vez un pequeño orbe.' WHERE `entry` = 14206;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Por fin!$B$B¡Ha llegado $Gel:la; $R $C que anunciaban las profecías!' WHERE `entry` = 14208;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Sí, $Gpequeño:pequeña;?' WHERE `entry` = 14209;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Está bien, $C. ¿Crees que tienes lo que hay que tener para entrar en las arenas del Círculo de Lucha Bonvapor?', `text0_1_loc7` = 'Rápido, $R. Estoy cansada y no tengo tiempo para galanterías…' WHERE `entry` = 14215;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuidado, $R.$B$BEste lugar no está hecho para los enclenques.' WHERE `entry` = 14216;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ahí estás!' WHERE `entry` = 14218;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Lok\'tar! ¿Has venido para servir a la Horda en la batalla?' WHERE `entry` = 14221;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Oye, si la gente quiere corretear por ahí embadurnándose por unos huevos de colores, ¿quién soy yo para juzgarlos? Me llevo un pellizco de los beneficios de la pintura, y este año he invertido en pollos.', `text0_1_loc7` = 'Oye, si la gente quiere corretear por ahí embadurnándose por unos huevos de colores, ¿quién soy yo para juzgarlos? Me llevo un pellizco de los beneficios de la pintura, y este año he invertido en pollos.' WHERE `entry` = 14231;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ha llegado la celebración del Jardín Noble! Yo ya he encontrado un montón de huevos. El tiempo acompaña para la búsqueda, ¿eh?', `text0_1_loc7` = '¡Ha llegado la celebración del Jardín Noble! Yo ya he encontrado un montón de huevos. El tiempo acompaña para la búsqueda, ¿eh?' WHERE `entry` = 14232;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Saludos, $Gamigo:amiga;! ¿Has venido a servir a los hijos de Azeroth en la batalla?' WHERE `entry` = 14238;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, camarada. Espero que estés disfrutando de la celebración del Jardín Noble. ¿Por qué no te unes a la diversión?', `text0_1_loc7` = 'Saludos, camarada. Espero que estés disfrutando de la celebración del Jardín Noble. ¿Por qué no te unes a la diversión?' WHERE `entry` = 14239;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C.$B$BCuando la Alianza controle la Fortaleza de Conquista del Invierno, abriré un portal para aquellos que deseen viajar hasta allí.$B$BLa batalla está teniendo lugar ahora. ¡Ve corriendo a Conquista del Invierno y apoya a nuestras tropas!', `text0_1_loc7` = 'Saludos, $C.$B$BCuando la Alianza controle la Fortaleza de Conquista del Invierno, abriré un portal para aquellos que deseen viajar hasta allí.$B$BLa batalla está teniendo lugar ahora. ¡Ve corriendo a Conquista del Invierno y apoya a nuestras tropas!' WHERE `entry` = 14240;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C.$B$BCuando la Horda controle la Fortaleza de Conquista del Invierno, abriré un portal para aquellos que deseen viajar hasta allí.$B$BLa batalla está teniendo lugar ahora. ¡Ve corriendo a Conquista del Invierno y apoya a nuestras tropas!', `text0_1_loc7` = 'Saludos, $C.$B$BCuando la Horda controle la Fortaleza de Conquista del Invierno, abriré un portal para aquellos que deseen viajar hasta allí.$B$BLa batalla está teniendo lugar ahora. ¡Ve corriendo a Conquista del Invierno y apoya a nuestras tropas!' WHERE `entry` = 14241;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Con la ayuda de nuestros nuevos aliados taunka, Rasganorte pertenecerá a la Horda.' WHERE `entry` = 14245;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Saludos, $C! Te doy la bienvenida a bordo de El Besanubes. Rasganorte puede ser un lugar muy arduo. Espero que hayas hecho las preparaciones necesarias.' WHERE `entry` = 14248;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entonces querrás hablar con Benik Cortapernos. Está en el corazón del Distrito de los Enanos, junto a los herreros.', `text0_1_loc7` = 'Entonces querrás hablar con Benik Cortapernos. Está en el corazón del Distrito de los Enanos, junto a los herreros.' WHERE `entry` = 14249;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si estás buscando a un cerrajero, te sugiero las cloacas. Allí es donde encontrarás a más... tipos de dudosa reputación.', `text0_1_loc7` = 'Si estás buscando a un cerrajero, te sugiero las cloacas. Allí es donde encontrarás más... tipos de dudosa reputación.' WHERE `entry` = 14251;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los hipogrifos de Vesprystus te conducirán rápidamente a tu destino.', `text0_1_loc7` = 'Los hipogrifos de Vesprystus te conducirán rápidamente a tu destino.', `text1_0_loc7` = 'Es a Vesprystus a quien buscas. Guarda a los hipogrifos que llegan y parten de la base de Teldrassil.', `text1_1_loc7` = 'Es a Vesprystus a quien buscas. Guarda a los hipogrifos que llegan y parten de la base de Teldrassil.' WHERE `entry` = 14254;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¿Qué haces aquí? ¡Deberías estar dentro batiéndote contra el Vuelo Azul!' WHERE `entry` = 14271;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ya sé… \"¿pescar en una cloaca?\" Pero te sorprendería lo que puedes pescar aquí. He oído historias sobre tiburones, aunque te parezca increíble.' WHERE `entry` = 14272;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo no juzgo a los demás por su estatura, $R, sino por su coraje y lealtad.' WHERE `entry` = 14281;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nos hemos metido en una buena. Ya tendríamos que estar con nuestras fuerzas en Corona de Hielo y ahora estamos inmiscuidos en este debacle, encerrados entre la Plaga y los Drakkari.$B$BLe dije al Alto Señor que era un error venir aquí.$B$BVe qué puedes hacer para ayudar.' WHERE `entry` = 14284;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Lo oyes, $C? Los elementos gritan que esta tierra está casi muerta. Los Drakkari están robando el poder de sus dioses al matarlos, solo para defenderse de la Plaga.$B$BSon unos imprudentes y al final solo conseguirán su propia perdición.' WHERE `entry` = 14285;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Después de lo de las Tierras de la Peste y de la batalla por la Capilla de la Esperanza de la Luz, jamás habría pensado que veríamos algo más horrible.$B$BMe equivocaba.' WHERE `entry` = 14286;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Bienvenidos, campeones! Todos nuestros intentos por acabar con ella han fracasado. Nos vendría bien una mano para derrotarla con esos arpones.', `text0_1_loc7` = '¡Bienvenidos, campeones! Todos nuestros intentos por acabar con ella han fracasado. Nos vendría bien una mano para derrotarla con esos arpones.' WHERE `entry` = 14317;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Pensé que jamás me libraría de esa terrible maldición. Tienes mi gratitud, $Gforastero:forastera;.' WHERE `entry` = 14319;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Acéptala con mi gratitud, $C.' WHERE `entry` = 14320;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué puedo hacer por ti, $Gseñor:señora;?' WHERE `entry` = 14324;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Seguro que te has preparado para el combate sobre montura?', `text0_1_loc7` = '¿Seguro que te has preparado para el combate sobre montura?' WHERE `entry` = 14335;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Gloria a Ventormenta y al rey Wrynn!' WHERE `entry` = 14359;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Vaya vaya, nunca pensé que tendría la oportunidad de ver a Ulduar con mis propios ojos. Bueno, cuando Brann me pidió ayuda para organizar esta expedición, ¡¿cómo iba a negarme?!' WHERE `entry` = 14362;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Pensé que no íbamos a escapar nunca de esa jungla de vapor! Ahora soy el ingeniero jefe de esta expedición.' WHERE `entry` = 14363;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Puede que estas preciosidades parezcan chatarra, pero pueden embestir a los enemigos. El espolón central ha sido sobrecargado con un híper condensador electroscópico que le permite propinar descargas eléctricas. $B$BIncluso la torreta ha sido mejorada con cohetes antiaéreos, algo que proporciona, definitivamente, una ventaja sobre esos malditos demoledores.', `text0_1_loc7` = 'Puede que estas preciosidades parezcan chatarra, pero pueden embestir a los enemigos. El espolón central ha sido sobrecargado con un híper condensador electroscópico que le permite propinar descargas eléctricas. $B$BIncluso la torreta ha sido mejorada con cohetes antiaéreos, algo que proporciona, definitivamente, una ventaja sobre esos malditos demoledores.' WHERE `entry` = 14364;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tuvimos la suerte de encontrar suficientes piezas de recambio para hacer que estas preciosidades volvieran a funcionar!$B¿En qué puedo ayudarte?' WHERE `entry` = 14367;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No hay nada que escupa fuego mejor que estas preciosidades. Incluso hemos conectado un cañón secundario y un gancho prensil, y hemos añadido un sistema manual de inyección de combustible. Si puedes obtener pirita líquida de los mecagnomos, puedes usarla para arrojar explosivos de alta densidad, o para incrementar tu velocidad de movimiento.$B$B¡A ver si esos mequetrefes de la Liga de Expedicionarios pueden hacer algo mejor!', `text0_1_loc7` = 'No hay nada que escupa fuego mejor que estas preciosidades. Incluso hemos conectado un cañón secundario y un gancho prensil, y hemos añadido un sistema manual de inyección de combustible. Si puedes obtener pirita líquida de los mecagnomos, puedes usarla para arrojar explosivos de alta densidad, o para incrementar tu velocidad de movimiento.$B$B¡A ver si esos mequetrefes de la Liga de Expedicionarios pueden hacer algo mejor!' WHERE `entry` = 14368;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Que os hayais presentado son buenas noticias! Los enanos férreos nos han hecho retroceder hasta este lugar, y apenas estamos consiguiendo mantener la posición. $B$BY, lo que es peor, han empezado a reforzar sus defensas mágicas. ¡Si no atacamos pronto serán capaces de aniquilarnos en un abrir y cerrar de ojos!$B$BNos las hemos apañado para reconstruir varias máquinas de asedio, demoledores y motocicletas de expediciones de la Alianza y de la Horda que estuvieron aquí antes que nosotros.$B$B¡Por favor, $N! ¡Eres nuestra última esperanza!' WHERE `entry` = 14369;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Puedo hacer algo por ti, jefe?' WHERE `entry` = 14372;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos.' WHERE `entry` = 14375;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Pacto de Plata te da la bienvenida al torneo. La Cruzada Argenta nos ha pedido que inscribamos y entrenemos a aquellos que estén interesados en competir.' WHERE `entry` = 14379;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Seguro que te has preparado para luchar contra un valeroso?', `text0_1_loc7` = '¿Seguro que te has preparado para luchar contra un valeroso?', `text1_0_loc7` = 'No sería sensato asumir que pelear con un valeroso es tarea fácil. ¿Seguro que te has preparado?', `text1_1_loc7` = 'No sería sensato asumir que pelear con un valeroso es tarea fácil. ¿Seguro que te has preparado?', `text2_0_loc7` = '¿Seguro que estás en condiciones de pelear conmigo?', `text2_1_loc7` = '¿Seguro que estás en condiciones de pelear conmigo?' WHERE `entry` = 14384;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La doble especialización de talentos te permite mantener activas dos especializaciones de talentos. Podrás cambiar fácilmente entre estas dos especializaciones. Cuando cambies de especialización tendrás acceso también a un segundo panel de glifos y a un segundo conjunto de barras de acción. El cambio no puede efectuarse en combate, y consumirás tus recursos disponibles.', `text0_1_loc7` = 'La doble especialización de talentos te permite mantener activas dos especializaciones de talentos. Podrás cambiar fácilmente entre estas dos especializaciones. Cuando cambies de especialización tendrás acceso también a un segundo panel de glifos y a un segundo conjunto de barras de acción. El cambio no puede efectuarse en combate, y consumirás tus recursos disponibles.' WHERE `entry` = 14391;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Felicidades, tu mente ha sido expandida.', `text0_1_loc7` = 'Felicidades, tu mente ha sido expandida.' WHERE `entry` = 14393;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos venido a competir por el honor, el renombre y el derecho a combatir la Plaga junto a los hermanos de la Cruzada Argenta.' WHERE `entry` = 14395;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'El rey Magni espera que ganemos este torneo y enorgullecerse de nosotros. No le fallaremos.' WHERE `entry` = 14396;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Lo mejor de Gnomeregan ha venido a competir en el torneo. ¡Deséanos suerte!' WHERE `entry` = 14398;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nosotros, los de Darnassus, ansiamos competir en el torneo.' WHERE `entry` = 14399;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando empiece el torneo, todos verán como los Renegados salen victoriosos.' WHERE `entry` = 14402;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Competimos no sólo por la gloria, sino para honrar a nuestros ancestros y a nuestra pueblo.' WHERE `entry` = 14404;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Lanza Negra están listos para enfrentarse a la competición y al enemigo.' WHERE `entry` = 14405;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En nombre de los Atracasol, te doy la bienvenida al torneo. La Cruzada Argenta nos ha pedido que inscribamos y entrenemos a todo aquel que esté interesado en competir.' WHERE `entry` = 14406;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás preparad$Go:a; para pelear contra un valeroso de la Cruzada Argenta?', `text0_1_loc7` = '¿Estás preparad$Go:a; para pelear contra un valeroso de la Cruzada Argenta?' WHERE `entry` = 14407;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Cuando estés preparad$Go:a; para ponerte a prueba para el rango de Valeros$Go:a;, llamaré a un miembro de la Cruzada Argenta.', `text0_1_loc7` = 'Cuando estés preparad$Go:a; para ponerte a prueba para el rango de Valeros$Go:a;, llamaré a un miembro de la Cruzada Argenta.' WHERE `entry` = 14408;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay una liza en la que los campeones se enfrentan unos a otros para entrenarse. Mi trabajo es llamar a los caballeros a sus batallas de entrenamiento.' WHERE `entry` = 14409;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Seguro que te has preparado para luchar contra un campeón?', `text0_1_loc7` = '¿Seguro que te has preparado para luchar contra un campeón?', `text1_0_loc7` = 'No sería sensato asumir que pelear contra un campeón es tarea fácil. ¿Seguro que te has preparado?', `text1_1_loc7` = 'No sería sensato asumir que pelear contra un campeón es tarea fácil. ¿Seguro que te has preparado?', `text2_0_loc7` = '¿Seguro que estás en condiciones de pelear conmigo?', `text2_1_loc7` = '¿Seguro que estás en condiciones de pelear conmigo?' WHERE `entry` = 14421;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El teletransportador parece activo y estable.', `text0_1_loc7` = 'El teletransportador parece activo y estable.' WHERE `entry` = 14424;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'En nombre de la Cruzada Argenta, te doy la bienvenida al Torneo Argenta.' WHERE `entry` = 14425;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Nadie construye más deprisa, más barato y mejor que los hermanos Cargarrayo!' WHERE `entry` = 14426;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encantado, $C.$B$BEntreno a los Aspirantes en el arte del combate con montura.' WHERE `entry` = 14431;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los objetivos cuerpo a cuerpo entrenan a los aspirantes tanto en el ataque como en la defensa sobre montura.$B$BAcércate al objetivo cuerpo a cuerpo y golpéalo con un ataque de Estocada. La fuerza del golpe a menudo hace que el objetivo gire sobre sí mismo con suficiente fuerza para herirte a ti o a tu montura. Ahí es donde entra en juego la defensa.$B$BAsegúrate de tener al menos una capa de Defender antes de atacar al objetivo, y continúa acumulando y renovando Defender durante el ejercicio de entrenamiento.$B$BLo mismo sucede en el verdadero combate sobre montura: Mantén un balance entre defensa y ataque, lanzando Estocada cuando tengas la oportunidad y manteniendo tus capas de Defender lo mejor que puedas.' WHERE `entry` = 14434;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Eh, ¿qué tal estás?$B$B¿Vienes a aprender el arte de Cargar, $C?' WHERE `entry` = 14436;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El secreto de una buena carga reside en medir bien la distancia y el tiempo. No puedes estar DEMASIADO cerca, o no alcanzarás suficiente velocidad. ¡Pero tampoco puedes estar demasiado lejos o tu montura se cansará! En cuanto al tiempo...$b$bTienes que asegurarte de que el objetivo de carga no tiene una capa de Defender activa antes de avalanzarte. Si es así, emplea tu Romper escudo para hacerlo vulnerable, y ¡ENTONCES utiliza Cargar!$b$bLo mismo pasa con el combate de verdad: ¡intenta reducir las defensas de tu oponente antes de cargar!' WHERE `entry` = 14437;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bien. Otr$Go:a; $C. ¿Vienes a aprender Romper escudo?' WHERE `entry` = 14438;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El ataque Romper escudo a distancia es fácil de aprender pero difícil de dominar. Puede que por sí solo parezca débil, pero en conjunción con Estocada y Cargar, Romper escudo hace que ganes una justa.$B$BEncuentra un objetivo a distancia. Tómate tu tiempo para entender el alcance de Romper escudo... cuánto espacio necesitas, y a cuánta distancia puedes arrojar tu lanza.$B$B¿Después de eso? Bueno. Ponte a arrojar y no pares hasta que hayas roto hasta la última capa de Defender y tu objetivo sea vulnerable.$B$BEsta táctica te será útil en el auténtico combate sobre montura: destruye las defensas de tus oponentes... ¡y aplástalos!' WHERE `entry` = 14439;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todo a su tiempo, camarada. Pronto tendré más trabajo para ti...' WHERE `entry` = 14441;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Desde luego, camarada. ¡Nuestras reservas de cristales de añublo son casi suficientes!' WHERE `entry` = 14442;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ya no nos estorbará más la incompetencia de Darmuk! Paciencia, camarada. Pronto habrá más que puedas hacer...' WHERE `entry` = 14443;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Con los cabecillas fuera de circulación, estamos listos para hacer nuestro ataque! Pronto, $N...' WHERE `entry` = 14444;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ahora ya lo sabes todo, camarada! Prepárate... ¡pronto atacaremos!' WHERE `entry` = 14445;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Estaba aquí visitando unos parientes cuando Lordaeron cayó, y entonces... Bien. Supongo que ya no voy a regresar a los Páramos de Poniente.$B$BA veces me pregunto si mi familia sigue viva. Aunque, más a menudo me pregunto por qué ya no me importa.' WHERE `entry` = 14446;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Antes de que pueda instruirte, debes montar una montura de torneo.$B$BEquipa tu lanza y busca tu montura de torneo justo frente al pabellón de tu facción.' WHERE `entry` = 14453;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Mis consejos no te servirán de nada a menos que estés sobre una montura de torneo. $B$BEquipa esa lanza que tienes y sube a una montura de torneo frente al pabellón de tu facción.' WHERE `entry` = 14455;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Solo los Campeones de Ventormenta pueden comprar lo que tengo en venta.' WHERE `entry` = 14456;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡No tan deprisa, $r!$b$bSolo los Campeones de Forjaz pueden comprar lo que tengo en venta.' WHERE `entry` = 14457;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Disculpa, $R, pero solo los Campeones de Cima del Trueno pueden comprar lo que tengo en venta.' WHERE `entry` = 14458;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Largo de aquí, $C!$B$BSolo los Campeones de Entrañas pueden comprar lo que tengo en venta.' WHERE `entry` = 14459;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Solo los Campeones de Orgrimmar pueden comprar lo que tengo en venta.' WHERE `entry` = 14460;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Solo los Campeones de El Exodar pueden comprar lo que tengo en venta.' WHERE `entry` = 14461;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Hola!$B$BPerdona, pero solo los Campeones de Gnomeregan pueden comprar lo que tengo.' WHERE `entry` = 14462;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ay!, solo los Campeones de Darnassus pueden comprar lo que tengo en venta.' WHERE `entry` = 14463;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Samamba piensa que e\'htah en el lugá\' equivoca\'o! $B$BLo que tiene eh\' solo p\'a los Campeones de Sen\'jin...' WHERE `entry` = 14464;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ay!, solo los estimados Campeones de Ciudad de Lunargenta pueden comprar lo que tengo en venta.' WHERE `entry` = 14465;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bienvenid$Go:a; al torneo, $N.' WHERE `entry` = 14472;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Me alegro de verte, $N.' WHERE `entry` = 14473;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hace falta mucha dedicación para domar y montar un pellejo venenoso.' WHERE `entry` = 14474;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los jinetes de la Cruzada Argenta creen en el equilibrio y en la precisión, y combinan con destreza el ataque y la defensa.$B$BSin embargo, a menudo se expondrán a una carga al intentar tomar distancia contigo.$B$BReduce esa distancia empleando Cargar antes de que ellos hagan lo mismo, y después emplea Romper escudo mientras te das la vuelta para utilizar Estocada de nuevo.', `text0_1_loc7` = 'Los jinetes de la Cruzada Argenta creen en el equilibrio y en la precisión, y combinan con destreza el ataque y la defensa.$B$BSin embargo, a menudo se expondrán a una carga al intentar tomar distancia contigo.$B$BReduce esa distancia empleando Cargar antes de que ellos hagan lo mismo, y después emplea Romper escudo mientras te das la vuelta para utilizar Estocada de nuevo.' WHERE `entry` = 14476;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Bienvenid$Go:a; a nuestra pequeña capilla de la Luz. Por favor, toma asiento. Disfruta de un descanso de los males del mundo.', `text0_1_loc7` = 'Bienvenid$Go:a; a nuestra pequeña capilla de la Luz. Por favor, toma asiento. Disfruta de un descanso de los males del mundo.' WHERE `entry` = 14487;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás aquí por el sermón de la confesora? Suerte encontrando asiento...' WHERE `entry` = 14491;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hoy ya has derrotado a uno de nuestros jinetes, así que no puedo aceptar tu desafío.', `text0_1_loc7` = 'Hoy ya has derrotado a uno de nuestros jinetes, así que no puedo aceptar tu desafío.' WHERE `entry` = 14492;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es un honor representar a la Cruzada Argenta en este torneo.' WHERE `entry` = 14495;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Torneo Argenta ha empezado en el extremo norte de Corona de Hielo, $C. Si todavía has de encontrar el camino al lugar, puedo pagarte el viaje.$B$BCuantos más participantes tengamos en el combate cuerpo a cuerpo, más fuertes seremos cuando ataquemos al Rey Exánime.' WHERE `entry` = 14500;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Veamos si podemos congelar al enemigo ahí mismo, $R.'  WHERE `entry` = 14520;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<*Estruendo*Estruendo*Estruendo*>'  WHERE `entry` = 14523;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<La prole te mira como si intentara averiguar si eres o no su madre.>' WHERE `entry` = 14527;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Seguro que en algún momento de tu vida fuiste un estudiante, $C. Aprendiste de un instructor y, de algún modo, comenzaste a respetarlo, ¿verdad? Quizá no como persona, pero al menos como alguien que sabía más que tú y estaba dispuesto a enseñarte. Si no, usa tu imaginación... $B$B¿Qué harías si te dieras cuenta de que tu instructor podría no saber tanto como pensabas y que tú has encontrado un camino mejor?' WHERE `entry` = 14560;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Esto es terrible! No debería estar aquí sentada, inútil, cuando hay tanto que hacer. Si no hubiera sufrido lesiones en el brazo en Rasganorte... ¡Pero no vale la pena lamentarse! Tahu me curará y seguiremos ayudando por aquí mientras.$BQue no estemos en el frente no significa que no podamos ayudar de algún modo.' WHERE `entry` = 14561;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Me estoy quedando sordo. No ha habido manera de callar a esa enorme arpía verde durante cinco segundos. Sé cómo conducir esta cosa, ¿vale? ¡No soy un idiota!' WHERE `entry` = 14613;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Zippik nos llevaría directos contra la cima si yo no estuviera aquí. Estúpido.' WHERE `entry` = 14614;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es un viaje bastante tranquilo si no te molesta el polvo.', `text0_1_loc7` = 'Es un viaje bastante tranquilo si no te molesta el polvo.', `text1_0_loc7` = 'Aléjate del borde, $Gcampeón:campeona;. No queremos que te caigas.', `text1_1_loc7` = 'Aléjate del borde, $Gcampeón:campeona;. No queremos que te caigas.', `text2_0_loc7` = 'Estoy trabajando.', `text2_1_loc7` = 'Estoy trabajando.' WHERE `entry` = 14615;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Oye. Estoy en mi descanso.' WHERE `entry` = 14617;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; al Céfiro, el viaje más tranquilo que tendrás en Los Baldíos. Relájate y disfruta del vuelo, ¿vale? No hay nada como el dulce aire seco mezclado con el aroma del aceite.' WHERE `entry` = 14618;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo ser Tolwe. Trabajar manteniendo paz y vigilando. Así que yo vigilar.' WHERE `entry` = 14619;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hola, cariño. El zepelín que para aquí te llevará directamente a Cima del Trueno, sin coste alguno. ¿Quién necesita dracoleones, cuando nos tenéis a los goblins? ¿Verdad?' WHERE `entry` = 14621;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Puedes creerlo? ¡Al fin nos han permitido volar en zepelín por este lugar! Ya no es una zona prohibida al vuelo, ¿verdad? Solo estrellamos un zepelín en un montículo en el viaje de prueba. No entiendo por qué estaban tan preocupados.' WHERE `entry` = 14622;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hola, $N. Si buscas instrucción en cocina, has venido al lugar adecuado. Normalmente recibo un montón de nuevos estudiantes cuando se acerca la Generosidad del Peregrino.', `text0_1_loc7` = 'Hola, $N. Si buscas instrucción en cocina, has venido al lugar adecuado. Normalmente recibo un montón de nuevos estudiantes cuando se acerca la Generosidad del Peregrino.' WHERE `entry` = 14627;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has venido a celebrar la Generosidad del Peregrino con nosotros?' WHERE `entry` = 14630;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$GBienvenido:Bienvenida;! ¿Has venido para unirte a un festín de la Generosidad del Peregrino? Siéntate en una mesa generosa.$B$B¡Pruébalo todo! Estoy segura de que encontrarás algo que te guste.' WHERE `entry` = 14633;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡$GBienvenido:Bienvenida;! ¿Has venido para unirte a un festín de la Generosidad del Peregrino? Siéntate en una mesa generosa.$B$B¡Pruébalo todo! Estoy segura de que encontrarás algo que te guste.' WHERE `entry` = 14634;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta fiesta contrasta con el comportamiento típico de los humanos. ¿Quién hubiera pensado que una gente tan implacablemente voraz crearía una celebración basada en dar y compartir?$B$BEs agradable verlo. Darnassus debería hacer todo lo que sea posible para apoyarla y alentarla.', `text0_1_loc7` = 'Esta fiesta contrasta con el comportamiento típico de los humanos. ¿Quién hubiera pensado que una gente tan implacablemente voraz crearía una celebración basada en dar y compartir?$B$BEs agradable verlo. Darnassus debería hacer todo lo que sea posible para apoyarla y alentarla.' WHERE `entry` = 14641;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los orcos y los tauren tienen suerte de haberse encontrado los unos a los otros, ya que los puntos fuertes de unos complementan a los otros. Nos alegra unirnos a ellos en esta celebración.$B$BIncluso los renegados toman parte en la celebración, aunque no sé con qué finalidad...', `text0_1_loc7` = 'Los orcos y los tauren tienen suerte de haberse encontrado los unos a los otros, ya que los puntos fuertes de unos complementan a los otros. Nos alegra unirnos a ellos en esta celebración.$B$BIncluso los renegados toman parte en la celebración, aunque no sé con qué finalidad...' WHERE `entry` = 14642;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'En cada mesa generosa se sirven cinco comidas de la Generosidad del Peregrino, una delante de cada silla.$B$BMientras estés en una de esta sillas, podrás comer tanto como quieras, además de pasar raciones de esa comida a las demás sillas. ¡Come y comparte con los amigos!$B$BSi comes suficiente de una comida, obtendrás beneficios únicos de esa comida.$B$B¡Además, si comes suficiente de cada comida, obtendrás El espíritu de compartir!' WHERE `entry` = 14648;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontrarás a Krendle Bolsones cerca del pedestal del zepelín en el lateral oeste del Alto de los Espíritus. No me hace demasiada gracia que el artilugio goblin vuele por aquí... pero te llevará a Orgrimmar si eres capaz de soportarlo.', `text0_1_loc7` = 'Encontrarás a Krendle Bolsones cerca del pedestal del zepelín en el lateral oeste del Alto de los Espíritus. No me hace demasiada gracia que el artilugio goblin vuele por aquí... pero te llevará a Orgrimmar si eres capaz de soportarlo.' WHERE `entry` = 14658;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El verdadero cruzado es más que un héroe en la lucha contra el mal... es un modelo, un abanderado de la causa, un campeón a los ojos de su gente.$B$BComo Alto Cruzado, es mi deber otorgar misiones dignas a esos hombres y mujeres.' WHERE `entry` = 14659;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hay algunas recompensas que solo son accesibles a quienes han conseguido el título de Cruzado, exaltados tanto a los ojos de la Cruzada Argenta como a los de sus aliados.$B$BEl propio Tirion me ha encargado que encuentre a esos héroes y les otorgue las condecoraciones pertinentes.' WHERE `entry` = 14661;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡$GBienvenido:Bienvenida;, $Gcampeón:campeona;! ¿Has reunido tus tropas como preparación para los retos de la Prueba del Cruzado?' WHERE `entry` = 14664;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Entonces, ¿estáis preparados para el primer reto?' WHERE `entry` = 14665;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás $Gpreparado:preparada; para tu primer reto, $C?' WHERE `entry` = 14668;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todavía no eres suficientemente fuerte para luchar en la Isla de la Conquista. Vuelve cuando hayas ganado más experiencia.', `text0_1_loc7` = 'Todavía no eres suficientemente fuerte para luchar en la Isla de la Conquista. Vuelve cuando hayas ganado más experiencia.' WHERE `entry` = 14669;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Isla de la Conquista está repleta de valiosos recursos que no podemos permitir que caigan en manos de la Horda. ¿Te unirás a la lucha?', `text0_1_loc7` = 'La Isla de la Conquista está repleta de valiosos recursos que no podemos permitir que caigan en manos de la Horda. ¿Te unirás a la lucha?' WHERE `entry` = 14670;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Isla de la Conquista está repleta de valiosos recursos que no podemos permitir que caigan en manos de la Alianza. ¿Te unirás a la lucha?', `text0_1_loc7` = 'La Isla de la Conquista está repleta de valiosos recursos que no podemos permitir que caigan en manos de la Alianza. ¿Te unirás a la lucha?' WHERE `entry` = 14671;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Queremos que todo el mundo se divierta en la Fiesta de la cerveza. También nos ayudaría que la gente lo recordara.$B$BEstas gafas permiten que cualquiera experimente lo que es estar boracho, pero sin tener que beber. ¡Ponte las gafas y estarás $Gborracho:borracha;! ¡Quítatelas y estarás $Gsobrio:sobria;!$B$B¿Te interesa un par?', `text0_1_loc7` = 'Queremos que todo el mundo se divierta en la Fiesta de la cerveza. También nos ayudaría que la gente lo recordara.$B$BEstas gafas permiten que cualquiera experimente lo que es estar boracho, pero sin tener que beber. ¡Ponte las gafas y estarás $Gborracho:borracha;! ¡Quítatelas y estarás $Gsobrio:sobria;!$B$B¿Te interesa un par?' WHERE `entry` = 14672;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Todavía no estás $Gpreparado:preparada; para entrar en un campo de batalla, $C. Vuelve cuando hayas ganado más experiencia.', `text0_1_loc7` = 'Todavía no estás $Gpreparado:preparada; para entrar en un campo de batalla, $C. Vuelve cuando hayas ganado más experiencia.' WHERE `entry` = 14673;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás $Gpreparado:preparada; para luchar por la Horda, $C?', `text0_1_loc7` = '¿Estás $Gpreparado:preparada; para luchar por la Horda, $C?' WHERE `entry` = 14674;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás $Gpreparado:preparada; para luchar por la Alianza, $C?', `text0_1_loc7` = '¿Estás $Gpreparado:preparada; para luchar por la Alianza, $C?' WHERE `entry` = 14675;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Bien hecho! ¡Has luchado contra esas bestias de forma espléndida! ¡He oído que el siguiente reto será más duro!' WHERE `entry` = 14678;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'De momento estamos reteniendo a Lord Jaraxxus, pero no podremos mantenerlo amarrado mucho tiempo más.' WHERE `entry` = 14679;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Pronto lo descubrirás. ¿Estás $Gpreparado:preparada; para el siguiente reto?' WHERE `entry` = 14680;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Eso es genial, tan solo hazme una señal para que detenga los hechizos que lo retienen.' WHERE `entry` = 14682;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Estás $Gpreparado:preparada; para tu primer reto, $C?' WHERE `entry` = 14688;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Vienes para aprender a montar?' WHERE `entry` = 14689;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<¡El hipogrifo parece ansioso y listo para partir!>' WHERE `entry` = 14692;
UPDATE `locales_npc_text` SET `text1_0_loc7` = '$gBienvenido:Bienvenida; a nuestra tienda. Tenemos todo lo que necesitas... si necesitas suministros de alquimia.' WHERE `entry` = 14712;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los espíritus de los elementos son sabios. Estaban aquí antes que nosotros y continuarán mucho después de que nos hayamos ido. Cuando nos hablan, debemos escuchar.' WHERE `entry` = 14723;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los espíritus de los elementos nos rodean. Debemos escuchar siempre. He visto de primera mano las consecuencias de ignorar sus avisos...' WHERE `entry` = 14724;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay quienes desean dejar de ganar experiencia con sus acciones, mantenerse por siempre en el nivel de fuerza en el que se encuentran ahora y no volverse más fuertes.$B$BPuedo hacerlo posible... pero tiene un precio, por supuesto.$B$BPero que quede claro: cuando me pagues, ya NO GANARÁS EXPERIENCIA. Nada. Ni un poquito.$B$BSi cambias de idea después y quieres volver a ganar experiencia de nuevo, puedo deshacer el proceso por el mismo precio.', `text0_1_loc7` = 'Hay quienes desean dejar de ganar experiencia con sus acciones, mantenerse por siempre en el nivel de fuerza en el que se encuentran ahora y no volverse más fuertes.$B$BPuedo hacerlo posible... pero tiene un precio, por supuesto.$B$BPero que quede claro: cuando me pagues, ya NO GANARÁS EXPERIENCIA. Nada. Ni un poquito.$B$BSi cambias de idea después y quieres volver a ganar experiencia de nuevo, puedo deshacer el proceso por el mismo precio.' WHERE `entry` = 14736;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ha sido una demostración impresionante. Pero, ¿estás $Gpreparado:preparada; para el siguiente reto?' WHERE `entry` = 14737;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hoy has impresionado a todo el mundo con tus habilidades en la arena. Ya es hora de que todos te conozcan como $Gel campeón:la campeona; del torneo.' WHERE `entry` = 14738;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El viento corta esta cima despiadadamente y la vida ha huido de este rincón de Corona de Hielo. Sin embargo, solo hará falta un poco de coacción para que regrese y por eso camino por estas tierras. Algún día, estos árboles se alzarán sobre nosotros.' WHERE `entry` = 14751;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El primer reto requiere que estés montado sobre un caballo de guerra Argenta. Encontrarás estas monturas a lo largo de los muros del Coliseo.$B$BMonta el caballo de guerra, habla conmigo y entonces podremos continuar.' WHERE `entry` = 14757;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los magnatauros no son tan estúpidos como parecen. Retener a Gormok hasta que llegue su momento no es tarea fácil, pero está tan bien atado que no hay riesgo.' WHERE `entry` = 14759;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Yo me encargo de estas criaturas de Gormok ya que él no puede. No estoy totalmente de acuerdo con esta matanza, pero al menos tendrá la oportunidad de luchar.' WHERE `entry` = 14760;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El yeti es una criatura feroz y astuta, y este es uno de los más fuertes de su especie. Lo mantenemos dormido echándole hierbas en la comida.$B$BPero no creas que estará en letargo cuando salte a la arena.' WHERE `entry` = 14761;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Buenos días, $N. Si necesitas mis servicios como subastador, siento informarte de que no. Solo los grandes maestros ingenieros poseen la capacidad intelectual mínima para interactuar con mis funciones superiores.$B$BLo que es aún más importante, ¿por qué sigues hablándome?' WHERE `entry` = 14764;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C.$B$BActualmente la Horda controla la Fortaleza de Conquista del Invierno. Cuando la batalla esté a punto de comenzar, habla conmigo para unirte.$B$BLa siguiente batalla comenzará en $4354k.', `text0_1_loc7` = 'Saludos, $C.$B$BActualmente la Horda controla la Fortaleza de Conquista del Invierno. Cuando la batalla esté a punto de comenzar, habla conmigo para unirte.$B$BLa siguiente batalla comenzará en $4354k.' WHERE `entry` = 14775;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hemos estado encargándonos de estos gusanos desde hace algún tiempo. Espero que estén en forma a tiempo para el evento de la arena de esta organización. Después de todo debes enfrentarte a dos jormungar, no uno.' WHERE `entry` = 14779;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C.$B$BActualmente la Alianza controla la Fortaleza de Conquista del Invierno, pero la batalla ha comenzado. ¡Ve corriendo a Conquista del Invierno y ayuda a nuestras tropas!', `text0_1_loc7` = 'Saludos, $C.$B$BActualmente la Alianza controla la Fortaleza de Conquista del Invierno, pero la batalla ha comenzado. ¡Ve corriendo a Conquista del Invierno y ayuda a nuestras tropas!' WHERE `entry` = 14781;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C.$B$BActualmente la Alianza controla la Fortaleza de Conquista del Invierno. Cuando la batalla esté a punto de comenzar, habla conmigo para unirte.$B$BLa siguiente batalla comenzará en $4354k.', `text0_1_loc7` = 'Saludos, $C.$B$BActualmente la Alianza controla la Fortaleza de Conquista del Invierno. Cuando la batalla esté a punto de comenzar, habla conmigo para unirte.$B$BLa siguiente batalla comenzará en $4354k.' WHERE `entry` = 14782;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Buenos días, $Gseñor:señora;! ¿En qué puedo ayudarle?' WHERE `entry` = 14783;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta brecha en el tejido del espacio y el tiempo no presagia nada bueno. Distintas escenas pasan a una velocidad aterradora. Puede que una persona muy valiente intente atravesarla en el momento adecuado...' WHERE `entry` = 14785;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Tú! ¡Sí, tú!$B$BMírame y aprecia este momento de encuentro, pues pronto el mundo conocerá y temerá el nombre y el severo rostro de Wilfred Chispobang. ¡En cuanto mi magnífica invocación se complete, nadie dudará de mi habilidad!' WHERE `entry` = 14787;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $C.$B$BActualmente la Horda controla la Fortaleza de Conquista del Invierno, pero la próxima batalla comenzará pronto. ¡Prepárate para ir a Conquista del Invierno y apoyar a nuestras tropas!$B$BLa siguiente batalla comenzará en $4354k..', `text0_1_loc7` = 'Saludos, $C.$B$BActualmente la Horda controla la Fortaleza de Conquista del Invierno, pero la próxima batalla comenzará pronto. ¡Prepárate para ir a Conquista del Invierno y apoyar a nuestras tropas!$B$BLa siguiente batalla comenzará en $4354k..' WHERE `entry` = 14790;
