UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El desollador de aquí se llama Killian Hagey. Trabaja en el Barrio de los Pícaros. En el anillo central.', `text0_1_loc7` = 'El desollador de aquí se llama Killian Hagey. Trabaja en el Barrio de los Pícaros. En el anillo central.', `text1_0_loc7` = 'Si quieres aprender a desollar cosas, dirígete al Barrio de los Pícaros. El desollador tiene su puesto en el anillo interior.', `text1_1_loc7` = 'Nosotros tener gancho desollador. Nos dio Killian Hagey que nos enseña a desollar. Tú tener piel bonita. Lo encontrarás en el anillo interior del Barrio de los Pícaros.' WHERE `entry` = 14923;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si realmente confiarías a tus mascotas a algún no-muerto, puedes hablar con Anya Maulray en la posada, en el anillo superior del Barrio del Comercio.', `text0_1_loc7` = 'Si realmente confiarías a tus mascotas a algún no-muerto, puedes hablar con Anya Maulray en la posada, en el anillo superior del Barrio del Comercio.', `text1_0_loc7` = 'Puedes hablar con Anya Maulray en la posada, en el anillo superior del Barrio del Comercio. Yo no te lo recomendaría, pero son tus mascotas.', `text1_1_loc7` = 'Puedes hablar con Anya Maulray en la posada, en el anillo superior del Barrio del Comercio. Yo no te lo recomendaría, pero son tus mascotas.' WHERE `entry` = 14924;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El sastre de aquí es Josef Gregorian. Se encuentra en el Barrio de la Magia, en el anillo interior.', `text0_1_loc7` = 'El sastre de aquí es Josef Gregorian. Se encuentra en el Barrio de la Magia, en el anillo interior.', `text1_0_loc7` = 'Ve al Barrio de la Magia y dirígete al anillo interior. El sastre está allí.', `text1_1_loc7` = 'Ve al Barrio de la Magia y dirígete al anillo interior. El sastre está allí.' WHERE `entry` = 14925;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tienes muchas agallas al preguntarme acerca de los brujos. Ve al anillo exterior del Barrio de la Magia y desaparece de mi vista.', `text0_1_loc7` = 'Tienes muchas agallas al preguntarme acerca de los brujos. Ve al anillo exterior del Barrio de la Magia y desaparece de mi vista.', `text1_0_loc7` = 'Encontrarás a los brujos en el edificio principal del Barrio de la Magia, pero más vale que no me entere de que esto genera algún problema.', `text1_1_loc7` = 'Encontrarás a los brujos en el edificio principal del Barrio de la Magia, pero más vale que no me entere de que esto genera algún problema.' WHERE `entry` = 14926;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los guerreros están en el Barrio de la Guerra, practicando en el anillo exterior.', `text0_1_loc7` = 'Los guerreros están en el Barrio de la Guerra, practicando en el anillo exterior.', `text1_0_loc7` = 'En el Barrio de la Guerra es donde encontrarás a los guerreros. Dirígete al anillo exterior allí.', `text1_1_loc7` = 'En el Barrio de la Guerra es donde encontrarás a los guerreros. Dirígete al anillo exterior allí.' WHERE `entry` = 14927;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Si quieres aprender a blandir armas, ve a ver a Archibald al anillo interior del Barrio de la Guerra.', `text0_1_loc7` = 'Si quieres aprender a blandir armas, ve a ver a Archibald al anillo interior del Barrio de la Guerra.', `text1_0_loc7` = 'Me alegra ver que alguien quiere aprender algo útil. Ve a ver a Archibald en el anillo interior del Barrio de la Guerra.', `text1_1_loc7` = 'Me alegra ver que alguien quiere aprender algo útil. Ve a ver a Archibald en el anillo interior del Barrio de la Guerra.' WHERE `entry` = 14928;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No te culpo por querer salir aquí. Sube y dirígete hacia el norte de las ruinas. Los zepelines salen desde las dos torres de allí.', `text0_1_loc7` = 'No te culpo por querer salir aquí. Sube y dirígete hacia el norte de las ruinas. Los zepelines salen desde las dos torres de allí.', `text1_0_loc7` = 'Las torres de zepelín están justo al norte de Entrañas.', `text1_1_loc7` = 'Las torres de zepelín están justo al norte de Entrañas.' WHERE `entry` = 14929;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Este pozo apesta y estoy rodeado de serpientes, pero he venido a trabajar.$B$BDesde que Putress montó su numerito en la Puerta de Cólera, el Jefe de Guerra no se fía un pelo de esta gente. Por eso estamos aquí. Los boticarios no pueden hacer nada sin mí.' WHERE `entry` = 14934;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Es verdad que nuestros nuevos... invitados están limitando un poco nuestro trabajo, pero es normal no fiarse cuando ha habido un intento de genocidio. Quizá con el tiempo se convenzan de que Putress fue una desafortunada anomalía y nos permitan continuar con nuestro trabajo en paz.' WHERE `entry` = 14940;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Supongo que la fría bienvenida no debería sorprenderme. Llevamos tiempo escondiéndonos y la separación de nuestros hermanos fue necesaria.$B$BDe todos modos, ese tiempo ha pasado. Tanto si los habitantes de este joven árbol nos aceptan o no, los Altonato regresamos a la luz y no daremos la espalda a lo que somos.' WHERE `entry` = 14984;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Deberías buscar en Entrañas. En las antiguas ruinas de Lordaeron, al sur de este lugar, encontrarás un elevador que baja. Cuando estés allí, pide a un guardián de Entrañas que te diga cómo llegar.', `text0_1_loc7` = 'Deberías buscar en Entrañas. En las antiguas ruinas de Lordaeron, al sur de este lugar, encontrarás un elevador que baja. Cuando estés allí, pide a un guardián de Entrañas que te diga cómo llegar.' WHERE `entry` = 15005;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay quienes no creen en los legendarios elfos de la noche mohicanos.$B$BEstán como una maraca.' WHERE `entry` = 15037;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida;, $Ghermano mohicano:hermana mohicana;.' WHERE `entry` = 15038;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Debido a un, eh, \"malentendido\" en el Cementerio de Dragones hemos tenido que trasladar nuestras instalaciones a este castillo decrépito.$b$bAunque ahora tendremos espacio de sobra para trabajar en nuevos y encantadores... productos...' WHERE `entry` = 15040;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El primer reto requiere que estés $Gmontado:montada; sobre un huargo de batalla Argenta. Encontrarás estas monturas a lo largo de los muros del Coliseo.$B$BMonta en el huargo y habla conmigo para que podamos continuar.' WHERE `entry` = 15043;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Te sorprende verme aquí? Todo el mundo necesita ropa limpia y ¿qué mejor manera de lavarla que con tu propio elemental de agua?$B$BTan solo trae tu colada, paga una tarifa simbólica y antes de que te des cuenta, todo estará limpio.' WHERE `entry` = 15066;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Qué haces aquí, $C?' WHERE `entry` = 15074;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No te fíes de los Atracasol. Están trabajando muy duro para desautorizarnos ante los ojos de nuestros aliados de la Cruzada Argenta.' WHERE `entry` = 15076;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No puedo retenerlo mucho tiempo, ¡debemos marcharnos!' WHERE `entry` = 15081;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Esta es nuestra batalla final. El paso del tiempo se hará eco de lo que aquí ocurra. Sin importar el resultado, sabrán que luchamos con honor. ¡Que luchamos por la libertad y la seguridad de nuestro pueblo!$B$BRecuerda, $Ghéroe:heroína;, el miedo es el mayor enemigo en estas cámaras infectas. Fortalece tu corazón y tu alma brillará más que mil soles. El enemigo vacilará al verte. ¡Caerán en cuanto la luz de la rectitud los envuelva!' WHERE `entry` = 15103;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Pero si eres $Gel:la:c; $C que estaba buscando! Estoy probando un nuevo dispositivo volador propulsado por cohetes y necesito tu ayuda.$B$BCoge uno de estos arneses y colócalo bajo tu armadura. Pulsa el botón del lado, y BUM, ¡allá vas!$B$BNo intentes robarme el trabajo, ¿eh? Estas bellezas tienen integrado un transponstador 8000 vinculado al de mi mano. Si te alejas demasiado, se desactivará el cohete principal y no podrás despegar hasta que vuelvas.$B$BVeamos, ¿dónde he metido los contratos de responsabilidad?' WHERE `entry` = 15134;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Yo esperaré aquí hasta que lleguen refuerzos, pero vosotros debéis seguir. No tenemos mucho tiempo antes de que Arthas se dé cuenta de que estamos aquí.' WHERE `entry` = 15152;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No puedo retenerlo mucho tiempo, ¡debemos marcharnos!' WHERE `entry` = 15157;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Prepárate, $C.$B$BHa llegado el momento de que termine el reinado del Rey Exánime.' WHERE `entry` = 15158;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Veredicto Cinéreo es un grupo selecto de los artesanos con más talento de la Espada de Ébano y la Cruzada Argenta. Su creación simboliza la unión de nuestras fuerzas a medida que ascendemos hacia el asalto final contra el Rey Exánime y sus esbirros.$B$BEs gracias al esfuerzo del Veredicto Cinéreo que hemos descubierto como amoldar la saronita primordial a nuestra voluntad.' WHERE `entry` = 15159;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Habla con el Alquimista Finklestein, $C. Puede guiarte por el buen camino.$B$BDemuestra tu lealtad y te revelará sus métodos.' WHERE `entry` = 15160;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El Rey Exánime está cerca, $N. Su voz me provoca...$B$B¡Acallémoslo de una vez!' WHERE `entry` = 15166;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Eh! Vengo en representación de Químicos La Corona, S.L.$b$b¿Buscas un regalo? ¿Un trabajo? ¿Algo para ti? Bueno, has venido al lugar adecuado, $gamigo:amiga;.$b$bSi no tengo lo que buscas, nuestra mascota Kwee o la mercader preciosa a mi lado lo tendrán.' WHERE `entry` = 15170;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Utilizaremos estos suministros para preparar a todos para el combate.$B$BSeguid adelante. Os encontraremos más adelante.' WHERE `entry` = 15171;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Os seguiremos, héroes. El Señor de la Plaga morirá hoy.' WHERE `entry` = 15174;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Perdonadme, héroes. Debí escuchar a Uther. Tenía… tenía que… verlo por mí misma. Poder mirarle a los ojos una última vez. Lo siento.' WHERE `entry` = 15189;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Estamos a salvo… por ahora. Su fuerza se ha multiplicado desde nuestra última batalla. Necesitamos un ejército poderoso para destruir al Rey Exánime, mejor incluso que el que la Horda puede convocar.' WHERE `entry` = 15190;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Yo esperaré aquí hasta que lleguen refuerzos, pero vosotros debéis seguir. Pronto Arthas se dará cuenta de que estamos aquí.' WHERE `entry` = 15191;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No tenemos tiempo que perder, $C. ¿Qué haces aún aquí?' WHERE `entry` = 15206;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'No tenemos mucho tiempo, $Ghéroe:heroína;. ¿Qué necesitas?' WHERE `entry` = 15207;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has oído hablar de nuestro programa \"Talismanes caritativos\"?$B$BLas mascotas oficiales de eventos de La Corona, S.L. como yo podemos distribuir Juegos de coleccionista de talismanes preciosos.$B$BPuedes usar los talismanes preciosos para crear pulseras de talismanes preciosos para tus amigos y amados, o entregárselas a mercaderes preciosas a cambio de pruebas de amor.$B$BUna actividad popular es crear pulseras para tus líderes preferidos para que sepan que se les valora y adora...' WHERE `entry` = 15212;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Lo que necesites, Brazie Lopillas! ¿Lo pillas?$B$BY no solo hablo de las cosas \"legales\". ¡LO QUE NECESITES, $GAMIGO:AMIGA;! Lo que sea...' WHERE `entry` = 15214;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Al estar tan cerca de la espada que puso fin a mi vida… El dolor… vuelve.' WHERE `entry` = 15215;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He oído rumores de que mi hijo está aquí. Que ahora es un poderoso caballero de la Muerte en servicio del Rey Exánime. Si es cierto, la línea de descendencia acaba en mí.$B$BMi tarea ahora es dirigir el Martillo de Orgrim a la Ciudadela de la Corona de Hielo y destruir toda la Plaga en el camino. Partimos cuando tus aliados y tú estéis listos.' WHERE `entry` = 15219;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El teletransportador brilla con luz cristalina.', `text0_1_loc7` = 'El teletransportador brilla con luz cristalina.' WHERE `entry` = 15221;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No me gusta la pinta de estos Químicos La Corona, S.L. ni un poco.' WHERE `entry` = 15256;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una herida que me infligí, $Gamigo:amiga;. No podía soportar ver un mundo en el que yo había cometido tantos horrores bajo la presión del Rey Exánime.$B$BFue mi primer paso hacia la redención, y si mis pecados son imperdonables, entonces intentaré compensarlos durante el resto de mi vida.' WHERE `entry` = 15297;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Una vez fui un paladín, $N. Ahora la Luz me rechaza, pues mis pecados son muchos.$B$BAyudaré a mis aliados lo mejor que pueda, pero nunca más utilizaré los poderes de la oscuridad.' WHERE `entry` = 15298;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Varimathras? No, no ha presentado informes de estado desde hace tiempo. Debe estar haciendo algo importante para su reina.' WHERE `entry` = 15319;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Como gran parte de las fuerzas de Entrañas se han desplegado a Campo Venganza en Rasganorte, el Jefe de Guerra nos ha enviado para reforzar las defensas de la ciudad.', `text0_1_loc7` = 'Como gran parte de las fuerzas de Entrañas se han desplegado a Campo Venganza en Rasganorte, el Jefe de Guerra nos ha enviado para reforzar las defensas de la ciudad.' WHERE `entry` = 15321;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'La Agonía de Escarcha: la espada que destrozó nuestro reino.' WHERE `entry` = 15339;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $R. Mi presencia en este lugar me confunde. El resto tiene sospechas también, y no los culpo, después de lo que pasaron mis hermanos.$B$BHan pasado muchas cosas desde La Fuente del Sol, casi nada bueno. Y cae sobre mí tener que recoger los pedazos, e intentar rescatar el orgullo de mi vuelo. Hay mucho que decir, pero mucho más que hacer. Hay que saber que no todo el Vuelo Azul culpa a las razas mortales de Azeroth de los problemas que han sucedido en el mundo.$B$BAlgunos de nosotros tenemos algo más de sentido que todo eso.'  WHERE `entry` = 15381;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Eres $gastuto:astuta; al preguntar, joven $c.  Los escribas son capaces de inscribir cartas de tarot de gran poder, aunque no tienen total control sobre lo que hacen. El destino decide por ellos.$b$bSi consigues una baraja completa, tráemela y obtendrás una magnífica recompensa.' WHERE `entry` = 15400;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En cualquier sitio menos aquí...' WHERE `entry` = 15432;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura tejeazote de Freya para druidas.', `text0_1_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura tejeazote de Freya para druidas.' WHERE `entry` = 15445;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura bruja de escarcha para chamanes.', `text0_1_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura bruja de escarcha para chamanes.' WHERE `entry` = 15446;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura Hoja de las Sombras para pícaros.', `text0_1_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura Hoja de las Sombras para pícaros.' WHERE `entry` = 15447;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura cazador de sangre de Ahn\'kahar para cazadores.', `text0_1_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura cazador de sangre de Ahn\'kahar para cazadores.' WHERE `entry` = 15449;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura de señor Ymirjar para guerreros.' WHERE `entry` = 15450;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura juraluz para paladines.', `text0_1_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura juraluz para paladines.' WHERE `entry` = 15451;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura de acólito carmesí para sacerdotes.', `text0_1_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura de acólito carmesí para sacerdotes.' WHERE `entry` = 15452;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura de mago sangriento para magos.', `text0_1_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura de mago sangriento para magos.' WHERE `entry` = 15453;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura Aquelarre oscuro para brujos.', `text0_1_loc7` = 'Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura Aquelarre oscuro para brujos.' WHERE `entry` = 15454;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Prepárate, $R.$B$BLos nagas pueden atacar en cualquier momento.', `text1_0_loc7` = 'Esta región es uno de los epicentros de un cataclismo que amenaza con desgarrar nuestro mundo.$B$BSi conseguimos sobrevivir, debemos intentar descubrir su causa.', `text2_0_loc7` = 'Los ataques que han sufrido estos barcos son claramente intencionados.$B$BDebemos descubrir qué interés tienen los nagas en todo esto.'  WHERE `entry` = 15569;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'El continuo sacrificio del Veredicto Cinéreo y de los héroes como tú nos acerca a todos a la victoria final. He venido a la Ciudadela de la Corona de Hielo para prestar mi ayuda a aquellos que luchan contra la Plaga. ¡Juntos acabaremos con la tiranía del Rey Exánime!' WHERE `entry` = 15602;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Dices que podéis hacerlo solos? ¿Esto que veo ante mí es pura necedad o confianza infundada?$B$BNo, lo veo en vuestros ojos. Tenéis que saber. Tenéis que demostrar que podéis hacerlo solos. Bien. Me retiraré del campo de batalla si así lo deseáis.' WHERE `entry` = 15603;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Os hará falta algo más que coraje para derrotar al Rey Exánime. Buena suerte.' WHERE `entry` = 15604;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Habéis tomado una decisión. Ahora os corresponde vivir o morir por ella.' WHERE `entry` = 15605;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ahora que la Ofensiva Grito de Guerra está en la Ciudadela de la Corona de Hielo, la victoria está a nuestro alcance! ¡La muerte del Rey Exánime dará gloria a la Horda!' WHERE `entry` = 15606;
UPDATE `locales_npc_text` SET `text0_1_loc7` = '¡Claro! ¡Sí que te había visto aquí antes! ¿Cómo he podido olvidarlo? Puedo teletransportarte a Stratholme ahora si quieres.' WHERE `entry` = 15704;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Ugh...$B$BNo me encuentro... muy bien...'  WHERE `entry` = 15769;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '<Tose.>$B$B¿He... muerto?'  WHERE `entry` = 15783;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Te digo que Coren Cerveza Temible está loco.$B$B¡No es culpa MÍA que no le hayan invitado a competir en la Fiesta de la cerveza! Pero eso no ha impedido que él y sus colegas del Hierro Negro hayan dado una paliza a mis empleados y se hayan bebido toda mi cerveza.$B$B¡Alguien tiene que poner fin a esto!'  WHERE `entry` = 15853;
UPDATE `locales_npc_text` SET `text0_1_loc7` = 'Hay que detener al Señor de la Escarcha Ahune.$BEn estos mismos instantes está tomando forma al ser invocado de su prisión elemental por los cultores locos del Martillo Crepuscular.$BSi consigue entrar en nuestro mundo por completo, nos enterrará bajo el manto de un invierno perpetuo.' WHERE `entry` = 15855;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Alguien debe detener a Químicos La Corona S.L. Apuesto mi cohete abisal chapado en oro a que están fabricando la próxima gran plaga con el dinero de los ciudadanos adictos al chocolate...$B$B¡Ojalá encontrara a alguien que entregue los papeles del juzgado a sus boticarios! Entonces acabaríamos con esto... y ya que estamos, ¡quizá podríamos confiscar sus activos!' WHERE `entry` = 15857;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Te gustará Ciudad Manitas!' WHERE `entry` = 15863;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'En este fragmento de hielo retumba el eco del estruendo profundo y sonoro de los glaciares que se desplazan. El frío transforma tus pensamientos en un invierno eterno y sin vida.' WHERE `entry` = 15864;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Encontrarás a un par de draenei conversando con Tyrande Susurravientos en el piso superior del Templo de la Luna.$B$BEllos sabrán cómo ayudarte.', `text0_1_loc7` = 'Encontrarás a un par de draenei conversando con Tyrande Susurravientos en el piso superior del Templo de la Luna.$B$BEllos sabrán cómo ayudarte.' WHERE `entry` = 15906;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Dedicado a quienes cayeron ante la Plaga en la guerra de los Páramos Congelados.' WHERE `entry` = 15921;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Tengo que encontrar un lugar seguro pronto.$b$bEl viejo Mack no está tan bien.'  WHERE `entry` = 15943;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Aquí estamos aún más al descubierto que antes, $R.$B$B¡Debemos encontrar un refugio seguro cuanto antes!', `text1_0_loc7` = 'Debemos encontrar a tus aliados y descubrir los planes de los nagas para esta región.$B$BPero antes debemos encontrar un lugar más seguro.', `text2_0_loc7` = 'Tengo la impresión de que no alcanzamos a comprender realmente lo que ocurre con los nagas en este lugar.$B$BAntes nunca los había visto tan organizados ni tan disciplinados.'  WHERE `entry` = 16015;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Parece ser que estamos enfrentándonos a algo mayor que lo que cualquiera del Anillo de Tierra haya podido sospechar.', `text1_0_loc7` = 'Hay una gran concentración de nagas en el exterior de esa caverna.$B$BHemos tenido mucha suerte de haber podido llegar hasta aquí sin llamar su atención.'  WHERE `entry` = 16032;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los elemento\' gritan de ira. No es una coincidencia que los nagas eh\'tén aquí. Eh\'peran algo.'  WHERE `entry` = 16203;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La guerra de Rasganorte nos pasa factura, $N. Mientras salíamos victoriosos en los baldíos helados, nuestras tierras caían en la confusión y el desorden.'  WHERE `entry` = 16275;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has venido para entrenar, $Gcazador:cazadora;?', `text0_1_loc7` = '¿Has venido para entrenar, $Gcazador:cazadora;?'  WHERE `entry` = 16570;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Hay dos: la Sala de mercaderes en el Distrito de Mercaderes y la Casa de subastas de Ventormenta en el Distrito de los Enanos.', `text0_1_loc7` = 'Hay dos: la Sala de mercaderes en el Distrito de Mercaderes y la Casa de subastas de Ventormenta en el Distrito de los Enanos.'  WHERE `entry` = 16601;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Nos atacan!'  WHERE `entry` = 16612;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los nagas no se rendirán. Tenemo\' que encontrar a nueh\'tros amigos y lucha\' junto a ellos.'  WHERE `entry` = 16613;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '$GBienvenido:Bienvenida; a Ventormenta, $C.'  WHERE `entry` = 16641;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡La Cruzada Escarlata regresará a sus nobles raíces!'  WHERE `entry` = 16663;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Weldon no es más que un maldito cobarde. Huyó de la Plaga, ¿y ahora se atreve a venir como si fuera un héroe?$B$B¡Bah!'  WHERE `entry` = 16742;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No escuches nada de lo que diga Alexi. Fue un idiota al quedarse durante la peste, y ahora míralo: no es mejor que las demás abominaciones del lugar.$B$BNo te preocupes, recuperaremos Scholomance y las tierras circundantes para el último hijo viviente de Barov: su heredero legítimo.'  WHERE `entry` = 16743;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Armas... Armadura... Son las claves de la victoria. Todos los guerreros, incluso los más fuertes, están indefensos sin ellas.$b$bEn mis tiempos creé muchas leyendas, $r. No verás nada igual en todo Azeroth. Son la clave del éxito de mis hermanos.'  WHERE `entry` = 16779;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'He oído hablar mucho de ti, $N. El mundo necesita gente como tú, ahora más que nunca.'  WHERE `entry` = 16787;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Hermandad de la Luz supera la capacidad de cualquier otro grupo para erradicar las afrentas a la Luz. Tomaremos todas las decisiones que sean necesarias para liberar al mundo de bestias inmundas como estos no-muertos. Nada se interpondrá en nuestro camino.$b$b¿Estás con nosotros o contra nosotros?'  WHERE `entry` = 16796;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Nunca subestimes el poder de la buena gestión de suministros. Puede que los guerreros ganen las batallas, pero los suministros ganan la guerra.'  WHERE `entry` = 16850;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Altonato han regresado, y con ellos hechizos que llevaban siglos perdidos para el mundo.$B$B¿Tienes algo que quieras que examine?', `text0_1_loc7` = 'Los Altonato han regresado, y con ellos hechizos que llevaban siglos perdidos para el mundo.$B$B¿Tienes algo que quieras que examine?'  WHERE `entry` = 16982;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ueeeee! ¡Todo este lugar brilla!$B$BEcha un vistazo a lo que encontrado cavando justo aquí...$B$B¡Es un sombrero de respirar!'  WHERE `entry` = 17016;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¿Has visto mi sombrero de respirar?$B$B¡Budd ya nunca más tendrá que chupar almejas! No señooooor...'  WHERE `entry` = 17020;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'No es tan malo...$B$B¡Todavía tengo mi sombrero de respirar!'  WHERE `entry` = 17027;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Ah, del barco!'  WHERE `entry` = 17029;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡La máquina de guerra de la Horda te necesita!'  WHERE `entry` = 17030;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'La Alianza te necesita. ¡Responde a la llamada del heroísmo!'  WHERE `entry` = 17034;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Siempre hay algo nuevo que investigar.'  WHERE `entry` = 17217;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Los Colmillo de Sangre prevalecerán.'  WHERE `entry` = 17220;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Permanece estable siempre.'  WHERE `entry` = 17222;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Elige un destino.', `text0_1_loc7` = 'Elige un destino.' WHERE `entry` = 17791;
UPDATE `locales_npc_text` SET `text0_0_loc7` = '¡Largo! ¡No deberías estar aquí!'  WHERE `entry` = 17815;
UPDATE `locales_npc_text` SET `text0_0_loc7` = 'Parece que este dispositivo de teletransporte ha sido utilizado recientemente para teletransportar varios objetos a lo más profundo de la mina.', `text0_1_loc7` = 'Parece que este dispositivo de teletransporte ha sido utilizado recientemente para teletransportar varios objetos a lo más profundo de la mina.' WHERE `entry` = 17824;
